INSERT INTO `editcontacts` (`id`, `textfooter`, `phone`, `email`, `address`, `text`, `created_at`, `updated_at`, `image`) VALUES
(1, 'Trên hết, chúng tôi mong muốn đem đến cho các khách hàng, đối tác của mình những sản phẩm, dịch vụ với chất lượng vượt trội. Chọn chúng tôi, bạn hoàn toàn có thể yên tâm từ khi thiết kế đến lúc nhận nhà.', '(+84) 86 889 1253', 'hi@noithattichhop.vn', '05 Hàm Nghi, phường Nguyễn Thái Bình, Quận 1, TP Hồ Chí Minh', 'orem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.', '2018-06-30 23:32:00', '2018-07-01 00:14:32', 'editcontacts/July2018/opYKaA6mYRByJjKfKjS6.png');
INSERT INTO `infos` (`id`, `text1`, `text2`, `text`, `text3`, `created_at`, `updated_at`) VALUES
(1, 'AMONG AGRICULTURAL COMPANIES', 'We are prominent leader in the agribusiness; specializing in the cultivation, production and trading of animal feed and essential human food commodities such as corn, fruits, flour and vegetables. Since its inception, we have witnessed unmatched growth fuelled by an active foreign investment strategy, establishing various acquisitions and joint ventures with specialized feed and food producers worldwide. We own and operate a large asset base including a land bank of 340 thousand acres, 4 forage pressing and production plants and 2 flour milling plants.', 'A REMARKABLE POSITION', 'ĐỌC TIẾP', '2018-06-30 08:52:53', '2018-06-30 08:52:53');
