INSERT INTO `types` (`id`, `title`, `content`, `images`, `created_at`, `updated_at`, `category_id`) VALUES
(1, 'Phong cách bán cổ điển', 'Để thiết kế căn hộ theo phong cách bán cổ điển bạn nên chọn tông màu lạnh hoặc trung tính để thể hiện đúng tính chất của nó. Những món đồ nội thất mang nét cổ điển là điểm nhất trong căn phòng khách này như là: đèn trần, sofa và chân đèn. Tất cả hòa quyện lại tạo nên một tổng thể vô cùng trang nhã và thời thượng.\r\n\r\nGóc bếp được ốp vân đá làm bật lên nét thẩm mỹ trong từng đường nét giữa tông chủ đạo trắng trang nhã. Gạch lót sàn mang hơi hướm cổ điển Châu âu kết hợp cùng thảm hoa văn càng làm cho căn bếp thêm quyến rũ hơn\r\n\r\nPhòng ngủ đậm nét mỹ thuật cổ điển, tông màu lạnh giúp bạn cảm thấy như diện tích được mở rộng hơn, thông thoáng hơn và cảm giác thoải mái hơn cùng nét lãng mạn của đèn trang trí đầy nghệ thuật.', 'types/June2018/1STsKojbHd5q89fVMagP.jpeg', '2018-06-30 10:59:00', '2018-06-30 12:27:14', 20),
(2, 'Nếu như không có thì', 'Để thiết kế căn hộ theo phong cách bán cổ điển bạn nên chọn tông màu lạnh hoặc trung tính để thể hiện đúng tính chất của nó. Những món đồ nội thất mang nét cổ điển là điểm nhất trong căn phòng khách này như là: đèn trần, sofa và chân đèn. Tất cả hòa quyện lại tạo nên một tổng thể vô cùng trang nhã và thời thượng.\r\n\r\nGóc bếp được ốp vân đá làm bật lên nét thẩm mỹ trong từng đường nét giữa tông chủ đạo trắng trang nhã. Gạch lót sàn mang hơi hướm cổ điển Châu âu kết hợp cùng thảm hoa văn càng làm cho căn bếp thêm quyến rũ hơn\r\n\r\nPhòng ngủ đậm nét mỹ thuật cổ điển, tông màu lạnh giúp bạn cảm thấy như diện tích được mở rộng hơn, thông thoáng hơn và cảm giác thoải mái hơn cùng nét lãng mạn của đèn trang trí đầy nghệ thuật.', 'types/June2018/DntMMNZUX44pfix0URoP.jpeg', '2018-06-30 12:20:00', '2018-06-30 12:27:08', 20),
