INSERT INTO `news` (`id`, `title`, `content`, `link`, `images`, `created_at`, `updated_at`) VALUES
(4, 'Hơn 30 KH Đã Giảm 5 - 20kg Nhờ Phác Đồ Giảm Mỡ 90\' J-US UnFAT', 'Sau một tháng đưa phác đồ giảm mỡ 90 phút J-US UnFAT về Việt Nam, hơn 30 khách hàng đã trải nghiệm và tự chứng kiến sự thay đổi đáng kể về cân nặng, số đo các vòng một cách nhanh chóng mà không hề có bất kỳ can thiệp phẫu thuật nào, đảm bảo an toàn tuyệt đối.', '28', 'news/April2018/euRmH1dcOBAdoOoeXcTs.jpg', '2018-04-26 01:37:00', '2018-05-11 03:18:55'),
(6, 'Hơn 15.000 chị em làm đẹp hiệu quả tại Svan Clinic and Spa trong năm 2017', 'Trong năm 2017, hơn 15.000 phụ nữ đã tin tưởng và cùng Svan đồng hành trên con đường theo đuổi sứ mệnh duy trì sắc vóc, khẳng định giá trị của người phụ nữ.', '25', 'news/April2018/o0KjB1WDsjKVcBEBpIu6.jpg', '2018-04-26 01:40:00', '2018-05-11 03:18:40'),
(7, '“Đẹp tự nhiên, nhưng không tự nhiên mà đẹp”', '“Trong xã hội ngày nay, phụ nữ thông minh là phụ nữ biết cách làm đẹp. Đừng đi theo xu hướng mà hãy tạo ra nó! Khi làm được điều đó, bạn sẽ trở nên sáng tạo và khác biệt” – CEO Diệp Bùi.', '2', 'news/May2018/QW788z6RsQ0aE5aMtcAQ.jpg', '2018-04-26 01:41:00', '2018-05-11 03:18:22'),
(8, '“Đẹp tự nhiên, nhưng không tự nhiên mà đẹp”', '“Trong xã hội ngày nay, phụ nữ thông minh là phụ nữ biết cách làm đẹp. Đừng đi theo xu hướng mà hãy tạo ra nó! Khi làm được điều đó, bạn sẽ trở nên sáng tạo và khác biệt” – CEO Diệp Bùi.', '5', 'news/April2018/OoVc0Gfz8Rt25e8NCPX9.jpg', '2018-04-26 01:41:00', '2018-05-11 03:18:32');
