INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(1, '3', 'Đâu là cách dưỡng trắng da mặt nhanh nhất ?', 'Có nhiều bài báo đã bàn về các cách dưỡng trắng da mặt nhanh nhất bằng tự nhiên, nhưng liệu những phương pháp đó có đảm bảo an toàn?', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">C&oacute; nhiều người đ&atilde; ti&ecirc;u tốn rất nhiều v&agrave;o c&aacute;c sản phẩm tr&ecirc;n thị trường như mỹ phẩm, thực phẩm chức năng,&hellip; nhưng vẫn kh&ocirc;ng mang lại hiệu quả. H&atilde;y tham khảo c&aacute;c&nbsp;<strong style=\"box-sizing: border-box;\">c&aacute;ch dưỡng trắng da mặt</strong>&nbsp;nhanh sau đ&acirc;y để biết mẹo l&agrave;m trắng da mặt<strong style=\"box-sizing: border-box;\">&nbsp;</strong>hiệu quả chỉ từ những thứ tự nhi&ecirc;n gần gũi xung quanh ch&uacute;ng ta hằng ng&agrave;y lại chứa nhiều hoạt t&iacute;nh cần thiết cho da trắng hồng tươi mới.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 446.531px;\" src=\"http://www.svanclinic.vn/files/Files/Kien-thuc-lam-dep/%C4%90%C3%A2u%20l%C3%A0%20c%C3%A1ch%20d%C6%B0%E1%BB%A1ng%20tr%E1%BA%AFng%20da%20m%E1%BA%B7t%20nhanh%20nh%E1%BA%A5t/lam-sao-de-da-mat-trang-nhanh.jpg\" alt=\"meo-lam-trang-da-mat\" /><br style=\"box-sizing: border-box;\" /><em style=\"box-sizing: border-box;\">L&agrave;m sao để da mặt trắng nhanh?</em></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">Chỉ cần bỏ ra 15 &ndash; 30 ph&uacute;t mỗi ng&agrave;y bạn thực hiện&nbsp;<strong style=\"box-sizing: border-box;\">những c&aacute;ch l&agrave;m đẹp da mặt</strong>, bạn đ&atilde; c&oacute; thể&nbsp;<strong style=\"box-sizing: border-box;\">l&agrave;m trắng da mặt tự nhi&ecirc;n&nbsp;</strong>rồi đấy. Thực hiện n&agrave;o!</p>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; color: #3ab572;\">Hỗn hợp đường + dầu oliu (loại Extra Virgin) &ndash;&nbsp;<strong style=\"box-sizing: border-box;\">C&aacute;ch l&agrave;m đẹp da mặt đơn giản</strong>!</span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Trộn hai loại lại với nhau theo lượng t&ugrave;y &yacute; rồi thoa nhẹ nh&agrave;ng l&ecirc;n mặt. Nhớ lưu &yacute; tẩy tế b&agrave;o chết cho da mặt trước để dưỡng chất dễ d&agrave;ng thẩm thấu nh&eacute;.</li>\n<li style=\"box-sizing: border-box;\">Dầu oliu kh&ocirc;ng chỉ l&agrave;&nbsp;<strong style=\"box-sizing: border-box;\">c&aacute;ch dưỡng trắng da mặt</strong>&nbsp;m&agrave; c&ograve;n c&oacute; nhiều t&aacute;c dụng kh&aacute;c như dưỡng ẩm body, dưỡng m&ocirc;i, l&agrave;m đẹp t&oacute;c.</li>\n<li style=\"box-sizing: border-box;\">Sử dụng mật ong thay cho dầu oliu nếu da mặt thuộc loại da nhờn.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"2\"><span style=\"box-sizing: border-box; color: #3ab572;\">Chanh &ndash; Nguy&ecirc;n liệu&nbsp;<strong style=\"box-sizing: border-box;\">l&agrave;m trắng da mặt nhanh nhất</strong>!</span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Cho nước tinh khiết v&agrave; lượng nước chanh tươi nhất định với tỉ lệ 10:1 v&agrave;o b&igrave;nh xịt, v&agrave; xịt ch&uacute;ng mỗi ng&agrave;y như một loại xịt kho&aacute;ng.</li>\n<li style=\"box-sizing: border-box;\">Trộn một th&igrave;a sữa chua, mật ong v&agrave; nước chanh theo lượng vừa đủ rồi thoa l&ecirc;n mặt. Sữa chua&nbsp;<strong style=\"box-sizing: border-box;\">l&agrave;m trắng da mặt nhanh</strong>&nbsp;kết hợp với chanh kh&ocirc;ng chỉ gi&uacute;p trắng s&aacute;ng m&agrave; c&ograve;n gi&uacute;p mang lại l&agrave;n da mịn m&agrave;ng như em b&eacute;.</li>\n<li style=\"box-sizing: border-box;\">Ch&uacute; &yacute; chanh kh&ocirc;ng th&iacute;ch hợp với ai c&oacute; l&agrave;n da nhạy cảm.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"3\"><span style=\"box-sizing: border-box; color: #3ab572;\">Mặt nạ dưa chuột &ndash;&nbsp;<strong style=\"box-sizing: border-box;\">C&aacute;ch l&agrave;m đẹp da mặt</strong>&nbsp;ai cũng l&agrave;m được!</span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Cho dưa leo, &frac14; ch&eacute;n nước chanh, 4 muỗng c&agrave; ph&ecirc; mật ong v&agrave;o m&aacute;y xay, l&agrave;m lạnh trong v&ograve;ng 5-6 giờ v&agrave; thoa l&ecirc;n mặt mỗi ng&agrave;y sau khi tắm.</li>\n<li style=\"box-sizing: border-box;\">Đơn giản hơn bằng c&aacute;ch th&aacute;i mỏng dưa chuột th&agrave;nh l&aacute;t rồi đắp trực tiếp l&ecirc;n mặt, để khoảng 15 &ndash; 20 ph&uacute;t rồi rửa lại với nước sạch.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"4\"><span style=\"box-sizing: border-box; color: #3ab572;\">Mặt nạ cam tươi &ndash;&nbsp;<strong style=\"box-sizing: border-box;\">L&agrave;m đẹp da bằng thi&ecirc;n nhi&ecirc;n</strong></span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">&nbsp;Pha &frac14; nước với một lượng nước ấm vừa đủ , thoa l&ecirc;n mặt rồi để từ khoảng 15-20 ph&uacute;t.</li>\n<li style=\"box-sizing: border-box;\">Sau khi d&ugrave;ng cam &eacute;p lấy nước hoặc ăn trực tiếp, c&oacute; thể d&ugrave;ng vỏ cam để l&agrave;m trắng s&aacute;ng da, vỏ cam phơi kh&ocirc; rồi t&aacute;n mịn, th&ecirc;m &iacute;t bột nghệ v&agrave; mật ong để tạo th&agrave;nh hỗn hợp đặc sệt, cam rất tốt trong việc se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng gi&uacute;p da s&aacute;ng bừng l&ecirc;n tr&ocirc;ng thấy.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"5\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">L&agrave;m g&igrave; để da mặt trắng</strong>? H&atilde;y d&ugrave;ng mặt nạ bơ</span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Dằm nhuyễn &frac14; tr&aacute;i bơ với một muỗng mật ong đối với da kh&ocirc; v&agrave; với sữa chua cho da nhờn, ngo&agrave;i việc l&agrave; m&oacute;n ăn đầy dinh dưỡng, bơ rất hiệu quả v&igrave; c&oacute; t&iacute;nh kh&aacute;ng khuẩn ngăn ngừa mụn.</li>\n</ul>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px 0.75in; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 412.5px;\" src=\"http://www.svanclinic.vn/files/Files/Kien-thuc-lam-dep/%C4%90%C3%A2u%20l%C3%A0%20c%C3%A1ch%20d%C6%B0%E1%BB%A1ng%20tr%E1%BA%AFng%20da%20m%E1%BA%B7t%20nhanh%20nh%E1%BA%A5t/lam-sao-de-da-mat-trang.jpg\" /><br style=\"box-sizing: border-box;\" /><em style=\"box-sizing: border-box;\">L&agrave;m sao để da mặt trắng?</em></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px 0.75in; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">&nbsp;</p>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"6\"><span style=\"box-sizing: border-box; color: #3ab572;\">Mặt nạ chuối -&nbsp;<strong style=\"box-sizing: border-box;\">Dưỡng trắng da mặt</strong></span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Nghiền n&aacute;t &frac12; tr&aacute;i chuối v&agrave; trộn đều với sữa tươi kh&ocirc;ng đường, ngo&agrave;i trắng da chuối l&agrave; &ldquo;vũ kh&iacute;&rdquo; lợi hại chống lại c&aacute;c tế b&agrave;o gốc (nguy&ecirc;n nh&acirc;n g&acirc;y ra l&atilde;o h&oacute;a, n&aacute;m,&hellip;)</li>\n<li style=\"box-sizing: border-box;\">M&aacute;ch nhỏ với bạn l&agrave; vỏ chuối c&ograve;n c&oacute; t&aacute;c dụng l&agrave;m trắng răng nữa đ&oacute;.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"7\"><span style=\"box-sizing: border-box; color: #3ab572;\">Nước vo gạo -&nbsp;<strong style=\"box-sizing: border-box;\">C&aacute;ch l&agrave;m đẹp da mặt tự nhi&ecirc;n</strong></span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Đừng vội bỏ đi phần nước vo gạo m&agrave; h&atilde;y d&ugrave;ng n&oacute; để rửa mặt hằng ng&agrave;y nh&eacute;, trong nước vo gạo c&oacute; chứa một lượng vitamin B5 gi&uacute;p cho l&agrave;n da l&ecirc;n tone rất nhanh ch&oacute;ng v&agrave; loại bỏ vết th&acirc;m.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"8\"><span style=\"box-sizing: border-box; color: #3ab572;\">Mặt nạ nha đam -&nbsp;<strong style=\"box-sizing: border-box;\">C&aacute;ch l&agrave;m đẹp da mặt từ thi&ecirc;n nhi&ecirc;n</strong></span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Nha đam gọt vỏ, lấy phần thịt b&ecirc;n trong, n&ecirc;n rửa qua nước lạnh để bỏ hết phần mủ, sau đ&oacute; cắt nhỏ hoặc bỏ v&agrave;o m&aacute;y xay chung với sữa tươi th&ecirc;m ch&uacute;t mật ong, thoa l&ecirc;n mặt v&agrave; rửa lại bằng nước ấm sau 15 &ndash; 20 ph&uacute;t. Nha đam rất l&agrave;nh t&iacute;nh, ngo&agrave;i trắng da c&ograve;n c&oacute; t&aacute;c dụng kh&aacute;ng vi&ecirc;m, giảm đau rất hiệu quả.</li>\n</ul>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"9\"><span style=\"box-sizing: border-box; color: #3ab572;\">D&ugrave;ng khoai lang &ndash; Dưỡng ẩm da hiệu quả</span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">Khoai lang luộc&nbsp; ch&iacute;n, đừng vội bỏ đi phần nước m&agrave; h&atilde;y tận dụng để tẩy tế b&agrave;o chết.</li>\n<li style=\"box-sizing: border-box;\">Sau đ&oacute; khoai lang nghiền n&aacute;t, trộn đều với 1 muỗng sữa chua đến khi đặc sệt rồi thoa l&ecirc;n mặt, thoa đều l&ecirc;n da v&agrave; massage nhẹ nh&agrave;ng theo h&igrave;nh v&ograve;ng tr&ograve;n, chỉ cần để từ 5 &ndash; 7 ph&uacute;t rồi rửa lại với nước ấm. Khoai lang được xem l&agrave; một trong những c&aacute;ch dưỡng trắng da mặt cực tốt, l&agrave;m x&oacute;a mờ c&aacute;c vết nhăn hoặc l&atilde;o h&oacute;a tr&ecirc;n mặt.</li>\n</ul>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 412.5px;\" src=\"http://www.svanclinic.vn/files/Files/Kien-thuc-lam-dep/%C4%90%C3%A2u%20l%C3%A0%20c%C3%A1ch%20d%C6%B0%E1%BB%A1ng%20tr%E1%BA%AFng%20da%20m%E1%BA%B7t%20nhanh%20nh%E1%BA%A5t/lam-gi-de-trang-da-mat.jpg\" /><br style=\"box-sizing: border-box;\" /><em style=\"box-sizing: border-box;\">L&agrave;m g&igrave; để trắng da mặt?</em></p>\n<ol style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\" value=\"10\"><span style=\"box-sizing: border-box; color: #3ab572;\">Đi spa chăm s&oacute;c &ndash;&nbsp;<strong style=\"box-sizing: border-box;\">C&aacute;ch l&agrave;m đẹp da mặt nhanh nhất</strong>&nbsp;v&agrave; khoa học nhất!</span></li>\n</ol>\n<ul style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">\n<li style=\"box-sizing: border-box;\">L&ecirc;n list những spa chăm s&oacute;c uy t&iacute;n v&agrave; t&igrave;m hiểu th&ocirc;ng tin cụ thể để t&igrave;m cho m&igrave;nh một địa chỉ th&iacute;ch hợp.</li>\n<li style=\"box-sizing: border-box;\">Đến Svan Clinic &amp; Spa l&agrave; một trong những spa chăm s&oacute;c da mặt TPHCM tốt nhất hiện nay tọa lạc tại trung t&acirc;m th&agrave;nh phố.</li>\n</ul>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px 0.75in; color: #828c8b; font-family: san; font-size: 16.6875px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\">Nếu chị em muốn t&igrave;m hiểu&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn//tin-tuc/kien-thuc-lam-dep/dau-la-cach-duong-trang-da-mat-nhanh-nhat\">c&aacute;ch dưỡng trắng da mặt nhanh nhất</a>&nbsp;th&igrave; những lựa chọn th&ocirc;ng minh tr&ecirc;n l&agrave; c&aacute;ch nhanh nhất để chị em sở hữu l&agrave;n da trắng s&aacute;ng như mong muốn.</p>\n<p>&nbsp;</p>', 'dau-la-cach-d-ong-trang-da-mat-nhanh-nhat-', 'posts/April2018/ipqyAr7kn0L49DvbCshO.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Có nhiều bài báo đã bàn về các cách dưỡng trắng da mặt nhanh nhất bằng tự nhiên, nhưng liệu những phương pháp đó có đảm bảo an toàn?', NULL, '11', 'PUBLISHED', '0', NULL),
(2, '3', 'Hơn 30 KH Đã Giảm 5 - 20kg Nhờ Phác Đồ Giảm Mỡ 90\' J-US UnFAT', 'Nếu bạn từng thất bại với mọi biên pháp giảm béo trước đây và muốn nhanh chóng giảm 5-20kg sở hữu một vóc dáng cân đối, thu hút, thì phác đồ giảm mỡ 90 phút J-US UnFAT chính là phương pháp có thể thực hiện cho bạn.', '<div class=\"info-row\" style=\"box-sizing: border-box; color: #828c8b; font-family: san; font-size: 16.6875px;\">\r\n<div class=\"clearfix\" style=\"box-sizing: border-box; text-align: center;\">Sau một th&aacute;ng đưa ph&aacute;c đồ giảm mỡ 90 ph&uacute;t J-US UnFAT về Việt Nam, hơn 30 kh&aacute;ch h&agrave;ng đ&atilde; trải nghiệm v&agrave; tự chứng kiến sự thay đổi đ&aacute;ng kể về c&acirc;n nặng, số đo c&aacute;c v&ograve;ng một c&aacute;ch nhanh ch&oacute;ng m&agrave; kh&ocirc;ng hề c&oacute; bất kỳ can thiệp phẫu thuật n&agrave;o, đảm bảo an to&agrave;n tuyệt đối.</div>\r\n</div>\r\n<div class=\"text-content\" style=\"box-sizing: border-box; font-size: 16.6875px; margin-top: 31.2891px; color: #828c8b; font-family: san;\">\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">Chị Phạn Minh Thảo (36 tuổi) đ&atilde; giảm 20kg, 36cm v&ugrave;ng bụng</strong></span></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Chị Phan Minh Thảo (36 tuổi, trưởng ph&ograve;ng t&iacute;n dụng ng&acirc;n h&agrave;ng) hơn 2/3 cuộc đời gắn liền với th&acirc;n h&igrave;nh \"đồ sộ\". Nỗ lực giảm c&acirc;n bằng c&aacute;ch ăn ki&ecirc;ng v&agrave; tập thể dục nhưng cũng kh&ocirc;ng thể cải thiện. Sau khi t&igrave;m hiểu chị đến Svan để &nbsp;tư vấn v&agrave; đ&atilde; may mắn trở th&agrave;nh một trong những kh&aacute;ch h&agrave;ng đầu ti&ecirc;n được tham gia chương tr&igrave;nh trải nghiệm \"ph&aacute;c đồ giảm mỡ 90 ph&uacute;t J-US UnFAT\". Sau liệu tr&igrave;nh, chị Thảo đ&atilde; giảm được 20kg c&acirc;n nặng, 36cm số đo v&ugrave;ng bụng.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/chi-minh-thao-giam-beo-thanh-cong-bang-phac-do-giam-moi-90-phuy-j-us-unfat.jpg\" alt=\"khach-hang-phan-minh-thao\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">CEO Diệp B&ugrave;i (mẹ 3 con) giảm 7kg, 23cm v&ugrave;ng bụng</strong></span></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">CEO &amp; Founder của Lionbui Agency - Diệp B&ugrave;i (mẹ 3 con) vốn lu&ocirc;n bận rộn v&igrave; c&ocirc;ng việc, dồn t&acirc;m sức lo cho gia đ&igrave;nh v&agrave; con c&aacute;i đến qu&ecirc;n chăm ch&uacute;t cho bản th&acirc;n m&igrave;nh.V&igrave; th&acirc;n h&igrave;nh k&eacute;m ho&agrave;n hảo, chị Diệp B&ugrave;i từng cảm thấy v&oacute;c d&aacute;ng l&agrave; điểm khiến m&igrave;nh tự ti trong giao tiếp. Muốn bứt ph&aacute; khỏi h&igrave;nh ảnh cũ, trở th&agrave;nh người phụ nữ hiện đại th&agrave;nh đạt, \"n&agrave;ng sư tử\" Diệp B&ugrave;i đ&atilde; đến Svan giảm b&eacute;o.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Chị đ&atilde; lựa chọn trải nghiệm ph&aacute;c đồ giảm mỡ 90 ph&uacute;t J-US UnFAT, thay đổi \"thần th&aacute;i\" v&agrave; mở trang mới cho cuộc đời m&igrave;nh. Kết quả l&agrave; Chị Diệp giảm th&agrave;nh c&ocirc;ng 7kg, 23cm v&ugrave;ng bụng.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/ket-qua-giam-beo-cua-doanh-nhan-diep-bui.jpg\" alt=\"khach-hang-diep-bui\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">CEO Diệp B&ugrave;i h&agrave;o hứng: &ldquo;<em style=\"box-sizing: border-box;\">Kết quả giảm mỡ ngo&agrave;i mong đợi của c&aacute; nh&acirc;n t&ocirc;i, cảm ơn sự đồng h&agrave;nh của B&aacute;c sỹ Frederic Bey v&agrave; c&aacute;c bạn chuy&ecirc;n vi&ecirc;n trị liệu tại Svan rất nhiều</em>&rdquo;.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">Thạc sĩ t&acirc;m l&yacute; T&ocirc; Nhi A giảm 4,5 kg, 15cm v&ugrave;ng bụng</strong></span></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Vốn sở hữu th&acirc;n h&igrave;nh đầy đặn, chị chưa từng nghĩ đến vấn đề thừa c&acirc;n. Chỉ đến khi xương khớp chịu tải trọng lớn từ cơ thể, c&oacute; dấu hiệu đi xuống, chị mới thay đổi suy nghĩ.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Được chồng v&agrave; gia đ&igrave;nh hết sức ủng hộ, chị nhanh ch&oacute;ng đển Svan giảm b&eacute;o. Sau khi được B&aacute;c sỹ Mỹ Frederic Bey tư vấn v&agrave; thăm kh&aacute;m chị T&ocirc; Nhi A quyết định &aacute;p dụng ph&aacute;c đồ giảm mỡ 90 ph&uacute;t J-US UnFAT. Kết quả chị T&ocirc; Nhi A giảm liền 4,5 kg, thu gọn 15cm v&ugrave;ng bụng như mong muốn.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/a-thac-sy-to-nhi-a-giam-beo-sau-sinh-thanh-cong.jpg\" alt=\"khach-hang-to-nhi-a\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\">Chị T&ocirc; Nhi A chia sẻ: &ldquo;<em style=\"box-sizing: border-box;\">Sau liệu tr&igrave;nh trị liệu, t&ocirc;i thấy cơ thể nhẹ hẳn đi, những chiếc &aacute;o d&agrave;i m&igrave;nh mặc thướt tha hơn, học tr&ograve; ai gặp cũng khen v&agrave; bệnh xương khớp cũng khỏi hẳn</em>&rdquo;.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">Top 15 Hoa hậu Ho&agrave;n Vũ L&ecirc; Sang giảm 11kg, 23cm v&ugrave;ng bụng</strong></span></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Top 15 Hoa hậu Ho&agrave;n Vũ L&ecirc; Sang sau sinh 3 th&aacute;ng th&igrave; bụng vẫn &ldquo;ngồn ngộn&rdquo; mỡ, cơ thể mất c&acirc;n đối. Nhưng may mắn nhờ ph&aacute;c đồ giảm mỡ J-US UnFAT, L&ecirc; Sang nhanh ch&oacute;ng giảm 11kg, 23cm số đo v&ugrave;ng bụng.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/hoa-hau-sang-le-giam-beo-sau-sinh-thanh-cong.jpg\" alt=\"khach-hang-le-sang\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><em style=\"box-sizing: border-box;\">Ph&aacute;c đồ giảm mỡ 90 ph&uacute;t J-US UnFAT đ&atilde; đồng h&agrave;nh c&ugrave;ng Top 15 Hoa hậu Ho&agrave;n Vũ L&ecirc; Sang tr&ecirc;n h&agrave;nh tr&igrave;nh kh&ocirc;i phục v&oacute;c d&aacute;ng ho&agrave;n hảo sau khi sinh.</em></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">Chị B&iacute;ch San (38 tuổi, kinh doanh) giảm 5kg, 21 cm v&ugrave;ng bụng,</strong></span></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Hơn 10 năm sống chung với mỡ, chị kh&aacute; buồn l&ograve;ng v&igrave; c&agrave;ng ng&agrave;y cơ bụng của chị c&agrave;ng ch&ugrave;ng nh&atilde;o, chảy xệ. Mỡ t&iacute;ch tụ nhiều tại v&ugrave;ng bụng dưới khiến mỗi khi chị ngồi xuống l&agrave; cả một cục mỡ nổi cộm l&ecirc;n.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/chi-bich-san-giam-beo-thanh-cong-bang-phac-do-giam-moi-90-phuy-j-us-unfat.jpg\" alt=\"khach-hang-bich-san\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Sau khi đến Svan tư vấn v&agrave; chứng thực kết quả giảm b&eacute;o của c&aacute;c chị kh&aacute;ch h&agrave;ng trước đ&oacute; chị quyết định giảm b&eacute;o với J-US UnFAT. Sau liệu tr&igrave;nh chị giảm 5kg v&agrave; 21cm v&ugrave;ng bụng, lấy lại v&oacute;c d&aacute;ng c&acirc;n đối.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">Anh Đ&agrave;o Anh Duy (30 tuổi) xuống 5kg, 26cm v&ugrave;ng bụng</strong></span></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/anh-dao-anh-duy-giam-beo-thanh-cong-bang-phac-do-giam-moi-90-phuy-j-us-unfat.jpg\" alt=\"khach-hang-duy-anh\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Anh Đ&agrave;o Anh Duy (30 tuổi, trưởng ph&ograve;ng xuất nhập khẩu) thường xuy&ecirc;n phải giao tiếp c&ugrave;ng đối t&aacute;c nhưng ngại chiếc bụng bia của m&igrave;nh, cho đến khi thực hiện liệu tr&igrave;nh J-US UnFAT, anh đ&atilde; xuống 5kg, 26cm v&ugrave;ng bụng, nhanh ch&oacute;ng sở hữu v&oacute;c d&aacute;ng phong độ.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\"><span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">J-US UnFAT</strong></span>&nbsp;l&agrave;&nbsp;<strong style=\"box-sizing: border-box;\">ph&aacute;c đồ giảm mỡ d&agrave;nh ri&ecirc;ng cho cơ địa người Việt</strong>, được nghi&ecirc;n cứu v&agrave; ứng dụng th&agrave;nh c&ocirc;ng bởi 2 chuy&ecirc;n gia giảm b&eacute;o h&agrave;ng đầu: B&aacute;c sĩ Frederic Bey (Mỹ) v&agrave; B&aacute;c sĩ Machiko Miura (Nhật). Ứng dụng c&ocirc;ng nghệ Hoa Kỳ ti&ecirc;n tiến: s&oacute;ng si&ecirc;u &acirc;m hội tụ ph&aacute; hủy vĩnh viễn m&ocirc; mỡ . Kết hợp c&ugrave;ng nguy&ecirc;n l&yacute; đ&agrave;o thải mỡ tự th&acirc;n th&ocirc;ng qua t&aacute;c động cơ xương chậu từ Nhật Bản: ngăn chặn tận gốc việc t&aacute;i h&igrave;nh th&agrave;nh mỡ.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Ngo&agrave;i ra, ph&aacute;c đồ giảm mỡ 90 ph&uacute;t&nbsp;<strong style=\"box-sizing: border-box;\">J-US UnFAT</strong>&nbsp;c&ograve;n gi&uacute;p tăng sinh collagen tại v&ugrave;ng điều trị gi&uacute;p săn chắc, căng mịn da, khắc phục t&igrave;nh trạng da lồi l&otilde;m, ch&ugrave;ng nh&atilde;o của c&aacute;c phương ph&aacute;p th&ocirc;ng thường kh&aacute;c.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/Upload/News/6996f08e-b89d-4648-af95-34459cdbc61f/hoi-thao-cong-bo-phac-do-giam-mo-90-phut-j-us-unfat.jpg\" alt=\"Phac-do-giam-mo-90-jus-unfat\" /></p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">V&agrave;o ng&agrave;y&nbsp;<strong style=\"box-sizing: border-box;\">21/04/2018</strong>&nbsp;tới đ&acirc;y, trực tiếp B&aacute;c sỹ Frederic Bey sẽ chuyển giao ph&aacute;c đồ cho Svan Clinic &amp; Spa trong&nbsp;<span style=\"box-sizing: border-box; color: #3ab572;\"><strong style=\"box-sizing: border-box;\">Hội thảo c&ocirc;ng bố ph&aacute;c đồ giảm mỡ 90 ph&uacute;t J-US UnFAT</strong></span>&nbsp;tại TP.Hồ Ch&iacute; Minh.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">100 kh&aacute;ch mời tham dự hội thảo sẽ được trực tiếp b&aacute;c sỹ Frederic Bey sẽ tư vấn thăm kh&aacute;m v&agrave; x&acirc;y dựng ph&aacute;c đồ giảm mỡ c&aacute; nh&acirc;n cũng như hướng dẫn 8 quy tắc c&acirc;n bằng chuyển ho&aacute; để chị em trở th&agrave;nh chuy&ecirc;n gia dinh dưỡng của ch&iacute;nh bản th&acirc;n m&igrave;nh.</p>\r\n<p dir=\"ltr\" style=\"box-sizing: border-box; margin: 0px 0px 10px;\">Duy nhất tại hội thảo bộ đ&ocirc;i b&aacute;c sỹ c&ugrave;ng&nbsp;<strong style=\"box-sizing: border-box;\">Svan Clinic &amp; Spa</strong>&nbsp;sẽ d&agrave;nh tặng Ph&aacute;c đồ giảm mỡ to&agrave;n th&acirc;n ho&agrave;n to&agrave;n miễn ph&iacute; trị gi&aacute; 68.000.000đ c&ugrave;ng nhiều qu&agrave; tặng may mắn với tổng trị gi&aacute; l&ecirc;n đến&nbsp;<strong style=\"box-sizing: border-box;\">100.000.000 VNĐ</strong>&nbsp;đến kh&aacute;ch mời tham dự.</p>\r\n<div class=\"login\">\r\n<div class=\"login_inner\">\r\n<div class=\"login_inner__avatar\">&nbsp;</div>\r\n<input id=\"username-customer\" type=\"text\" placeholder=\"Họ v&agrave; T&ecirc;n\" /><input id=\"phone-customer\" type=\"text\" placeholder=\"Điện Thoại\" /> <input value=\"Đăng K&yacute;\" type=\"button\" class=\"btn-customer btn-customer__center\" /></div>\r\n<div class=\"login_inner__check\">&nbsp;</div>\r\n</div>\r\n<h2>&nbsp;</h2>\r\n</div>', 'h-n-30-kh-da-giam-5-20kg-nho-phac-do-giam-mo-90\'-j-us-unfat', 'posts/April2018/lvBDWuexLA06dqKumba9.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Nếu bạn từng thất bại với mọi biên pháp giảm béo trước đây và muốn nhanh chóng giảm 5-20kg sở hữu một vóc dáng cân đối, thu hút, thì phác đồ giảm mỡ 90 phút J-US UnFAT chính là phương pháp có thể thực hiện cho bạn.', NULL, '2', 'PUBLISHED', '0', NULL),
(5, '3', 'Hơn 15.000 chị em làm đẹp hiệu quả tại Svan Clinic and Spa trong năm 2017', 'Trong năm 2017, hơn 15.000 phụ nữ đã tin tưởng và cùng Svan đồng hành trên con đường theo đuổi sứ mệnh duy trì sắc vóc, khẳng định giá trị của người phụ nữ.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Mỗi người phụ nữ ch&uacute;ng ta đều sỡ hữu một n&eacute;t đẹp ri&ecirc;ng, điều quan trọng l&agrave; ch&uacute;ng ta l&agrave;m thế n&agrave;o để t&ocirc;n vinh vẻ đẹp của bản th&acirc;n. Svan Clinic &amp; Spa lu&ocirc;n mong mỏi c&oacute; thể s&aacute;t c&aacute;nh c&ugrave;ng c&aacute;c chị em tr&ecirc;n h&agrave;nh tr&igrave;nh t&igrave;m thấy vẻ đẹp của ch&iacute;nh m&igrave;nh.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\"><span style=\"box-sizing: border-box; color: #3d9c74;\"><strong style=\"box-sizing: border-box;\">Hơn 10.000 kh&aacute;ch h&agrave;ng đ&atilde; giảm b&eacute;o th&agrave;nh c&ocirc;ng:</strong></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/%C4%90%E1%BA%B8P%20T%E1%BB%B0%20NHI%C3%8AN%2C%20NH%C6%AFNG%20KH%C3%94NG%20T%E1%BB%B0%20NHI%C3%8AN%20M%C3%80%20%C4%90%E1%BA%B8P/ket-qua-giam-beo-diep-bui-min.png\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">CEO Diệp B&ugrave;i l&agrave; điển h&igrave;nh cho mẫu phụ nữ hiện đại, xinh đẹp, giỏi giang, lu&ocirc;n tự tin trong giao tiếp. Thật tự h&agrave;o v&igrave; Svan đ&atilde; gắn b&oacute; c&ugrave;ng chị tr&ecirc;n h&agrave;nh tr&igrave;nh đi t&igrave;m lại sự tự tin của ch&iacute;nh m&igrave;nh. Bằng phương ph&aacute;p giảm b&eacute;o Dưỡng sinh Nhật Bản, trong 10 ng&agrave;y chị Diệp B&ugrave;i đ&atilde; ho&agrave;n th&agrave;nh mục ti&ecirc;u giảm 7kg v&agrave; 8cm v&ugrave;ng eo, lấy lại v&oacute;c d&aacute;ng thon gọn v&agrave; v&ograve;ng eo săn chắc.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><em style=\"box-sizing: border-box;\">Xem th&ecirc;m:&nbsp;T&igrave;m hiểu về Dưỡng sinh Nhật Bản<span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span><strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/phuong-phap-giam-beo-duong-sinh-nhat-ban/\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại đ&acirc;y</span></a></strong></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px solid; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Kien-thuc-dich-vu/Ch%E1%BB%8B%20em%20ph%E1%BB%A5%20n%E1%BB%AF%20%E2%80%9C%C4%91%E1%BB%93ng%20lo%E1%BA%A1t%20tho%C3%A1t%20b%C3%A9o%E2%80%9D%20ch%E1%BB%89%20nh%E1%BB%9D%20v%C3%A0o%20b%C3%AD%20quy%E1%BA%BFt%20gi%E1%BA%A3m%20c%C3%A2n%20nhanh%201%20tu%E1%BA%A7n/lam-sao-de-giam%20can-sau-sinh.png\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">Chị Kim Minh (52 tuổi) đ&atilde; sống chung với bụng b&eacute;o 30 năm cho đến khi đến Svan v&agrave; &aacute;p dụng c&ocirc;ng nghệ giảm b&eacute;o Contri UltraShape, chị đ&atilde; giảm 4kg c&acirc;n nặng v&agrave; 15cm v&ugrave;ng bụng. V&oacute;c d&aacute;ng thon gọn hơn cũng khiến chị trẻ ra tr&ocirc;ng thấy.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><em style=\"box-sizing: border-box;\">Xem th&ecirc;m:&nbsp;Contri UltraShape l&agrave; g&igrave;?&nbsp;<strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/cong-nghe-giam-beo-contri-ultrashape/\"><span style=\"box-sizing: border-box; color: #3d9c74;\">Tại đ&acirc;y</span></a></strong></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px solid; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/H%C6%A1n%2015.000%20ch%E1%BB%8B%20em%20l%C3%A0m%20%C4%91%E1%BA%B9p%20hi%E1%BB%87u%20qu%E1%BA%A3%20t%E1%BA%A1i%20Svan%20Clinic%20and%20Spa%20trong%20n%C4%83m%202017/ket-qua-Duong-Cam_Lynh-Perfect-Shape.jpeg\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">H&agrave;nh tr&igrave;nh t&igrave;m lại v&oacute;c d&aacute;ng của Dương Cẩm Lynh sau khi mang bầu cũng l&agrave; một niềm tự h&agrave;o của Svan. Sau 10 ng&agrave;y &aacute;p dụng phương ph&aacute;p giảm b&eacute;o tạo form Perfect Shape, c&ocirc; đ&atilde; giảm 15kg. Trong 20 ng&agrave;y tiếp theo, Dương Cẩm Lynh đ&atilde; giảm th&ecirc;m được 7kg, 10cm v&ograve;ng bụng, 6cm v&ograve;ng đ&ugrave;i v&agrave; trở về th&acirc;n h&igrave;nh quyến rũ vốn c&oacute;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><em style=\"box-sizing: border-box;\">Xem th&ecirc;m:&nbsp;Giảm b&eacute;o tạo form Perfect Shape<span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span><strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/phuong-phap-giam-beo-perfect-shape/\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại đ&acirc;y</span></a></strong></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Tại Svan Clinic &amp; Spa, 100% kh&aacute;ch h&agrave;ng h&agrave;i l&ograve;ng bởi hiệu quả&nbsp;đạt được sau qu&aacute; tr&igrave;nh trị liệu giảm b&eacute;o, c&aacute;c phương ph&aacute;p được x&acirc;y dựng v&agrave; điều chỉnh linh hoạt nhằm đạt kết quả giảm số đo &ndash; c&acirc;n nặng dư thừa tốt nhất cho từng trường hợp cơ địa kh&aacute;c nhau. B&ecirc;n cạnh việc kết hợp&nbsp; kỹ thuật masssage chuy&ecirc;n s&acirc;u từ Nhật Bản, Svan lu&ocirc;n cải tiến v&agrave; ứng dụng c&aacute;c c&ocirc;ng nghệ bắn ph&aacute; mỡ tầng s&acirc;u ti&ecirc;n tiến như Velas Shape, UltraShape, Hifu Lipo&hellip; ch&iacute;nh h&atilde;ng từ Hoa Kỳ, ch&iacute;nh v&igrave; thế, kể cả những trường hợp kh&aacute;ch h&agrave;ng thuộc &ldquo;ca kh&oacute;&rdquo; mỡ cứng, mỡ l&acirc;u năm&hellip; cũng trị liệu th&agrave;nh c&ocirc;ng. Quan trọng nhất, tại Svan, kh&aacute;ch h&agrave;ng lu&ocirc;n được đảm bảo bởi cam kết ho&agrave;n 100% chi ph&iacute; k&yacute; kết bằng văn bản&nbsp;trước khi bắt đầu bất kỳ một lộ tr&igrave;nh giảm b&eacute;o n&agrave;o!</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\"><span style=\"box-sizing: border-box; color: #3d9c74;\"><strong style=\"box-sizing: border-box;\">Số lượng kh&aacute;ch h&agrave;ng thay đổi l&agrave;n da, ho&agrave;n thiện vẻ đẹp gương mặt vượt qua con số 5.000:</strong></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px solid; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/Kim%20Dung%20-%20B%C6%B0%E1%BB%9Bc%20ngo%E1%BA%B7t%20cu%E1%BB%99c%20%C4%91%E1%BB%9Di%20t%E1%BB%AB%20khi%20%C4%91%E1%BA%A7u%20t%C6%B0%20nhan%20s%E1%BA%AFc/kim-dung-buoc-ngoat-cuoc-doi-tu-khi-dau-tu-nhan-sac-3.png\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">Qu&aacute;n qu&acirc;n Vietnam&rsquo;s Next Top Model 2017 &ndash; Kim Dung cũng c&ugrave;ng Svan trải nghiệm phương ph&aacute;p Thải độc trắng da Hydrogen kết hợp với Đặc trị mụn Oxy tinh khiết, thay đổi đ&aacute;ng kể l&agrave;n da của m&igrave;nh. Kh&ocirc;ng những mụn m&agrave; c&aacute;c vết th&acirc;m, sạm, v&ugrave;ng da tối m&agrave;u đều được đ&aacute;nh bay.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><em style=\"box-sizing: border-box;\">Xem th&ecirc;m:&nbsp;Kim Dung : Bước ngoặt cuộc đời từ khi đầu tư nhan sắc<span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span><strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/tin-tuc/tin-tuc-dich-vu/kim-dung-buoc-ngoat-cuoc-doi-tu-khi-dau-tu-nhan-sac\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại </span></a></strong></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">Do t&iacute;nh chất nghề nghiệp, ngưỡi mẫu ảnh Thiều Thu Thảo (28 tuổi), thường xuy&ecirc;n thức khuya, makeup l&agrave;m việc dưới &aacute;nh nắng mặt trời, l&agrave;n da Thu Thảo bắt đầu xuất hiện những vết sạm, sần s&ugrave;i, lỗ ch&acirc;n l&ocirc;ng to v&agrave; lốm đốm t&agrave;n nhang. Bằng phương ph&aacute;p l&agrave;m trắng da trẻ h&oacute;a Rose White sau liệu tr&igrave;nh 10 buổi, Thu Thảo đ&atilde; t&igrave;m lại l&agrave;n da trắng s&aacute;ng, căng mịn, tươi hồng vốn c&oacute;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><em style=\"box-sizing: border-box;\">Xem th&ecirc;m:&nbsp;Phục hồi l&agrave;n da đen sạm, l&atilde;o h&oacute;a sớm chỉ trong 10 ng&agrave;y<span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span><strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/tin-tuc/tin-tuc-dich-vu/rose-white-phuc-hoi-lan-da-den-sam-lao-hoa-som-chi-trong-10-ngay\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại đ&acirc;y</span></a></strong></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/T%E1%BB%B1%20tr%E1%BB%8B%20n%C3%A1m%20r%C3%B2ng%20r%C3%A3%202%20n%C4%83m%20kh%C3%B4ng%20kh%E1%BB%8Fi%2C%20da%20s%E1%BA%A1ch%20n%C3%A1m%20ch%E1%BB%89%20trong%2010%20ng%C3%A0y/TRU%CC%9BO%CC%9B%CC%81C%20SAU_nam-thien-nga-min.jpg\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\">Sau khi thăm kh&aacute;m v&agrave; kiểm tra t&igrave;nh trạng da bằng m&aacute;y ph&acirc;n t&iacute;ch tại Svan Clinic &amp; Spa, Chị Thi&ecirc;n Nga (52 tuổi, G&ograve; Vấp) được b&aacute;c sĩ chỉ định v&agrave; đưa ra ph&aacute;c đồ điều trị cụ thể 10 ng&agrave;y bằng c&ocirc;ng nghệ Laser Nano Plus. Kết th&uacute;c liệu tr&igrave;nh, l&agrave;n da chị Thi&ecirc;n Nga sạch n&aacute;m đến 90%, da s&aacute;ng khoẻ, mịn m&agrave;ng hơn, lỗ ch&acirc;n l&ocirc;ng được se kh&iacute;t.&nbsp;<em style=\"box-sizing: border-box;\">Xem chi tiết h&agrave;nh tr&igrave;nh trị n&aacute;m của chị Thi&ecirc;n Nga<span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span><strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/tin-tuc/tin-tuc-dich-vu/tu-tri-nam-rong-ra-2-nam-khong-khoi-da-sach-nam-chi-trong-10-ngay\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại đ&acirc;y</span></a></strong></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Hơn 15.000 phụ nữ đ&atilde; tin tưởng đồng h&agrave;nh c&ugrave;ng Svan Clinic &amp; Spa v&agrave; đạt được kết quả như &yacute;. Bạn c&oacute; muốn đẹp hơn c&ugrave;ng Svan?</p>\r\n<div style=\"box-sizing: border-box; font-family: san; font-size: 16.6875px; border: 1px solid #000000; padding: 10px; color: #3ab572;\">\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;\">Duy nhất trong th&aacute;ng 1 &ndash; th&aacute;ng v&agrave;ng tri &acirc;n, Svan Clinic &amp; Spa đặc biệt d&agrave;nh tặng c&aacute;c chị em ưu đ&atilde;i giảm gi&aacute; trực tiếp 50% chi ph&iacute; tất cả c&aacute;c dịch vụ giảm b&eacute;o, trắng da, điều trị trẻ ho&aacute;&hellip;.. Nhanh tay đăng k&yacute; ngay để nhận ưu đ&atilde;i v&agrave; được tư vấn miễn ph&iacute; c&ugrave;ng Svan nh&eacute;!</p>\r\n<div class=\"login\">\r\n<div class=\"login_inner\" style=\"text-align: center;\">\r\n<div class=\"login_inner__avatar\">&nbsp;</div>\r\n<input id=\"username-customer\" type=\"text\" placeholder=\"Họ v&agrave; T&ecirc;n\" /><input id=\"phone-customer\" type=\"text\" placeholder=\"Điện Thoại\" /> <input value=\"Đăng K&yacute;\" type=\"button\" class=\"btn-customer btn-customer__center\" /></div>\r\n<div class=\"login_inner__check\">&nbsp;</div>\r\n</div>\r\n</div>', 'h-n-15-000-chi-em-lam-dep-hieu-qua-tai-svan-clinic-and-spa-trong-n-m-2017', 'posts/April2018/WV45OdOGEEG3JY3lw1Jy.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Trong năm 2017, hơn 15.000 phụ nữ đã tin tưởng và cùng Svan đồng hành trên con đường theo đuổi sứ mệnh duy trì sắc vóc, khẳng định giá trị của người phụ nữ.', NULL, '2', 'PUBLISHED', '0', NULL);
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(6, '3', '“Đẹp tự nhiên, nhưng không tự nhiên mà đẹp”', 'Bận rộn với 2 công ty riêng, việc học buổi tối và chăm sóc 3 đứa con nhưng “nàng sư tử Diệp Bùi” vẫn có bí quyết sở hữu một nhan sắc, một vóc dáng “vạn người  mê”.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">&ldquo;Trong x&atilde; hội ng&agrave;y nay, phụ nữ th&ocirc;ng minh l&agrave; phụ nữ biết c&aacute;ch l&agrave;m đẹp. Đừng đi theo xu hướng m&agrave; h&atilde;y tạo ra n&oacute;! Khi l&agrave;m được điều đ&oacute;, bạn sẽ trở n&ecirc;n s&aacute;ng tạo v&agrave; kh&aacute;c biệt&rdquo; &ndash; CEO Diệp B&ugrave;i.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\"><span style=\"box-sizing: border-box; color: #3d9c74;\"><strong style=\"box-sizing: border-box;\">Phụ nữ th&ocirc;ng minh l&agrave; phụ nữ biết c&aacute;ch l&agrave;m đẹp!</strong></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Quan điểm l&agrave;m đẹp của t&ocirc;i l&agrave; kh&ocirc;ng c&oacute; bản sao. M&igrave;nh l&agrave; ch&iacute;nh m&igrave;nh. M&igrave;nh l&agrave;m đẹp c&oacute; suy nghĩ, c&oacute; chọn lọc, dựa tr&ecirc;n con người của m&igrave;nh, dựa tr&ecirc;n sự tự tin của m&igrave;nh để tạo n&ecirc;n sự kh&aacute;c biệt.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/%C4%90%E1%BA%B8P%20T%E1%BB%B0%20NHI%C3%8AN%2C%20NH%C6%AFNG%20KH%C3%94NG%20T%E1%BB%B0%20NHI%C3%8AN%20M%C3%80%20%C4%90%E1%BA%B8P/diep-bui2.jpg\" alt=\"\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">T&ocirc;i c&oacute; một c&acirc;u thế n&agrave;y: &ldquo;Đẹp tự nhi&ecirc;n, nhưng kh&ocirc;ng tự nhi&ecirc;n m&agrave; đẹp&rdquo;. D&ugrave; bận rộn nhưng t&ocirc;i vẫn d&agrave;nh thời gian chạy bộ 5km mỗi s&aacute;ng, chăm s&oacute;c sắc đẹp đều đặn, cũng như thực hiện chế độ ăn uống rất nghi&ecirc;m khắc. Khi ra đường t&ocirc;i lu&ocirc;n trang điểm, cũng như thường xuy&ecirc;n b&atilde;o dưỡng sắc đẹp để da m&igrave;nh mịn m&agrave;ng hơn, v&oacute;c d&aacute;ng đẹp hơn, khiến m&igrave;nh c&oacute; thể tự tin hơn. T&ocirc;i thường đến Svan Clinic &amp; Spa 2-3 tuần lần để thải độc ch&igrave; cho da, trẻ h&oacute;a da bằng Tế b&agrave;o no&atilde;n, hay định h&igrave;nh v&oacute;c d&aacute;ng bằng phương ph&aacute;p Dưỡng sinh Nhật Bản.<em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span>Xem chi tiết<span style=\"box-sizing: border-box; color: #3d9c74;\">&nbsp;</span><strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/dich-vu/giam-can-giam-beo/giam-beo-duong-sinh\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại đ&acirc;y</span></a></strong><span style=\"box-sizing: border-box; color: #3d9c74;\">.</span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Thật ra, quan điểm tập thể dục sẽ c&oacute; v&oacute;c d&aacute;ng đẹp l&agrave; điều hiển nhi&ecirc;n, nhưng việc t&ocirc;i đến giảm b&eacute;o ở Svan th&igrave; ngo&agrave;i giảm c&acirc;n, t&ocirc;i c&ograve;n định h&igrave;nh được form người của m&igrave;nh. Tập thể dục chủ yếu mang lại sức khỏe chứ chưa định h&igrave;nh được form người của t&ocirc;i, đặc biệt l&agrave; v&ograve;ng eo. Bạn thấy đ&oacute;, Svan giải quyết được vấn đề n&agrave;y rất tốt!</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/%C4%90%E1%BA%B8P%20T%E1%BB%B0%20NHI%C3%8AN%2C%20NH%C6%AFNG%20KH%C3%94NG%20T%E1%BB%B0%20NHI%C3%8AN%20M%C3%80%20%C4%90%E1%BA%B8P/giam-beo-DSNB-diep-bui-min.JPG\" alt=\"\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\"><span style=\"box-sizing: border-box; color: #3d9c74;\"><strong style=\"box-sizing: border-box;\">H&atilde;y bức ph&aacute; ra khỏi giới hạn!</strong></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">C&oacute; một thời điểm, khi t&ocirc;i đang định hướng nghề nghiệp ph&aacute;t triển thương hiệu c&aacute; nh&acirc;n, t&ocirc;i đ&atilde; gặp trục trặc trong h&ocirc;n nh&acirc;n. Sau bước ngoặc n&agrave;y, t&ocirc;i đ&atilde; chựng lại một thời gian. T&ocirc;i suy nghĩ định hướng con đường m&igrave;nh đi ph&iacute;a trước sẽ như thế n&agrave;o, t&ocirc;i tự đặt ra c&acirc;u hỏi &ldquo;T&ocirc;i sẽ l&agrave; ai?&rdquo;. V&agrave; khi trả lời được, t&ocirc;i đ&atilde; thay đổi cuộc sống của m&igrave;nh. T&ocirc;i t&iacute;ch cực hơn trong vấn đề chăm s&oacute;c sức khỏe v&agrave; sắc đẹp. T&ocirc;i nhận ra m&igrave;nh phải lu&ocirc;n xinh đẹp, rạng rời v&agrave; kh&ocirc;ng mờ nhạt.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Khi nhận ra được điều đ&oacute;, t&ocirc;i đ&atilde; bắt đầu bằng phương ph&aacute;p giảm b&eacute;o dưỡng sinh Nhật Bản tại hệ thống Svan Clinic and Spa. Chỉ sau 10 ng&agrave;y, t&ocirc;i đạt được mục ti&ecirc;u giảm 7kg v&agrave; 8cm v&ugrave;ng eo, lấy lại v&oacute;c d&aacute;ng thon gọn v&agrave; v&ograve;ng eo săn chắc.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px;\" align=\"center\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/%C4%90%E1%BA%B8P%20T%E1%BB%B0%20NHI%C3%8AN%2C%20NH%C6%AFNG%20KH%C3%94NG%20T%E1%BB%B0%20NHI%C3%8AN%20M%C3%80%20%C4%90%E1%BA%B8P/ket-qua-giam-beo-diep-bui-min.png\" alt=\"\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Ch&uacute;ng ta l&agrave; phụ nữ! Ngo&agrave;i gia đ&igrave;nh v&agrave; c&ocirc;ng việc, bạn cũng n&ecirc;n y&ecirc;u thương v&agrave; chiều chuộng bản th&acirc;n m&igrave;nh. Bạn c&oacute; biết, cảm gi&aacute;c khi đắm m&igrave;nh thư gi&atilde;n trong kh&ocirc;ng gian spa sang trọng, tận hưởng c&aacute;c dịch vụ chăm s&oacute;c sắc đẹp, cảm nhận được v&oacute;c d&aacute;ng m&igrave;nh thon gọn hẳn đi, l&agrave;n da m&igrave;nh được trẻ h&oacute;a hẳn ra tuyệt vời thế n&agrave;o kh&ocirc;ng&nbsp;? Khi bạn cho ph&eacute;p bản th&acirc;n m&igrave;nh đẹp hơn, t&ocirc;i tin bạn sẽ cảm nhận được s&acirc;u sắc nhất gi&aacute; trị của một người phụ nữ!</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Mọi người thường hay trầm trồ v&igrave; l&agrave;n da, v&oacute;c d&aacute;ng v&agrave; thần th&aacute;i tự tin của t&ocirc;i. Thật ra, phụ nữ ai cũng c&oacute; thể trở n&ecirc;n xinh đẹp v&agrave; tự tin. T&ocirc;i vẫn giống như những người phụ nữ kh&aacute;c. Duy nhất một điều l&agrave; t&ocirc;i d&aacute;m bức ph&aacute; bức ph&aacute; khỏi giới hạn của ch&iacute;nh m&igrave;nh. Khi vượt qua được giới hạn đ&oacute;, với quan điểm t&iacute;ch cực hơn, suy nghĩ cho bản th&acirc;n nhiều hơn, l&uacute;c đ&oacute; bạn sẽ cảm thấy cuộc sống của m&igrave;nh trở n&ecirc;n &yacute; nghĩa hơn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: auto; margin-bottom: 10px; width: 515.625px;\" src=\"http://www.svanclinic.vn/files/Files/Tin%20t%E1%BB%A9c/Tin%20t%E1%BB%A9c%20d%E1%BB%8Bch%20v%E1%BB%A5/%C4%90%E1%BA%B8P%20T%E1%BB%B0%20NHI%C3%8AN%2C%20NH%C6%AFNG%20KH%C3%94NG%20T%E1%BB%B0%20NHI%C3%8AN%20M%C3%80%20%C4%90%E1%BA%B8P/diep-bui.jpg\" alt=\"\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">Be your self and just do it! H&atilde;y l&agrave; ch&iacute;nh m&igrave;nh! H&atilde;y cứ l&agrave;m! H&atilde;y cứ bứt ph&aacute;! Như t&ocirc;i, r&otilde; r&agrave;ng sau khi thay đổi về diện mạo v&agrave; v&oacute;c d&aacute;ng, t&ocirc;i cảm nhận được sự kh&aacute;c biệt rất lớn. T&ocirc;i trở n&ecirc;n rất tự tin, v&agrave; thấy m&igrave;nh hạnh ph&uacute;c! C&ograve;n bạn, bạn c&oacute; muốn hạnh ph&uacute;c hơn kh&ocirc;ng?&nbsp;<em style=\"box-sizing: border-box;\">Xem chi tiết&nbsp;<strong style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #3ab572; text-decoration-line: none;\" href=\"http://www.svanclinic.vn/tin-tuc/tin-tuc-dich-vu/giam-beo-co-the-mua-duoc-hanh-phuc\"><span style=\"box-sizing: border-box; color: #3d9c74;\">tại đ&acirc;y</span></a></strong><span style=\"box-sizing: border-box; color: #3d9c74;\">.</span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #828c8b; font-family: san; font-size: 16.6875px; text-align: justify;\">&nbsp;</p>\r\n<div style=\"box-sizing: border-box; color: #828c8b; font-family: san; font-size: 16.6875px;\">\r\n<div style=\"box-sizing: border-box; border: 1px solid #000000; padding: 10px; color: #3ab572;\">\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;\">Bạn muốn sở hữu vẻ đẹp tự nhi&ecirc;n c&ugrave;ng v&oacute;c d&aacute;ng c&acirc;n đối như nữ doanh nh&acirc;n Diệp B&ugrave;i! Th&aacute;ng v&agrave;ng tri &acirc;n, duy nhất trong th&aacute;ng 1, Svan Clinic &amp; Spa d&agrave;nh tặng c&aacute;c chị em ưu đ&atilde;i&nbsp;<strong style=\"box-sizing: border-box;\">giảm gi&aacute; trực tiếp 50%</strong>&nbsp;chi ph&iacute; tất cả c&aacute;c dịch vụ l&agrave;m đẹp.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;\">Nhanh tay đăng k&yacute; ngay để nhận ưu đ&atilde;i v&agrave; được tư vấn miễn ph&iacute; c&ugrave;ng Svan nh&eacute;!</p>\r\n</div>\r\n</div>', '-dep-tu-nhien-nh-ng-khong-tu-nhien-ma-dep-', 'posts/April2018/gZSKssP7NYLIGfIz4hfr.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Bận rộn với 2 công ty riêng, việc học buổi tối và chăm sóc 3 đứa con nhưng “nàng sư tử Diệp Bùi” vẫn có bí quyết sở hữu một nhan sắc, một vóc dáng “vạn người  mê”.', NULL, '2', 'PUBLISHED', '0', NULL),
(7, '3', 'Giảm cân trong 1 tháng tối đa được bao nhiêu kg là phù hợp ?', 'Giảm cân trong 1 tháng tối đa được bao nhiêu kg là phù hợp? Có phương pháp nào\r\ngiảm cân an toàn hiệu quả nhanh chóng trong 1 tháng.', '<p>Theo nghi&ecirc;n cứu của rất nhiều nh&agrave; khoa học th&igrave; trong 1 th&aacute;ng việc bạn giảm khoảng 3 - 5kg với c&aacute;c phương ph&aacute;p giảm c&acirc;n khoa học l&agrave; hợp l&yacute; v&agrave; an to&agrave;n cho sức khỏe của bạn. Tuy nhi&ecirc;n, điều n&agrave;y c&ograve;n ph&ugrave; thuộc v&agrave;o cơ địa, sức khỏe t&igrave;nh trạng thừa c&acirc;n của từng người. Giảm c&acirc;n hợp l&yacute; l&agrave; giảm bớt lượng mỡ v&agrave; chất b&eacute;o trong cơ thể m&igrave;nh.</p>\r\n<p>Ch&uacute;ng ta n&ecirc;n lưu &yacute; rằng, giảm lượng thức ăn c&oacute; thể l&agrave;m giảm lượng cơ, chứ kh&ocirc;ng phải l&agrave;m giảm xương v&agrave; lượng mỡ trong cơ thể. Như vậy, ngay khi c&acirc;n nặng của bạn giảm, mức chuyển h&oacute;a cơ thể nghỉ ngơi cũng giảm, dẫn đến xu hướng cơ thể c&agrave;ng tăng th&ecirc;m lượng mỡ. Đ&oacute; kh&ocirc;ng phải l&agrave; mục ti&ecirc;u của giảm c&acirc;n.</p>\r\n<p>V&igrave; vậy để giảm c&acirc;n trong 1 th&aacute;ng an to&agrave;n mang lại hiệu quả cao nhất, ch&uacute;ng ta cần phải hiểu r&otilde; về tỉ lệ mỡ, tỉ lệ cơ xương&hellip; giảm c&acirc;n cần đi theo 1 lộ tr&igrave;nh nhất định, cũng kh&ocirc;ng phải l&agrave; việc một sớm một chiều m&agrave; bạn n&oacute;ng vội được, đ&oacute; l&agrave; một chiến lược l&acirc;u d&agrave;i kh&ocirc;ng chỉ đơn giản l&agrave; ăn ki&ecirc;ng, tập luyện m&agrave; để c&oacute; được v&oacute;c d&aacute;ng đẹp bạn cần phải kết hợp rất nhiều phương ph&aacute;p kh&aacute;c nhau.</p>\r\n<p>Ch&iacute;nh v&igrave; vậy, khi c&oacute; &yacute; định giảm c&acirc;n nhanh trong 1 th&aacute;ng ch&uacute;ng ta n&ecirc;n đi đến c&aacute;c cơ sở thẩm mỹ, spa giảm b&eacute;o uy t&iacute;n để được c&aacute;c c&aacute;c chuy&ecirc;n gia c&acirc;n đo, ph&acirc;n t&iacute;ch x&aacute;c định ch&iacute;nh x&aacute;c n&ecirc;n giảm bao nhi&ecirc;u phần trọng lượng cơ thể cũng như xem x&eacute;t c&aacute;c loại thuốc v&agrave; điều kiện sức khỏe hiện tại của bạn v&agrave; x&aacute;c định t&iacute;nh an to&agrave;n của việc giảm c&acirc;n đối với cơ thể. Ngo&agrave;i ra c&aacute;c chuy&ecirc;n gia sẽ cho bạn lời khuy&ecirc;n cơ bản c&aacute;ch t&iacute;nh to&aacute;n lượng calo v&agrave; v&agrave; đưa ra lộ tr&igrave;nh giảm b&eacute;o ph&ugrave; hợp mang lại hiệu quả cao nhất m&agrave; kh&ocirc;ng ảnh hưởng đến sức khỏe.</p>\r\n<p><strong>Contri UltraShape giảm c&acirc;n trong 7 ng&agrave;y - đ&aacute;nh bay 32% lượng mỡ thừa</strong></p>\r\n<p>C&oacute; một thực tế rằng đối với trường hợp c&aacute;c chị em bị t&iacute;ch mỡ l&acirc;u năm muốn giảm c&acirc;n trong 1 th&aacute;ng bằng c&aacute;c chế độ ăn uống, tập luyện th&igrave; thật sự kh&oacute; khăn bởi với đối tượng n&agrave;y, c&aacute;c phương ph&aacute;p như ăn ki&ecirc;ng, tập luyện, kem hay thuốc đều gần như kh&ocirc;ng c&oacute; t&aacute;c dụng. Muốn loại bỏ mỡ hiệu quả, bạn cần một giải ph&aacute;p c&oacute; thể trực tiếp t&aacute;c động v&agrave; chuyển h&oacute;a được c&aacute;c m&ocirc; mỡ đ&atilde; t&iacute;ch tụ.</p>\r\n<p>Theo c&aacute;c chuy&ecirc;n gia, c&aacute;ch l&agrave;m giảm c&acirc;n nhanh trong 1 th&aacute;ng v&agrave; cũng l&agrave; duy nhất trong trường hợp n&agrave;y l&agrave; &aacute;p dụng c&aacute;c kỹ thuật giảm mỡ c&ocirc;ng nghệ cao. Theo c&aacute;c chuy&ecirc;n gia với phương ph&aacute;p giảm b&eacute;o bằng c&ocirc;ng nghệ cao, thay v&igrave; phải mất 1 th&aacute;ng để cật lực giảm c&acirc;n th&igrave; hiện nay c&aacute;c bạn c&oacute; thể mất 7 ng&agrave;y để sở hữu 1 v&oacute;c d&aacute;ng thon gọn &ndash; 7 ng&agrave;y thời gian ph&ugrave; hợp để cơ thể th&iacute;ch nghi v&agrave; giảm c&acirc;n hiệu quả nhất.</p>\r\n<p>Giảm b&eacute;o c&ocirc;ng nghệ Contri Ultrashape - bước tiến lớn nhất năm 2017 trong lĩnh vực giảm b&eacute;o của Nhật Bản, được FDA chứng nhận hiệu quả v&agrave; an to&agrave;n ho&agrave;n to&agrave;n với sức khỏe với người sử dụng gi&uacute;p loại bỏ triệt để mỡ thừa ở c&aacute;c v&ugrave;ng tr&ecirc;n cơ thể gi&uacute;p bạn tiết kiệm thời gian m&agrave; vẫn c&oacute; được kết quả giảm mỡ tuyệt đối.</p>\r\n<p>Được đ&aacute;nh gi&aacute; l&agrave; si&ecirc;u phẩm giảm b&eacute;o, giảm c&acirc;n an to&agrave;n v&agrave; nhanh ch&oacute;ng nhất hiện nay, được trung t&acirc;m ph&ograve;ng chống v&agrave; kiểm so&aacute;t bệnh b&eacute;o ph&igrave; Hoa Kỳ nghi&ecirc;n cứu v&agrave; thử nghiệm th&agrave;nh c&ocirc;ng cho h&agrave;ng ng&agrave;n kh&aacute;ch h&agrave;ng tr&ecirc;n thế giới.</p>\r\n<p><strong>Contri Ultrashape c&ocirc;ng nghệ giảm c&acirc;n giảm b&eacute;o được ch&uacute; &yacute; nhiều nhất 2018.</strong></p>\r\n<p>Contri UltraShape ứng dụng t&iacute;ch hợp 3 c&ocirc;ng nghệ ti&ecirc;u hủy mỡ th&ocirc;ng minh tr&ecirc;n c&ugrave;ng 1 hệ thống bao gồm: c&ocirc;ng nghệ bắn ph&aacute; mỡ tầng s&acirc;u Contri UltraShape giảm đến 32% lượng mỡ thừa đ&atilde; được FDA Hoa Kỳ chứng nhận hiệu quả sử dụng s&oacute;ng si&ecirc;u &acirc;m ph&aacute; hủy m&ocirc; mỡ kh&ocirc;ng g&acirc;y tổn thương cơ địa. Cấy Cellulysis c&ocirc;ng nghệ cao gi&uacute;p thủy ph&acirc;n v&agrave; h&oacute;a lỏng m&ocirc; mỡ nhanh ch&oacute;ng, giảm thiểu t&igrave;nh trạng t&iacute;ch nước g&acirc;y ph&ugrave; nề khi bị b&eacute;o. Kết hợp với massage Hosomi Nhật Bản, phương ph&aacute;p massage vận dụng từ thế v&otilde; Aikido Nhật Bản trong việc t&aacute;c động v&agrave;o v&ugrave;ng trị liệu, đ&agrave;o thải độc tố, mỡ thừa đ&atilde; h&oacute;a lỏng ra khỏi cơ thể một c&aacute;ch triệt để.</p>\r\n<p>Chỉ sau 1 tuần giảm 3 -5kg c&acirc;n nặng, 10 - 15cm v&ograve;ng bụng, kết th&uacute;c liệu tr&igrave;nh c&acirc;n nặng vẫn tiếp tục giảm m&agrave; kh&ocirc;ng cần ăn ki&ecirc;ng luyện tập. Quan trọng hơn, đ&acirc;y l&agrave; liệu tr&igrave;nh giảm c&acirc;n nhanh, duy tr&igrave; kết quả ổn định, l&acirc;u d&agrave;i đồng thời c&ograve;n gi&uacute;p l&agrave;m săn chắc, căng mịn v&ugrave;ng da giảm b&eacute;o kh&ocirc;ng bị nhăn nheo, chảy xệ sau khi điều trị.</p>\r\n<p>Hiện nay, tại Việt Nam c&ocirc;ng nghệ giảm mỡ bụng Contri UltraShape đ&atilde; v&agrave; đang được &aacute;p dụng th&agrave;nh c&ocirc;ng tại Svan Clinic and Spa - một trong những spa giảm mỡ b&eacute;o uy t&iacute;n c&oacute; thương hiệu giảm b&eacute;o h&agrave;ng đầu được rất nhiều chị em tin tưởng lựa chọn.</p>\r\n<p>Kết quả giảm b&eacute;o đầy ấn tượng trong năm 2017 bằng c&ocirc;ng nghệ giảm b&eacute;o Contri Ultrashape tại Svan Clinic And Spa</p>\r\n<p>Chị Kim Minh (52 tuổi, Kinh doanh tự do) giảm liền 4kg v&agrave; 15cm v&ograve;ng bụng sở hữu th&acirc;n h&igrave;nh thon gọn, săn chắc.</p>\r\n<p>Chị Phan Minh Thảo (38 tuổi, nh&acirc;n vi&ecirc;n ng&acirc;n h&agrave;ng) giảm được 4kg v&agrave; 7cm v&ograve;ng bụng sau nhiều năm giảm b&eacute;o thất bại.</p>\r\n<p>Chị M&ocirc;c Tr&agrave; Chuy&ecirc;n vi&ecirc;n trang điểm giảm 3kg c&acirc;n nặng v&agrave; 8cm v&ograve;ng bụng, giảm b&eacute;o ngo&agrave;i mong đợi chị đ&atilde; phải thay đổi cả tủ quần &aacute;o mới v&igrave; những trang phục cũ kh&ocirc;ng c&ograve;n ph&ugrave; hợp.</p>\r\n<p>Th.s T&acirc;m L&yacute; T&ocirc; Nhi A giảm c&acirc;n th&agrave;nh c&ocirc;ng nhờ c&ocirc;ng nghệ bụng Contri Ultra Shape. Sau liệu tr&igrave;nh C&aacute;c vết rạn da, nhăn nheo v&ugrave;ng bụng cũng biến mất</p>\r\n<p>Như vậy, qua b&agrave;i viết n&agrave;y chị em đ&atilde; c&oacute; c&acirc;u trả lời cho vấn đề giảm c&acirc;n trong 1 th&aacute;ng tối đa được bao nhi&ecirc;u kg l&agrave; ph&ugrave; hợp cũng như t&igrave;m cho m&igrave;nh phương ph&aacute;p giảm b&eacute;o nhanh ch&oacute;ng, an to&agrave;n, hiệu quả nhất hiện nay. Nếu c&ograve;n bất cứ c&acirc;u hỏi thắc mắc n&agrave;o bạn c&oacute; thể li&ecirc;n hệ đến số 1900 0117 hoặc đến c&aacute;c chi nh&aacute;nh Svan Clinic And Spa gần nhất để được c&aacute;c chuy&ecirc;n gia giảm b&eacute;o h&agrave;ng đầu tư vấn giải đ&aacute;p, thăm kh&aacute;m miễn ph&iacute; 24/24.</p>', 'giam-can-trong-1-thang-toi-da-duoc-bao-nhieu-kg-la-phu-hop', 'posts/May2018/TQmfGZDmcGo9R9ETFW8N.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Giảm cân trong 1 tháng tối đa được bao nhiêu kg là phù hợp? Hãy lắng nghe các\r\nchuyên gia đưa ra câu trả lời và gợi ý phương pháp giảm cân hiệu quả nhất.', NULL, '17', 'PUBLISHED', '0', NULL),
(8, '3', 'Giảm cân sau sinh 2 tháng hay 4 tháng tốt hơn?', 'Giảm cân sau sinh 2 tháng hay 4 tháng tốt hơn là băn khoăn của không ít chị em. Bài viết dưới đây sẽ giải đáp và gợi ý cho bạn..', '<p>Nhiều chị em sau sinh c&oacute; &yacute; định giảm b&eacute;o để lấy lại v&ograve;ng eo thon v&agrave; v&oacute;c d&aacute;ng c&acirc;n đối, tuy nhi&ecirc;n, giảm c&acirc;n sau sinh 2 th&aacute;ng hay 4 th&aacute;ng tốt hơn khiến nhiều chị em băn khoăn thắc mắc bới kh&ocirc;ng phải chị em n&agrave;o cũng biết đ&acirc;u l&agrave; thời điểm th&iacute;ch hợp để gi&uacute;p cơ thể về phom chuẩn hiệu quả. Để c&oacute; c&acirc;u trả lời cho vấn đề n&agrave;y, h&atilde;y đọc b&agrave;i viết sau đ&acirc;y.</p>\r\n<p>Thời điểm th&iacute;ch hợp giảm c&acirc;n sau sinh</p>\r\n<p>Theo b&aacute;c sĩ Nguyễn Thị Minh Trang &ndash; Chuy&ecirc;n gia tư vấn giảm b&eacute;o tại Svan Clinic And Spa cho biết: &ldquo;Tr&ecirc;n thực tế, thời điểm th&iacute;ch hợp để chị em giảm c&acirc;n sau sinh sẽ phụ thuộc nhiều yếu tố như: t&igrave;nh trạng sức khỏe, thể chất, tiền sử bệnh tật, trạng th&aacute;i t&acirc;m l&yacute;, tinh thần... v&agrave; đặc biệt l&agrave; chọn sinh mổ hay sinh thường trước đ&oacute;&rdquo;.</p>\r\n<p>Với trường hợp chị em sinh thường sau sinh khoảng sau 2-3 th&aacute;ng đ&atilde; c&oacute; thể tiến h&agrave;nh giảm mỡ bụng bằng c&aacute;c phương ph&aacute;p kh&ocirc;ng phẫu thuật với điều kiện sức khỏe cho ph&eacute;p v&agrave; trạng th&aacute;i thể chất, tinh thần được b&aacute;c sĩ đ&aacute;nh gi&aacute; ph&ugrave; hợp. Đ&acirc;y l&agrave; thời điểm th&iacute;ch hợp để giảm mỡ, giảm c&acirc;n bởi lớp mỡ dưới da l&uacute;c n&agrave;y c&ograve;n mềm, v&ugrave;ng bụng đang được định h&igrave;nh n&ecirc;n dễ đạt hiệu quả cao.</p>\r\n<p>2-3 th&aacute;ng sau sinh thường v&agrave; 6 th&aacute;ng sau khi sinh mổ l&agrave; thời điểm th&iacute;ch hợp cho chị em c&oacute; mong muốn giảm c&acirc;n.</p>\r\n<p>C&ograve;n với những chị em sinh mổ thời điểm tốt nhất để giảm c&acirc;n thường gấp đ&ocirc;i thời gian của người sinh thường, tức l&agrave; 6 th&aacute;ng. Bởi, với trường hợp sinh mổ, v&ugrave;ng bụng phụ nữ bị t&aacute;c động mạnh bởi phẫu thuật n&ecirc;n sẽ bị tổn thương nhiều, đau l&acirc;u hơn v&agrave; dễ bị nhiễm tr&ugrave;ng.</p>\r\n<p>Tuy nhi&ecirc;n, d&ugrave; sinh mổ hay sinh thường b&aacute;c sĩ Minh Trang cũng khuy&ecirc;n chị em, để c&aacute;c cơ v&agrave; lớp mỡ kh&ocirc;ng bị t&aacute;ch ra, tr&aacute;nh chảy nh&atilde;o nặng nề về sau sau sinh 2 th&aacute;ng chị em n&ecirc;n bắt đầu l&ecirc;n kế hoạch giảm c&acirc;n cho m&igrave;nh với chế độ ăn uống khoa học, thường xuy&ecirc;n vận động cơ thể nhẹ nh&agrave;ng sẽ tạo điều kiện gi&uacute;p qu&aacute; tr&igrave;nh giảm mỡ bụng sau n&agrave;y đạt hiệu quả tốt.</p>\r\n<p><strong>Chọn phương ph&aacute;p ph&ugrave; hợp để giảm b&eacute;o sau sinh an to&agrave;n hiệu quả.</strong></p>\r\n<p>Giải th&iacute;ch về t&igrave;nh trạng tăng c&acirc;n, cơ thể ph&aacute;t tướng sau sinh b&aacute;c sĩ Minh Trang cho biết, thực chất đ&acirc;y l&agrave; t&igrave;nh trạng t&iacute;ch lũy qu&aacute; mức tế b&agrave;o mỡ trắng trong cơ thể. Năng lượng nạp v&agrave;o dư thừa mỗi ng&agrave;y qu&aacute; nhiều t&iacute;ch lũy được chuyển h&oacute;a th&agrave;nh chất b&eacute;o dẫn đến t&igrave;nh trạng ph&igrave; đại tế b&agrave;o mỡ trắng. Ch&iacute;nh khả năng gia tăng k&iacute;ch thước của tế b&agrave;o mỡ trắng dẫn đến t&igrave;nh trạng tăng c&acirc;n, thậm ch&iacute; mất kiểm so&aacute;t c&acirc;n nặng, b&eacute;o ph&igrave;.</p>\r\n<p>Do vậy để kiểm so&aacute;t c&acirc;n nặng, gi&uacute;p giảm mỡ giảm c&acirc;n sau sinh 2 th&aacute;ng đến 3 th&aacute;ng đối với chị em sinh thường hoặc giảm c&acirc;n sau sinh 6 th&aacute;ng đối với chị em sinh mổ an to&agrave;n đạt kết quả cao nhất chị em cần ch&uacute; &yacute; c&oacute; chế độ ăn uống hợp l&yacute;, kiểm so&aacute;t đầu v&agrave;o từ 1.200 - 1.400 Kcal/ng&agrave;y.</p>\r\n<p><strong>Dưỡng Sinh Nhật Bản &ndash; Phương ph&aacute;p giảm c&acirc;n sau sinh tốt nhất hiện nay.</strong></p>\r\n<p>Hạn chế ăn c&aacute;c chất b&eacute;o, thức ăn nhiều đường bột như: cơm, b&uacute;n phở, b&aacute;nh kẹo ngọt, tr&aacute;i c&acirc;y ngọt, thức ăn nhanh, đồ chi&ecirc;n r&aacute;n...Tăng cường ăn nhiều rau xanh, củ, quả ngọt, uống đủ nước.</p>\r\n<p>Ngo&agrave;i ra, vận động cơ thể bằng c&aacute;c b&agrave;i tập ph&ugrave; hợp tối thiểu 30 ph&uacute;t mỗi ng&agrave;y. Mỗi tuần n&ecirc;n duy tr&igrave; 5 buổi tập để đạt hiệu quả giảm c&acirc;n sau sinh 2 th&aacute;ng nhanh nhất.</p>\r\n<p>Gần đ&acirc;y, xu hướng giảm c&acirc;n bằng c&ocirc;ng nghệ cao triệt ti&ecirc;u lượng mỡ thừa mỡ trắng thủ phạm ch&iacute;nh g&acirc;y ra t&igrave;nh trạng thừa c&acirc;n b&eacute;o ph&igrave; đang ng&agrave;y c&agrave;ng được ưa chuộng do t&iacute;nh khoa học v&agrave; hiệu quả giảm c&acirc;n l&acirc;u d&agrave;i của n&oacute;.</p>\r\n<p>Trong đ&oacute;, c&ocirc;ng nghệ Dưỡng Sinh Nhật Bản , một phương ph&aacute;p giảm c&acirc;n được kế thừa nền tảng v&agrave; ưu điểm của c&aacute;c phương ph&aacute;p giảm b&eacute;o, giảm c&acirc;n tự nhi&ecirc;n kết hợp c&ocirc;ng nghệ hiện đại Nhật Bản trước đ&oacute;. Đ&acirc;y l&agrave; một trong những phương ph&aacute;p giảm c&acirc;n hiệu quả ph&ugrave; hợp d&agrave;nh cho phụ nữ sau sinh, cả sinh thường v&agrave; sinh mổ.</p>\r\n<p>Ứng dụng 4 c&ocirc;ng_nghệ s&oacute;ng v&agrave; &aacute;nh s&aacute;ng bao gồm tia hồng ngoại sinh học, s&oacute;ng si&ecirc;u &acirc;m cộng hưởng từ, lực h&uacute;t ch&acirc;n kh&ocirc;ng v&agrave; s&oacute;ng BIO kết hợp kĩ thuật massage ấn huyệt chuẩn x&aacute;c, t&aacute;c động đ&uacute;ng m&ocirc; mỡ cần h&oacute;a lỏng đ&aacute;nh tan mỡ, ph&aacute; vỡ v&agrave; h&oacute;a lỏng c&aacute;c m&ocirc; mỡ cứng ra khỏi cơ thể qua đường b&agrave;i tiết tự nhi&ecirc;n m&ocirc;i h&ocirc;i v&agrave; nước tiểu m&agrave; kh&ocirc;ng cần phẫu thuật.</p>\r\n<p>Liệu tr&igrave;nh giảm b&eacute;o Dưỡng Sinh Nhật Bản ho&agrave;n to&agrave;n kh&ocirc;ng đau đớn, kh&ocirc;ng t&aacute;i t&iacute;ch mỡ m&agrave; lại thư gi&atilde;n như một k&igrave; dưỡng sinh đ&uacute;ng nghĩa.</p>\r\n<p>Sau liệu tr&igrave;nh 10 ng&agrave;y khoảng 60 &ndash; 90 ph&uacute;t/ buổi giảm b&eacute;o Dưỡng Sinh Nhật Bản, bạn c&oacute; thể giảm ngay 3-5kg, 15-20cm v&ugrave;ng bụng c&ugrave;ng nhiều cm số đo v&ugrave;ng kh&aacute;c. Ngo&agrave;i ra, độc tố t&iacute;ch tụ được đ&agrave;o thải, thanh lọc cơ thể gi&uacute;p thư gi&atilde;n v&agrave; ngăn ngừa bệnh tật.V&ugrave;ng da giảm b&eacute;o của bạn sẽ săn chắc trở lại, kh&ocirc;ng c&ograve;n ch&ugrave;ng nh&atilde;o, kh&ocirc;ng g&acirc;y bất kỳ t&aacute;c dụng phụ n&agrave;o cho cơ thể.</p>\r\n<p>CEO&amp; Founder của Lionbui Agency - Diệp B&ugrave;i (mẹ 3 con) vốn từng cảm thấy v&oacute;c d&aacute;ng l&agrave; điểm khiến m&igrave;nh tự ti trong giao tiếp. Sau liệu tr&igrave;nh Dưỡng Sinh Nhật Bản, chị Diệp B&ugrave;i đ&atilde; giảm 7kg, 23cm v&ugrave;ng bụng, sở hữu v&oacute;c d&aacute;ng \"vạn người m&ecirc;\" v&agrave; tự tin vững bước tr&ecirc;n con đường ph&aacute;t triển sự nghiệp của m&igrave;nh.</p>\r\n<p>Lu&ocirc;n đi đầu trong việc ứng dụng c&aacute;c c&ocirc;ng nghệ giảm c&acirc;n giảm b&eacute;o ti&ecirc;n tiến Svan Clinic And Spa đang &aacute;p dụng độc quyền phương ph&aacute;p giảm b&eacute;o Dưỡng Sinh Nhật Bản gi&uacute;p lấy lại sự tự tin v&agrave; th&acirc;n h&igrave;nh thon thả cho rất nhiều chị em sau sinh.</p>\r\n<p>Đến với Svan Clinic And Spa, bạn sẽ được c&aacute;c nh&acirc;n vi&ecirc;n đ&oacute;n tiếp tận t&igrave;nh v&agrave; được tư vấn trực tiếp bởi đội ngũ b&aacute;c sĩ, chuy&ecirc;n gia giảm b&eacute;o đầu ng&agrave;nh thăm kh&aacute;m tư vấn theo s&aacute;t cả liệu tr&igrave;nh.</p>\r\n<p>Sau khi thực hiện giảm c&acirc;n, c&aacute;c b&aacute;c sĩ thăm kh&aacute;m lại v&agrave; tư vấn chế độ chăm s&oacute;c để đảm bảo mang lại cho bạn kết quả giảm b&eacute;o tối ưu nhất.</p>\r\n<p>Hy vọng qua b&agrave;i viết n&agrave;y c&aacute;c chị đ&atilde; c&oacute; c&acirc;u trả lời cho vấn đề giảm c&acirc;n sau sinh 2 th&aacute;ng hay 4 th&aacute;ng tốt hơn? Biết được thời điểm n&agrave;o th&iacute;ch hợp để giảm b&eacute;o cũng như c&oacute; cho m&igrave;nh phương ph&aacute;p giảm b&eacute;o sau sinh an to&agrave;n hiệu quả.</p>\r\n<p>Nếu c&oacute; c&acirc;u hỏi, vấn đề g&igrave; thắc mắc hay cần t&igrave;m hiều th&ecirc;m về phương ph&aacute;p giảm c&acirc;n sau sinh 2 th&aacute;ng Dưỡng Sinh Nhật Bản c&aacute;c chị c&oacute; thể li&ecirc;n hệ đến 1900 0117 hay trực tiếp đến Svan Clinic &amp; Spa để được c&aacute;c chuy&ecirc;n gia giảm b&eacute;o h&agrave;ng đầu tư vấn, thăm kh&aacute;m đưa ra ph&aacute;c đồ giảm b&eacute;o an to&agrave;n nhanh ch&oacute;ng v&agrave; mang lại hiệu quả cao nhất.</p>\r\n<p>Ch&uacute;c c&aacute;c chị giảm c&acirc;n th&agrave;nh c&ocirc;ng v&agrave; nhanh ch&oacute;ng lấy lại v&oacute;c d&aacute;ng thon gọn như m&igrave;nh mong muốn.</p>', 'giam-can-sau-sinh-2-thang-hay-4-thang-tot-hon', 'posts/May2018/ajL3mGnyBsIW9u0Caryn.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Giảm cân sau sinh 2 tháng hay 4 tháng tốt hơn là băn khoăn của không ít chị em. Bài viết dưới đây sẽ giải đáp và gợi ý cho bạn phương pháp giảm cân nhanh và an toàn nhất hiện nay.', NULL, '17', 'PUBLISHED', '0', NULL),
(9, '3', '[THÁNG 05 RỰC RỠ] SVAN CLINIC AND SPA ĐÀO TẠO MIỄN PHÍ CHUYÊN VIÊN TRỊ LIỆU', 'Phục vụ nhu cầu mở rộng và phát triển cơ sở mới sang trọng, đẳng cấp chuẩn 5 sao của hệ thống Svan Clinic and Spa. Với mong muốn chiêu ngộ các bạn nữ tham gia vào hệ thống của Svan Clinic and Spa như sau', '<p>Phục vụ nhu cầu mở rộng v&agrave; ph&aacute;t triển cơ sở mới sang trọng, đẳng cấp chuẩn 5 sao của hệ thống Svan Clinic and Spa.</p>\r\n<p>Với mong muốn chi&ecirc;u ngộ c&aacute;c bạn nữ tham gia v&agrave;o hệ thống của Svan Clinic and Spa như sau:</p>\r\n<p><strong>***Vị tr&iacute;: Chuy&ecirc;n vi&ecirc;n trị liệu GIẢM B&Eacute;O/ BODY + CHĂM S&Oacute;C DA (SL: 15)</strong></p>\r\n<p>M&Ocirc; TẢ C&Ocirc;NG VIỆC</p>\r\n<ul>\r\n<li>Thực hiện c&aacute;c trị liệu chuy&ecirc;n nghiệp về chăm s&oacute;c da v&agrave; chăm s&oacute;c cơ thể, đồng bộ theo ti&ecirc;u chuẩn của spa</li>\r\n<li>Sử dụng m&aacute;y m&oacute;c c&ocirc;ng nghệ: l&agrave;m mặt, l&agrave;m body, triệt l&ocirc;ng, tắm trắng, giảm b&eacute;o&hellip;</li>\r\n<li>Tư vấn kh&aacute;ch h&agrave;ng về c&aacute;c dịch vụ v&agrave; sản phẩm tại c&ocirc;ng ty.</li>\r\n<li>Thời gian l&agrave;m việc từ: Theo ca</li>\r\n<li>Cụ thể sẽ được trao đổi khi phỏng vấn.</li>\r\n</ul>\r\n<p>Y&Ecirc;U CẦU CHUNG:</p>\r\n<ul>\r\n<li>Nếu chưa c&oacute; tay nghề sẽ được đ&agrave;o tạo miễn ph&iacute; về nghiệp vụ. Được l&agrave;m tại spa v&agrave; đảm bảo thu nhập khi ra nghề.</li>\r\n<li>Hoặc c&oacute; kinh nghiệm trong lĩnh vực spa, thẩm mỹ, c&aacute;c ng&agrave;nh dịch vụ tương tự</li>\r\n<li>Độ tuổi: 18-30 tuổi</li>\r\n<li>Khả năng đi c&ocirc;ng t&aacute;c tại hệ thống Svan Clinic and Spa</li>\r\n<li>Y&ecirc;u th&iacute;ch về thẩm mỹ, l&agrave;m đẹp v&agrave; c&oacute; kiến thức li&ecirc;n quan về sản phẩm mỹ phẩm v&agrave; thiết bị c&ocirc;ng nghệ, chế độ dinh dưỡng.</li>\r\n</ul>\r\n<p>QUYỀN LỢI:</p>\r\n<ul>\r\n<li>Đảm bảo lương cao (Mức lương cơ bản + thưởng + Hoa hồng)</li>\r\n<li>Được l&agrave;m việc trong m&ocirc;i trường chuy&ecirc;n nghiệp v&agrave; năng động</li>\r\n<li>Thưởng c&aacute;c dịp lễ tết</li>\r\n<li>Được k&yacute; HĐLĐ v&agrave; trợ cấp BHXH</li>\r\n<li>Được tặng voucher l&agrave;m đẹp h&agrave;ng th&aacute;ng</li>\r\n<li>Được nghỉ 4 ng&agrave;y/ th&aacute;ng.</li>\r\n</ul>\r\n<p>Y&Ecirc;U CẦU ỨNG VI&Ecirc;N CHUẪN BỊ ĐẦY ĐỦ HỒ SƠ DỰ TUYỂN</p>\r\n<ul>\r\n<li>Ảnh 4x6</li>\r\n<li>Đơn xin việc</li>\r\n<li>Sơ yếu l&yacute; lịch</li>\r\n<li>Hộ khẩu (photo)+ CMND</li>\r\n<li>Mọi chi tiết vui l&ograve;ng li&ecirc;n hệ: Ms Nga &ndash; P.HCNS: 0912 988 978</li>\r\n</ul>\r\n<p>** CHẤP NHẬN HỒ SƠ PHOTO KH&Ocirc;NG CẦN C&Ocirc;NG CHỨNG, MIỄN TRẢ HỒ SƠ KHI KH&Ocirc;NG TR&Uacute;NG TUYỂN</p>', 'thang-05-ruc-ro-svan-clinic-and-spa-dao-tao-mien-phi-chuyen-vien-tri-lieu', 'posts/May2018/rkGOdhFkG1Ko9bobORIm.JPG', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Phục vụ nhu cầu mở rộng và phát triển cơ sở mới sang trọng, đẳng cấp chuẩn 5 sao của hệ thống Svan Clinic and Spa. Với mong muốn chiêu ngộ các bạn nữ tham gia vào hệ thống của Svan Clinic and Spa như sau', NULL, '12', 'PUBLISHED', '0', NULL),
(10, '3', 'Giảm cân cấp tốc trong 1 ngày có tiềm ẩn biến chứng', 'Giảm cân cấp tốc trong 1 ngày có tiềm ẩn biến chứng? Trong bài viết sau đây các\r\nchuyên gia giảm béo hàng đầu sẽ giải đáp cho bạn.', '<p>Hiện nay c&oacute; rất nhiều phương ph&aacute;p giảm c&acirc;n cấp tốc trong 1 ng&agrave;y c&oacute; khả năng đem lại v&oacute;c d&aacute;ng thon gọn, săn chắc cho chị em phụ nữ. Vậy <strong>giảm c&acirc;n cấp tốc trong 1 ng&agrave;y</strong> c&oacute; tiềm ẩn biến chứng, c&oacute; nguy hiểm đến sức khỏe ch&uacute;ng ta hay kh&ocirc;ng?. H&atilde;y lắng nghe chuy&ecirc;n gia giảm b&eacute;o Phạm Th&ugrave;y V&acirc;n tại Svan Clinic And Spa giải đ&aacute;p về vấn đề n&agrave;y trong b&agrave;i viết sau đ&acirc;y.</p>\r\n<p><strong>Giảm c&acirc;n cấp tốc trong 1 ng&agrave;y c&oacute; tiềm ẩn biến chứng</strong></p>\r\n<p>Theo chuy&ecirc;n gia giảm b&eacute;o Phạm Th&ugrave;y V&acirc;n tại Svan Clinic And Spa &ldquo;<strong>Giảm c&acirc;n cấp tốc</strong> l&agrave; c&aacute;ch &aacute;p dụng một hoặc nhiều biện ph&aacute;p giảm b&eacute;o để c&oacute; thể hạ thấp c&acirc;n nặng trong thời gian ngắn nhất. Đối với mong muốn của chị em <strong>giảm c&acirc;n cấp tốc trong 1 ng&agrave;y</strong> l&agrave; điều ho&agrave;n to&agrave;n c&oacute; thể thực hiện. Tuy nhi&ecirc;n, bất cứ một biện ph&aacute;p giảm b&eacute;o cấp tốc n&agrave;o cũng đều phải tu&acirc;n theo cơ chế th&iacute;ch nghi của sinh l&yacute; cơ thể v&agrave; nguy&ecirc;n tắc khoa học của giảm c&acirc;n nếu kh&ocirc;ng sẽ g&acirc;y ra những hậu quả nghi&ecirc;m trọng.&rdquo;</p>\r\n<p style=\"text-align: center;\">Giảm c&acirc;n cấp tốc sai phương ph&aacute;p tiềm ẩn nhiều nguy cơ cho sức khỏe.</p>\r\n<p><strong>Tụt huyết &aacute;p, mệt mỏi, suy nhược cơ thể</strong></p>\r\n<p>Việc giảm c&acirc;n cấp tốc bằng c&aacute;ch nhịn ăn, nhịn uống hay tập luyện gắt gao với 1 cường độ cao sẽ khiến cơ thể đột ngột kh&ocirc;ng kịp xử l&yacute; dẫn đến mệt mỏi dẫn đến hiện tượng tụt huyết &aacute;p, ảnh hưởng trực tiếp tới sức khỏe. Về mặt dinh dưỡng, giảm c&acirc;n nhanh khiến cơ thể suy nhược, mất sức kh&aacute;ng của cơ thể.</p>\r\n<p><strong>Đột tử, co giật, dẫn đến tai biến</strong></p>\r\n<p>Đ&acirc;y l&agrave; biến chứng gặp phải khi &aacute;p dụng giảm c&acirc;n cấp tốc trong 1 ng&agrave;y bằng phương ph&aacute;p phẫu thuật h&uacute;t mỡ. Việc phẫu thuật lấy đi một lượng lớn mỡ thừa của cơ thể chỉ trong thời gian qu&aacute; ngắn khiến cơ thể kh&ocirc;ng th&iacute;ch nghi kịp thời sẽ dẫn đến t&igrave;nh trạng sốc phản vệ, co giật, nguy hiểm đến t&iacute;nh mạng.</p>\r\n<p><strong>Phản ứng ngược khi giảm c&acirc;n nhanh</strong></p>\r\n<p>Khi &aacute;p dụng c&aacute;c phương ph&aacute;p giảm c&acirc;n cấp tốc kh&ocirc;ng khoa học, kh&ocirc;ng &iacute;t trường hợp gặp phải phản ứng ngược v&iacute; dụ như cảm gi&aacute;c th&egrave;m ăn, ăn nhiều hơn b&igrave;nh thường, cơ thể t&iacute;ch nước khiến tăng c&acirc;n đột ngột. Điều n&agrave;y khiến c&ocirc;ng cuộc giảm c&acirc;n ng&agrave;y c&agrave;ng bế tắc.</p>\r\n<p style=\"text-align: center;\">100% người cảm thấy mệt mỏi khi &aacute;p dụng h&igrave;nh thức nhịn ăn, uống thuốc giảm c&acirc;n.</p>\r\n<p><strong>Giảm c&acirc;n cấp tốc &ndash; cần lựa chọn đ&uacute;ng phương ph&aacute;p an to&agrave;n</strong></p>\r\n<p>Bởi những hệ lụy ở tr&ecirc;n, chuy&ecirc;n gia giảm b&eacute;o Phạm Th&ugrave;y V&acirc;n khuy&ecirc;n bạn kh&ocirc;ng n&ecirc;n &ldquo;vội v&agrave;ng&rdquo; &aacute;p dụng c&aacute;c <strong>c&aacute;ch giảm c&acirc;n cấp tốc</strong> m&agrave; n&ecirc;n lựa chọn cẩn thận.</p>\r\n<p>Giảm c&acirc;n nhanh đồng thời an to&agrave;n cần tu&acirc;n thủ theo c&aacute;c nguy&ecirc;n tắc nhất định. Đ&aacute;p ứng c&aacute;c ti&ecirc;u ch&iacute; đ&oacute;, chuy&ecirc;n gia khuy&ecirc;n d&ugrave;ng phương ph&aacute;p giảm c&acirc;n/ giảm mỡ c&ocirc;ng nghệ Hifu Lipo. Được đ&aacute;nh gi&aacute; cao về t&iacute;nh an to&agrave;n bởi n&oacute; ph&ugrave; hợp với thể trạng chung của người &Aacute; Đ&ocirc;ng mặt kh&aacute;c sử dụng s&oacute;ng si&ecirc;u &acirc;m hội tụ t&aacute;c động ch&iacute;nh x&aacute;c với độ s&acirc;u từ 8-13mm dưới da &ndash; từ đ&oacute; ph&aacute; hủy kh&ocirc;ng phục hồi c&aacute;c m&ocirc; mỡ, c&aacute;c tế b&agrave;o mỡ sẽ mềm, tan r&atilde; ra v&agrave; chuyển h&oacute;a ra khỏi cơ thể qua đường b&agrave;i tiết tự nhi&ecirc;n (tuyến mồ h&ocirc;i, đường nước tiểu), m&ocirc; mỡ bị ph&aacute; huỷ kh&ocirc;ng c&oacute; khả năng phục hồi cho hiệu quả giảm b&eacute;o l&acirc;u d&agrave;i.</p>\r\n<p style=\"text-align: center;\"><strong>Hifu Lipo c&ocirc;ng nghệ giảm c&acirc;n cấp tốc hiệu quả</strong> an to&agrave;n được FDA chứng nhận.</p>\r\n<p>Chỉ sau 60 &ndash; 90 ph&uacute;t trị liệu chị em giảm tối đa 10-15cm, 3-5 c&acirc;n nặng. Trong v&ograve;ng 8-12 tuần sau đ&oacute;, c&aacute;c m&ocirc; mỡ bị ph&aacute; hủy sẽ tiếp tục được đ&agrave;o thải dần dần ra khỏi cơ thể theo qu&aacute; tr&igrave;nh tự nhi&ecirc;n. Đặc biệt, với phương ph&aacute;p Hifu Lipo bạn kh&ocirc;ng cần phải ăn ki&ecirc;ng hay thực hiện c&aacute;c b&agrave;i tập thể dục cực khổ m&agrave; vẫn duy tr&igrave; được v&oacute;c d&aacute;ng thon gọn.</p>\r\n<p>Sau khi điều trị, kh&aacute;ch h&agrave;ng c&oacute; thể về nh&agrave; lu&ocirc;n m&agrave; kh&ocirc;ng mất nhiều thời gian chăm s&oacute;c hay nghỉ dưỡng, nhanh ch&oacute;ng ổn định c&ocirc;ng việc v&agrave; sinh hoạt b&igrave;nh thường. Phương ph&aacute;p n&agrave;y được FDA chứng nhận t&iacute;nh an to&agrave;n, hiệu quả v&agrave; được ứng dụng th&agrave;nh c&ocirc;ng ở nhiều nước tr&ecirc;n thế giời trong đ&oacute; c&oacute; Việt Nam.</p>\r\n<p>Ng&agrave;y c&agrave;ng c&oacute; nhiều trung t&acirc;m thẩm mỹ ra đời khiến cho nhiều người băn khoăn khi lựa chọn địa chỉ thẩm mỹ uy t&iacute;n. Hiện nay, tại Th&agrave;nh phố Hồ Ch&iacute; Minh, Svan Clinic And Spa l&agrave; cơ sở được đ&aacute;nh gi&aacute; l&agrave; địa chỉ giảm mỡ an to&agrave;n v&agrave; hiệu quả nhất, &aacute;p dụng th&agrave;nh c&ocirc;ng c&ocirc;ng nghệ giảm c&acirc;n cấp tốc Hifu Lipo đạt chuẩn, sở hữu đội ngũ b&aacute;c sĩ, chuy&ecirc;n vi&ecirc;n trị liệu c&oacute; tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao, kh&aacute;ch h&agrave;ng h&agrave;i l&ograve;ng về kết quả cũng như th&aacute;i độ phục vụ trước, trong v&agrave; sau khi giảm thực hiện liệu tr&igrave;nh giảm b&eacute;o.</p>\r\n<p style=\"text-align: center;\"><strong>H&Igrave;NH ẢNH TRƯỚC SAU CHỊ PHẠM THY THƯ</strong></p>\r\n<p>Chị Phạm Thy Thư - 27 tuổi, &ldquo; Hifu Lipo đem lại hiệu quả rất tốt sau trị liệu đầu ti&ecirc;n, mỡ bụng của t&ocirc;i giảm xuống hơn 5cm. Kết hợp với tay nghề của kỹ thuật vi&ecirc;n Svan Clinic &amp; Spa mỡ bụng của t&ocirc;i giảm đi rất nhiều. Điều n&agrave;y thật tuyệt vời, c&aacute;c chỉ số cơ thể tốt hơn, mọi hoạt động thường ng&agrave;y thoải m&aacute;i v&agrave; dễ chịu&rdquo; .</p>\r\n<p>Hoa hậu victoria Th&uacute;y Vy lại ấn tượng với giải ph&aacute;p giảm c&acirc;n cấp tốc Hifu Lipo bởi yếu tố an to&agrave;n: &ldquo;L&uacute;c đầu khi biết Hifu Lipo giảm b&eacute;o 1 lần duy nhất th&igrave; hơi lo lắng về độ an to&agrave;n. Nhưng thực sự đến khi trải nghiệm mới thấy kh&ocirc;ng ảnh hưởng đến sức khỏe, kh&ocirc;ng cần thời gian nghỉ dưỡng. Cũng c&oacute; thể do t&ocirc;i may mắn được m&aacute;ch đến Svan &ndash; một địa chỉ uy t&iacute;n n&ecirc;n c&oacute; c&ocirc;ng nghệ ch&iacute;nh h&atilde;ng, b&aacute;c sỹ tay nghề cao.&rdquo;</p>\r\n<p>Diễn vi&ecirc;n Dương Cẩm Lynh giảm được tổng 35cm v&ograve;ng eo khi kết hợp Hifu Lipo v&agrave; Pefect Shape tại Svan Clinic, t&ocirc;i c&oacute; thể tự tin diện những bộ v&aacute;y khoe đường cong cơ thể\".</p>\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; những ph&acirc;n t&iacute;ch v&agrave; giải đ&aacute;p của chuy&ecirc;n gia Phạm Th&ugrave;y V&acirc;n về vấn đề giảm c&acirc;n cấp tốc trong 1 ng&agrave;y c&oacute; tiềm ẩn biến chứng, hi vọng gi&uacute;p bạn hiểu r&otilde; hơn trước khi đưa ra sự lựa chọn cho m&igrave;nh.</p>\r\n<p>Nếu bạn c&ograve;n thắc mắc g&igrave; về c&ocirc;ng nghệ Hifu Lipo &ndash; c&ocirc;ng nghệ giảm b&eacute;o 1 lần duy nhất c&oacute; thể đến trực tiếp Svan hoặc li&ecirc;n hệ đến số 1900 0117 để được c&aacute;c chuy&ecirc;n gia giảm b&eacute;o tư vấn giải đ&aacute;p, thăm kh&aacute;m miễn ph&iacute; v&agrave; c&oacute; cơ hội trở th&agrave;nh kh&aacute;ch h&agrave;ng may mắn nhận được nhiều ưu đ&atilde;i khủng trong th&aacute;ng.</p>', 'giam-can-cap-toc-trong-1-ngay-co-tiem-an-bien-chung', 'posts/May2018/jFF7uzEdVckXkPKyONkO.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Giảm cân cấp tốc trong 1 ngày có tiềm ẩn biến chứng? Trong bài viết sau đây các\r\nchuyên gia giảm béo hàng đầu sẽ giải đáp cho bạn.', NULL, '17', 'PUBLISHED', '0', NULL),
(11, '3', 'Mẹ 8x giảm cân trong 1 tuần xuấn 4kg, không ai nhận ra', '“Giấu” chồng con mỗi ngày biến mất khoảng hơn 3 tiếng đồng hồ. Bà mẹ 8x Phương Thảo bất ngờ giảm cân cấp tốc trong 1 tuần, “biến hình” thon gọn.', '<p>D&ugrave; kh&aacute; bận rộn với gia đ&igrave;nh v&agrave; cả c&ocirc;ng việc kinh doanh ri&ecirc;ng, chị Phương Thảo (32 tuổi, B&igrave;nh Dương) vẫn rất tranh thủ chăm s&oacute;c sắc đẹp của bản th&acirc;n. Tuy nhi&ecirc;n, quỹ thời gian hạn hẹp kh&ocirc;ng đủ để chị &ldquo;ti&ecirc;u diệt&rdquo; những ngấn mỡ dần to hơn kể từ sau khi sinh em b&eacute;.</p>\r\n<p><strong>C&acirc;u chuyện của b&agrave; mẹ 8x v&agrave; h&agrave;nh tr&igrave;nh giảm c&acirc;n hiệu quả trong 1 tuần</strong></p>\r\n<p>L&agrave; người kinh doanh, chị Phương Thảo lu&ocirc;n &yacute; thức được rằng việc x&acirc;y dựng h&igrave;nh tượng c&aacute; nh&acirc;n tốt sẽ hỗ trợ rất nhiều trong c&ocirc;ng việc, h&igrave;nh tượng đ&oacute; bao gồm cả ngoại h&igrave;nh, kiến thức lẫn c&aacute;ch giao tiếp trong cuộc sống.</p>\r\n<p>Tuy nhi&ecirc;n, kể từ khi sinh em b&eacute;, quỹ thời gian vốn hạn hẹp của chị lại c&agrave;ng thu nhỏ, v&ograve;ng bụng tưởng chừng sẽ được chị nhanh ch&oacute;ng &ldquo;giải quyết&rdquo; sau khi hết thời gian ở cử, lại đi theo chị dai dẳng k&yacute; th&ecirc;m bao nhi&ecirc;u c&aacute;i hợp đồng.</p>\r\n<p>Phấn đấu giảm c&acirc;n bao nhi&ecirc;u lần đều thất bại, bởi tập gym hay c&aacute;c b&agrave;i tập để giảm c&acirc;n đều đ&ograve;i hỏi sự ki&ecirc;n tr&igrave; v&agrave; cường độ tập luyện cao, c&ograve;n ăn ki&ecirc;ng th&igrave; kh&ocirc;ng đủ năng lượng để chị &ldquo;chống chọi&rdquo; qua một ng&agrave;y l&agrave;m việc căng thẳng.</p>\r\n<p>Chị chia sẻ: &ldquo;C&aacute;c thực đơn ăn ki&ecirc;ng low carb kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; khoa học, nhưng chỉ d&agrave;nh cho những người c&oacute; nhiều thời gian nghỉ ngơi v&agrave; tập trung thư gi&atilde;n để giảm c&acirc;n. Với khối lượng c&ocirc;ng việc một ng&agrave;y t&ocirc;i phải giải quyết, nếu b&aacute;m theo thực đơn đ&oacute; t&ocirc;i sợ m&igrave;nh kh&ocirc;ng đủ sức khỏe để ho&agrave;n th&agrave;nh tốt mọi thứ.</p>\r\n<p>Thực đơn ăn ki&ecirc;ng low carb được x&acirc;y dựng khoa học nhưng kh&ocirc;ng d&agrave;nh cho những người phải l&agrave;m việc dưới cường độ, &aacute;p lực cao</p>\r\n<p>D&ugrave; kh&ocirc;ng hy vọng g&igrave; v&agrave;o việc tập luyện hay ăn ki&ecirc;ng để giảm c&acirc;n, nhưng chị Thảo cũng kh&ocirc;ng nghĩ đến chuyện h&uacute;t mỡ thừa v&igrave; &ldquo;T&ocirc;i lu&ocirc;n đặt yếu tố sức khỏe l&ecirc;n h&agrave;ng đầu, d&ugrave; đẹp cũng phải an to&agrave;n, chỉ cần c&oacute; khả năng rủi ro, t&ocirc;i cũng kh&ocirc;ng th&iacute;ch đ&aacute;nh cược. T&ocirc;i để tin thần đ&aacute;nh cược của m&igrave;nh v&agrave;o c&ocirc;ng kinh doanh sẽ tốt hơn!&rdquo;</p>\r\n<p>Với khao kh&aacute;t lấy lại v&oacute;c d&aacute;ng ng&agrave;y xưa, khao kh&aacute;t giảm mỡ thừa trong cơ thể, kh&ocirc;ng chỉ v&igrave; chị muốn lấy lại v&oacute;c d&aacute;ng thời son rỗi, m&agrave; c&ograve;n v&igrave; lo nghĩ đến sức khỏe bản th&acirc;n. &ldquo;Ở thởi buổi n&agrave;y hầu như ai cũng biết mỡ thừa c&oacute; thể tiềm ẩn đủ c&aacute;c loại bệnh, từ tim mạch, tiểu đường&hellip; đến rối loại c&aacute;c homone khiến da dẻ xấu đi, c&ograve;n ảnh hưởng đến t&acirc;m sinh l&yacute;.</p>\r\n<p>Nếu m&igrave;nh kh&ocirc;ng đủ sức khỏe, con m&igrave;nh sẽ kh&ocirc;ng được chăm s&oacute;c tốt, c&ocirc;ng việc của m&igrave;nh cũng rất dễ bị dở dang&rdquo; &ndash; chị chia sẻ.</p>\r\n<p><strong>Điều g&igrave; tạo n&ecirc;n b&iacute; quyết giảm c&acirc;n nhanh trong 1 tuần?</strong></p>\r\n<p>Thời đại c&ocirc;ng nghệ l&ecirc;n ng&ocirc;i, ngay cả trong việc giảm b&eacute;o từ mấy năm nay cũng đ&atilde; &ldquo;rục rịch&rdquo; kh&aacute;c nhiều loại c&ocirc;ng nghệ ti&ecirc;n tiến ra đời. Biết được điều n&agrave;y, chị Thảo đ&atilde; t&igrave;m hiểu cẩn thận, cuối c&ugrave;ng chị quyết định &ldquo;trốn&rdquo; chồng con mỗi ng&agrave;y hơn 3 tiếng để &ldquo;lặn lội&rdquo; từ B&igrave;nh Dương l&ecirc;n tận S&agrave;i G&ograve;n để giảm b&eacute;o. Sau 7 ng&agrave;y &ldquo;lặng lẽ&rdquo; giảm b&eacute;o, chị bất ngờ giảm được 4kg, với 27cm v&ugrave;ng bụng, một con số qu&aacute; ấn tượng.</p>\r\n<p style=\"text-align: center;\"><em>Contri Ultrashape b&iacute; quyết giảm c&acirc;n nhanh trong 1 tuần của chị Phương Thảo.</em></p>\r\n<p>Chị cho biết: &ldquo;Đ&uacute;ng l&agrave; kh&ocirc;ng tiếc 7 ng&agrave;y của m&igrave;nh. Với ph&aacute;c đồ điều trị giảm b&eacute;o Contri Ultrashape m&agrave; b&aacute;c sĩ tại Svan Clinic and Spa đ&atilde; x&acirc;y dựng cho t&ocirc;i, cộng với thời gian chạy đi v&agrave; về giữa B&igrave;nh Dương &ndash; S&agrave;i G&ograve;n hơn 1 tiếng. Như vậy tổng cộng t&ocirc;i chỉ mất 3 tiếng mỗi ng&agrave;y, m&agrave; lại c&oacute; kết quả kh&ocirc;ng tưởng&rdquo;.</p>\r\n<p>Phương ph&aacute;p giảm b&eacute;o Contri Ultrashape được xem như một bước tiến c&ocirc;ng nghệ của Hoa Kỳ, được FDA chứng nhận an to&agrave;n v&agrave; hiệu quả, c&oacute; khả năng ph&aacute; hủy tế b&agrave;o mỡ dưới da ở độ s&acirc;u 1,5cm, gi&uacute;p giảm 32% lượng mỡ thừa, thu gọn 10-15cm v&ograve;ng bụng chỉ trong v&ograve;ng 7 ng&agrave;y. Với trường hợp của chị Thảo, với c&ugrave;ng khoảng thời gian lại giảm được đến 27cm l&agrave; một kết quả ngo&agrave;i mong đợi.</p>\r\n<p>Chị Phương Thảo giảm c&acirc;n cấp tốc trong 1 tuần xuống 4kg, thu gọn 27cm v&ograve;ng bụng bằng c&ocirc;ng nghệ Contri Ultrashape tại Svan Clinic and Spa, g&acirc;y bất ngờ cho &ocirc;ng x&atilde; v&agrave; con Với cơ chế đ&agrave;o thải mỡ tự nhi&ecirc;n, kết hợp c&ocirc;ng nghệ Hoa Kỳ v&agrave; phương ph&aacute;p massage truyền thống Nhật Bản, Contri Ultrashape n&agrave;y c&oacute; khả năng triệt ti&ecirc;u được cả mỡ cứng l&acirc;u năm, giải quyết được cả những trường hợp đ&atilde; sử dụng nhiều h&igrave;nh thức trị liệu giảm b&eacute;o kh&aacute;c nhưng kh&ocirc;ng hiệu quả.</p>\r\n<p>Tại Svan, kh&aacute;ch h&agrave;ng sẽ được b&aacute;c sĩ trực tiếp thăm kh&aacute;m, c&acirc;n đo v&agrave; ph&acirc;n t&iacute;ch ch&iacute;nh x&aacute;c t&igrave;nh trạng mỡ thừa theo từng cơ địa với m&aacute;y INBODY nhập khẩu từ HOA KỲ, sau đ&oacute; sẽ l&ecirc;n ph&aacute;t đồ điều trị giảm b&eacute;o ph&ugrave; hợp cho từng cơ địa cụ thể.</p>\r\n<p>Bạn c&oacute; biết, Contri Ultrashape được người d&ugrave;ng b&igrave;nh chọn phương ph&aacute;p giảm b&eacute;o an to&agrave;n hiệu quả tốt nhất 2018, l&agrave; phương ph&aacute;p được c&aacute;c sao Việt v&agrave; nhiều phụ nữ lựa chọn.</p>\r\n<p><strong>Muốn giảm c&acirc;n nhanh nhất trong 1 tuần</strong>? H&atilde;y đăng k&yacute; ngay để được tư vấn, thăm kh&aacute;m miễn ph&iacute; v&agrave; giảm b&eacute;o c&ugrave;ng c&ocirc;ng nghệ Contri Ultrashape độc quyền tại Svan Clinic and Spa c&aacute;c bạn nh&eacute;!</p>', 'me-8x-giam-can-trong-1-tuan-xuan-4kg-khong-ai-nhan-ra', 'posts/May2018/WUryacJBD7jqcTRz3Ava.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, '“Giấu” chồng con mỗi ngày biến mất khoảng hơn 3 tiếng đồng hồ. Bà mẹ 8x Phương Thảo bất ngờ giảm cân cấp tốc trong 1 tuần, “biến hình” thon gọn.', NULL, '17', 'PUBLISHED', '0', NULL);
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(12, '3', 'Giảm mỡ không phẫu thuật duy nhất 1 lần chỉ mất 90 phút', 'Hifu Lipo- công nghệ giảm mỡ không phẫu thuật từ Hoa Kỳ giúp giảm tối đa 10 -15 cm mỡ thừa chỉ sau 90 phút thực hiện, cam kết hiệu quả bằng văn bản.', '<p>Kh&ocirc;ng chỉ l&agrave; phương ph&aacute;p giảm b&eacute;o hiệu quả, nhanh ch&oacute;ng chỉ sau 90 ph&uacute;t giảm tối đa 10-15cm, 3-5kg c&acirc;n nặng. Hifu Lipo c&ograve;n l&agrave; c&ocirc;ng nghệ được FDA v&agrave; c&aacute;c tổ chức y tế uy t&iacute;n c&ocirc;ng nhận l&agrave; phương ph&aacute;p giảm mỡ kh&ocirc;ng phẫu thuật nhanh ch&oacute;ng, hiệu quả, an to&agrave;n tuyệt đối.</p>\r\n<p><strong>Hifu Lipo &ndash; <em>c&ocirc;ng nghệ giảm mỡ kh&ocirc;ng phẫu thuật</em> tốt nhất hiện n&agrave;y</strong></p>\r\n<p>Trở th&agrave;nh c&ocirc;ng nghệ giảm mỡ t&acirc;n tiến bậc nhất, Hifu Lipo mang lại kết quả giảm mỡ đ&ugrave;i đ&aacute;ng kinh ngạc m&agrave; c&ocirc;ng nghệ th&ocirc;ng thường trước đ&oacute; kh&ocirc;ng thể l&agrave;m được. C&ocirc;ng nghệ Hifu Lipo được FDA c&ocirc;ng nhận mang lại kết quả tốt nhất m&agrave; kh&ocirc;ng cần phẫu thuật, gi&uacute;p loại bỏ mỡ thừa triệt để chỉ sau 1 lần thực hiện, kh&ocirc;ng lo b&eacute;o lại, đặc biệt kh&ocirc;ng cần đến chế độ ăn ki&ecirc;ng, tập luyện cực khổ phức tạp.</p>\r\n<p>M&aacute;y c&ocirc;ng nghệ Hifu Lipo ch&iacute;nh h&atilde;ng. Hifu Lipo sử dụng năng lượng s&oacute;ng si&ecirc;u &acirc;m hội tụ cường độ cao t&aacute;c động ở độ s&acirc;u tập trung dưới da, t&aacute;c động v&agrave;o m&ocirc; mỡ d&agrave;y ở tầng s&acirc;u&hellip; Dẫn truyền năng lượng với nhiệt độ 56-60*C, năng lượng của Hifu Lipo sẽ ph&aacute; vỡ li&ecirc;n kết mỡ v&agrave; m&agrave;ng chắn độc tố bao bọc, từ đ&oacute; ph&aacute; hủy kh&ocirc;ng phục hồi c&aacute;c m&ocirc; mỡ, c&aacute;c tế b&agrave;o mỡ sẽ mềm v&agrave; chuyển h&oacute;a ra khỏi cơ thể qua đường b&agrave;i tiết tự nhi&ecirc;n (tuyến mồ h&ocirc;i, đường nước tiểu).</p>\r\n<p style=\"text-align: center;\"><em>Chỉ sau 90 <strong>giảm mỡ kh&ocirc;ng phẫu thuật</strong> Hifu Lipo hiệu quả giảm tối đa 10-15cm, 3-5kg c&acirc;n nặng v&ugrave;ng giảm b&eacute;o.</em></p>\r\n<p>Dựa tr&ecirc;n nguy&ecirc;n l&yacute; đốt ch&aacute;y mỡ thừa Hifu Lipo cho hiệu quả giảm mỡ thật, giảm số đo tối đa 10-15cm v&ugrave;ng giảm b&eacute;o chỉ 1 lần duy nhất, 90 ph&uacute;t cho c&aacute;c v&ugrave;ng mỡ kh&oacute; giảm tại bụng, lưng, vai, bắp tay, đ&ugrave;i, bắp ch&acirc;n&hellip;.Đốt ch&aacute;y mỡ thừa cả ở cả 2 dạng cứng v&agrave; mềm, từ lớp mỡ n&ocirc;ng cho tới lớp mỡ s&acirc;u nhất. Từ đ&oacute; giảm mỡ từng phần tới giảm c&acirc;n tổng thể nếu muốn. Đặc biệt, sau 8-12 tuần c&aacute;c m&ocirc; mỡ bị ph&aacute; hủy sẽ tiếp tục được đ&agrave;o thải, những ngấn mỡ thừa sẽ được x&oacute;a sổ ho&agrave;n to&agrave;n, kh&ocirc;ng c&oacute; khả năng t&iacute;ch tụ trở lại.</p>\r\n<p>Kh&ocirc;ng chỉ t&aacute;c động v&agrave;o m&ocirc; mỡ, s&oacute;ng si&ecirc;u &acirc;m hội tụ đồng thời k&iacute;ch th&iacute;ch tăng sinh collagen v&agrave; elastin cả về chất lượng v&agrave; số lượng gi&uacute;p bề mặt da tại v&ugrave;ng trị liệu kh&ocirc;ng bị tr&ugrave;ng nh&atilde;o, chảy xệ, tăng sự đ&agrave;n hồi, cải thiện t&igrave;nh trạng rạn sau sinh. Ngay sau hỗ trợ điều trị kh&aacute;ch h&agrave;ng vẫn c&oacute; thể c&aacute;c hoạt động sinh hoạt thường ng&agrave;y m&agrave; kh&ocirc;ng cần mất thời gian nghỉ dưỡng. Đ&acirc;y được xem l&agrave; ưu điểm vượt trội của c&ocirc;ng nghệ Hifu Lipo, được nhiều kh&aacute;ch h&agrave;ng quan t&acirc;m, lựa chọn.</p>\r\n<p><strong>Svan Clinic And Spa &ndash; Địa chỉ <em>giảm b&eacute;o kh&ocirc;ng phẫu thuật</em> uy t&iacute;n tại Tp.HCM</strong></p>\r\n<p>C&ocirc;ng nghệ Hifu Lipo &ndash; liệu ph&aacute;p giảm mỡ kh&ocirc;ng phẫu thuật đ&atilde; v&agrave; đang được &aacute;p dụng độc quyền tại Svan Clinic And Spa. Rất nhiều chị em phụ nữ khi đến Svan đều chọn sử dụng c&ocirc;ng nghệ giảm mỡ đ&ugrave;i n&agrave;y. 100% kh&aacute;ch h&agrave;ng sau khi thực hiện đều cảm thấy h&agrave;i l&ograve;ng. Svan Clinic &amp; Spa l&agrave; một trong &iacute;t địa chỉ giảm b&eacute;o đ&atilde; được Bộ y tế chứng nhận v&agrave; cấp ph&eacute;p hoạt động. B&ecirc;n cạnh đ&oacute;, Svan lu&ocirc;n ch&uacute; trọng v&agrave; cập nhật những c&ocirc;ng nghệ giảm mỡ kh&ocirc;ng phẫu thuật ti&ecirc;n tiến h&agrave;ng đầu từ ph&iacute;a c&aacute;c đối t&aacute;c nước ngo&agrave;i.</p>\r\n<p>Trước khi giảm b&eacute;o kh&aacute;ch h&agrave;ng sẽ được c&aacute;c b&aacute;c sĩ tư vấn, thăm kh&aacute;m v&agrave; đưa ra ph&aacute;c đồ giảm b&eacute;o ph&ugrave; hợp nhất.</p>\r\n<p>Đội ngũ b&aacute;c sĩ, chuy&ecirc;n vi&ecirc;n trị liệu tại Svan Clinic And Spa 100% đều được đ&agrave;o tạo chuy&ecirc;n s&acirc;u, tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao, c&oacute; nhiều năm kinh nghiệm trong ng&agrave;nh. Trước khi tiến h&agrave;nh giảm b&eacute;o bụng bằng Hifu Lipo, b&aacute;c sĩ sẽ tiến h&agrave;nh thăm kh&aacute;m c&acirc;n đo tư vấn đưa ra ph&aacute;c đồ điều trị ph&ugrave; hợp với thể trạng bản th&acirc;n. Hơn nữa, kh&aacute;ch h&agrave;ng cũng sẽ hiểu r&otilde; hơn về cơ chế t&aacute;c động, hiệu quả hay độ an to&agrave;n của c&ocirc;ng nghệ&hellip; th&ocirc;ng qua sự tư vấn của b&aacute;c sĩ để c&oacute; thể đi đến quyết định cuối c&ugrave;ng l&agrave; c&oacute; n&ecirc;n tiến h&agrave;nh giảm mỡ hay kh&ocirc;ng.</p>\r\n<p>Đặc biệt, duy nhất tại Svan Clinic And Spa kh&aacute;ch h&agrave;ng sẽ được k&yacute; văn bản cam kết ho&agrave;n tiền 100% nếu sau liệu tr&igrave;nh kh&ocirc;ng c&oacute; hiệu quả giảm b&eacute;o. Svan chắc chắn sẽ l&agrave; nơi chọn lựa tốt nhất d&agrave;nh cho bạn.</p>\r\n<p>Trong năm 2017 tại Svan hơn 10.000 kh&aacute;ch h&agrave;ng đ&atilde; giảm b&eacute;o th&agrave;nh c&ocirc;ng với phương ph&aacute;p giảm mỡ kh&ocirc;ng phẫu thuật Hifu Lipo. 100% cảm thấy h&agrave;i l&ograve;ng với qu&aacute; tr&igrave;nh trị liệu cũng như kết quả giảm b&eacute;o đạt được sau liệu tr&igrave;nh.</p>\r\n<p>H&igrave;nh trước sau Dương Cẩm Lynh</p>\r\n<p>Diễn vi&ecirc;n Dương Cẩm Lynh: \"T&ocirc;i đ&atilde; giảm được tổng 35cm v&ograve;ng eo khi kết hợp Hifu Lipo v&agrave; Pefect Shape tại Svan Clinic, t&ocirc;i c&oacute; thể tự tin diện những bộ v&aacute;y khoe đường cong cơ thể\".</p>\r\n<p>H&igrave;nh trước sau B&iacute;ch San</p>\r\n<p>Chị B&iacute;ch San (38 tuổi) \"Bụng m&igrave;nh l&uacute;c trước bị ngấn mỡ n&ecirc;n m&igrave;nh rất thiếu tự tin khi diện những chiếc v&aacute;y body. Nhưng sau khi giảm b&eacute;o với Hifu Lipo, v&ograve;ng eo m&igrave;nh nhỏ đi tr&ocirc;ng thấy, v&ugrave;ng da trị liệu cũng rất căng phẳng. Tới đ&acirc;y m&igrave;nh quyết định sẽ trị liệu th&ecirc;m v&ugrave;ng đ&ugrave;i để c&oacute; v&oacute;c d&aacute;ng thon gọn như mong muốn\".</p>\r\n<p>Hy vọng với những th&ocirc;ng tin tr&ecirc;n đ&atilde; gi&uacute;p bạn biết r&otilde; hơn về c&ocirc;ng nghệ Hifu Lipo phương ph&aacute;p giảm mỡ kh&ocirc;ng phẫu thuật hiệu quả cũng như địa chỉ uy t&iacute;n đ&atilde; v&agrave; đang &aacute;p dụng c&ocirc;ng nghệ n&agrave;y. Nếu bạn c&ograve;n thắc mắc cần giải đ&aacute;p về c&aacute;c c&ocirc;ng nghệ giảm mỡ kh&ocirc;ng phẫu thuật c&oacute; thể đến trực tiếp đến Svan Clinic And Spa hoặc đăng k&yacute; tư vấn miễn ph&iacute; qua form đăng k&yacute; dưới đ&acirc;y để được c&aacute;c chuy&ecirc;n gia giảm mỡ h&agrave;ng đầu tư vấn nhanh nhất.</p>', 'giam-mo-khong-phau-thuat-duy-nhat-1-lan-chi-mat-90-phut', 'posts/May2018/nvOgEnyqTYO2BD8Ql8P6.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, 'Hifu Lipo- công nghệ giảm mỡ không phẫu thuật từ Hoa Kỳ giúp giảm tối đa 10 -15 cm mỡ thừa chỉ sau 90 phút thực hiện, cam kết hiệu quả bằng văn bản.', NULL, '17', 'PUBLISHED', '0', NULL),
(13, '3', 'Cách giảm mỡ bụng không cần ăn kiêng hiệu quả 100%', 'Công nghệ Perfect Shape  - cách giảm mỡ bụng không cần ăn kiêng, tập luyện. giảm ngay 15-20cm vùng bụng chỉ sau 10 ngày. Tìm hiểu ngay.', '<p>Việc ăn ki&ecirc;ng, luyện tập để giảm lượng mỡ thừa v&ograve;ng bụng cần phải được thực hiện một c&aacute;ch l&acirc;u d&agrave;i v&agrave; ki&ecirc;n nhẫn. Tuy nhi&ecirc;n, với c&aacute;ch giảm mỡ bụng kh&ocirc;ng phẫu thuật bằng c&ocirc;ng nghệ cao - Perfect Shape gi&uacute;p bạn giảm 15-20cm v&ograve;ng bụng sở hữu v&oacute;c d&aacute;ng thon gọn, chiếc bụng phẳng l&igrave; chỉ sau 10 ng&agrave;y. Bạn c&oacute; tin?</p>\r\n<p>Perfect Shape &ndash; c&aacute;ch giảm mỡ bụng kh&ocirc;ng cần ăn ki&ecirc;ng hiệu quả 100%</p>\r\n<p>Perfect Shape l&agrave; c&ocirc;ng nghệ giảm b&eacute;o, c&aacute;ch giảm mỡ bụng kh&aacute; phổ biến v&agrave; được chị em ưa chuộng nhất hiện nay. Kh&ocirc;ng phẫu thuật, kh&ocirc;ng phải ăn ki&ecirc;ng, tập thể dục cực khổ, kh&ocirc;ng phải uống thuốc giảm c&acirc;n, an to&agrave;n tuyệt đối, kh&ocirc;ng g&acirc;y ảnh hưởng tới những cơ quan nội tạng kh&aacute;c trong cơ thể. Đồng thời cải thiện l&agrave;n da trở n&ecirc;n mềm mại v&agrave; săn chắc hơn.</p>\r\n<p>C&ocirc;ng nghệ ứng dụng c&ocirc;ng nghệ Velas - Shape với 3 đầu trị liệu ri&ecirc;ng biệt kết hợp Lipo Laser, bi- polar năng lượng tần số v&ocirc; tuyến RF v&agrave; Cavitation si&ecirc;u &acirc;m mạnh mẽ... c&oacute; khả năng tạo ra nhiệt từ 45 đến 65 độ C với bước s&oacute;ng 650nm xuy&ecirc;n s&acirc;u v&agrave;o v&ugrave;ng t&iacute;ch mỡ cứng nằm tận đ&aacute;y hạ b&igrave; đ&aacute;nh ta, h&oacute;a lỏng mỡ th&agrave;nh v&ocirc; v&agrave;n ph&acirc;n tử si&ecirc;u nhỏ.</p>\r\n<p>Sau đ&oacute;, kết hợp thao t&aacute;c massage chuy&ecirc;n s&acirc;u truyền thống sẽ k&iacute;ch th&iacute;ch tuần ho&agrave;n m&aacute;u, tăng cường trao đổi chất v&agrave; b&agrave;i tiết, gi&uacute;p dịch mỡ nhanh ch&oacute;ng bị đ&agrave;o thải qua tuyến mồ h&ocirc;i, đường nước tiểu một c&aacute;ch dễ d&agrave;ng.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, c&ograve;n tổ chức sắp xếp lại c&aacute;c sợi collagen, gi&uacute;p cho l&agrave;n da trở n&ecirc;n mềm mại v&agrave; săn chắc hơn. Hạn chế được t&igrave;nh trạng chảy xệ, ch&ugrave;ng da sau khi kết th&uacute;c qu&aacute; tr&igrave;nh trị liệu.</p>\r\n<p>Chỉ 10 buổi, khoảng 60 &ndash; 90 ph&uacute;t/ buổi giảm ngay 15-20cm, 3-5kg c&acirc;n nặng sở hữu eo thon d&aacute;ng gọn. Đặc biệt với c&aacute;ch giảm mỡ bụng bằng c&ocirc;ng nghệ Perfect Shape kh&ocirc;ng cần nghỉ dưỡng, kh&ocirc;ng ảnh hưởng đến sinh hoạt h&agrave;ng ng&agrave;y. Vơi những chị em bận rộn c&oacute; thể tranh thủ vừa ngủ trưa vừa giảm b&eacute;o.</p>\r\n<p>Giảm mỡ bụng kh&ocirc;ng cần ăn ki&ecirc;ng bằng c&ocirc;ng nghệ Perfect Shape ở đ&acirc;u uy t&iacute;n?</p>\r\n<p>L&agrave; một thương hiệu chăm s&oacute;c sắc đẹp uy t&iacute;n tại Tp.HCM lu&ocirc;n đ&oacute;n đầu những xu hướng l&agrave;m đẹp tr&ecirc;n thế giới, cập nhật v&agrave; ứng dụng li&ecirc;n tục những c&ocirc;ng nghệ giảm b&eacute;o giảm mỡ bụng hiện đại ti&ecirc;n tiến. Trong đ&oacute; c&oacute; c&ocirc;ng nghệ giảm mỡ bụng kh&ocirc;ng cần ăn ki&ecirc;ng kh&ocirc;ng phẫu thuật Perfect Shape.</p>\r\n<p>Svan tự h&agrave;o c&oacute; đội ngũ b&aacute;c sĩ giỏi chuy&ecirc;n m&ocirc;n cao, chuy&ecirc;n vi&ecirc;n trị liệu gi&agrave;u kinh nghiệm; nh&acirc;n vi&ecirc;n tư vấn chuyện nghiệp v&agrave; thấu hiểu kh&aacute;ch h&agrave;ng.</p>\r\n<p>Trang thiết bị m&aacute;y m&oacute;c được nhập khẩu ho&agrave;n to&agrave;n từ nước ngo&agrave;i với c&ocirc;ng nghệ tối t&acirc;n được ứng dụng v&agrave;o qu&aacute; tr&igrave;nh trị liệu v&agrave; chăm s&oacute;c thẩm mỹ. C&aacute;c thiết bị m&aacute;y m&oacute;c được nhập khẩu trực tiếp từ Ch&acirc;u u, Mỹ, H&agrave;n Quốc, Nhật Bản&hellip;</p>\r\n<p>Kh&aacute;ch h&agrave;ng sẽ được thăm kh&aacute;m tận t&acirc;m v&agrave; tư vấn tận t&igrave;nh để t&igrave;m ra c&aacute;ch giảm mỡ bụng tối ưu nhất nhằm mang lại hiệu quả cao v&agrave; tiết kiệm cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>Đội ngũ y b&aacute;c sĩ của ch&uacute;ng t&ocirc;i sẽ lu&ocirc;n đồng h&agrave;nh c&ugrave;ng kh&aacute;ch h&agrave;ng trong suốt qu&aacute; tr&igrave;nh điều trị v&agrave; sau điều trị với mong muốn mang đến một kết quả tối ưu nhất.</p>\r\n<p>Tại Svan t&ugrave;y thuộc nhu cầu giảm mỡ to&agrave;n th&acirc;n của chị em sẽ được đ&aacute;p ứng hiệu quả t&ugrave;y t&igrave;nh trạng cũng như điều kiện của mỗi người. Đảm bảo mang đến sự h&agrave;i l&ograve;ng nhất cho chị em. Đặc biệt, trước khi thực hiện quy tr&igrave;nh giảm b&eacute;o, giảm mỡ bụng kh&aacute;ch h&agrave;ng sẽ được k&yacute; văn bản cam kết hiệu quả nếu sau liệu tr&igrave;nh kh&ocirc;ng giảm sẽ ho&agrave;n tiền 100%.</p>\r\n<p>Đặc biệt, trong năm 2017 đ&atilde; c&oacute; hơn 10.000 kh&aacute;ch h&agrave;ng đ&atilde; đ&atilde; trải nghiệm c&aacute;ch giảm mỡ bụng Perfect Shape tại Svan v&agrave; 100 % h&agrave;i l&ograve;ng hiệu quả của phương ph&aacute;p mang lại.</p>\r\n<p>B&agrave; mẹ 1 con B&iacute;ch Tr&acirc;m đ&atilde; giảm b&eacute;o th&agrave;nh c&ocirc;ng giảm 4kg v&agrave; 21cm (tương đương 3 size quần &aacute;o) nhờ c&ocirc;ng nghệ giảm b&eacute;o Perfect Shape tại Svan.</p>\r\n<p>H&igrave;nh trước sau Dương Cẩm Lynh</p>\r\n<p>Thần h&igrave;nh ph&aacute;t tướng sau sinh, nhưng sau 10 ng&agrave;y &aacute;p dụng phương ph&aacute;p giảm b&eacute;o tạo form Perfect Shape, Diễn vi&ecirc;n Dương Cẩm Lynh đ&atilde; giảm 7kg c&acirc;n nặng, 10cm v&ograve;ng bụng v&agrave; trở về th&acirc;n h&igrave;nh quyến rũ vốn c&oacute;.</p>\r\n<p>Chị Minh Hoa đ&atilde; lấy lại được v&ograve;ng eo l&yacute; tuởng trước đ&acirc;y nhờ liệu tr&igrave;nh giảm b&eacute;o m&agrave; Perfect Shape tại Svan.</p>\r\n<p>&ldquo;T&ocirc;i đ&atilde; giảm hơn 30cm v&ograve;ng bụng, từ 52kg sau liệu tr&igrave;nh 10 ng&agrave;y c&ograve;n 48kg. Đ&acirc;y l&agrave; đều t&ocirc;i chưa bao giờ d&aacute;m mơ trước đ&oacute;&rdquo;, chị Minh Hoa chia sẻ. Sau khi ho&agrave;n th&agrave;nh liệu tr&igrave;nh, v&ograve;ng bụng chị săn chắc hơn hẳn, mọi vận động cơ thể trở n&ecirc;n thoải m&aacute;i so với trước l&uacute;c điều trị. \"Giờ đ&acirc;y t&ocirc;i đ&atilde; tự tin diện những bộ quần &aacute;o y&ecirc;u th&iacute;ch m&agrave; chưa d&aacute;m thử một lần v&igrave; ngại b&eacute;o\", chị n&oacute;i th&ecirc;m.</p>\r\n<p>Để c&oacute; thể nắm kĩ hơn những th&ocirc;ng tin li&ecirc;n quan về c&aacute;ch giảm mỡ bụng kh&ocirc;ng phẫu thuật Perfect Shape cũng như quy tr&igrave;nh giảm b&eacute;o tại Svan Clinic And Spa c&aacute;c bạn c&oacute; thể đăng k&yacute; ngay để được tư vấn miễn ph&iacute; nh&eacute;.</p>\r\n<div class=\"service-detail\">\r\n<div class=\"service-detail-wrapper\" style=\"margin-top: 0;\">\r\n<div class=\"btn-wrapper\">\r\n<div class=\"btn-icon btn-form\" data-toggle=\"modal\" data-target=\"#modal-19\">\r\n<p>ĐĂNG K&Yacute; TƯ VẤN</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', 'cach-giam-mo-bung-khong-can-n-kieng-hieu-qua-100', 'posts/May2018/xsgDypDHVfG00PLt3clp.jpg', '2018-04-25 18:09:14', '2018-05-13 08:39:47', NULL, 'Công nghệ Perfect Shape  - cách giảm mỡ bụng không cần ăn kiêng, tập luyện. giảm ngay 15-20cm vùng bụng chỉ sau 10 ngày. Tìm hiểu ngay.', NULL, '17', 'PUBLISHED', '0', 'old'),
(23, '3', 'Cách ăn giảm cân đảm bảo ngon miệng', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"text-align: justify;\">Khi thực hiện&nbsp;c&aacute;ch ăn giảm c&acirc;n, đồng nghĩa với việc chấp nhận hạn chế ăn nhiều m&oacute;n ngon, hấp dẫn. Thay v&agrave;o đ&oacute;, ăn nhiều rau quả, loại thực phẩm &iacute;t chất b&eacute;o, tinh bột.</span></p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Khi thực hiện&nbsp;c&aacute;ch ăn giảm c&acirc;n, đồng nghĩa với việc chấp nhận hạn chế ăn nhiều m&oacute;n ngon, hấp dẫn. Thay v&agrave;o đ&oacute;, ăn nhiều rau quả, loại thực phẩm &iacute;t chất b&eacute;o, tinh bột. Điều n&agrave;y dễ dẫn đến t&igrave;nh trạng ch&aacute;n ăn, ăn kh&ocirc;ng ngon miệng. Tuy nhi&ecirc;n, với một số m&oacute;n như rau muốn x&agrave;o bạch tuộc, canh mồng tơi nấu thịt nạc, xu h&agrave;o luộc...th&igrave; ho&agrave;n to&agrave;n c&oacute; thể khiến vị gi&aacute;c bạn c&oacute; cảm gi&aacute;c rất ngon miệng đấy nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Bạch tuộc x&agrave;o rau muống</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bạch tuộc c&oacute; thể chế biến th&agrave;nh nhiều m&oacute;n, nhưng nếu đang thực hiện chế độ giảm c&acirc;n th&igrave; n&ecirc;n l&agrave;m m&oacute;n bạch tuộc x&agrave;o rau muống. Bạch tuộc nhiều vitamin, trong khi đ&oacute;, rau muống chứa chất xơ n&ecirc;n ch&uacute;ng l&agrave; sự kết hợp ho&agrave;n hảo để giảm c&acirc;n v&agrave; vẫn c&oacute; thể gi&uacute;p bạn ngon miệng.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-an-giam-can-4.jpg\" style=\"width: 500px; height: 393px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Canh mồng tơi nấu thịt nạc</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Mồng tơi nấu thịt nạc chắc hẳn được hầu hết c&aacute;c mẹ ưa chuộng v&igrave; n&oacute; gi&uacute;p bạn ngon miệng, lại thanh m&aacute;t cơ thể. Song song đ&oacute;, trong mồng tơi nhiều chất xơ v&agrave; vitamin, kho&aacute;ng chất v&ocirc; c&ugrave;ng c&oacute; lợi trong việc loại bỏ mữa thừa.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-an-giam-can-1.jpg\" style=\"width: 500px; height: 335px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Hu h&agrave;o luộc</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Su h&agrave;o luộc chế biến đơn giản nhưng vị ngọt của n&oacute; khiến bạn lu&ocirc;n thấy ngon miệng v&agrave; kh&ocirc;ng ng&aacute;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-an-giam-can-2.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n\n<p>&nbsp;</p>\n', 'cach-an-giam-can-dam-bao-ngon-mieng', '/Upload/News/c7e99bae-23b5-4f76-ac12-2588762a1cb6/cach-an-giam-can-1.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách ăn giam cân với những món đảm bảo ngon miệng', 'Bạch tuộc xào rau muốn, canh mồng tơi thịt nạt, su hào luộc... là những món ăn ngon miệng cho bạn bổ sung vào những cách ăn giảm cân của mình.', 'cách ăn giảm cân', '44', 'PUBLISHED', '', 'old'),
(24, '3', 'Không cần tắm trắng, chỉ cần đắp thử sau một lần da sẽ \'sáng cả đời\'', '<p>Những c&aacute;ch dưỡng trắng tại nh&agrave; từ những nguy&ecirc;n liệu dễ kiếm dưới đ&acirc;y sẽ gi&uacute;p bạn c&oacute; được l&agrave;n da đẹp như mong ước.</p>\n', '<p>Những c&aacute;ch dưỡng trắng tại nh&agrave; từ những nguy&ecirc;n liệu dễ kiếm dưới đ&acirc;y sẽ gi&uacute;p bạn c&oacute; được l&agrave;n da đẹp như mong ước.</p>\n\n<div>\n<p><span style=\"color:#3ab572;\"><strong>Sử dụng đường trắng</strong></span></p>\n\n<p style=\"text-align: justify;\">Đường trắng kh&ocirc;ng chỉ l&agrave; nguồn cung cấp năng lượng nhanh ch&oacute;ng cho cơ thể m&agrave; c&ograve;n l&agrave; gia vị kh&ocirc;ng thể thiếu trong nhiều m&oacute;n ăn của người Việt. B&ecirc;n cạnh đ&oacute;, đường trắng cũng l&agrave; giải ph&aacute;p cải thiện da mặt, mang lại cho bạn l&agrave;n da trắng rất hữu &iacute;ch, gi&aacute; th&agrave;nh rẻ, dễ kiếm.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cham-soc-da-3-ngoisao_vn_.jpg\" style=\"width: 500px; height: 334px;\" /></p>\n\n<p style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Chăm s&oacute;c da với đường trắng</strong></span></p>\n\n<p style=\"text-align: justify;\">C&aacute;ch l&agrave;m: Bạn trộn đều 1 th&igrave;a đường trắng với 1 th&igrave;a dầu oliu tạo th&agrave;nh một hỗn hợp đặc sệt. Sau đ&oacute;, thoa đều ch&uacute;ng l&ecirc;n tr&ecirc;n bề mặt da. Bạn c&oacute; thể massage nhẹ nh&agrave;ng gi&uacute;p c&aacute;c tinh chất thẩm thấu v&agrave;o da nhanh hơn. Sau đ&oacute;, đợi khoảng 15-20 ph&uacute;t rồi rửa sạch mặt với nước ấm. Đ&acirc;y được coi l&agrave; c&aacute;ch l&agrave;m mặt nạ trắng da cấp tốc m&agrave; kh&ocirc;ng bị kh&ocirc; da được nhiều chị em &aacute;p dụng.</p>\n\n<p style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>C&aacute;ch l&agrave;m trắng da với sữa chua</strong></span></p>\n\n<p style=\"text-align: justify;\">Sữa chua chứa một h&agrave;m lượng cao vitamin A, vitamin B1, B2, vitamin E&hellip; sữa chua c&oacute; thể ngăn chặn t&igrave;nh trạng oxi h&oacute;a, chống kh&ocirc; v&agrave; bị sừng h&oacute;a, th&ocirc; r&aacute;p. V&igrave; vậy sữa chua ch&iacute;nh được xem l&agrave; nguy&ecirc;n liệu l&agrave;m đẹp ho&agrave;n hảo, c&oacute; thể gi&uacute;p ch&uacute;ng ta chăm s&oacute;c to&agrave;n diện cơ thể.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cham-soc-da-2-ngoisao_vn_.jpg\" style=\"width: 500px; height: 334px;\" /></p>\n\n<p style=\"text-align: justify;\">Để l&agrave;m đẹp da mặt, bạn lấy &frac12; hộp sữa chua kh&ocirc;ng đường trộn đều c&ugrave;ng vỏ cam t&aacute;n nhuyễn. D&ugrave;ng hỗn hợp n&agrave;y b&ocirc;i l&ecirc;n da mặt, giữ trong khoảng 15 ph&uacute;t rồi rửa lại bằng nước m&aacute;t.</p>\n\n<p style=\"text-align: justify;\">Hỗn hợp l&agrave;m mịn da to&agrave;n th&acirc;n: trộn 10 th&igrave;a c&agrave; ph&ecirc; sữa chua với 2 th&igrave;a c&agrave; ph&ecirc; tinh dầu olive rồi thoa hỗn hợp n&agrave;y l&ecirc;n người, massage nhẹ nh&agrave;ng v&agrave; tắm sạch lại sau khoảng 15 ph&uacute;t. Chắc chắn bạn sẽ cảm nhận được vẻ mịn m&agrave;ng v&agrave; trắng l&ecirc;n từng ng&agrave;y của l&agrave;n da.</p>\n\n<p style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Trắng da với nha đam</strong></span></p>\n\n<p style=\"text-align: justify;\">Trong l&ocirc; hội c&oacute; chứa nhiều chất dinh dưỡng, kho&aacute;ng chất như canxi, natri, kẽm&hellip; v&agrave; c&aacute;c vitamin B1, C, A, E cần thiết cho một l&agrave;n da đẹp v&agrave; khỏe mạnh. Ngo&agrave;i ra, nguy&ecirc;n liệu n&agrave;y c&ograve;n l&agrave;m da tươi tắn v&agrave; điều h&ograve;a độ acid của da, gi&uacute;p da trắng hồng tự nhi&ecirc;n do độ pH của gel nha đam gần giống với PH của da. Ch&iacute;nh v&igrave; thế, l&ocirc; hội trở th&agrave;nh một trong những lựa chọn l&agrave;m đẹp quen thuộc của nhiều c&ocirc; g&aacute;i.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cham-soc-da-1-ngoisao_vn_.jpg\" style=\"width: 500px; height: 237px;\" /></p>\n\n<p style=\"text-align: justify;\">C&aacute;ch l&agrave;m: Nha đam gọt vỏ, th&aacute;i nhỏ rồi cho v&agrave;o đun s&ocirc;i c&ugrave;ng nước. Sau đ&oacute; lấy cả miếng nha đam v&agrave; phần nước ho&agrave; với mật ong. Đợi khoảng 2 ph&uacute;t cho hỗn hợp thấm đều th&igrave; d&ugrave;ng tay thoa hỗn hợp nhẹ tr&ecirc;n da mặt khoảng 10-15 ph&uacute;t.</p>\n</div>\n', 'khong-can-tam-trang-chi-can-dap-thu-sau-mot-lan-da-se-sang-ca-doi', '/Upload/News/fdbd5a78-7ef2-4b69-979d-2595ed3657ef/hfgfhjgfhjghfjd.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Không cần tắm trắng, chỉ cần đắp thử sau một lần da sẽ \'sáng cả đời\'', 'Những cách dưỡng trắng tại nhà từ những nguyên liệu dễ kiếm dưới đây sẽ giúp bạn có được làn da đẹp như mong ước.', 'cách dưỡng trắng', '44', 'PUBLISHED', '', 'old'),
(25, '3', 'Tóc hết bạc kỳ diệu chỉ với vỏ khoai tây', '<p><span style=\"color: rgb(62, 77, 92); font-family: Arial, sans-serif;\">D&ugrave; c&oacute; nhiều sản phẩm nhuộm t&oacute;c tr&ecirc;n thị trường, thế nhưng bạn vẫn n&ecirc;n sử dụng sản phẩm từ tự nhi&ecirc;n để an to&agrave;n m&agrave; hiệu quả vẫn rất tuyệt vời chỉ với vỏ khoai t&acirc;y.</span></p>\n', '<p style=\"text-align: justify;\">D&ugrave; c&oacute; nhiều sản phẩm nhuộm t&oacute;c tr&ecirc;n thị trường, thế nhưng bạn vẫn n&ecirc;n sử dụng sản phẩm từ tự nhi&ecirc;n để an to&agrave;n m&agrave; hiệu quả vẫn rất tuyệt vời chỉ với <em><strong>vỏ khoai t&acirc;y</strong></em>.</p>\n\n<div>\n<p style=\"text-align: justify;\">V&igrave; vậy, nếu t&oacute;c bạn đang xuất hiện những sợi t&oacute;c x&aacute;m, bạc m&agrave; bạn muốn che phủ n&oacute; th&igrave; h&atilde;y sử dụng một số phương ph&aacute;p chữa trị tự nhi&ecirc;n sau để kh&ocirc;i phục lại m&agrave;u sắc t&oacute;c tự nhi&ecirc;n.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/toc-het-bac-vo-khoai-ngoisao_vn_.jpg\" style=\"width: 500px; height: 271px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>T&oacute;c hết bạc kỳ diệu chỉ với vỏ khoai t&acirc;y</strong></p>\n\n<p style=\"text-align: justify;\">Th&agrave;nh phần:</p>\n\n<p style=\"text-align: justify;\">Khoai t&acirc;y rửa sạch gọt lấy vỏ</p>\n\n<p style=\"text-align: justify;\">Chuẩn bị:</p>\n\n<p style=\"text-align: justify;\">D&ugrave;ng 5 củ khoai t&acirc;y v&agrave; gọt lấy vỏ. (Bạn sẽ kh&ocirc;ng cần ruột khoai t&acirc;y, do đ&oacute; bạn c&oacute; thể tự do sử dụng ch&uacute;ng để chế biến cho bữa ăn). Sau đ&oacute;, đổ hai ch&eacute;n nước lạnh v&agrave;o nồi v&agrave; cho vỏ khoai t&acirc;y v&agrave;o. Khi nước s&ocirc;i th&igrave; vặn giảm nhiệt v&agrave; đun nhỏ lửa trong năm ph&uacute;t. Tắt bếp v&agrave; để cho thật nguội.</p>\n\n<p style=\"text-align: justify;\">Tiếp đ&oacute;, bạn th&ecirc;m một ch&uacute;t dầu hương thảo hoặc oải hương pha c&ugrave;ng cho thơm, sau đ&oacute; cho hỗn hợp v&agrave;o một chiếc b&igrave;nh đựng bằng thủy tinh c&oacute; nắp đậy.</p>\n\n<p style=\"text-align: justify;\">Mỗi lần sau khi gội đầu, bạn d&ugrave;ng hỗn hợp n&agrave;y gội nước cuối. (Đầu ti&ecirc;n bạn vẫn d&ugrave;ng dầu gội đầu như b&igrave;nh thường sau khi xả sạch dầu gội với nước, bạn massage t&oacute;c bằng nước vỏ khoai t&acirc;y v&agrave; kh&ocirc;ng gội lại với nước). Cuối c&ugrave;ng, để t&oacute;c kh&ocirc; như b&igrave;nh thường.</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n</div>\n', 'toc-het-bac-ky-dieu-chi-voi-vo-khoai-tay', '/Upload/News/a73a260d-c94b-4cfe-923d-16550c8ac931/vo-khoai-tay.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Tóc hết bạc kỳ diệu chỉ với vỏ khoai tây', 'Dù có nhiều sản phẩm nhuộm tóc trên thị trường, thế nhưng bạn vẫn nên sử dụng sản phẩm từ tự nhiên để an toàn mà hiệu quả vẫn rất tuyệt vời chỉ với vỏ khoai tây.', 'vỏ khoai tây', '44', 'PUBLISHED', '', 'old'),
(26, '3', 'Cách giảm cân cấp tốc trong 3 ngày', '<p><span style=\"text-align: justify;\">Trong x&atilde; hội hiện đại, quỹ thời gian d&agrave;nh cho c&aacute; nh&acirc;n của phụ nữ c&oacute; xu hướng b&oacute; hẹp dần. Ch&iacute;nh v&igrave; thế, những&nbsp;c&aacute;ch giảm c&acirc;n cấp tốc&nbsp;đạt hiệu quả cao rất được chị em đang bị thừa c&acirc;n quan t&acirc;m.</span></p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Trong x&atilde; hội hiện đại, quỹ thời gian d&agrave;nh cho c&aacute; nh&acirc;n của phụ nữ c&oacute; xu hướng b&oacute; hẹp dần. Ch&iacute;nh v&igrave; thế, những&nbsp;c&aacute;ch giảm c&acirc;n cấp tốc&nbsp;đạt hiệu quả cao rất được chị em đang bị thừa c&acirc;n quan t&acirc;m. Trong b&agrave;i viết n&agrave;y, ch&uacute;ng t&ocirc;i xin chia sẻ với bạn thực đơn để giảm c&acirc;n cấp tốc,&nbsp;chỉ cần thực hiện trong 3 ng&agrave;y sẽ cho kết quả r&otilde; rệt.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Ng&agrave;y 1:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa s&aacute;ng: 1 quả t&aacute;o, pho m&aacute;t, 5 c&aacute;i b&aacute;nh quy, nước lọc&nbsp;hay c&agrave; ph&ecirc;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa trưa: 1 quả trứng luộc, 1 l&aacute;t b&aacute;nh m&igrave; kh&ocirc;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa tối: 1 ch&eacute;n c&aacute; ngừ, 1 ch&eacute;n c&agrave; rốt, 1 ch&eacute;n b&ocirc;ng cải trắng, 1 ch&eacute;n dưa leo.</p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-giam-can-cap-toc-1.jpg\" style=\"width: 500px; height: 250px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\">Đọc th&ecirc;m :&nbsp;\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://&quot;http://svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-bi-quyet-giup-ban-giam-can-khi-dang-cho-con-bu\">c&aacute;ch giảm c&acirc;n cho phụ nữ đang cho con b&uacute;</a><br />\n<em>C&aacute; ngừ thường kh&ocirc;ng thể thiếu trong hầu hết c&aacute;c thực đơn giảm c&acirc;n</em></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Ng&agrave;y 2:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa s&aacute;ng: 1 ch&eacute;n sữa chua trộn hoa quả tươi</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa trưa: 1 quả c&agrave; chua, rau x&agrave; l&aacute;ch, 1 quả dưa chuột, 2 miếng pho m&aacute;t.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa tối: Thịt g&agrave; nướng, 1 ly sinh tố c&agrave; chua.</p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-giam-can-cap-toc-2.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\">Bạn c&oacute; thể muốn xem :&nbsp;\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/lam-cach-nao-de-giam-mo-bung-sau-khi-sinh-em-be\">l&agrave;m sao để giảm mỡ bụng sau sinh nhanh nhất</a><br />\n<em>Thịt g&agrave; nướng</em></p>\n\n<p style=\"text-align: justify;\"><strong>Ng&agrave;y 3:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa s&aacute;ng: 1 cốc c&agrave; ph&ecirc; đen hoặc ly nước tr&agrave; hoa c&uacute;c, 1 quả trứng g&agrave; ta, nửa quả chuối.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa trưa: 1 cốc sữa chua hoặc c&aacute; r&aacute;n, 8 c&aacute;i b&aacute;nh quy</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Bữa tối: 2 l&aacute;t thịt b&ograve; r&aacute;n, 1 b&aacute;t canh, nửa củ c&agrave; rốt, nửa quả chuối.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</p>\n', 'cach-giam-can-cap-toc-trong-3-ngay', '/Upload/News/fa523658-5e28-453b-9e6d-ad552e90c125/do-an-giam-beo-4-1600x900.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách giảm cân cấp tốc trong 3 ngày', 'Cách giảm cân cấp tốc trong 3 ngày vẫn có thể cho kết quả tốt nếu bạn tuân thủ nghiêm ngặt chế độ ăn uống và thực hiện đúng giờ giấc', 'cách giảm cân cấp tốc', '44', 'PUBLISHED', '', 'old'),
(27, '3', 'Công thức làm đẹp kỳ diệu từ hạt tiêu đen', '<p>Hạt ti&ecirc;u đen c&oacute; chứa c&aacute;c loại vitamin v&agrave; kho&aacute;ng chất phong ph&uacute; m&agrave; cơ thể của ch&uacute;ng ta cần h&agrave;ng ng&agrave;y. N&oacute; c&ograve;n c&oacute; t&aacute;c dụng tuyệt vời trong vấn đề l&agrave;m đẹp da.</p>\n', '<p>Hạt ti&ecirc;u đen c&oacute; chứa c&aacute;c loại vitamin v&agrave; kho&aacute;ng chất phong ph&uacute; m&agrave; cơ thể của ch&uacute;ng ta cần h&agrave;ng ng&agrave;y. N&oacute; c&ograve;n c&oacute; t&aacute;c dụng tuyệt vời trong vấn đề <em><strong>l&agrave;m đẹp da</strong></em>.</p>\n\n<div>\n<p>V&iacute; dụ, n&oacute; c&oacute; chứa beta carotene (được biết đến rộng r&atilde;i như l&agrave; vitamin A) cần thiết để giữ sức khỏe của mắt. Tiếp đ&oacute; l&agrave; chứa vitamin K cần thiết cho lưu th&ocirc;ng m&aacute;u v&agrave; giữ chức năng trao đổi chất tốt, đồng thời gi&uacute;p xương v&agrave; cơ bắp khỏe mạnh.</p>\n\n<p>Ngo&agrave;i ra, ti&ecirc;u đen c&oacute; chứa kali, selen v&agrave; canxi. Kali điều chỉnh huyết &aacute;p, selen l&agrave; &lsquo;ch&igrave;a kh&oacute;a&rsquo; để h&igrave;nh th&agrave;nh xương, m&oacute;ng, t&oacute;c v&agrave; răng chắc khỏe, n&oacute; cũng cần thiết để bộ n&atilde;o hoạt động đ&uacute;ng c&aacute;ch. Canxi cũng được biết đến trong việc gi&uacute;p xương khỏe mạnh v&agrave; đặc biệt tốt khi chống lại c&aacute;c bệnh xương như: lo&atilde;ng xương v&agrave; thấp khớp từ việc ăn ti&ecirc;u đen.</p>\n\n<p>Bạn c&oacute; thể sử dụng hạt ti&ecirc;u đen, hoặc dầu hạt ti&ecirc;u đen để giải quyết rất nhiều vấn đề về da.</p>\n\n<p>Dưới đ&acirc;y 3 c&ocirc;ng thức tự nhi&ecirc;n tự chế với hạt ti&ecirc;u đen v&agrave; hạt ti&ecirc;u đen tinh dầu để l&agrave;m ẩm da của bạn v&agrave; gi&uacute;p tẩy tế b&agrave;o chết tr&ecirc;n da, tẩy vết bẩn v&agrave; vết sẹo.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/tieu-den-lam-dep-ngoisao_vn_.jpg\" /></p>\n\n<div>\n<h3>C&ocirc;ng thức l&agrave;m đẹp v&agrave; trị mụn từ hạt ti&ecirc;u đen</h3>\n\n<p>1 - Ti&ecirc;u đen v&agrave; sữa chua</p>\n\n<p>Th&agrave;nh phần:</p>\n</div>\n\n<ul>\n	<li>1 muỗng canh hạt ti&ecirc;u đen</li>\n	<li>2 muỗng canh sữa chua</li>\n</ul>\n\n<p>Trộn 2 th&agrave;nh phần lại với nhau th&agrave;nh một hỗn hợp. Rửa mặt sạch với nước ấm để mở c&aacute;c lỗ ch&acirc;n l&ocirc;ng sau đ&oacute; đắp hỗn hợp l&ecirc;n mặt ch&agrave; x&aacute;t v&agrave;o da theo chuyển động tr&ograve;n v&agrave; để lại v&agrave;i ph&uacute;t. Cuối c&ugrave;ng, rửa sạch với nước lạnh v&agrave; thấm kh&ocirc; khu&ocirc;n mặt của bạn.</p>\n\n<p>Điều n&agrave;y sẽ gi&uacute;p bạn loại bỏ c&aacute;c vết bẩn v&agrave; vết sẹo, đồng thời cũng sẽ l&agrave;m sạch l&agrave;n da của bạn v&agrave; cung cấp dưỡng chất gi&uacute;p cho da s&aacute;ng đẹp rạng rỡ.</p>\n\n<p>2 - C&ocirc;ng thức ti&ecirc;u đen trị mụn</p>\n\n<p>Bạn sẽ cần:</p>\n\n<ul>\n	<li>1 muỗng canh mật ong</li>\n	<li>1/2 một th&igrave;a c&agrave; ph&ecirc; hạt ti&ecirc;u đen</li>\n	<li>1 l&ograve;ng trứng trắng</li>\n</ul>\n\n<p>Với mặt nạ n&agrave;y, bạn sẽ l&agrave;m sạch được khu&ocirc;n mặt của bạn; từ mụn trứng c&aacute; v&agrave; mụn nhọt. Chỉ cần pha trộn c&aacute;c th&agrave;nh phần (mật ong - ti&ecirc;u đen v&agrave; l&ograve;ng trắng trứng) với nhau, đắp mặt nạ tr&ecirc;n mặt v&agrave; để trong khoảng nửa giờ. Sau đ&oacute;, rửa mặt bằng nước ấm.</p>\n\n<p>3 - Tinh dầu hạt ti&ecirc;u đen v&agrave; kem dưỡng da</p>\n\n<p>Bạn sẽ cần:</p>\n\n<ul>\n	<li>3 giọt tinh dầu hạt ti&ecirc;u đen</li>\n	<li>100 ml kem cơ thể hoặc kem dưỡng da</li>\n</ul>\n\n<p>Th&ecirc;m ba giọt tinh dầu v&agrave;o kem chăm s&oacute;c cơ thể y&ecirc;u th&iacute;ch của bạn hoặc kem dưỡng da v&agrave; trộn, sau đ&oacute; ch&agrave; x&aacute;t n&oacute; trong quần của bạn mỗi buổi s&aacute;ng v&agrave; tối. N&oacute; sẽ gi&uacute;p bạn loại bỏ cellulite (l&agrave; hiện tượng da sần vỏ cam hay mỡ t&iacute;ch tụ dưới da) m&agrave; kh&ocirc;ng cần nhiều nỗ lực, đặc biệt nếu bạn c&oacute; th&oacute;i quen &aacute;p dụng kem cơ thể thường xuy&ecirc;n.</p>\n</div>\n', 'cong-thuc-lam-dep-ky-dieu-tu-hat-tieu-den', '/Upload/News/a6a9bf28-161b-4421-90c9-fdb9a1fed3ec/lam-dep-da.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Công thức làm đẹp kỳ diệu từ hạt tiêu đen', 'Hạt tiêu đen có chứa các loại vitamin và khoáng chất phong phú mà cơ thể của chúng ta cần hàng ngày. Nó còn có tác dụng tuyệt vời trong vấn đề làm đẹp da.', 'làm đẹp da', '44', 'PUBLISHED', '', 'old'),
(28, '3', '7 cách thoát khỏi mụn đầu đen dễ dàng', '<p>H&atilde;y loại bỏ mụn đầu đen kinh tởm bằng liệu ph&aacute;p tự nhi&ecirc;n v&agrave; kh&ocirc;ng ti&ecirc;u tốn nhiều tiền với những phương ph&aacute;p dưới đ&acirc;y.</p>\n', '<p style=\"text-align: justify;\">H&atilde;y loại bỏ mụn đầu đen kinh tởm bằng liệu ph&aacute;p tự nhi&ecirc;n v&agrave; kh&ocirc;ng ti&ecirc;u tốn nhiều tiền với những phương ph&aacute;p dưới đ&acirc;y.</p>\n\n<div>\n<p style=\"text-align: justify;\"><strong>1. Baking Soda tẩy tế b&agrave;o chết</strong></p>\n\n<p style=\"text-align: justify;\">Tự chế sữa rửa mặt tẩy tế b&agrave;o chết bằng c&aacute;ch: Trộn baking soda với một ch&uacute;t nước.</p>\n\n<p style=\"text-align: justify;\">Baking soda l&agrave;m tr&oacute;c da, tế b&agrave;o chết, mang đến l&agrave;n da mềm mại v&agrave; mịn m&agrave;ng. Baking soda gi&uacute;p trung h&ograve;a độ pH của l&agrave;n da v&agrave; hạn chế việc sản xuất dầu &iacute;t hơn, ngăn chặn ho&agrave;n to&agrave;n mụn đầu đen.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/4-cach-tay-te-bao-chet-an-toan-don-gian-tai-nha-2-ngoisao_vn_(1).jpg\" style=\"width: 500px; height: 332px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>2. Nước &eacute;p c&agrave; chua</strong></p>\n\n<p style=\"text-align: justify;\">Sử dụng nước &eacute;p c&agrave; chua hoặc bột c&agrave; chua như một mặt nạ l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng. Đắp mặt nạ mỗi tối trước khi đi ngủ.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nuoc-ep-trai-cay-ngoisao_vn_.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p><span style=\"text-align: justify;\">C&agrave; chua chứa h&agrave;m lượng vitamin C, vitamin A v&agrave; axit citric, l&agrave; một phương thuốc tự nhi&ecirc;n trị mụn đầu đen.</span></p>\n\n<p style=\"text-align: justify;\"><strong>3. L&ograve;ng trắng trứng</strong></p>\n\n<p style=\"text-align: justify;\">Sử dụng l&ograve;ng trắng trứng để chống mụn đầu đen. Chỉ cần &aacute;p dụng một lớp l&ograve;ng trắng trứng để đắp l&ecirc;n khu&ocirc;n mặt của bạn. Đợi đến khi kh&ocirc;, bạn c&oacute; thể dễ d&agrave;ng lấy mặt nạ ra.</p>\n\n<p style=\"text-align: justify;\">L&ograve;ng trắng trứng c&oacute; thể được sử dụng l&agrave;m thắt chặt lỗ ch&acirc;n l&ocirc;ng, do đ&oacute; l&agrave;m giảm cơ hội ph&aacute;t triển của mụn đầu đen trong tương lai, đồng thời sẽ loại bỏ mụn đầu đen hiện c&oacute;.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/tri-seo-lom-mun-bang-long-trang-trung-ga2-ngoisao_vn_.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>4. Tr&agrave; xanh</strong></p>\n\n<p style=\"text-align: justify;\">Chỉ cần sử dụng tr&agrave; xanh ướp lạnh đắp l&ecirc;n mụn đầu đen v&agrave; rửa sạch sau 15 ph&uacute;t.</p>\n\n<p style=\"text-align: justify;\">Uống nước tr&agrave; xanh cũng gi&uacute;p ti&ecirc;u trừ mụn đầu đen rất tốt.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/mat-na-tra-xanh-ngoisao_vn_.jpg\" style=\"width: 500px; height: 307px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>5. B&agrave;n chải đ&aacute;nh răng</strong></p>\n\n<p style=\"text-align: justify;\">Ch&agrave; mụn đầu đen của bạn với một b&agrave;n chải đ&aacute;nh răng mềm mại, nhẹ nh&agrave;ng theo chuyển động tr&ograve;n. H&atilde;y rất cẩn thận bởi phương ph&aacute;p n&agrave;y c&oacute; thể g&acirc;y tổn thương da, v&igrave; vậy kh&ocirc;ng n&ecirc;n l&agrave;m mỗi ng&agrave;y.</p>\n\n<p style=\"text-align: justify;\">Bạn c&oacute; thể nh&uacute;ng b&agrave;n chải đ&aacute;nh răng trong một cốc nước chanh hoặc dầu jojoba.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/hai-meo-tri-mun-dau-den-thu-vi-6-ngoisao_vn_.jpg\" style=\"width: 500px; height: 312px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>6. Liệu mật ong</strong></p>\n\n<p style=\"text-align: justify;\">Chỉ cần sử dụng mật ong nguy&ecirc;n chất thoa tr&ecirc;n khu&ocirc;n mặt của bạn v&agrave; để trong 15 ph&uacute;t trước khi rửa. L&agrave;m điều n&agrave;y một v&agrave;i lần một tuần. Bạn cũng c&oacute; thể sử dụng mật ong ấm.</p>\n\n<p style=\"text-align: justify;\">Mật ong đ&oacute;ng vai tr&ograve; như một kh&aacute;ng sinh tự nhi&ecirc;n v&agrave; gi&uacute;p k&eacute;o bụi bẩn từ lỗ ch&acirc;n l&ocirc;ng da bằng c&aacute;ch loại bỏ mụn đầu đen. N&oacute; dưỡng ẩm v&agrave; l&agrave;m săn chắc da lỗ ch&acirc;n l&ocirc;ng.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/mat-ong-nguyen-chat-ngoisao_vn_.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>7. Thay đổi c&aacute;c vỏ gối</strong></p>\n\n<p style=\"text-align: justify;\">Nếu bạn dễ bị mụn đầu đen, bạn n&ecirc;n thay đổi gối của bạn thường xuy&ecirc;n hơn.</p>\n\n<p style=\"text-align: justify;\">Dầu c&oacute; thể tạo n&ecirc;n tr&ecirc;n gối của bạn v&agrave; trong khi bạn đang ngủ. Thay đổi v&agrave; giặt sạch gối của bạn thường xuy&ecirc;n c&oacute; thể gi&uacute;p giảm bớt những vấn đề với mụn đầu đen.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/vo-goi51048625-ngoisao_vn_.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n\n<p style=\"font-family: Arial, &quot;Times New Roman&quot;, &quot;Bitstream Charter&quot;, Times, serif; font-size: 16px;\">&nbsp;</p>\n</div>\n', '7-cach-thoat-khoi-mun-dau-den-de-dang', '/Upload/News/acd138ce-f8d7-4b8f-8a58-3053b5a5c9d9/tra-xanh.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', '7 cách thoát khỏi mụn đầu đen dễ dàng', 'Hãy loại bỏ mụn đầu đen kinh tởm bằng liệu pháp tự nhiên và không tiêu tốn nhiều tiền với những phương pháp dưới đây.', 'loại bỏ mụn đầu đen', '44', 'PUBLISHED', '', 'old'),
(29, '3', 'Các vùng khớp tay đen mấy cũng trắng bóc với thành phần tự nhiên', '<p>M&ugrave;a h&egrave;, tiếp x&uacute;c với nắng khiến da đổi m&agrave;u, đặc biệt ở c&aacute;c khớp ng&oacute;n tay trở n&ecirc;n đen sạm sẫm m&agrave;u hơn, đ&acirc;y l&agrave; một vấn đề thẩm mỹ phổ biến ảnh hưởng đến nhiều phụ nữ.</p>\n', '<p style=\"text-align: justify;\">M&ugrave;a h&egrave;, tiếp x&uacute;c với nắng khiến da đổi m&agrave;u, đặc biệt ở c&aacute;c khớp ng&oacute;n tay trở n&ecirc;n đen sạm sẫm m&agrave;u hơn, đ&acirc;y l&agrave; một vấn đề thẩm mỹ phổ biến ảnh hưởng đến nhiều phụ nữ.</p>\n\n<div>\n<p style=\"text-align: justify;\">C&oacute; rất nhiều nguy&ecirc;n nh&acirc;n dẫn đến sắc tố da v&agrave; phổ biến nhất l&agrave; do tiếp x&uacute;c với &aacute;nh nắng qu&aacute; mức hoặc kh&ocirc;ng bảo vệ b&agrave;n tay khi l&agrave;m c&aacute;c m&oacute;n ăn hoặc dọn dẹp nh&agrave; cửa.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/lam-trang-tay-ngoisao_vn_.jpg\" style=\"width: 500px; height: 246px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>Khắc ph&uacute;c v&ugrave;ng khớp tay đen do bắt nắng với c&ocirc;ng thức tự nhi&ecirc;n</strong></p>\n\n<p style=\"text-align: justify;\">Dưới đ&acirc;y l&agrave; một số liệu ph&aacute;p l&agrave;m đẹp gi&uacute;p c&aacute;c v&ugrave;ng đốt tay trắng trở lại hiệu quả chỉ bằng nguy&ecirc;n liệu tự nhi&ecirc;n.</p>\n\n<p style=\"text-align: justify;\"><strong>Chanh - đường - mật ong</strong></p>\n\n<p style=\"text-align: justify;\">Phương ph&aacute;p n&agrave;y c&oacute; thể l&agrave;m s&aacute;ng da ở v&ugrave;ng tối khớp tay của bạn cũng như l&agrave;m c&aacute;c khớp xương mượt m&agrave; hơn. Bạn chỉ cần 3 th&agrave;nh phần để l&agrave;m một hỗn hợp tẩy tế b&agrave;o chết hiệu quả.</p>\n\n<p style=\"text-align: justify;\">C&ocirc;ng thức:</p>\n\n<p style=\"text-align: justify;\">- 2 th&igrave;a mật ong<br />\n- 1 muỗng c&agrave; ph&ecirc; nước cốt chanh<br />\n- 1 muỗng c&agrave; ph&ecirc; đường</p>\n\n<p style=\"text-align: justify;\">Hướng dẫn:</p>\n\n<p style=\"text-align: justify;\">- Trộn c&aacute;c th&agrave;nh phần với nhau. Thoa hỗn hợp n&agrave;y tr&ecirc;n tay v&agrave; ch&agrave; kỹ c&aacute;c v&ugrave;ng khớp ng&oacute;n tay đen một c&aacute;ch nhẹ nh&agrave;ng.</p>\n\n<p style=\"text-align: justify;\">- Bạn cũng c&oacute; thể massage tay cho &ecirc;m &aacute;i th&ecirc;m.</p>\n\n<p style=\"text-align: justify;\">- Để lại hỗn hợp tr&ecirc;n tay trong khoảng 15 ph&uacute;t cho da hấp thụ c&aacute;c loại nước &eacute;p. C&aacute;c vitamin C từ nước chanh v&agrave; axit glycolic từ đường sẽ l&agrave;m s&aacute;ng đốt ng&oacute;n tay của bạn một c&aacute;ch hiệu quả.</p>\n\n<p style=\"text-align: justify;\"><strong>Đường v&agrave; dầu &Ocirc; liu</strong></p>\n\n<p style=\"text-align: justify;\">Một th&agrave;nh phần dưỡng ẩm tuyệt vời được l&agrave;m với đường v&agrave; dầu &ocirc; liu. Dầu &ocirc;liu kh&ocirc;ng những c&oacute; lợi cho việc cải thiện chất lượng m&oacute;ng tay của bạn, m&agrave; c&ograve;n loại bỏ l&agrave;n da bị đổi m&agrave;u đen do tiếp x&uacute;c với &aacute;nh nắng mặt trời v&agrave; sản xuất qu&aacute; mức melanin trong da. Đường l&agrave; một chất tẩy tế b&agrave;o da chết tuyệt vời để loại bỏ c&aacute;c tế b&agrave;o da đen sẫm m&agrave;u một c&aacute;ch hiệu quả.</p>\n\n<p style=\"text-align: justify;\">- Trộn 3 phần dầu oliu với 2 phần đường.</p>\n\n<p style=\"text-align: justify;\">- Sử dụng hỗn hợp n&agrave;y để ch&agrave; l&ecirc;n đốt ng&oacute;n tay đen.</p>\n\n<p style=\"text-align: justify;\">- Để lại n&oacute; tr&ecirc;n tay th&ecirc;m 5 ph&uacute;t để hydrat h&oacute;a th&ecirc;m b&agrave;n tay của bạn sau đ&oacute; rửa sạch với nước.</p>\n\n<p style=\"text-align: justify;\">- &Aacute;p dụng một loại kem dưỡng ẩm nhẹ sau đ&oacute;.</p>\n\n<p style=\"text-align: justify;\"><strong>Dưỡng ẩm cho tay</strong></p>\n\n<p style=\"text-align: justify;\">Bạn c&oacute; thể nhận được nhiều lợi thế bằng c&aacute;ch sử dụng phương thuốc n&agrave;y - l&agrave;m s&aacute;ng đốt ng&oacute;n tay đen v&agrave; l&agrave;m b&agrave;n tay mềm mại v&agrave; mượt m&agrave; hơn. N&oacute; cũng l&agrave; yếu tố loại bỏ bất kỳ sự đổi m&agrave;u n&agrave;o tr&ecirc;n m&oacute;ng tay của bạn, kể cả vấn đề nang kh&ocirc;.</p>\n\n<p style=\"text-align: justify;\">Th&agrave;nh phần:</p>\n\n<p style=\"text-align: justify;\">-&nbsp;Dầu jojoba<br />\n- Dầu hạnh nh&acirc;n<br />\n- Dầu hương thảo<br />\n- Nước cốt chanh</p>\n\n<p style=\"text-align: justify;\">C&ocirc;ng thức:</p>\n\n<p style=\"text-align: justify;\">- Trộn &frac12; dầu jojoba; &frac12; muỗng c&agrave; ph&ecirc; dầu hạnh nh&acirc;n; &frac12; muỗng c&agrave; ph&ecirc; dầu hương thảo v&agrave; c&ugrave;ng 2-3 giọt nước cốt chanh.</p>\n\n<p style=\"text-align: justify;\">- Sử dụng dầu đ&atilde; pha tr&ecirc;n để xoa b&oacute;p khớp đốt ng&oacute;n tay, b&agrave;n tay, l&ograve;ng b&agrave;n tay v&agrave; cổ tay.</p>\n\n<p style=\"text-align: justify;\">- Thời gian tốt nhất để sử dụng dầu n&agrave;y l&agrave; trước khi đi ngủ. N&oacute; sẽ l&agrave;m việc qua đ&ecirc;m v&agrave; dưỡng ẩm cho b&agrave;n tay của bạn, nu&ocirc;i dưỡng m&oacute;ng tay của bạn v&agrave; gi&uacute;p l&agrave;n da mềm mại hơn.</p>\n\n<p style=\"text-align: justify;\"><strong>Kem sữa - nghệ v&agrave; dầu hạnh nh&acirc;n</strong></p>\n\n<p style=\"text-align: justify;\">- Trộn 1 muỗng c&agrave; ph&ecirc; kem sữa v&agrave; &frac12; muỗng c&agrave; ph&ecirc; bột nghệ với 2-3 giọt dầu hạnh nh&acirc;n.</p>\n\n<p style=\"text-align: justify;\">- Trộn đều c&aacute;c th&agrave;nh phần tr&ecirc;n sau đ&oacute; thoa v&agrave;o khớp ng&oacute;n tay của bạn.</p>\n\n<p style=\"text-align: justify;\">- H&atilde;y để n&oacute; trong 20 ph&uacute;t sau đ&oacute; rửa sạch lại bằng nước.</p>\n\n<p style=\"text-align: justify;\">- Để c&oacute; kết quả tối ưu, l&agrave;m việc n&agrave;y 3 lần một tuần.</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n</div>\n', 'cac-vung-khop-tay-den-may-cung-trang-boc-voi-thanh-phan-tu-nhien', '/Upload/News/64de9c7b-5e79-45dc-b5c4-6158235297a1/khop-tay.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Các vùng khớp tay đen mấy cũng trắng bóc với thành phần tự nhiên', 'Mùa hè tiếp xúc với nắng khiến da các khớp ngón tay trở nên đen sạm sẫm màu hơn. Đây là một vấn đề thẩm mỹ phổ biến ảnh hưởng đến nhiều phụ nữ.', 'trắng bóc vùng khớp tay', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(30, '3', 'Cách điều chế mặt nạ trị nám da - tàn nhang tại nhà', '<p style=\"text-align: justify;\">C&aacute;ch trị n&aacute;m t&agrave;n nhang tr&ecirc;n da bằng mặt nạ tại nh&agrave; thường được mọi người sử dụng để tiết kiệm chi ph&iacute; v&agrave; thời gian trong qu&aacute; tr&igrave;nh l&agrave;m đẹp cơ thể.</p>\n', '<p>C&aacute;ch trị n&aacute;m t&agrave;n nhang tr&ecirc;n da bằng mặt nạ&nbsp;tại nh&agrave;&nbsp;thường được mọi người sử dụng để tiết kiệm chi ph&iacute; v&agrave; thời gian trong qu&aacute; tr&igrave;nh l&agrave;m đẹp cơ thể. Nhưng để c&oacute; thể dễ d&agrave;ng c&oacute; được một l&agrave;n da tuyệt đẹp từ thi&ecirc;n nhi&ecirc;n ngay tại nh&agrave; th&igrave; ch&uacute;ng ta cần phải sở hữu nhiều b&iacute; quyết v&agrave; phương ph&aacute;p kh&aacute;c nhau thật hiệu quả. Ch&iacute;nh v&igrave; thế, h&ocirc;m nay&nbsp;Svan Clinic &amp; Spa&nbsp;sẽ giới thiệu tới c&aacute;c bạn<strong> </strong>c&aacute;ch l&agrave;m<strong>&nbsp;<em>mặt nạ trị n&aacute;m</em></strong>&nbsp;v&agrave; t&agrave;n nhang rất hiệu quả tại nh&agrave;,&nbsp;m&agrave; c&ograve;n&nbsp;gi&uacute;p cho l&agrave;n da trở n&ecirc;n mịn m&agrave;ng v&agrave; trắng s&aacute;ng khiến mọi người phải h&acirc;m mộ.</p>\n\n<p><strong>Mặt nạ đu đủ</strong></p>\n\n<p>Đu đủ kh&ocirc;ng chỉ l&agrave; một trong những loại tr&aacute;i c&acirc;y chứa vitamin rất tốt cho sức khỏe v&agrave; cả l&agrave;n da khi sử dụng đ&uacute;ng c&aacute;ch. Vậy n&ecirc;n, để c&oacute; thể sử dụng mặt nạ đu đủ điều trị t&agrave;n nhang hiệu quả th&igrave; c&aacute;c bạn h&atilde;y chuẩn bị 2 muỗng đu đủ đ&atilde; xoay nhuyễn trộn điều với 1/2 th&igrave;a đường n&acirc;u v&agrave; sữa chua để tạo th&agrave;nh một loại hỗn hợp đắp l&ecirc;n l&agrave;n da.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/mat-na-tri-nam-tan-nhang-tai-nha-2.jpg\" style=\"width: 500px; height: 324px;\" /></p>\n\n<p><strong>Mặt nạ c&agrave; chua</strong></p>\n\n<p>C&agrave; chua thường được mọi người sử dụng nhiều trong cuộc sống hằng ng&agrave;y, đặc biệt l&agrave; phục vụ trong c&aacute;c m&oacute;n ăn hằng ng&agrave;y. Nhưng nếu như ch&uacute;ng ta cắt nhỏ c&agrave; chua đ&atilde; được ướp lạnh đắp&nbsp;l&ecirc;n l&agrave;n da trong v&ograve;ng 25 ph&uacute;t sẽ gi&uacute;p điều trị n&aacute;m da rất hiệu quả ngay sau khi sử dụng.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/mat-na-tri-nam-tan-nhang-tai-nha-3.jpg\" style=\"width: 500px; height: 335px;\" /></p>\n\n<p><strong>Mặt nạ đậu đỏ</strong></p>\n\n<p>Việc sử dụng đậu đỏ để l&agrave;m th&agrave;nh một chiếc mặt nạ&nbsp;sẽ&nbsp;rất tuyệt vời để bảo vệ l&agrave;n da&nbsp;khỏi sự tấn c&ocirc;ng của c&aacute;c tia cực t&iacute;m v&agrave; m&ocirc;i trường b&ecirc;n ngo&agrave;i. Ch&iacute;nh v&igrave; thế, ch&uacute;ng ta n&ecirc;n xoay mịn đậu đỏ đ&atilde; được rang ch&iacute;n&nbsp;v&agrave; pha với nước ấm để tạo th&agrave;nh một hỗn hợp đắp l&ecirc;n l&agrave;n da trong v&ograve;ng 10 ph&uacute;t.</p>\n\n<p>&nbsp;</p>\n', 'cach-dieu-che-mat-na-tri-nam-da-tan-nhang-tai-nha', '/Upload/News/0b922126-a3b7-4beb-b463-c2262faaa079/du-du.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách điều chế mặt nạ trị nám da - tàn nhang tại nhà', 'Cách trị nám tàn nhang trên da bằng mặt nạ tại nhà thường được mọi người sử dụng để tiết kiệm chi phí và thời gian trong quá trình làm đẹp cơ thể.', ' mặt nạ trị nám', '44', 'PUBLISHED', '', 'old'),
(31, '3', 'Top 3 cách trị vết thâm mụn ở mặt dễ dàng', '<p style=\"text-align: justify;\">N&oacute;i đến c&aacute;ch trị vết th&acirc;m mụn ở mặt th&igrave; hầu hết mọi người điều rất quan t&acirc;m v&agrave; đưa ra nhiều phương ph&aacute;p kh&aacute;c nhau để điều trị.</p>\n', '<p style=\"text-align: justify;\">N&oacute;i đến&nbsp;<em><strong>c&aacute;ch trị vết th&acirc;m</strong></em> mụn ở mặt&nbsp;th&igrave; hầu hết mọi người điều rất quan t&acirc;m v&agrave; đưa ra nhiều phương ph&aacute;p kh&aacute;c nhau để điều trị. Nhưng để c&oacute; được kết quả tuyệt vời như mong muốn một c&aacute;ch dễ d&agrave;ng th&igrave; ch&uacute;ng ta h&atilde;y sử dụng&nbsp;c&aacute;c phương ph&aacute;p&nbsp;m&agrave;&nbsp;Svan Clinic &amp; Spa&nbsp;thường hay &aacute;p dụng đối với l&agrave;n da th&acirc;m.</p>\n\n<p style=\"text-align: justify;\"><strong>Sử dụng chanh</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-vet-tham-mun-o-mat-1.jpg\" style=\"width: 500px; height: 440px;\" /></p>\n\n<p style=\"text-align: justify;\">Chanh được biết như l&agrave; một trong những loại tr&aacute;i c&acirc;y bổ sung lượng lớn vitamin cho cơ thể. Ch&iacute;nh v&igrave; thế, để c&oacute; thể dễ d&agrave;ng loại bỏ c&aacute;c vết th&acirc;m v&agrave; t&aacute;i tạo lại l&agrave;n da th&igrave; việc sử dụng chanh l&agrave; một phương ph&aacute;p rất tuyệt vời. Ch&uacute;ng ta c&oacute; thể cắt chanh ra v&agrave; đắp trực tiếp l&ecirc;n l&agrave;n da hoặc lấy nước cốt kết hợp với l&ograve;ng trắng của trứng g&agrave; để gia tăng nhanh c&ocirc;ng dụng.</p>\n\n<p style=\"text-align: justify;\"><strong>Nước muối</strong></p>\n\n<p style=\"text-align: justify;\">Muối được biết đến l&agrave; chất c&oacute; khả năng s&aacute;t khuẩn rất cao khi sử dụng. Vậy n&ecirc;n, để loại bỏ c&aacute;c vết th&acirc;m khỏi l&agrave;n da th&igrave; đ&acirc;y cũng l&agrave; một trong những phương ph&aacute;p rất hiệu quả m&agrave; lại đơn giản. Để sử dụng một c&aacute;ch hợp l&yacute; th&igrave; c&aacute;c bạn h&atilde;y rửa sạch vết th&acirc;m bằng nước muối hoặc xoa nước muối v&agrave;o những nơi m&agrave; mụn sắp vỡ.</p>\n\n<p style=\"text-align: justify;\"><strong>Dưa leo</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-vet-tham-mun-o-mat-3.jpg\" style=\"width: 500px; height: 333px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Việc sử dụng dưa leo để l&agrave;m đẹp l&agrave;n da chắc chắn đ&atilde; kh&ocirc;ng c&ograve;n xa lạ đối với mỗi người phụ nữ. Nhưng c&ocirc;ng dụng loại bỏ vết th&acirc;m hiệu quả th&igrave; chắc hẳn kh&ocirc;ng phải ai cũng biết đến. Vậy n&ecirc;n, ch&uacute;ng ta h&atilde;y ki&ecirc;n tr&igrave; sử dụng dưa leo đắp l&ecirc;n c&aacute;c v&ugrave;ng cần điều trị th&acirc;m 3 lần trong tuần để thấy ngay hiệu quả.</p>\n', 'top-3-cach-tri-vet-tham-mun-o-mat-de-dang', '/Upload/News/92f3eccd-3701-49d3-9944-ef459a7219e8/chanh.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Top 3 cách trị vết thâm mụn ở mặt dễ dàng', 'Nói đến cách trị vết thâm mụn ở mặt thì hầu hết mọi người điều rất quan tâm và đưa ra nhiều phương pháp khác nhau để điều trị.', 'cách trị thâm', '44', 'PUBLISHED', '', 'old'),
(32, '3', 'Giảm cân sau sinh nhanh nhóng bằng đậu đỏ', '<p>Đậu đỏ l&agrave; một trong những nguy&ecirc;n liệu gi&uacute;p chị em&nbsp;giảm c&acirc;n sau sinh&nbsp;nhanh ch&oacute;ng, lại dễ thực hiện v&agrave;&nbsp;t&iacute;nh an to&agrave;n cao. H&atilde;y c&ugrave;ng &aacute;p dụng phương ph&aacute;p&nbsp;giảm c&acirc;n sau sinh nhanh nh&oacute;ng bằng đậu đỏ nh&eacute;.</p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Đậu đỏ l&agrave; một trong những nguy&ecirc;n liệu gi&uacute;p chị em&nbsp;giảm c&acirc;n sau sinh&nbsp;nhanh ch&oacute;ng, lại dễ thực hiện v&agrave;&nbsp;t&iacute;nh an to&agrave;n cao.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Ch&iacute;nh v&igrave; thế, hằng ng&agrave;y, c&aacute;c mẹ n&ecirc;n bổ sung đậu đổ&nbsp;v&agrave;o trong những&nbsp;bữa ăn nếu c&oacute; nhu cầu giảm c&acirc;n. V&agrave; để đa dạng c&aacute;c m&oacute;n ăn chế biến từ loại thực phẩm n&agrave;y, h&atilde;y tham khảo ngay sau đ&acirc;y nh&eacute;!</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Canh đậu đỏ</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Ng&acirc;m đậu trong khoảng 6 tiếng v&agrave; nấu ch&iacute;n nhừ rồi d&ugrave;ng ăn như canh (kh&ocirc;ng n&ecirc;n cho đường v&agrave;o nếu bạn đang muốn giảm c&acirc;n). Bạn c&oacute; thể ăn canh đậu đỏ v&agrave;o mọi thời điểm trong ng&agrave;y, đặc biệt n&ecirc;n d&ugrave;ng v&agrave;o buổi tối.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/giam-can-sau-sinh-bang-canh-dau-do.jpg\" style=\"width: 500px; height: 334px;\" /><br />\n<em>C&oacute; thể bạn quan t&acirc;m &gt;&gt;&nbsp;</em>\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/lam-cach-nao-de-giam-mo-bung-sau-khi-sinh-em-be\" target=\"_blank\"><strong><span data-sheets-userformat=\"{&quot;2&quot;:14851,&quot;3&quot;:[null,0],&quot;4&quot;:[null,2,16777215],&quot;12&quot;:0,&quot;14&quot;:[null,2,3355443],&quot;15&quot;:&quot;Arial&quot;,&quot;16&quot;:10}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;làm thế nào để giảm mỡ bụng sau sinh&quot;}\" style=\"font-size: 10pt; font-family: Arial;\">l&agrave;m thế n&agrave;o để giảm mỡ bụng sau sinh</span></strong></a></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\">&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Bột đậu đỏ</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">C&oacute; thể thay thế canh bằng uống nước bột đậu đỏ. Bạn đem say mịn đậu đỏ th&agrave;nh bột v&agrave; pha lo&atilde;ng c&ugrave;ng nước để thay thế nước đậu đỏ bằng những lần uống nước lọc.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Hy vọng bạn sẽ &aacute;p dụng th&agrave;nh c&ocirc;ng c&aacute;ch giảm c&acirc;n sau sinh bằng đậu đỏ để nhanh ch&oacute;ng lấy lại v&oacute;c d&aacute;ng đẹp. Song song với nhiều c&aacute;ch gi&uacute;p giảm c&acirc;n sau sinh, ng&agrave;y nay, c&ocirc;ng nghệ giảm b&eacute;o Dưỡng sinh Nhật Bản cũng đang được nhiều chị em lựa chọn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/giam-can-sau-sinh-bang-bot-dau-do.jpg\" style=\"width: 500px; height: 300px;\" /><br />\nXem th&ecirc;m&gt;&gt;&nbsp;\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://&quot;http://svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-bi-quyet-giup-ban-giam-can-khi-dang-cho-con-bu                             &quot;\" target=\"_blank\"><strong><em><span data-sheets-userformat=\"{&quot;2&quot;:14851,&quot;3&quot;:[null,0],&quot;4&quot;:[null,2,16777215],&quot;12&quot;:0,&quot;14&quot;:[null,2,3355443],&quot;15&quot;:&quot;Arial&quot;,&quot;16&quot;:10}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;cách giảm cân cho bà mẹ cho con bú&quot;}\" style=\"font-size: 10pt; font-family: Arial;\">c&aacute;ch giảm c&acirc;n cho b&agrave; mẹ cho con b&uacute;</span></em></strong></a></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"text-align: justify;\">Trong đậu đỏ chứa nhiều chất dinh dưỡng, c&oacute; t&aacute;c dụng bổ m&aacute;u v&agrave; v&ocirc; c&ugrave;ng tốt cho sức khỏe. Đồng thời, loại hạt n&agrave;y&nbsp;gi&agrave;u vitamin, kho&aacute;ng chất, chất xơ n&ecirc;n c&oacute; t&aacute;c dụng ti&ecirc;u hao lượng mỡ thừa kh&aacute; lớn. V&agrave;&nbsp;một lượng kh&ocirc;ng nhỏ&nbsp;chất kiềm thạch c&oacute; trong đậu đỏ mang lại khả năng ngăn ngừa mỡ thừa t&iacute;ch tụ, từ đ&oacute; gia tăng hiệu quả giảm c&acirc;n. Phương ph&aacute;p <a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/giam-can-sau-sinh-nhanh-nhong-bang-dau-do\" target=\"_blank\"><strong>g</strong></a></span><strong><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/giam-can-sau-sinh-nhanh-nhong-bang-dau-do\" target=\"_blank\">iảm c&acirc;n sau sinh nhanh nh&oacute;ng bằng đậu đỏ</a> </strong>l&agrave; một trong những phương ph&aacute;p an to&agrave;n bậc nhất hiện nay<strong>. </strong>Chị em đừng n&ecirc;n bỏ qua thần dược từ thi&ecirc;n nhi&ecirc;n n&agrave;y nh&eacute;.</p>\n', 'giam-can-sau-sinh-nhanh-nhong-bang-dau-do', '/Upload/News/97d0d6b6-0e9c-4431-8a26-03ed13d5a1a5/giam-can-sau-sinh-bang-bot-dau-do.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Giảm cân sau sinh nhanh chóng bằng đậu đỏ', 'Giảm cân sau sinh nhanh chóng có thể thực hiện bằng việc ăn thường xuyên một số món chế biến từ đậu đỏ hoặc sử dụng công nghệ giảm béo dưỡng sinh Nhật Bản', 'giảm cân sau sinh', '44', 'PUBLISHED', '', 'old'),
(33, '3', 'Phương pháp giảm cân hiệu quả và khoa học nhất', '<p><em>Để giảm c&acirc;n, bạn c&oacute; đến h&agrave;ng trăm h&agrave;ng ngh&igrave;n phương ph&aacute;p nhưng đ&acirc;u l&agrave; phương ph&aacute;p giảm c&acirc;n hiệu quả v&agrave; khoa học nhất, bạn đ&atilde; biết chưa?</em></p>\n', '<p align=\"center\"><em><img alt=\"Description: meso-slim-.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/PH%C6%AF%C6%A0NG%20PH%C3%81P%20GI%E1%BA%A2M%20C%C3%82N%20HI%E1%BB%86U%20QU%E1%BA%A2%20V%C3%80%20KHOA%20H%E1%BB%8CC%20NH%E1%BA%A4T/phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat-chinh(1).jpg\" style=\"width: 500px; height: 355px;\" /></em></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: justify;\">Giảm c&acirc;n hiệu quả kh&ocirc;ng chỉ l&agrave; số c&acirc;n nặng giảm được nhiều m&agrave; sức khoẻ vẫn được đảm bảo th&igrave; mới được xem l&agrave; th&agrave;nh c&ocirc;ng. Muốn đạt được điều đ&oacute;, bạn cần phải c&oacute; một kế hoạch giảm c&acirc;n cụ thể, r&otilde; r&agrave;ng v&agrave; khoa học.</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<p style=\"text-align: justify;\">Vậy kế hoạch giảm c&acirc;n như thế n&agrave;o l&agrave; khoa học? Trong qu&aacute; tr&igrave;nh giảm c&acirc;n, bạn vẫn phải cung cấp đầy đủ chất dinh dưỡng cho cơ thể để c&oacute; năng lượng hoạt động trong 1 ng&agrave;y, kh&ocirc;ng thiếu hụt chất để cơ thể phải mệt mỏi, suy nhược, ... Sau đ&acirc;y l&agrave; những phương ph&aacute;p giảm c&acirc;n hiệu quả v&agrave; khoa học nhất được nhiều người &aacute;p dụng:</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 style=\"text-align: justify;\"><span style=\"color: rgb(61, 156, 116);\"><strong>Luyện tập Yoga</strong></span></h3>\n\n<p>&nbsp;</p>\n\n<p align=\"center\"><img alt=\"Description: maxresdefault.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/PH%C6%AF%C6%A0NG%20PH%C3%81P%20GI%E1%BA%A2M%20C%C3%82N%20HI%E1%BB%86U%20QU%E1%BA%A2%20V%C3%80%20KHOA%20H%E1%BB%8CC%20NH%E1%BA%A4T/phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat-1.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p align=\"center\">&nbsp;</p>\n\n<p style=\"text-align: justify;\">Nếu bạn biết đến Yoga với t&aacute;c dụng n&acirc;ng cao sức khoẻ, gi&uacute;p tinh thần sảng kho&aacute;i, giảm căng thẳng mệt mỏi, cơ thể dẻo dai th&igrave; chưa đủ đ&acirc;u, yoga c&ograve;n gi&uacute;p giữ g&igrave;n v&oacute;c d&aacute;ng v&agrave; giảm c&acirc;n rất hiệu quả. Chỉ với 10 ph&uacute;t luyện tập yoga mỗi ng&agrave;y sẽ gi&uacute;p bạn c&oacute; được v&oacute;c d&aacute;ng thon gọn, săn chắc như mong muốn. Tuy nhi&ecirc;n, nếu muốn đạt được hiệu quả bạn cần ki&ecirc;n tr&igrave; luyện tập từ 3 th&aacute;ng trở l&ecirc;n.</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 style=\"text-align: justify;\"><span style=\"color: rgb(61, 156, 116);\"><strong>Ăn uống khoa học</strong></span></h3>\n\n<p style=\"text-align: justify;\">Chế độ ăn uống ảnh hưởng kh&aacute; nhiều đến v&oacute;c d&aacute;ng của bạn. Khẩu phần ăn chứa nhiều chất xơ từ rau, đặc biệt l&agrave; những loại rau nhiều nhớt l&agrave;m tăng cảm gi&aacute;c no v&agrave; gi&uacute;p bạn kiểm so&aacute;t được c&acirc;n nặng trong thời gian d&agrave;i.</p>\n\n<p>&nbsp;</p>\n\n<p align=\"center\"><img alt=\"Description: depositphotos_92868172-stock-photo-healthy-food-background-top-view.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/PH%C6%AF%C6%A0NG%20PH%C3%81P%20GI%E1%BA%A2M%20C%C3%82N%20HI%E1%BB%86U%20QU%E1%BA%A2%20V%C3%80%20KHOA%20H%E1%BB%8CC%20NH%E1%BA%A4T/phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat-2.jpg\" style=\"width: 500px; height: 331px;\" /></p>\n\n<p style=\"text-align: justify;\">Uống nhiều nước cũng gi&uacute;p bạn c&oacute; cảm gi&aacute;c no l&acirc;u, kh&ocirc;ng th&egrave;m ăn. Nhiều nghi&ecirc;n cứu cho thấy c&agrave; ph&ecirc; chứa nhiều chất chống oxy h&oacute;a v&agrave; c&oacute; lợi cho sức khỏe. Chất caffeine trong c&agrave; ph&ecirc; c&oacute; thể tăng hoạt động trao đổi chất từ 3-11% v&agrave; tăng tốc độ đốt mỡ thừa từ 10-29%. Giống c&agrave; ph&ecirc;, tr&agrave; xanh cũng c&oacute; t&aacute;c dụng giảm c&acirc;n. Mặc d&ugrave; chỉ chứa lượng nhỏ caffeine, nhưng tr&agrave; xanh lại chứa chất chống oxy h&oacute;a rất tốt c&oacute; t&ecirc;n catechin, hoạt động phối hợp với caffeine l&agrave;m tăng cường qu&aacute; tr&igrave;nh đốt mỡ thừa.</p>\n\n<p>&nbsp;</p>\n\n<h3><span style=\"color: rgb(61, 156, 116);\"><strong>Ngủ điều độ</strong></span></h3>\n\n<p align=\"center\"><img alt=\"Description: pillow_cover_waterproof.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/PH%C6%AF%C6%A0NG%20PH%C3%81P%20GI%E1%BA%A2M%20C%C3%82N%20HI%E1%BB%86U%20QU%E1%BA%A2%20V%C3%80%20KHOA%20H%E1%BB%8CC%20NH%E1%BA%A4T/phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat-3.jpg\" style=\"width: 500px; height: 332px;\" /></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: justify;\">L&agrave;m việc qu&aacute; sức, thường xuy&ecirc;n căng thẳng, mệt mỏi kh&ocirc;ng chỉ ảnh hưởng đến sức khoẻ của bạn m&agrave; c&ograve;n khiến bạn kh&oacute; kiểm so&aacute;t được c&acirc;n nặng. Ngo&agrave;i những hoạt đồng b&igrave;nh thường, bạn n&ecirc;n ch&uacute; trọng để c&oacute; giấc ngủ chất lượng. Việc ngủ kh&ocirc;ng đủ giấc l&agrave; một trong những yếu tố nguy cơ g&acirc;y bệnh b&eacute;o ph&igrave; cao nhất, với tỷ lệ 89% ở trẻ em v&agrave; 55% ở người lớn.</p>\n\n<p>&nbsp;</p>\n\n<h3><span style=\"color: rgb(61, 156, 116);\"><strong>Đừng ăn ki&ecirc;ng, h&atilde;y ăn l&agrave;nh mạnh</strong></span></h3>\n\n<p align=\"center\"><img alt=\"Description: bao-ve-xuong-4(1).jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/PH%C6%AF%C6%A0NG%20PH%C3%81P%20GI%E1%BA%A2M%20C%C3%82N%20HI%E1%BB%86U%20QU%E1%BA%A2%20V%C3%80%20KHOA%20H%E1%BB%8CC%20NH%E1%BA%A4T/phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat-4.jpg\" style=\"width: 500px; height: 281px;\" /></p>\n\n<p style=\"text-align: justify;\">Một trong những vấn đề lớn nhất của &ldquo;ăn ki&ecirc;ng&rdquo; l&agrave; ch&uacute;ng về l&acirc;u về d&agrave;i gần như chẳng c&oacute; t&aacute;c dụng g&igrave;, những người &ldquo;ăn ki&ecirc;ng&rdquo; lại dễ tăng c&acirc;n hơn trong tương lai. Do đ&oacute;, thay v&igrave; &ldquo;ăn ki&ecirc;ng,&quot; h&atilde;y đặt mục ti&ecirc;u trở th&agrave;nh một người khỏe mạnh hơn, vui vẻ hơn v&agrave; c&oacute; th&acirc;n h&igrave;nh đẹp hơn. H&atilde;y cung cấp đủ chất dinh dưỡng cho cơ thể bạn thay v&igrave; bỏ đ&oacute;i n&oacute;. V&agrave; bạn sẽ thấy c&acirc;n nặng của m&igrave;nh giảm xuống một c&aacute;ch tự nhi&ecirc;n.</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<p style=\"text-align: justify;\">Bạn cần biết rằng, giảm c&acirc;n hiệu quả, đạt được th&acirc;n h&igrave;nh như mong muốn kh&ocirc;ng phải l&agrave; việc một sớm một chiều m&agrave; đ&ograve;i hỏi sự ki&ecirc;n tr&igrave; v&agrave; thời gian d&agrave;i. C&ograve;n nếu bạn l&agrave; người bận rộn v&agrave; đ&ograve;i hỏi một phương ph&aacute;p giảm c&acirc;n nhanh ch&oacute;ng, hiệu quả, an to&agrave;n th&igrave; Contri Ultra Shape l&agrave; giải ph&aacute;p tuyệt vời d&agrave;nh cho bạn. Tham khảo chi tiết về Contri Ultra Shape&nbsp;<strong><a href=\"http://www.svanclinic.vn/cong-nghe-giam-beo-contri-ultrashape/\"><span style=\"color: rgb(61, 156, 116);\">tại đ&acirc;y</span></a>.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/PH%C6%AF%C6%A0NG%20PH%C3%81P%20GI%E1%BA%A2M%20C%C3%82N%20HI%E1%BB%86U%20QU%E1%BA%A2%20V%C3%80%20KHOA%20H%E1%BB%8CC%20NH%E1%BA%A4T/giam-beo-khong-phau-thuat.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p style=\"text-align: justify;\">Li&ecirc;n hệ ngay hotline&nbsp;<span style=\"color: rgb(61, 156, 116);\"><span style=\"font-size: 1em;\"><strong>1900 0117</strong></span></span>&nbsp;để được chuy&ecirc;n gia giảm b&eacute;o từ Svan Clinic &amp; Spa tư vấn miễn ph&iacute; v&agrave; nhận nhiều ưu đ&atilde;i.</p>\n\n<p>&nbsp;</p>\n', 'phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat', '/Upload/News/48a3f433-ed5e-4b6f-86ca-13bcac0ff153/phuong-phap-giam-can-hieu-qua-va-khoa-hoc-nhat-chinh.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Phương pháp giảm cân hiệu quả và khoa học nhất', 'Để lấy lại vóc dáng đẹp, bạn có thể thực hiện nhiều cách. Tuy nhiên, hãy áp dụng phương pháp giảm cân hiệu quả và khoa học nhất để không gây hại sức khỏe', 'phương pháp giảm cân hiệu quả', '44', 'PUBLISHED', '', 'old'),
(34, '3', 'Cách làm mặt nạ trứng gà giúp da mặt trắng hồng tại nhà', '<p style=\"text-align: justify;\">C&aacute;ch l&agrave;m mặt nạ trứng g&agrave; gi&uacute;p da mặt trở n&ecirc;n trắng hồng v&agrave; mịn m&agrave;ng ngay tại nh&agrave; sẽ gi&uacute;p mọi người đạt được mơ ước bấy l&acirc;u trong qu&aacute; tr&igrave;nh l&agrave;m đẹp của m&igrave;nh.</p>\n', '<p style=\"text-align: justify;\">Với&nbsp;c&aacute;ch l&agrave;m <em><strong>mặt nạ trứng g&agrave;</strong>&nbsp;</em>sẽ dễ d&agrave;ng gi&uacute;p da mặt trở n&ecirc;n trắng hồng v&agrave; mịn m&agrave;ng ngay tại nh&agrave;. Nhưng để c&oacute; thể sử dụng trứng g&agrave; một c&aacute;ch hợp l&yacute; th&igrave; ch&uacute;ng ta cần phải tham khảo c&aacute;c bước đơn giản đến từ b&ecirc;n dưới. Nhờ v&agrave;o đ&oacute;, ch&uacute;ng ta&nbsp;sẽ c&oacute; được một l&agrave;n da trắng s&aacute;ng tự nhi&ecirc;n nhanh nhất khiến mọi người phải h&acirc;m mộ khi nh&igrave;n thấy.</p>\n\n<p style=\"text-align: justify;\"><strong>Sử dụng l&ograve;ng đỏ trứng g&agrave;</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-mat-na-trung-ga-trang-da-2.jpg\" style=\"width: 500px; height: 334px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Để c&oacute; thể sử dụng l&ograve;ng đỏ trứng g&agrave; gi&uacute;p l&agrave;n da trắng s&aacute;ng cấp tốc th&igrave; ch&uacute;ng ta h&atilde;y đ&aacute;nh đều l&ecirc;n v&agrave; thoa l&ecirc;n l&agrave;n da trong v&ograve;ng 15 ph&uacute;t rồi rửa lại với nước ấm để nhận được c&ocirc;ng hiệu tối da. Ngo&agrave;i ra, trong qu&aacute; tr&igrave;nh sử dụng phương ph&aacute;p n&agrave;y th&igrave; l&agrave;n da ch&uacute;ng ta sẽ được dưỡng ẩm v&agrave; ngăn ngừa qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a rất tuyệt vời.</p>\n\n<p style=\"text-align: justify;\"><strong>Sử dụng l&ograve;ng trắng trứng g&agrave;</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-mat-na-trung-ga-trang-da-1.jpg\" style=\"width: 500px; height: 334px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Ngo&agrave;i việc sử dụng l&ograve;ng đỏ ra th&igrave; ch&uacute;ng ta c&oacute; thể tận dụng l&ograve;ng trắng của trứng g&agrave; để gia tăng c&ocirc;ng dụng trắng s&aacute;ng từ phương ph&aacute;p n&agrave;y. Vậy n&ecirc;n, để đạt được c&ocirc;ng dụng tối đa từ l&ograve;ng trắng trứng g&agrave; th&igrave; ch&uacute;ng ta h&atilde;y khăn ấm để lau qua l&agrave;n da một lần để gi&uacute;p l&agrave;n da nhanh ch&oacute;ng hấp thu dưỡng chất. Tiếp đ&oacute;, ch&uacute;ng ta h&atilde;y khuấy đều l&ograve;ng trắng trứng g&agrave; l&ecirc;n v&agrave; đắp l&ecirc;n l&agrave;n da hoặc c&oacute; thể kết hợp th&ecirc;m với một &iacute;t nước cốt chanh để gia tăng c&ocirc;ng dụng.</p>\n\n<p style=\"text-align: justify;\">Ngo&agrave;i phương ph&aacute;p l&agrave;m trắng da từ trứng g&agrave; ra th&igrave; c&aacute;c bạn cũng c&oacute; thể li&ecirc;n hệ với Svan Clinic &amp; Spa để nhận th&ecirc;m nhiều b&iacute; quyết trong qu&aacute; tr&igrave;nh gi&uacute;p l&agrave;n da trắng s&aacute;ng v&agrave; mịn m&agrave;ng. Th&ecirc;m v&agrave;o đ&oacute;, để nhanh ch&oacute;ng gi&uacute;p l&agrave;n da được l&agrave;m đẹp nhanh ch&oacute;ng th&igrave; c&aacute;c bạn h&atilde;y đến với&nbsp;c&aacute;c dịch vụ trắng da của&nbsp;Svan Clinic &amp; Spa v&agrave; nhận th&ecirc;m nhiều&nbsp;voucher khuyến m&atilde;i mỗi th&aacute;ng.</p>\n\n<div 1faipqlsdlocjj2zwl3-i7uwk5jir-mfnebujky5yy_rukzfway4zxyq=\"\" class=\"widget-element widget-snap\" d=\"\" docs.google.com=\"\" e=\"\" forms=\"\" https:=\"\" id=\"FORM505\" lp-action=\"https://docs.google.com/forms/d/e/1FAIpQLSdLocJj2ZwL3-I7UWk5JIr-MfnEbUjky5yy_ruKzfWaY4ZxYQ/formResponse\" lp-data-form=\"{\" lp-display=\"block\" lp-id-pop-sub=\"POPUP516\" lp-lang=\"FORM\" lp-message-form=\"Cám ơn bạn đã quan tâm\" lp-type=\"contact_form\" lp-type-form=\"google\">\n<form class=\"widget-content\">\n<div class=\"widget-element widget-snap widget-dragg widget-item-child\" id=\"ITEM_FORM506\" lp-display=\"block\" lp-lang=\"ITEM_FORM\" lp-type=\"item_form\"><input class=\"widget-content\" lp-label=\"Họ và tên\" name=\"name\" placeholder=\"Họ và tên\" required=\"required\" type=\"text\" /></div>\n\n<div class=\"widget-element widget-snap widget-dragg widget-item-child\" id=\"ITEM_FORM508\" lp-display=\"block\" lp-lang=\"ITEM_FORM\" lp-type=\"item_form\"><input class=\"widget-content\" lp-label=\"Điện thoại\" name=\"phone\" placeholder=\"Nhập Số điện thoại\" required=\"required\" type=\"tel\" /></div>\n</form>\n</div>\n\n<p><a class=\"widget-element widget-snap style-1 ladi-drop\" id=\"BUTTON509\" lp-background=\"button\" lp-display=\"table\" lp-lang=\"BUTTON\" lp-parent=\"FORM505\" lp-type=\"button\"><span class=\"widget-content\">ĐĂNG K&Yacute; ƯU Đ&Atilde;I V&Agrave; TƯ VẤN MIỄN PH&Iacute;</span> </a></p>\n', 'cach-lam-mat-na-trung-ga-giup-da-mat-trang-hong-tai-nha', '/Upload/News/539f59bb-ad72-4feb-941f-a8a67a09340f/trung-ga-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách làm mặt nạ trứng gà giúp da mặt trắng hồng tại nhà', 'Cách làm mặt nạ trứng gà giúp da mặt trở nên trắng hồng và mịn màng ngay tại nhà sẽ giúp mọi người đạt được mơ ước bấy lâu trong quá trình làm đẹp của mình.', 'mặt nạ trứng gà', '44', 'PUBLISHED', '', 'old'),
(35, '3', 'Các món ăn giảm cân được chế biến từ đậu phụ', '<p>Đậu phụ l&agrave; dạng thực phẩm ăn chay v&agrave; được chế biến th&agrave;nh nhiều&nbsp;m&oacute;n ăn giảm c&acirc;n&nbsp;rất hiệu quả. Trong đậu phụ rất gi&agrave;u protein, chất đạm n&ecirc;n khiến ch&uacute;ng ta c&oacute; cảm gi&aacute;c no l&acirc;u hơn, hạn chế cảm gi&aacute;c th&egrave;m ăn</p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Đậu phụ l&agrave; dạng thực phẩm ăn chay v&agrave; được chế biến th&agrave;nh<em><strong> c&aacute;c&nbsp;m&oacute;n ăn giảm c&acirc;n</strong></em>&nbsp;rất hiệu quả. Trong đậu phụ rất gi&agrave;u protein, chất đạm n&ecirc;n khiến ch&uacute;ng ta c&oacute; cảm gi&aacute;c no l&acirc;u hơn, hạn chế cảm gi&aacute;c th&egrave;m ăn. C&ugrave;ng với đ&oacute;, chất đạm sẽ cung cấp năng lượng th&uacute;c đẩy chuyển h&oacute;a chất b&eacute;o, l&agrave;m giảm nguy cơ t&iacute;ch tụ mỡ n&ecirc;n v&ocirc; c&ugrave;ng tốt cho chị em muốn giảm c&acirc;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Mặc d&ugrave; hiệu quả giảm c&acirc;n đ&atilde; được chứng nhận, tuy nhi&ecirc;n, nếu chỉ ăn m&atilde;i một m&oacute;n đậu phụ sẽ rất nhanh ch&aacute;n ng&aacute;n. V&igrave; thế, Svan Clinic &amp; Spa sẽ gợi &yacute; một số m&oacute;n ăn được chế biến từ loại thực phẩm n&agrave;y, nhằm đa dạng khẩu vị, gi&uacute;p bạn ăn ngon hơn v&agrave; ăn được trong suốt một thời gian d&agrave;i.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Đậu phụ d&ugrave;ng để giảm c&acirc;n v&agrave;o bữa s&aacute;ng</strong></span></h3>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">H&atilde;y thay thế trứng g&agrave; bằng đậu phụ non ăn k&egrave;m x&agrave; l&aacute;ch, sốt c&agrave; chua v&agrave;o buổi s&aacute;ng. Những bữa s&aacute;ng tiếp theo, bạn c&oacute; thể ăn b&aacute;nh m&igrave; kẹp đậu phụ chi&ecirc;n d&ograve;n hoặc đậu phụ chi&ecirc;n ăn với b&uacute;n, hủ tiếu hoặc m&igrave; k&egrave;m rau sống. Bạn n&ecirc;n th&ecirc;m ớt, tương... để tạo cảm gi&aacute;c ngon miệng hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"bua sang giam can bang dau phu\" src=\"/files/Files/Kien-thuc-lam-dep/mon-an-giam-can-tu-dau-phu.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Bữa&nbsp;trưa giảm c&acirc;n bằng đậu phụ</strong></span></h3>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">V&agrave;o buổi trưa, c&oacute; thể bổ sung trong thực đơn&nbsp;c&aacute;c m&oacute;n giảm c&acirc;n&nbsp;từ&nbsp;đậu phụ. Với bữa ăn n&agrave;y, đậu phụ c&oacute; thể chế biến th&agrave;nh m&oacute;n chi&ecirc;n&nbsp;sả, kho tương hạt, nấu c&aacute;c loại canh... v&agrave; d&ugrave;ng ăn với cơm. Hoặc kh&eacute;o tay hơn, c&oacute; thể ướp th&ecirc;m gia vị l&agrave;m th&agrave;nh m&oacute;n đậu phụ nướng.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>Giảm c&acirc;n bằng đậu phụ của bữa tối</strong></span></h3>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><strong><img alt=\"thuc don giam can buoi toi bang dau phu\" src=\"/files/Files/Kien-thuc-lam-dep/cac-mon-an-giam-can-tu-dau-phu.jpg\" style=\"width: 500px; height: 300px;\" /></strong><br />\n<em>C&oacute; Thể Bạn Cần Biết &gt;&gt;&nbsp;</em>\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://&quot;http://svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-bi-quyet-giup-ban-giam-can-khi-dang-cho-con-bu                             &quot;\" target=\"_blank\"><em><span data-sheets-userformat=\"{&quot;2&quot;:14851,&quot;3&quot;:[null,0],&quot;4&quot;:[null,2,16777215],&quot;12&quot;:0,&quot;14&quot;:[null,2,3355443],&quot;15&quot;:&quot;Arial&quot;,&quot;16&quot;:10}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;giảm cân cho phụ nữ đang cho con bú&quot;}\" style=\"font-size: 10pt; font-family: Arial;\">giảm c&acirc;n cho phụ nữ đang cho con b&uacute;</span></em></a></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Với người giảm c&acirc;n, buổi tối thường hạn chế tối đa mọi thức ăn nhưng bạn ho&agrave;n to&agrave;n c&oacute; thể ăn một số m&oacute;n ăn nhẹ nh&agrave;ng được chế biến từ đậu phụ như: hủ tiếu chay, phở chay, b&uacute;n ri&ecirc;u chay, canh b&uacute;n chay, m&igrave; x&agrave;o rau... v&agrave; kết th&uacute;c bữa ăn với một cốc tr&agrave; ấm.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Thực đơn <a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cac-mon-an-giam-can-duoc-che-bien-tu-dau-phu\" target=\"_blank\"><span style=\"color:#3ab572;\"><strong>c&aacute;c m&oacute;n ăn giảm c&acirc;n bằng đậu phụ</strong></span></a> cho 3 buổi trong ng&agrave;y sẽ gi&uacute;p chị em dễ d&agrave;ng giảm c&acirc;n m&agrave; kh&ocirc;ng cần phải nhịn ăn hoặc uống thuốc giảm c&acirc;n để tr&aacute;nh ảnh hưởng tới sức khỏe.</p>\n\n<p style=\"text-align: justify;\">Xem th&ecirc;m &gt;&gt;&nbsp;\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/lam-cach-nao-de-giam-mo-bung-sau-khi-sinh-em-be\" target=\"_blank\"><em><strong><span data-sheets-userformat=\"{&quot;2&quot;:14851,&quot;3&quot;:[null,0],&quot;4&quot;:[null,2,16777215],&quot;12&quot;:0,&quot;14&quot;:[null,2,3355443],&quot;15&quot;:&quot;Arial&quot;,&quot;16&quot;:10}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;làm thế nào để giảm mỡ bụng sau sinh&quot;}\" style=\"font-size: 10pt; font-family: Arial;\">l&agrave;m thế n&agrave;o để giảm mỡ bụng sau sinh</span></strong></em></a></p>\n', 'cac-mon-an-giam-can-duoc-che-bien-tu-dau-phu', '/Upload/News/ed6a9e60-d5a1-4983-b9d6-e2b3b8ea1c70/cac-mon-an-giam-can-tu-dau-phu.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Các món ăn giảm cân được chế biến từ đậu phụ', 'Các món ăn giảm cân được chế biến từ đậu phụ không những ngon, đa dạng khẩu vị giúp bạn ăn được thường xuyên mà còn giúp chị em giảm cân rất tốt.', 'các món ăn giảm cân', '44', 'PUBLISHED', '', 'old'),
(36, '3', 'Cách chăm sóc và dưỡng da mặt mịn màng', '<p style=\"text-align: justify;\">Dưỡng da mặt đ&uacute;ng c&aacute;ch ch&iacute;nh l&agrave; một trong những phương ph&aacute;p rất hiệu quả gi&uacute;p chăm s&oacute;c l&agrave;n da của bạn trước điều kiện thời tiết ng&agrave;y c&agrave;ng xấu đi.</p>\n', '<p><em><strong>Dưỡng&nbsp;da mặt&nbsp;</strong></em>đ&uacute;ng c&aacute;ch ch&iacute;nh l&agrave; một trong những phương ph&aacute;p rất hiệu quả gi&uacute;p&nbsp;chăm s&oacute;c l&agrave;n da&nbsp;của bạn trước điều kiện thời tiết ng&agrave;y c&agrave;ng xấu đi. Vậy n&ecirc;n, để c&oacute; được một phương ph&aacute;p đ&uacute;ng đắn th&igrave; ch&uacute;ng ta n&ecirc;n đến với c&aacute;c chuy&ecirc;n gia để nhận được qu&aacute; tr&igrave;nh tư vấn tuyệt vời v&agrave; ho&agrave;n hảo nhất cho mỗi l&agrave;n da. V&agrave; sau đ&acirc;y,&nbsp;Svan Clinic &amp; Spa&nbsp;cũng chia sẻ một số b&iacute; quyết rất đặc biệt nhưng lại rất đơn giản gi&uacute;p bạn dễ d&agrave;ng chăm s&oacute;c được l&agrave;n da của m&igrave;nh m&agrave; kh&ocirc;ng ti&ecirc;u tốn qu&aacute; nhiều thời gian v&agrave;o đ&oacute;:</p>\n\n<p><strong>Rửa mặt thường xuy&ecirc;n</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-cham-soc-va-duong-da-mat-2.jpg\" style=\"width: 500px; height: 335px;\" /></p>\n\n<p>Đ&ocirc;i khi phương ph&aacute;p đơn giản nhất ch&iacute;nh l&agrave; phương ph&aacute;p hiệu quả nhất m&agrave; rất nhiều người đ&atilde; l&atilde;ng qu&ecirc;n đi trong qu&aacute; tr&igrave;nh chăm s&oacute;c cơ thể của m&igrave;nh. V&agrave; phương ph&aacute;p rửa sạch l&agrave;n da với nước sạch thường xuy&ecirc;n ch&iacute;nh l&agrave; c&aacute;ch c&oacute; thể gi&uacute;p ch&uacute;ng ta loại bỏ c&aacute;c loại bụi bẩn v&agrave; tế b&agrave;o chết. Th&ecirc;m v&agrave;o đ&oacute;, c&aacute;c bạn h&atilde;y sử dụng nước ấm để gia tăng c&ocirc;ng dụng trong qu&aacute; tr&igrave;nh dưỡng da mịn m&agrave;ng hằng ng&agrave;y.</p>\n\n<p><strong>Sử dụng nước cốt chanh</strong></p>\n\n<p>Nước chanh l&agrave; một trong những thức uống gi&uacute;p giải kh&aacute;t v&agrave; bổ sung rất nhiều dưỡng chất cho cơ thể, l&agrave;n da mỗi ng&agrave;y. Nhưng nếu như ch&uacute;ng ta sử dụng nước cốt chanh để rửa mặt h&agrave;ng ng&agrave;y sẽ tạo dễ d&agrave;ng loại bỏ đi tế b&agrave;o chết tr&ecirc;n l&agrave;n da v&agrave; cung cấp chất dinh dưỡng cần thiết cho qu&aacute; tr&igrave;nh t&aacute;i tạo lại l&agrave;n da mịn m&agrave;ng mới. Nhờ v&agrave;o đ&oacute;, l&agrave;n da của ch&uacute;ng ta sẽ trở n&ecirc;n mịn m&agrave;ng v&agrave; trắng s&aacute;ng sau mỗi lần sử dụng.</p>\n\n<p><strong>Sử dụng kem chống nắng</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-cham-soc-va-duong-da-mat-1.jpg\" style=\"width: 500px; height: 338px;\" /></strong></p>\n\n<p>Với c&aacute;c loại kem chống nắng khi sử dụng sẽ gi&uacute;p l&agrave;n da của ch&uacute;ng ta được bảo vệ bởi c&aacute;c thời tiết khắc nghiệt b&ecirc;n ngo&agrave;i. Th&ecirc;m v&agrave;o c&aacute;c phương ph&aacute;p từ thi&ecirc;n nhi&ecirc;n ở tr&ecirc;n th&igrave; sẽ đảm bảo l&agrave;n da được chăm s&oacute;c một c&aacute;ch tuyệt vời. Ngo&agrave;i ra, việc sử dụng kem chống nắng sẽ gi&uacute;p ngăn chặn sự t&aacute;c động của c&aacute;c tia tử ngoại, hạn chế qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a một c&aacute;ch tuyệt vời.</p>\n\n<p>Ngo&agrave;i ra, để nhận được th&ecirc;m nhiều phương ph&aacute;p hơn nữa hay c&oacute; c&aacute;c thắc mắc trong qu&aacute; tr&igrave;nh thực hiện c&aacute;c phương ph&aacute;p dưỡng da th&igrave; c&aacute;c bạn h&atilde;y li&ecirc;n hệ&nbsp;08.3888.3999 để nhận được qu&aacute; tr&igrave;nh tư vấn tận t&igrave;nh nhất đến từ Svan Clinic &amp; Spa.</p>\n', 'cach-cham-soc-va-duong-da-mat-min-mang', '/Upload/News/b98e4165-6ab2-45ff-a0e7-ddfd45cd68f8/trang-da.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách chăm sóc và dưỡng da mặt mịn màng', 'Dưỡng da mặt đúng cách chính là một trong những phương pháp rất hiệu quả giúp chăm sóc làn da của bạn trước điều kiện thời tiết ngày càng xấu đi.', 'dưỡng da mặt', '44', 'PUBLISHED', '', 'old'),
(37, '3', 'Bí quyết làm đẹp da mặt đến từ thiên nhiên', '<p>Với c&aacute;c c&aacute;ch l&agrave;m đẹp da mặt từ thi&ecirc;n nhi&ecirc;n th&igrave; sẽ dễ d&agrave;ng gi&uacute;p mọi người c&oacute; được vẻ đẹp tuyệt vời m&agrave; kh&ocirc;ng ti&ecirc;u tốn qu&aacute; nhiều chi ph&iacute; v&agrave;o trong đ&oacute;.</p>\n', '<p>Với c&aacute;c&nbsp;c&aacute;ch l&agrave;m đẹp da mặt từ thi&ecirc;n nhi&ecirc;n&nbsp;sẽ&nbsp;dễ d&agrave;ng gi&uacute;p mọi người c&oacute; được vẻ đẹp tuyệt vời m&agrave; kh&ocirc;ng ti&ecirc;u tốn qu&aacute; nhiều chi ph&iacute; v&agrave;o trong đ&oacute;. Nhưng để sở hữu được những&nbsp;b&iacute; quyết l&agrave;m đẹp&nbsp;tuyệt vời cho l&agrave;n da của m&igrave;nh th&igrave; c&aacute;c bạn h&atilde;y lắng nghe&nbsp;Svan Clinic &amp; Spa&nbsp;chia sẻ c&aacute;c phương ph&aacute;p rất hiệu quả m&agrave; kh&ocirc;ng phải ai cũng biết gi&uacute;p thực hiện mơ ước bấy l&acirc;u nay.</p>\n\n<p><strong>1. Củ đậu:</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/bi-quyet-lam-dep-da-mat-thien-nhien-1.jpg\" style=\"width: 500px; height: 313px;\" /></strong></p>\n\n<p>Củ đậu l&agrave; một trong những thực phẩm m&agrave; mọi người thường hay sử dụng hằng ng&agrave;y. Nhưng việc ch&uacute;ng ta cần quan t&acirc;m nhiều nhất ch&iacute;nh l&agrave; trong th&agrave;nh phần của củ đậu c&oacute; chứa rất nhiều vitamin v&agrave; dưỡng chất cần thiết cho l&agrave;n da trở n&ecirc;n trắng mịn. Th&ecirc;m v&agrave;o đ&oacute;, củ đậu chứa rất nhiều nước n&ecirc;n sẽ dễ d&agrave;ng gi&uacute;p l&agrave;n da được dưỡng ẩm ho&agrave;n hảo. Vậy n&ecirc;n, để sử dụng củ đậu một c&aacute;ch c&ocirc;ng hiệu cho l&agrave;n da th&igrave; c&aacute;c bạn h&atilde;y xoay nhuyễn ra v&agrave; thoa đều l&ecirc;n l&agrave;n da trong 10 ph&uacute;t. Tiếp đ&oacute; h&atilde;y rửa sạch lại với nước ấm v&agrave; tận hưởng sự kh&aacute;c biệt ho&agrave;n to&agrave;n khi thực hiện xong.</p>\n\n<p><strong>2. Chuối:</strong></p>\n\n<p>Đ&acirc;y l&agrave; thực phẩm thường xuy&ecirc;n xuất hiện trong mỗi gia đ&igrave;nh hiện nay. V&igrave; c&ocirc;ng dụng của chuối đối với cơ thể rất tuyệt vời m&agrave; ai cũng phải c&ocirc;ng nhận. Th&ecirc;m v&agrave;o đ&oacute;, chuối chứa rất nhiều dưỡng chất cần thiết để gi&uacute;p l&agrave;n da trở n&ecirc;n trắng mịn v&agrave; hồng h&agrave;o. Vậy n&ecirc;n, c&aacute;c bạn h&atilde;y đắp m&ocirc;t chiếc mặt nạ từ chuối hoặc kết hợp với c&aacute;c thực phẩm kh&aacute;c để gia tăng c&ocirc;ng dụng khi thực hiện.</p>\n\n<p><strong>3. Sữa tươi:</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/bi-quyet-lam-dep-da-mat-thien-nhien-2.jpg\" style=\"width: 500px; height: 375px;\" /></strong></p>\n\n<p>C&oacute; thể bạn muốn xem :</p>\n\n<ul>\n	<li><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cach-lam-da-mat-trang-min-nhanh-va-hieu-qua-nhat-tu-thien-nhien\"><span style=\"color:#3ab572;\">C&aacute;ch l&agrave;m trắng da mặt nhanh nhất từ thi&ecirc;n nhi&ecirc;n</span></a></li>\n	<li><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/lam-trang-da-toan-than-nhanh-nhat-voi-vitamin-c\"><span style=\"color:#3ab572;\">L&agrave;m thế n&agrave;o để trắng da to&agrave;n th&acirc;n nhanh nhất</span></a></li>\n	<li><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cac-cach-lam-trang-da-toan-than-nhanh-va-hieu-qua\"><span style=\"color:#3ab572;\">C&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n nhanh trong 1 tuần</span></a></li>\n</ul>\n\n<p>Sữa đ&atilde; trở th&agrave;nh một trong những thức uống kh&ocirc;ng thể thiếu đối với nhu cầu của mọi người hiện nay. Nhưng ngo&agrave;i việc uống sữa để bổ sung dưỡng chất cho cơ thể v&agrave; l&agrave;n da th&igrave; ch&uacute;ng ta cũng c&oacute; thể thoa đều l&ecirc;n cơ thể để l&agrave;n dễ d&agrave;ng hấp thụ trực tiếp chất dinh dưỡng. Nhờ v&agrave;o đ&oacute;, l&agrave;n da của bạn sẽ nhanh ch&oacute;ng trở n&ecirc;n trắng s&aacute;ng v&agrave; mịn m&agrave;ng ngay sau những lần thực hiện.</p>\n', 'bi-quyet-lam-dep-da-mat-den-tu-thien-nhien', '/Upload/News/c045ca17-3764-4896-bbee-43caf0020990/cu-?au.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Bí quyết làm đẹp da mặt đến từ thiên nhiên', 'Với các cách làm đẹp da mặt từ thiên nhiên thì sẽ dễ dàng giúp mọi người có được vẻ đẹp tuyệt vời mà không tiêu tốn quá nhiều chi phí vào trong đó.', 'làm đẹp da mặt', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(38, '3', 'Những bài tập yoga giảm mỡ bụng nhanh nhất', '<p><em>Kh&ocirc;ng nhất thiết phải đến ph&ograve;ng tập hay phẫu thuật mới c&oacute; thể giảm b&eacute;o. Bạn ho&agrave;n to&agrave;n c&oacute; thể giảm b&eacute;o bằng c&aacute;c động t&aacute;c yoga đơn giản n&agrave;y ngay tại nh&agrave;.</em></p>\n', '<p align=\"center\"><em><img alt=\"yoga-2.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-chinh.jpg\" style=\"width: 500px; height: 333px;\" /></em></p>\n\n<p align=\"center\">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: justify;\">Hầu hết c&aacute;c chị em phụ nữ đến ph&ograve;ng tập yoga chỉ với 2 mục đ&iacute;ch: 1 l&agrave; để tăng cường sức khoẻ, 2 l&agrave; giảm mỡ để c&oacute; th&acirc;n h&igrave;nh thon gọn. Yoga được biết đến như &ldquo;ph&eacute;p m&agrave;u&rdquo; gi&uacute;p k&eacute;o d&agrave;i vẻ đẹp thanh xu&acirc;n v&agrave; tuổi thọ.</p>\n\n<p style=\"text-align: justify;\">Nếu bạn kh&ocirc;ng c&oacute; thời gian đến ph&ograve;ng tập gym hay chơi một m&ocirc;n thể thao n&agrave;o đ&oacute;, h&atilde;y luyện tập yoga để c&oacute; được th&acirc;n h&igrave;nh như mong muốn.Nhưngliệu tập yoga c&oacute; thể giảm mỡ bụng được kh&ocirc;ng? Những b&agrave;i tập đơn giản, c&oacute; thể thực hiện ở nh&agrave; cho những người mới tập sau đ&acirc;y sẽ l&agrave; c&acirc;u trả lời cho bạn.</p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"color:#3d9c74;\"><strong>Tư thế con thuyền &ndash; Navasana</strong></span></p>\n\n<p align=\"center\"><strong><img alt=\"Paripurna-Navasana_Yoga-Asana_Nina-Mel.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-1.jpg\" style=\"width: 500px; height: 500px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Ngồi tr&ecirc;n s&agrave;n, từ từ n&acirc;ng hau ch&acirc;n l&ecirc;n khoảng 45 độ, nghi&ecirc;ng người về ph&iacute;a sau, tựa hau tay tr&ecirc;n s&agrave;n để giữ c&acirc;n bằng. Khi bạn đ&atilde; giữ thăng bằng được, h&atilde;y từ từ n&acirc;ng hai tay, duỗi thẳng hai tay vể ph&iacute;a trước ngang chiều cao của vao, hai l&ograve;ng b&agrave;n tay &uacute;p v&agrave;o hai gối.</p>\n\n<p>Giữ y&ecirc;n trong 30 gi&acirc;y. Tiếp tục tăng dần l&ecirc;n.</p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"color:#3d9c74;\"><strong>Tư thế Plank &ndash; Phalakasana</strong></span></p>\n\n<p align=\"center\"><strong><img alt=\"Chaturanga-Dandasana_high_Yoga-Asana_Nina-Mel.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-2.jpg\" style=\"width: 500px; height: 476px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Tạo tư thế h&iacute;t đất tr&ecirc;n thảm, hai b&agrave;n tay đặt cố định dưới hai gối, th&acirc;n người giữ cố định, thằng từ đầu tới ch&acirc;n. Kh&ocirc;ng được để h&ocirc;ng x&agrave; xuống. Giữ cố định v&agrave; căng cứng cơ bụng.</p>\n\n<p>Giữ y&ecirc;n trong 30 gi&acirc;y, tăng dần l&ecirc;n 2 ph&uacute;t.</p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"color:#3d9c74;\"><strong>Tư thế nửa vầng trăng &ndash; Purvottanasana</strong></span></p>\n\n<p align=\"center\"><strong><img alt=\"Hình/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-3.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-3.jpg\" style=\"width: 500px; height: 500px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Đứng thẳng người, nghi&ecirc;ng về b&ecirc;n tr&aacute;i, chống tay tr&aacute;i xuống s&agrave;n. Đồng thời n&acirc;ng ch&acirc;n phải l&ecirc;n sao cho vu&ocirc;ng g&oacute;c với ch&acirc;n tr&aacute;i, tay phải duỗi thẳng về ph&iacute;a trần.</p>\n\n<p>Giữ cố định trong 30 gi&acirc;y, thực hiện cho 2 b&ecirc;n.</p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"color:#3d9c74;\"><strong>Tư thế si&ecirc;u nh&acirc;n &ndash; Viparita Shalabhasana</strong></span></p>\n\n<p align=\"center\"><strong><img alt=\"Bai-tap-Yoga-giam-can-cho-nguoi-moi-tap-sieu-nhan.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-4.jpg\" style=\"width: 500px; height: 375px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Từ từ n&acirc;ng đầu, ngực, hai tay v&agrave; hai ch&acirc;n l&ecirc;n đồng thời. &Eacute;p chặt bụng dưới v&agrave; h&ocirc;ng xuống s&agrave;n. N&acirc;ng l&ecirc;n c&agrave;ng cao c&agrave;ng tốt, nếu c&oacute; thể v&agrave; giữ y&ecirc;n c&agrave;ng l&acirc;u c&agrave;ng tốt. Mắt nh&igrave;n thẳng về ph&iacute;a trước.</p>\n\n<p>Giữ y&ecirc;n trong 30 gi&acirc;y. Cố gắng tập tăng dần thời gian l&ecirc;n.</p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"color:#3d9c74;\"><strong>Tư thế Plank nh&uacute;n thấp &ndash; Chaturanga Dandasana</strong></span></p>\n\n<p align=\"center\"><strong><img alt=\"Bai-tap-Yoga-giam-can-cho-nguoi-moi-tap-Plank-nhun-thap.jpg\" src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-5.jpg\" style=\"width: 500px; height: 475px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Từ vị tr&iacute; Plank thẳng tay, hạ th&acirc;n người xuống cho tới khi n&agrave;o hai tay tr&ecirc;n song song với th&acirc;n người. Đảm bảo hơi n&acirc;ng m&ocirc;ng cao hơn th&acirc;n người 1 ch&uacute;t v&agrave; kh&ocirc;ng được để bụng x&agrave; xuống.</p>\n\n<p>Giữ cố định trong 10 gi&acirc;y, sau đ&oacute; tăng dần l&ecirc;n 30, 60 gi&acirc;y hoặc l&acirc;u hơn.</p>\n\n<p style=\"text-align: justify;\">B&ecirc;n cạnh việc tập yoga để giảm b&eacute;o v&ugrave;ng bụng, bạn cũng cần đảm bảo chế độ ăn uống nghỉ ngơi hợp l&iacute; để c&oacute; th&acirc;n h&igrave;nh như mơ ước. Với Yoga, bạn phải ki&ecirc;n tr&igrave; luyện tập trong thời gian l&agrave; 4 th&aacute;ng để nhận thấy được hiệu quả. Đối với những người bận rộn, kh&ocirc;ng c&oacute; nhiều thời gian cho luyện tập yoga hay gym, phương ph&aacute;p giảm b&eacute;o nhanh ch&oacute;ng, an to&agrave;n, hiệu quả nhất ch&iacute;nh l&agrave; Dưỡng sinh Nhật Bản.</p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><img src=\"/files/Files/Tin%20t%E1%BB%A9c/Ki%E1%BA%BFn%20th%E1%BB%A9c%20l%C3%A0m%20%C4%91%E1%BA%B9p/Nh%E1%BB%AFng%20b%C3%A0i%20t%E1%BA%ADp%20yoga%20gi%E1%BA%A3m%20m%E1%BB%A1%20b%E1%BB%A5ng%20nhanh%20nh%E1%BA%A5t/giam-beo-DSNB-diep-bui-min.JPG\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p align=\"center\"><em>Tham khảo chi tiết phương ph&aacute;p giảm b&eacute;o Dưỡng sinh Nhật Bản <strong><a href=\"http://www.svanclinic.vn/dich-vu/giam-can-giam-beo/giam-beo-duong-sinh\"><span style=\"color:#3d9c74;\">tại đ&acirc;y</span></a></strong>&nbsp;</em></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: justify;\">Đ&acirc;y l&agrave; phương ph&aacute;p giảm b&eacute;o ho&agrave;n to&agrave;n tự nhi&ecirc;n, sử dụng massage ấn huyệt kỹ thuật cao để đ&agrave;o thải mỡ v&agrave; độc tố ra khỏi cơ thể qua đường mồ h&ocirc;i v&agrave; nước tiểu. C&ocirc;ng nghệ n&agrave;y đ&atilde; được nhiều phụ nữ Việt Nam v&agrave; Nhật Bản tin d&ugrave;ng.</p>\n\n<p style=\"text-align: justify;\">Nhận ngay tư vấn miễn ph&iacute; từ chuy&ecirc;n gia giảm b&eacute;o tại Svan Clinic &amp; Spa bằng c&aacute;ch li&ecirc;n hệ hotline 1900 0117 nh&eacute;!</p>\n', 'nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat', '/Upload/News/7871835d-612b-4cb2-8707-f3acd4901417/nhung-bai-tap-yoga-giam-mo-bung-nhanh-nhat-chinh.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Những bài tập yoga giảm mỡ bụng nhanh nhất', 'Yoga giảm mỡ bụng là những bài tập có khả năng giúp bạn giảm mỡ vùng bụng, vùng đùi nhanh nhất. Các bài tập này có thể tập ngay tại nhà dành cho chị em phụ nữ.', 'yoga giam mo bung, yoga giảm mỡ bụng', '44', 'PUBLISHED', '', 'old'),
(39, '3', 'Những bài tập thể dục giảm cân tại nhà hiệu quả', '<p><span style=\"text-align: justify;\">Thể dục giảm c&acirc;n</span><span style=\"text-align: justify;\">&nbsp;đ&atilde; kh&ocirc;ng c&ograve;n xa lạ với c&aacute;c chị em đang bị thừa c&acirc;n. C&oacute; rất nhiều b&agrave;i tập kh&aacute;c nhau để lấy lại v&oacute;c d&aacute;ng chuẩn v&agrave; kh&ocirc;ng &iacute;t người&nbsp;chọn đến ph&ograve;ng tập b&agrave;i bản v&agrave; c&aacute;ch <strong>tập thể dục giảm c&acirc;n tại nh&agrave;</strong> được chị em ưu ti&ecirc;n trước nhất.</span></p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Thể dục giảm c&acirc;n&nbsp;đ&atilde; kh&ocirc;ng c&ograve;n xa lạ với c&aacute;c chị em đang bị thừa c&acirc;n. C&oacute; rất nhiều b&agrave;i tập kh&aacute;c nhau để lấy lại v&oacute;c d&aacute;ng chuẩn v&agrave; kh&ocirc;ng &iacute;t người&nbsp;chọn đến ph&ograve;ng tập b&agrave;i bản. Tuy nhi&ecirc;n, với một số b&agrave;i tập tại nh&agrave; sau đ&acirc;y, nếu thực hiện một c&aacute;ch thường xuy&ecirc;n cũng ho&agrave;n to&agrave;n mang lại hiệu quả giảm c&acirc;n tương tự.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</p>\n\n<h2 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>B&agrave;i số 1 của b&agrave;i tập giảm c&acirc;n tại nh&agrave;</strong></span></h2>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Đầu ti&ecirc;n, h&atilde;y nằm sấp xuống s&agrave;n, sau đ&oacute; n&acirc;ng đầu v&agrave; th&acirc;n l&ecirc;n cao từ từ, đồng thời n&acirc;ng ch&acirc;n l&ecirc;n v&agrave; cong lưng. Cần phải uống cong đầu gối đến khi gần chạm đến m&ocirc;ng. Hai tay nắm lấy mắt c&aacute; ch&acirc;n v&agrave; k&eacute;o l&ecirc;n, đầu ngưỡng cao, mắt nh&igrave;n về trước. L&uacute;c n&agrave;y, phần bụng &aacute;p s&aacute;t s&agrave;n v&agrave; chịu to&agrave;n bộ trọng lượng cơ thể.&nbsp;H&atilde;y giữ nguy&ecirc;n tư thế n&agrave;y đến khi bạn kh&ocirc;ng thể tiếp tục.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"bai tap uon bung giam can tại nha\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-the-duc-giam-can.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Đ&acirc;y l&agrave; b&agrave;i tập nhằm l&agrave;m căn cơ bắp đ&ugrave;i, bụng, ngực, h&ocirc;ng để k&iacute;ch th&iacute;ch v&agrave; l&agrave;m lượng mỡ trong cơ thể n&oacute;ng l&ecirc;n, th&uacute;c đẩy qu&aacute; tr&igrave;nh đốt ch&aacute;y calo.</p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;&gt;&gt;&gt;&gt;&nbsp;\n<style type=\"text/css\"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}-->\n</style>\n<a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/lam-cach-nao-de-giam-mo-bung-sau-khi-sinh-em-be\" target=\"_blank\"><em><strong>L&Agrave;M SAO ĐỂ GIẢM MỠ BỤNG SAU SINH NHANH NHẤT</strong></em></a></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<h2 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color: rgb(58, 181, 114);\"><strong>B&agrave;i số 2&nbsp;của b&agrave;i tập giảm c&acirc;n ở&nbsp;nh&agrave;</strong></span></h2>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Đứng thẳng người, ch&acirc;n tr&aacute;i đứng l&agrave;m trụ, ch&acirc;n phải co l&ecirc;n v&agrave; tựa v&agrave;o b&ecirc;n trong ch&acirc;n tr&aacute;i c&agrave;ng cao c&agrave;ng tốt. Tốt nhất l&agrave; tr&ecirc;n đầu gối để c&aacute;c cơ gi&atilde;n đều. C&aacute;c ng&oacute;n ch&acirc;n của b&agrave;n ch&acirc;n phải n&agrave;y n&ecirc;n đặt thẳng nhọn xuống dưới mặt đất. Ch&acirc;n phải giữ cong nhưng để thoải m&aacute;i, b&agrave;n ch&acirc;n tr&aacute;i &eacute;p v&agrave;o s&agrave;n nh&agrave;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Hai tay chấp lại với nhau, khi đưa hai tay l&ecirc;n tr&ecirc;n đầu c&ugrave;ng h&iacute;t thở s&acirc;u, khi đưa tay xuống dưới thở nhẹ nh&agrave;ng. Giữ tư thế n&agrave;y trong 30 gi&acirc;y v&agrave; đổi ch&acirc;n tập lại. Với b&agrave;i tập n&agrave;y tập khoảng 20 ph&uacute;t cho buổi s&aacute;ng v&agrave; buổi tối.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"phuong phap dung thang nguoi trong bai tap giam can tại nha\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-the-duc-giam-can-2.jpg\" style=\"width: 500px; height: 326px;\" /></p>\n\n<div data-mce-style=\"text-align: justify;\" id=\"attachment_1117\" style=\"text-align: justify;\">Đ&acirc;y l&agrave; b&agrave;i tập l&agrave;m to&agrave;n bộ c&aacute;c bộ phận cơ thể căn gi&atilde;n ra, cần rất nhiều năng lượng để thực hiện b&agrave;i tập, v&igrave; thế một lượng mỡ thừa lớn sẽ phải bị đốt ch&aacute;y để cung cấp năng lượng cho qu&aacute; tr&igrave;nh tập luyện.</div>\n\n<div data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</div>\n\n<div data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</div>\n\n<h2 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><span style=\"color: rgb(58, 181, 114);\"><strong>B&agrave;i số 3&nbsp;của b&agrave;i tập giảm c&acirc;n ở&nbsp;nh&agrave;</strong></span></h2>\n\n<div data-mce-style=\"text-align: justify;\">\n<p style=\"text-align: justify;\">Nằm ngửa, hai ch&acirc;n chạm v&agrave;o nhau, hai l&ograve;ng b&agrave;n tay để b&ecirc;n h&ocirc;ng. Sau đ&oacute;, từ từ n&acirc;ng 2 ch&acirc;n lại với nhau, đồng thời n&acirc;ng cao đầu v&agrave; th&acirc;n l&ecirc;n rồi dừng lại ở g&oacute;c 45 độ. L&uacute;c n&agrave;y, to&agrave;n bộ trọng lượng cơ thể dồn về m&ocirc;ng. Tiếp tục đưa hai b&agrave;n tay l&ecirc;n s&aacute;t v&agrave;o ch&acirc;n, l&ograve;ng b&agrave;n tay &aacute;p v&agrave;o b&ecirc;n đầu gối. Trong tư thế n&agrave;y, đầu hơi ngước l&ecirc;n cao. H&atilde;y giữ nguy&ecirc;n tư thế trong khoảng 3 ph&uacute;t, sau đ&oacute; trở về trạng th&aacute;i ban đầu v&agrave; tiếp tục lặp lại b&agrave;i tập trong tầm 15 ph&uacute;t mỗi ng&agrave;y.</p>\n\n<p style=\"text-align: center;\"><img alt=\"tu the tap the duc giam can\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-the-duc-giam-can-3.jpg\" style=\"width: 500px; height: 500px;\" /></p>\n\n<p>Ch&uacute;c Chị Em th&agrave;nh c&ocirc;ng với &nbsp;<strong><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/nhung-bai-tap-the-duc-giam-can-tai-nha-hieu-qua\" target=\"_blank\"><span style=\"color:#3ab572;\">n<span data-sheets-userformat=\"{&quot;2&quot;:513,&quot;3&quot;:[null,0],&quot;12&quot;:0}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;Những bài tập thể dục giảm cân tại nhà hiệu quả&quot;}\" style=\"font-size: 10pt; font-family: Arial;\">hững b&agrave;i tập thể dục giảm c&acirc;n tại nh&agrave; hiệu quả</span></span></a><span style=\"color:#3ab572;\">&nbsp;</span></strong>nh&eacute;. Nếu thấy hay th&igrave; h&atilde;y chia sẻ cho những người kh&aacute;c để c&ugrave;ng tập giảm c&acirc;n c&ugrave;ng m&igrave;nh nha.</p>\n\n<p><em>C&oacute; thể bạn quan t&acirc;m:</em><strong>&nbsp;</strong>&nbsp;<a href=\"http://&quot;http://svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-bi-quyet-giup-ban-giam-can-khi-dang-cho-con-bu                             &quot;\" target=\"_blank\"><strong><em>CHO CON B&Uacute; C&Oacute; GIẢM C&Acirc;N ĐƯỢC KH&Ocirc;NG?</em></strong></a></p>\n</div>\n\n<div data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">&nbsp;</div>\n', 'nhung-bai-tap-the-duc-giam-can-tai-nha-hieu-qua', '/Upload/News/f0e950a4-84ce-4f1a-8908-08ac9c969669/bai-tap-the-duc-giam-can.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Những bài tập thể dục giảm cân tại nhà hiệu quả', 'Thể dục giảm cân là cách áp dụng các bài tập thể dục để vận động cơ thể, kích thích quá trình đốt cháy năng lượng và cần thực hiện một cách thường xuyên', 'thể dục giảm cân tại nhà', '44', 'PUBLISHED', '', 'old'),
(40, '3', 'Nặn mụn đầu đen đúng cách không để lại vết thâm', '<p>C&oacute; rất nhiều loại mụn tr&ecirc;n da của ch&uacute;ng ta, trong đ&oacute; mụn đầu đen tương đối l&agrave;nh t&iacute;nh nhưng lại g&acirc;y mất thẩm mỹ.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">C&oacute; rất nhiều loại mụn tr&ecirc;n da của ch&uacute;ng ta, trong đ&oacute; mụn đầu đen tương đối l&agrave;nh t&iacute;nh nhưng lại g&acirc;y mất thẩm mỹ. Nhiều bạn kh&ocirc;ng biết c&aacute;ch l&agrave;m sao&nbsp;nặn mụn đầu đen&nbsp;dẫn đến v&ocirc; t&igrave;nh để lại vết th&acirc;m tr&ecirc;n da l&acirc;u ng&agrave;y chuyển th&agrave;nh t&agrave;n nhang xấu x&iacute;. C&aacute;c c&aacute;ch nặn mụn đầu đen n&agrave;y sẽ l&agrave; đ&aacute;p &aacute;n giải quyết mụn v&agrave; vết th&acirc;m hiệu quả tr&ecirc;n mặt.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nan-mun-tai-nha-6-1.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Thời điểm nặng mụn</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn n&ecirc;n biết rằng kh&ocirc;ng phải cứ thấy mụn vừa mới mọc l&ecirc;n l&agrave; nặn ngay, việc nặn mụn đầu đen cũng cần phải c&oacute; thời điểm th&iacute;ch hợp để lấy sạch nh&acirc;n mụn ra m&agrave; kh&ocirc;ng để lại vết th&acirc;m. Mỗi khi rửa mặt v&agrave;o buổi s&aacute;ng bạn n&ecirc;n để &yacute; kỹ những mụn n&agrave;o m&agrave; nh&acirc;n mụn trồi l&ecirc;n ph&iacute;a tr&ecirc;n da một ch&uacute;t tức l&agrave; nh&acirc;n mụn ch&iacute;n v&agrave; nốt mụn đ&oacute; bạn c&oacute; thể nặn ra được. Nếu bạn v&igrave; qu&aacute; hấp tấp muốn triệt sạch đ&aacute;m mụn đỏ khỏi da m&agrave; nặn sai thời điểm kh&ocirc;ng những mụn kh&ocirc;ng hết m&agrave; c&ograve;n g&acirc;y vi&ecirc;m nhiễm, tấy đỏ v&agrave; để lại vết th&acirc;m hoặc mụn sẽ lan sang c&aacute;c v&ugrave;ng da b&ecirc;n cạnh.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/chua-mun-1.jpg\" style=\"width: 500px; height: 286px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>C&aacute;ch nặng mụn</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn c&oacute; thể tham khảo những bước sau đ&acirc;y để &aacute;p dụng nặn mụn cho mặt tại nh&agrave;. Những bạn c&oacute; l&agrave;n da nhạy cảm qu&aacute; mức n&ecirc;n suy nghĩ kĩ trước khi thử nha. Đầu ti&ecirc;n bạn n&ecirc;n lột sạch mụn c&aacute;m xung quanh khu vực cạnh mụn đầu đen trước để n&oacute; sạch v&agrave; l&aacute;ng mịn th&igrave; ch&uacute;ng ta sẽ dễ nhận thấy nh&acirc;n mụn đầu đen hơn. Bạn n&agrave;o c&oacute; nhiều l&ocirc;ng mặt th&igrave; n&ecirc;n cạo sạch để khi b&ocirc;i kem lột mụn sẽ đỡ r&aacute;t v&agrave; đau sau khi lột ra.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/t7.jpg\" style=\"width: 500px; height: 237px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Tiếp theo sau khi lột sơ qua với mặt nạ lột mụn, bạn để da khoảng 15 ph&uacute;t sau th&igrave; lấy đ&aacute; lạnh lăn đều l&ecirc;n v&ugrave;ng da cần nặn mụn đầu đen rồi lau sạch đi qua một lần. Tiếp đến bạn vệ sinh dụng cụ nặn mụn qua với oxy gi&agrave; để triệt sạch vi khuẩn g&acirc;y hại. Da mụn được lăn qua với đ&aacute; lạnh sẽ l&agrave;m lỗ ch&acirc;n l&ocirc;ng se kh&iacute;t lại gi&uacute;m đẩy nh&acirc;n mụn l&ecirc;n phần n&agrave;o l&uacute;c n&agrave;y bạn chỉ việc d&ugrave;ng c&acirc;y nặn mụn ấn nhẹ l&agrave; nh&acirc;n mụn tự động trồi ra b&ecirc;n ngo&agrave;i. Bạn sử dụng b&ocirc;ng g&ograve;n thấm nước hoa hồng lau nhẹ qua v&agrave; để kh&ocirc; tự nhi&ecirc;n 30 ph&uacute;t. Cuối c&ugrave;ng bạn rửa mặt lại bằng sữa rửa mặt dịu nhẹ l&agrave; xong.</p>\n\n<p data-mce-style=\"text-align: justify;\">Lưu &yacute; chỉ &aacute;p dụng 1 - 2 lần/tuần để gi&uacute;p da sạch bớt mụn chứ kh&ocirc;ng n&ecirc;n lạm dụng v&igrave; sẽ khiến lỗ ch&acirc;n l&ocirc;ng to ra t&ugrave;y theo cơ địa mỗi người</p>\n', 'nan-mun-dau-den-dung-cach-khong-de-lai-vet-tham', '/Upload/News/d3217f5f-d4ea-4214-b5dc-b5d6c9dd6cf3/nan-mun-tai-nha-6-1.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Nặn mụn đầu đen đúng cách không để lại vết thâm', 'Bạn đã biết cách nặn mụn đầu đen đúng cách để không có vết thâm chưa? Bí quyết nhỏ sau đây sẽ giúp chúng ta nặn mụn đầu đen hiệu quả.', 'nặng mụn đầu đen', '44', 'PUBLISHED', '', 'old'),
(41, '3', 'Bài tập giảm béo sau khi sinh hiệu quả nhanh nhất', '<p>C&oacute; thể n&oacute;i c&aacute;ch giảm b&eacute;o hiệu quả nhanh nhất ch&iacute;nh l&agrave; luyện tập thể dục mỗi ng&agrave;y để cho cơ thể thi&ecirc;u đốt lượng lớn calo tồn đọng v&agrave; gia tăng sức khỏe.</p>\n', '<p style=\"text-align: justify;\">C&oacute; thể n&oacute;i&nbsp;c&aacute;ch&nbsp;giảm b&eacute;o hiệu quả nhanh nhất&nbsp;ch&iacute;nh l&agrave; luyện tập thể dục mỗi ng&agrave;y để cho cơ thể thi&ecirc;u đốt lượng lớn calo tồn đọng v&agrave; gia tăng sức khỏe. Nhưng l&agrave;m c&aacute;ch n&agrave;o hoặc l&agrave;m sao để c&oacute; được b&agrave;i tập giảm mỡ bụng v&agrave; giảm b&eacute;o mặt một c&aacute;ch hiệu quả ch&iacute;nh l&agrave; c&acirc;u hỏi của rất nhiều người khi nhắc đến vấn đề n&agrave;y. Vậy n&ecirc;n, h&ocirc;m nay&nbsp;Svan Clinic &amp; Spa&nbsp;sẽ gi&uacute;p c&aacute;c bạn c&oacute; được một cơ thể tuyệt đẹp một c&aacute;ch dễ d&agrave;ng nhờ v&agrave;o c&aacute;c <strong>b&agrave;i tập giảm b&eacute;o</strong>&nbsp;đơn giản đến từ b&ecirc;n dưới:</p>\n\n<p style=\"text-align: justify;\"><strong>B&agrave;i tập massage</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-giam-beo-sau-sinh-hieu-qua-nhanh-nhat-1.jpg\" style=\"width: 500px; height: 333px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Đối với b&agrave;i tập massage mặt sẽ gi&uacute;p&nbsp;giảm b&eacute;o mặt&nbsp;v&agrave; giải tỏa căng thẳng trong ng&agrave;y một c&aacute;ch hiệu quả ngay sau khi thực hiện. V&agrave; để gia tăng nhanh hiệu quả đối với b&agrave;i tập n&agrave;y th&igrave; ch&uacute;ng ta cần phải hoạt động cơ mặt thật mạnh trong ng&agrave;y hoặc trong qu&aacute; tr&igrave;nh massage bằng vi&ecirc;c ph&aacute;t &acirc;m chữ &quot;A&quot; hoặc &quot;&Ocirc;&quot;</p>\n\n<p style=\"text-align: justify;\"><strong>Chạy bộ</strong></p>\n\n<p style=\"text-align: justify;\">Theo nhiều kết quả nghi&ecirc;n cứu th&igrave; việc chạy bộ gi&uacute;p cơ thể ti&ecirc;u tốn rất nhiều calo trong cơ thể, đặc biệt l&agrave; lượng calo dự trữ. Vậy n&ecirc;n, việc chạy bộ giảm c&acirc;n ch&iacute;nh l&agrave; một trong những c&aacute;ch rất tuyệt vời để nhanh ch&oacute;ng lấy lại một v&oacute;c d&aacute;ng thon gọn đối với những ai sau khi sinh.</p>\n\n<p style=\"text-align: justify;\"><strong>Đi bộ</strong></p>\n\n<p style=\"text-align: justify;\">Kh&ocirc;ng kh&aacute;c g&igrave; so với qu&aacute; tr&igrave;nh chạy bộ cả, việc đi bộ thường xuy&ecirc;n mỗi ng&agrave;y ch&iacute;nh l&agrave; phương ph&aacute;p rất tuyệt vời gi&uacute;p bạn giảm b&eacute;o nhanh ch&oacute;ng. Theo c&aacute;c nghi&ecirc;n cứu gần đ&acirc;y th&igrave; việc đi bộ đều đặn trong 3 tuần sẽ gi&uacute;p giảm 0.5 kg v&agrave; gi&uacute;p săn chắc v&ugrave;ng bụng v&agrave; ch&acirc;n một c&aacute;ch tuyệt vời.</p>\n\n<p style=\"text-align: justify;\"><strong>Luyện tập&nbsp;Yoga</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-giam-beo-sau-sinh-hieu-qua-nhanh-nhat-2.jpg\" style=\"width: 500px; height: 333px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Yoga được xem l&agrave; phương ph&aacute;p gi&uacute;p giảm mỡ bụng rất tuyệt vời m&agrave; c&ograve;n gi&uacute;p cơ thể khỏe mạnh hơn hết. V&agrave; để đạt được c&ocirc;ng dụng tối ta từ việc luyện tập yoga th&igrave; c&aacute;c bạn h&atilde;y bắt đầu với những b&agrave;i tập c&aacute;nh cung, tư thế hổ mang v&agrave; động t&aacute;c tạo d&aacute;ng thuyền.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'bai-tap-giam-beo-sau-khi-sinh-hieu-qua-nhanh-nhat', '/Upload/News/62d5f917-0404-42fc-9429-b7a25d208fe4/massage.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Bài tập giảm béo sau khi sinh hiệu quả nhanh nhất', 'Có thể nói cách giảm béo hiệu quả nhanh nhất chính là luyện tập thể dục mỗi ngày để cho cơ thể thiêu đốt lượng lớn calo tồn đọng và gia tăng sức khỏe.', 'bài tập giảm béo', '44', 'PUBLISHED', '', 'old'),
(42, '3', 'Cách trị vết thâm mụn dành cho làn da nhạy cảm', '<p>L&agrave;m sao để trị hết vết th&acirc;m do mụn để lại l&agrave; c&acirc;u hỏi nhiều người quan t&acirc;m. Svan Clinic &amp; Spa xin chia sẻ đến bạn&nbsp;c&aacute;ch trị vết th&acirc;m mụn&nbsp;sau đ&acirc;y.</p>\n', '<p data-mce-style=\"text-align: justify;\">L&agrave;m sao để trị hết vết th&acirc;m do mụn để lại l&agrave; c&acirc;u hỏi nhiều người quan t&acirc;m. Svan Clinic &amp; Spa xin chia sẻ đến bạn&nbsp;c&aacute;ch trị vết th&acirc;m mụn&nbsp;sau đ&acirc;y. Hy vọng trong thời gian sớm nhất l&agrave;n da của bạn kh&ocirc;ng c&ograve;n vết th&acirc;m v&agrave; sẹo xấu nữa.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nguyen-nhan-va-cach-tri-vet-tham-sau-mun-hieu-qua.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<p alt=\"nguyen-nhan-va-cach-tri-vet-tham-sau-mun-hieu-qua\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/nguyen-nhan-va-cach-tri-vet-tham-sau-mun-hieu-qua.jpg\" src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/nguyen-nhan-va-cach-tri-vet-tham-sau-mun-hieu-qua.jpg\">&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bằng nghệ tươi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nghệ tươi l&agrave; b&iacute; quyết trị sạch mụn v&agrave; th&acirc;m hiệu quả lại dễ d&agrave;ng thực hiện mỗi ng&agrave;y m&agrave; kh&ocirc;ng sợ da dị ứng rất th&iacute;ch hợp với l&agrave;n da nhạy cảm của nhiều phụ nữ. Nghệ c&oacute; t&iacute;nh s&aacute;t khuẩn cao do đ&oacute; nhanh ch&oacute;ng l&agrave;m liền miệng vết thương hở ngo&agrave;i ra c&ograve;n c&oacute; t&aacute;c dụng l&agrave;m s&aacute;ng da nhưng v&ugrave;ng th&acirc;m sạm trả lại tone da như l&uacute;c đầu.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nghe-tuoi-voi-dau-oliu.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<p alt=\"nghe-tuoi-voi-dau-oliu\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/nghe-tuoi-voi-dau-oliu.jpg\" src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/nghe-tuoi-voi-dau-oliu.jpg\">&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch thực hiện kh&aacute; đơn giản bạn chỉ việc mua &iacute;t nghệ tươi, rửa sạch c&aacute;t c&ograve;n b&aacute;m ngo&agrave;i vỏ, gọt sơ lớp vỏ ngo&agrave;i. Sau đ&oacute; gi&atilde; nhuyễn nghệ ra rồi vắt lấy nước cốt, tiếp theo h&ograve;a nước cốt với 1 &iacute;t mật ong chuẩn bị trước đ&oacute; v&agrave; đắp l&ecirc;n v&ugrave;ng th&acirc;m mụn. Bạn chịu kh&oacute; massage th&ecirc;m 10 ph&uacute;t để tăng độ thẩm thấu cho v&ugrave;ng da th&acirc;m được nhanh ch&oacute;ng s&aacute;ng mịn trở lại nha.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bằng tr&agrave; xanh</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Bột tr&agrave; xanh chứa nhiều chất chống oxy h&oacute;a ngăn chặn tối đa&nbsp;t&igrave;nh trạng nhăn da chảy sệ. Đặc biệt hơn nếu sử dụng l&agrave;m mặt nạ đắp da trong thời gian d&agrave;i sẽ l&agrave;m da đều m&agrave;u hết th&acirc;m mụn v&agrave; mịn m&agrave;ng tr&ocirc;ng thấy. Bạn c&oacute; thể tự mua l&aacute; tr&agrave; xanh về phơi kh&ocirc; rồi đem rang l&ecirc;n cho thơm sau đ&oacute; xay nhuyễn th&agrave;nh bột hoặc mua ngay ngo&agrave;i si&ecirc;u thị tiện lợi cũng được. Bước tiếp theo bạn đổ 1 &iacute;t bột tr&agrave; xanh ra b&aacute;t h&ograve;a chung với sữa chua đ&aacute;nh đều l&ecirc;n cho hỗn hợp s&aacute;nh lại rồi đắp l&ecirc;n da mặt trong v&ograve;ng 15 ph&uacute;t. Mỗi tuần thực hiện từ 2 - 3 lần cho l&agrave;n da sạch mụn v&agrave; vết th&acirc;m. Một lưu &yacute; nhỏ l&agrave; bạn n&ecirc;n hạn chế ra đường sau khi vừa đắp xong mặt nạ n&agrave;y v&igrave; dễ bị bắt nắng g&acirc;y sạm da nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&oacute; thể bạn muốn xem :</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cac-cach-lam-trang-da-toan-than-nhanh-va-hieu-qua\"><span style=\"color: rgb(58, 181, 114);\">c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n đơn giản</span></a></li>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-cach-triet-tay-long-vinh-vien-nhanh-nhat-tai-tphcm\"><span style=\"color: rgb(58, 181, 114);\">tẩy l&ocirc;ng mặt bằng phương ph&aacute;p tự nhi&ecirc;n</span></a></li>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cach-lam-da-mat-trang-min-nhanh-va-hieu-qua-nhat-tu-thien-nhien\"><span style=\"color: rgb(58, 181, 114);\">c&aacute;ch l&agrave;m trắng da mặt nhanh nhất trong 1 tuần</span></a></li>\n</ul>\n\n<p alt=\"lamtrangdatubottraxanh\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lamtrangdatubottraxanh-1.jpg\" src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lamtrangdatubottraxanh-1.jpg\">&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n', 'cach-tri-vet-tham-mun-danh-cho-lan-da-nhay-cam', '/Upload/News/3092b140-252d-46c2-8ea5-da19adbd905c/nguyen-nhan-va-cach-tri-vet-tham-sau-mun-hieu-qua.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách trị vết thâm mụn dành cho làn da nhạy cảm', 'Làm sao để trị hết vết thâm do mụn để lại là câu hỏi nhiều người quan tâm. Svan Clinic & Spa xin chia sẻ đến bạn cách trị vết thâm mụn sau đây.', 'cách trị vết thâm mụn', '44', 'PUBLISHED', '', 'old'),
(43, '3', 'Mặt nạ trị mụn đầu đen tốt nhất tại nhà cho mọi người', '<p>Tr&ecirc;n thị trường hiện nay c&oacute; rất nhiều sản phẩm lột mụn hoặc chữa trị mụn đầu đen được quảng c&aacute;o kh&aacute; rầm rộ. Tuy nhi&ecirc;n hiệu quả n&oacute; mang lại chưa thấy r&otilde; nhưng mọi người lại phải tốn kh&aacute; nhiều tiền để mua.</p>\n', '<p data-mce-style=\"text-align: justify;\">Tr&ecirc;n thị trường hiện nay c&oacute; rất nhiều sản phẩm lột mụn hoặc chữa trị mụn đầu đen được quảng c&aacute;o kh&aacute; rầm rộ. Tuy nhi&ecirc;n hiệu quả n&oacute; mang lại chưa thấy r&otilde; nhưng mọi người lại phải tốn kh&aacute; nhiều tiền để mua. V&igrave; thế c&aacute;ch l&agrave;m&nbsp;mặt nạ trị mụn đầu đen&nbsp;dưới đ&acirc;y sẽ gi&uacute;p tiết kiệm phần n&agrave;o chi ph&iacute; m&agrave; da của mọi người lại an to&agrave;n hơn rất nhiều.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-dau-den-11.jpg\" style=\"width: 500px; height: 284px;\" /></p>\n\n<h5 data-mce-style=\"text-align: justify;\">1. DƯA LEO MẶT NẠ TRỊ MỤN ĐẦU ĐEN TẠI NH&Agrave; RẺ M&Agrave; ĐẸP</h5>\n\n<p data-mce-style=\"text-align: justify;\">Một nguy&ecirc;n liệu dễ mua với chi ph&iacute; rẻ m&agrave; lại l&agrave;m đẹp to&agrave;n diện cho da th&igrave; c&ograve;n g&igrave; tuyệt với hơn nựa. C&aacute;ch d&ugrave;ng dưa leo l&agrave;m mặt nạ trị mụn đầu đen đưa nhiều chị em nội trợ &aacute;p dụng lắm đ&oacute;. Hiệu quả m&agrave; n&oacute; mang lại cũng kh&ocirc;ng thể xem thường được đ&acirc;u v&igrave; trong dưa leo chứa nhiều vitamin A, B, C .... cực tốt để kh&aacute;ng vi&ecirc;m v&agrave; trị mụn đầu đen. Chỉ việc đem một tr&aacute;i dưa leo đ&atilde; rửa sạch th&aacute;i mỏng rồi đắp l&ecirc;n v&ugrave;ng da bị mụn đầu đen mỗi ng&agrave;y c&aacute;c nh&acirc;n mụn sẽ tự ch&iacute;n v&agrave; rụng đi sạch sẽ. Ngo&agrave;i ra nếu bạn thấy vẫn chưa đủ th&igrave; c&oacute; thể xay nhuyễn dưa leo rồi trộn chung với mật ong l&agrave;m mặt nạ vừa dưỡng da vừa trị mụn an to&agrave;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/Dua-leo.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<h5 data-mce-style=\"text-align: justify;\">2. DẦU DỪA MẶT NẠ TRỊ MỤN ĐẦU ĐEN KH&Ocirc;NG QU&Aacute; CẦU KỲ</h5>\n\n<p data-mce-style=\"text-align: justify;\">N&oacute;i đ&acirc;y l&agrave; c&aacute;ch dễ nhất chắc chắn kh&ocirc;ng chị em n&agrave;o phản đối đ&acirc;u v&igrave; chỉ cần ra chợ mua ngay một &iacute;t dầu dừa về để sẵn trong nh&agrave; l&agrave; bạn đ&atilde; c&oacute; c&aacute;ch trị bay mụn đầu đen kh&oacute; ưa rồi đấy. Chị em n&agrave;o sợ dầu dừa tạp th&igrave; c&oacute; thể mua 1 tr&aacute;i dừa về xay nhuyễn v&agrave; nấu th&agrave;nh dầu dừa để được nguy&ecirc;n chất hơn cũng được. C&aacute;c bước &aacute;p dụng mặt nạ dầu dừa trị mụn cũng đơn giản kh&ocirc;ng k&eacute;m. Đầu ti&ecirc;n bạn n&ecirc;n rửa mặt thật l&agrave; sạch để đ&aacute;nh bay bớt bụi bẩn tr&ecirc;n c&aacute;c lỗ ch&acirc;n l&ocirc;ng để dầu dừa dễ thấm s&acirc;u hơn v&agrave;o b&ecirc;n trong da. Sau đ&oacute; bạn b&ocirc;i dầu dừa pha với &iacute;t dầu oliu trước đ&oacute; l&ecirc;n da bị mụn v&agrave; xoa đều 10 ph&uacute;t cho hỗn hợp thấm v&agrave;o trong rồi để y&ecirc;n như vậy th&ecirc;m 5 ph&uacute;t nữa trước khi rửa mặt lại với sửa rửa mặt. Buổi s&aacute;ng trước khi ra đường bạn n&ecirc;n kết hợp th&ecirc;m với kem chống nắng để c&aacute;c nốt mụn c&ograve;n lại kh&ocirc;ng bị th&acirc;m sạm đi nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/7-meo-tri-viem-lo-chan-long-hieu-qua-hinh-4.jpg\" style=\"width: 500px; height: 342px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&oacute; thể bạn muốn xem :</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cac-cach-lam-trang-da-toan-than-nhanh-va-hieu-qua\"><span style=\"color: rgb(58, 181, 114);\">c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n đơn giản</span></a></li>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-cach-triet-tay-long-vinh-vien-nhanh-nhat-tai-tphcm\"><span style=\"color: rgb(58, 181, 114);\">tẩy l&ocirc;ng mặt bằng phương ph&aacute;p tự nhi&ecirc;n</span></a></li>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cach-lam-da-mat-trang-min-nhanh-va-hieu-qua-nhat-tu-thien-nhien\"><span style=\"color: rgb(58, 181, 114);\">c&aacute;ch l&agrave;m trắng da mặt nhanh nhất trong 1 tuần</span></a></li>\n</ul>\n', 'mat-na-tri-mun-dau-den-tot-nhat-tai-nha-cho-moi-nguoi', '/Upload/News/9b6aa750-d749-4c82-b966-8872a8fbf5fa/dua-leo-lam-dep-da-hieu-qua.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Mặt nạ trị mụn đầu đen tốt nhất tại nhà cho mọi người', 'Hạn chế sử dụng hóa chất để lột mụn gây hư da, cách làm mặt nạ trị mụn đầu đen trên đây hiệu quả và an toàn hơn rất nhiều cho mọi người làn da như ý.', 'mặt nạ trị mụn đầu đen', '44', 'PUBLISHED', '', 'old'),
(44, '3', 'Chế độ ăn kiêng để giảm cân nhanh nhất trong 1 tuần', '<p>Thực đơn ăn ki&ecirc;ng được xem l&agrave; một trong những c&aacute;ch giảm c&acirc;n hiệu quả nhanh nhất trong qu&aacute; tr&igrave;nh lấy lại v&oacute;c d&aacute;ng tuyệt đẹp vốn c&oacute;. Đặc biệt&nbsp;<strong>chế độ ăn ki&ecirc;ng để giảm c&acirc;n nhanh nhất trong 1 tuần </strong>được c&aacute;c chị em rất quan t&acirc;m.</p>\n', '<p style=\"text-align: justify;\">Thực đơn ăn ki&ecirc;ng&nbsp;được xem l&agrave; một trong những&nbsp;c&aacute;ch giảm c&acirc;n hiệu quả nhanh nhất&nbsp;trong qu&aacute; tr&igrave;nh lấy lại v&oacute;c d&aacute;ng tuyệt đẹp vốn c&oacute;. V&agrave; để biết được c&aacute;c loại&nbsp;<em><strong>thức ăn giảm c&acirc;n</strong></em> nhanh nhất trong 1 tuần&nbsp;th&igrave; ch&uacute;ng ta h&atilde;y đến với&nbsp;Svan Clinic &amp; Spa. Ch&uacute;ng t&ocirc;i sẽ chia sẻ tới c&aacute;c bạn những thực phẩm giảm c&acirc;n tuyệt vời gi&uacute;p bạn c&agrave;ng ăn th&igrave; c&agrave;ng giảm đi lượng mỡ thừa trong cơ thể:</p>\n\n<p style=\"text-align: justify;\"><strong>Hạnh nh&acirc;n</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/che-do-an-kieng-de-giam-can-nhanh-nhat-trong-1-tuan-2.jpg\" style=\"width: 500px; height: 325px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Hạnh nh&acirc;n được rất nhiều người sử dụng v&agrave; được liệt k&ecirc; v&agrave;o danh s&aacute;ch những&nbsp;thực đơn giảm c&acirc;n tuyệt vời đối với những ai đang trong qu&aacute; tr&igrave;nh ăn ki&ecirc;ng hay nhịn ăn giảm c&acirc;n. V&igrave; trong th&agrave;nh phần dinh dưỡng của hạnh nh&acirc;n c&oacute; chứa rất nhiều protein cần thiết cho sức khỏe v&agrave; lượng calo rất &iacute;t khi ăn.</p>\n\n<p style=\"text-align: justify;\"><strong>Trứng</strong></p>\n\n<p style=\"text-align: justify;\">C&aacute;c m&oacute;n ăn được chế biến từ trứng c&oacute; t&aacute;c dụng giảm c&acirc;n rất tuyệt vời v&agrave; được nhiều người tr&ecirc;n thế giới c&ocirc;ng nhận điều đ&oacute;, đặc biệt l&agrave; sử dụng trứng l&agrave;m thức ăn mỗi s&aacute;ng. V&agrave; để giảm c&acirc;n hiệu quả trong 1 tuần th&igrave; c&aacute;c bạn h&atilde;y tự l&agrave;m bữa s&aacute;ng của m&igrave;nh bằng trứng ốp la hoặc trứng luộc để thấy ngay c&ocirc;ng hiệu quả trứng.</p>\n\n<p style=\"text-align: justify;\"><strong>T&aacute;o</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/che-do-an-kieng-de-giam-can-nhanh-nhat-trong-1-tuan-1.jpg\" style=\"width: 500px; height: 344px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">T&aacute;o l&agrave; một thực phẩm chứa rất nhiều chất xơ hữu &iacute;ch cho cơ thể v&agrave; gi&uacute;p ch&uacute;ng ta no l&acirc;u hơn m&agrave; kh&ocirc;ng tạo ra chất b&eacute;o. Vậy n&ecirc;n, để c&oacute; thể giảm c&acirc;n hiệu quả trong qu&aacute; tr&igrave;nh ăn ki&ecirc;ng th&igrave; c&aacute;c bạn h&atilde;y th&ecirc;m t&aacute;o trong khẩu phần ăn mỗi ng&agrave;y để gia tăng t&aacute;c dụng. Ngo&agrave;i ra, c&aacute;c bạn h&atilde;y sử t&aacute;o v&agrave; c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c trong ng&agrave;y để thay đổi khẩu vị m&agrave; kh&ocirc;ng lo tăng c&acirc;n.</p>\n\n<p style=\"text-align: justify;\"><strong>Rau xanh</strong></p>\n\n<p style=\"text-align: justify;\">C&oacute; thể n&oacute;i, rau xanh l&agrave; một trong những thực phẩm giảm c&acirc;n tuyệt vời m&agrave; rất nhiều người sử dụng trong thực đơn hằng ng&agrave;y. Vậy n&ecirc;n, việc ăn thật nhiều rau xanh trong ng&agrave;y hoặc c&aacute;c m&oacute;n ăn được chế biến từ rau xanh để thay đổi khẩu vị m&agrave; lại nhận được t&aacute;c dụng như mong muốn.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\"><strong style=\"color:#3ab572;\">C&oacute; thể bạn muốn xem :</strong>\n\n<ul>\n	<li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 B&agrave;i tập yoga giảm mỡ bụng c&oacute; thể tập ngay tại nh&agrave;</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n	<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm c&acirc;n hiệu quả v&agrave; dễ l&agrave;m d&agrave;nh cho bạn</a></li>\n	<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 C&aacute;ch giảm c&acirc;n nhanh nhất v&agrave; hiệu quả nhất </a></li>\n	<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 B&iacute; quyết giảm mỡ bụng hiệu quả nhất </a></li>\n</ul>\n</div>\n\n<p>&nbsp;</p>\n', 'che-do-an-kieng-de-giam-can-nhanh-nhat-trong-1-tuan', '/Upload/News/1a253125-489d-435e-b1bd-135ebf1b912a/tao.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Chế độ ăn kiêng để giảm cân nhanh nhất trong 1 tuần', 'Thực đơn ăn kiêng được xem là một trong những cách giảm cân hiệu quả nhanh nhất trong quá trình lấy lại vóc dáng tuyệt đẹp vốn có.', 'thức ăn giảm cân', '44', 'PUBLISHED', '', 'old'),
(45, '3', 'Những bài tập giảm mỡ bụng trước khi đi ngủ', '<p>Vận động l&agrave; c&aacute;ch nhanh nhất để lấy lại v&ograve;ng eo thọn gọn. Nếu bạn qu&aacute; bận rộn mỗi ng&agrave;y th&igrave; h&atilde;y&nbsp;d&agrave;nh &iacute;t thời gian trước khi đi ngủ để thực hiện một v&agrave;i b&agrave;i&nbsp;tập giảm mỡ bụng&nbsp;sau đ&acirc;y.</p>\n', '<p data-mce-style=\"text-align: justify;\">Vận động l&agrave; c&aacute;ch nhanh nhất để lấy lại v&ograve;ng eo thọn gọn. Nếu bạn qu&aacute; bận rộn mỗi ng&agrave;y th&igrave; h&atilde;y&nbsp;d&agrave;nh &iacute;t thời gian trước khi đi ngủ để thực hiện một v&agrave;i b&agrave;i&nbsp;tập giảm mỡ bụng&nbsp;sau đ&acirc;y. C&aacute;c b&agrave;i tập n&agrave;y&nbsp;kh&ocirc;ng những tốt cho sức khỏe, gi&uacute;p bạn ngủ ngon giấc hơn m&agrave; hiệu quả giảm mỡ bụng cũng kh&aacute; tuyệt vời đấy.</p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\"><span style=\"font-size:1em;\"><span style=\"color:#3ab572;\"><strong>B&agrave;i tập xoay ch&acirc;n giảm mỡ bụng</strong></span></span></h3>\n\n<p data-mce-style=\"text-align: justify;\">Đ&acirc;y l&agrave; b&agrave;i tập đơn giản, nhẹ nh&agrave;ng nhưng hiệu quả giảm mỡ bụng nhanh nhất. Khi thực hiện b&agrave;i tập xoay ch&acirc;n, c&aacute;c động t&aacute;c t&aacute;c động mạnh l&ecirc;n v&ugrave;ng đ&ugrave;i v&agrave; bụng, mang lại hiệu quả giảm mỡ v&ograve;ng hai rất tốt.</p>\n\n<p data-mce-style=\"text-align: justify;\">Hướng dẫn tập: Nằm ngửa tr&ecirc;n s&agrave;n, hai tay, hai ch&acirc;n duỗi thẳng. Sau đ&oacute; đưa ch&acirc;n tr&aacute;i l&ecirc;n cao v&agrave; xoay tr&ograve;n. Hạ ch&acirc;n tr&aacute;i xuống v&agrave; thực hiện động t&aacute;c tương tự cho ch&acirc;n phải. Bạn n&ecirc;n tập &iacute;t&nbsp;nhất 20 lần như vậy để gia tăng hiệu quả giảm c&acirc;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/xoay-chan.jpg\" style=\"width: 500px; height: 700px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\"><span style=\"font-size:1em;\"><span style=\"color:#3ab572;\"><strong>Đứng l&ecirc;n ngồi xuống</strong></span></span></h3>\n\n<p data-mce-style=\"text-align: justify;\">B&agrave;i tập n&agrave;y sẽ gi&uacute;p to&agrave;n cơ thể được vận động, nhất l&agrave; cơ bụng, eo, bắp tay, bắp ch&acirc;n... Từ đ&oacute;, mỡ thừa cũng sẽ bị đốt ch&aacute;y v&agrave; mất dần sau một thời gian tập luyện.</p>\n\n<p data-mce-style=\"text-align: justify;\">Hướng dẫn tập: Đứng thẳng người, hai ch&acirc;n dang rộng v&agrave; đưa tay về ph&iacute;a trước. Sau khi trụ vững, bắt đầu hạ người xuống cho đ&ugrave;i song song mặt s&agrave;n, giữ lưng thẳng đứng, ch&uacute; &yacute; vị tr&iacute; 2 mũi ch&acirc;n kh&ocirc;ng cao hơn đầu gối. Duy tr&igrave; tư&nbsp;thế khoảng 30 gi&acirc;y rồi trở lại vị tr&iacute; ban đầu v&agrave; lặp đi lặp lại b&agrave;i tập nhiều lần.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-dung-len-ngoi-xuong.jpg\" style=\"width: 500px; height: 363px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>B&agrave;i tập gập người</strong></span></h3>\n\n<p data-mce-style=\"text-align: justify;\">Gập người l&agrave; một trong số những b&agrave;i tập giảm mỡ bụng c&oacute; t&aacute;c dụng tốt nhất. B&agrave;i tập n&agrave;y gi&uacute;p cơ bụng hoạt động tối đa, lượng mỡ thừa khu vực n&agrave;y sẽ bị ti&ecirc;u hao một lượng v&ocirc; c&ugrave;ng lớn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Hướng dẫn tập: Đứng thẳng người, hai ch&acirc;n dang rộng bằng vai. Sau đ&oacute; cuối người xuống sao cho mũi tay chạm ch&acirc;n, nhớ thẳng ch&acirc;n v&agrave; lưng khi thực hiện tư thế n&agrave;y. Cố định tư thế khoảng 30 gi&acirc;y rồi trở lại như ban đầu. Ki&ecirc;n tr&igrave; thực hiện chu kỳ b&agrave;i tập trong khoảng 15 ph&uacute;t mỗi ng&agrave;y để cho kết quả tốt.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/gap-nguoi.jpg\" style=\"width: 500px; height: 359px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<h3 data-mce-style=\"text-align: justify;\"><span style=\"color:#3ab572;\"><strong>B&agrave;i tập đạp xe</strong></span></h3>\n\n<p data-mce-style=\"text-align: justify;\">B&agrave;i tập đạp xe gi&uacute;p bạn ti&ecirc;u hao khoảng&nbsp;400 &ndash; 500 calo, mang lại hiệu quả tốt cho việc giảm mỡ bụng.</p>\n\n<p data-mce-style=\"text-align: justify;\">Hướng dẫn tập: Nằm ngửa song song mặt s&agrave;n, duỗi thẳng hai ch&acirc;n, đặt hai ta sau g&aacute;y. Bắt đầu n&acirc;ng đầu v&agrave; vai l&ecirc;n cao. Tiếp tục n&acirc;ng một gối l&ecirc;n ngang bụng, gối c&ograve;n lại c&oacute; thể thẳng v&agrave; bắt đầu vận động hai ch&acirc;n như đang đạp xe. H&atilde;y lặp đi lặp lại động t&aacute;c &iacute;t nhất 15 lần cho mỗi b&ecirc;n.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/bai-tap-dap-xe-giam-mo-bung.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\"><strong style=\"color:#3ab572;\">C&oacute; thể bạn muốn xem :</strong>\n\n<ul>\n	<li><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/lam-cach-nao-de-giam-mo-bung-sau-khi-sinh-em-be\" target=\"_blank\"><span data-sheets-userformat=\"{&quot;2&quot;:14851,&quot;3&quot;:[null,0],&quot;4&quot;:[null,2,16777215],&quot;12&quot;:0,&quot;14&quot;:[null,2,3355443],&quot;15&quot;:&quot;Arial&quot;,&quot;16&quot;:10}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;làm sao để giảm cân sau sinh&quot;}\" style=\"font-size: 10pt; font-family: Arial;\">L&agrave;m sao để giảm c&acirc;n sau sinh</span></a></li>\n	<li><span data-sheets-userformat=\"{&quot;2&quot;:14851,&quot;3&quot;:[null,0],&quot;4&quot;:[null,2,16777215],&quot;12&quot;:0,&quot;14&quot;:[null,2,3355443],&quot;15&quot;:&quot;Arial&quot;,&quot;16&quot;:10}\" data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;cách giảm cân cho bà mẹ cho con bú&quot;}\" style=\"font-size: 10pt; font-family: Arial;\"><a href=\"http://&quot;http://svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-bi-quyet-giup-ban-giam-can-khi-dang-cho-con-bu                             &quot;\" target=\"_blank\">C&aacute;ch giảm c&acirc;n cho b&agrave; mẹ cho con b&uacute;</a></span></li>\n	<li><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/nhung-bai-tap-giam-mo-bung-truoc-khi-di-ngu\" target=\"_blank\">Những b&agrave;i tập giảm mỡ bụng trước khi đi ngủ</a><a href=\"http://www.svanclinic.vn/tag/giam-can\"> </a></li>\n</ul>\n</div>\n\n<p>&nbsp;</p>\n', 'nhung-bai-tap-giam-mo-bung-truoc-khi-di-ngu', '/Upload/News/2f86b740-9af1-4338-b880-14a08298733b/bai-tap-dap-xe-giam-mo-bung.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Những bài tập giảm mỡ bụng trước khi đi ngủ', 'Những bài tập giảm mỡ bụng hoàn toàn có khả năng mang lại kết quả tốt nếu kiên trì tập luyện trong một thời gian dài. Thử áp dụng xem nhé các bạn!', 'tập giảm mỡ bụng', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(46, '3', 'Đi bộ giảm cân cho hiệu quả bất ngờ chỉ trong 5 ngày', NULL, '<p data-mce-style=\"text-align: justify;\">Chỉ trong 5 ng&agrave;y, nếu thực hiện đ&uacute;ng c&aacute;ch&nbsp;đi bộ giảm c&acirc;n&nbsp;sau đ&acirc;y bạn sẽ c&oacute; thể thay đổi h&igrave;nh thể của m&igrave;nh tr&ocirc;ng thon gọn v&agrave; săn chắc hơn.&nbsp;Bạn sẽ ho&agrave;n ho&agrave;n bất ngờ với kết quả đấy!</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Đi v&agrave;o s&aacute;ng sớm hoặc chiều tối</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">H&atilde;y thức dậy trước 6 giờ s&aacute;ng v&agrave; tập th&oacute;i quen đi bộ, đ&oacute;n b&igrave;nh m&igrave;nh để tinh thần phấn chấn, tạo cảm hứng bắt đầu c&ocirc;ng việc ng&agrave;y mới tốt hơn. Đ&oacute; cũng l&agrave; c&aacute;ch gi&uacute;p bạn l&agrave;m việc hăng say hơn, l&uacute;c n&agrave;y,&nbsp;cơ thể sẽ thực hiện đốt ch&aacute;y mỡ thừa để cung cấp nhiều năng lượng&nbsp;cho bạn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Trong khi đ&oacute;, đi bộ khoảng 30 ph&uacute;t v&agrave;o buổi tối, trước khi đi ngủ khoảng 1 tiếng sẽ gi&uacute;p đốt ch&aacute;y năng lượng dư thừa trong ng&agrave;y, ngăn ngừa nguy cơ chuyển h&oacute;a năng lượng dư đ&oacute; th&agrave;nh mỡ dưới da. Ch&iacute;nh v&igrave; thế, đi bộ v&agrave;o buổi s&aacute;ng v&agrave; tối l&agrave; c&aacute;ch giảm c&acirc;n khoa học v&agrave; cho kết quả rất tốt.</p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/di-bo-giam-can.jpg\" style=\"width: 500px; height: 311px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Đi trong địa h&igrave;nh cồng kềnh</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Địa h&igrave;nh cồng kềnh c&oacute; thể l&agrave; đồi n&uacute;i, dốc... Việc đi bộ ở c&aacute;c dạng địa h&igrave;nh n&agrave;y sẽ phải tốn nhiều&nbsp;sức lực hơn v&agrave; c&oacute; thể đốt ch&aacute;y mức năng lượng cao gấp 3 lần so với&nbsp;đi bộ nơi bằng phẳng. Tuy nhi&ecirc;n, kh&ocirc;ng phải nơi n&agrave;o cũng c&oacute; địa h&igrave;nh cồng kềnh cho bạn đi bộ, ở th&agrave;nh phố lại c&agrave;ng kh&oacute;. Thay v&agrave;o đ&oacute;, bạn c&oacute; thể chỉnh độ nghi&ecirc;ng của m&aacute;y chạy bộ, đ&oacute; cũng l&agrave; c&aacute;ch gia tăng việc đốt ch&aacute;y năng lượng.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/leo-nui-giam-can.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Đi cao gối</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;c động t&aacute;c n&acirc;ng cao cho việc đi bộ giảm c&acirc;n c&oacute; thể l&agrave; n&acirc;ng cao gối hoặc nhảy c&oacute;c. Nguy&ecirc;n l&yacute; giảm c&acirc;n của c&aacute;ch n&agrave;y tương tự như đi bộ trong địa h&igrave;nh cồng kềnh. Khi thực hiện n&acirc;ng cao gối, nhảy c&oacute;c, cơ thể cần tập trung một sức lực đủ lớn để thực hiện động t&aacute;c, đ&oacute; cũng ch&iacute;nh l&agrave; l&uacute;c năng lượng sẽ bị ti&ecirc;u hao một phần đ&aacute;ng kể, kh&ocirc;ng c&oacute; cơ hội cho mỡ t&iacute;ch tụ.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'di-bo-giam-can-cho-hieu-qua-bat-ngo-chi-trong-5-ngay', '/Upload/News/64fb2ea5-603d-4e1b-b2a0-34eb8430becb/leo-nui-giam-can.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Đi bộ giảm cân cho hiệu quả bất ngờ chỉ trong 5 ngày', 'Thực hiện đúng cách đi bộ giảm cân sẽ giúp bạn sở hữu thân hình thon gọn, ngoài ra, có thể giảm cân lâu dài bằng phương pháp Giảm béo công nghệ HIFU LIPO', 'đi bộ giảm cân', '44', 'PUBLISHED', '', 'old'),
(47, '3', 'Phương pháp giảm cân cấp tốc an toàn trong 3 ngày', '<p>L&agrave;m thế n&agrave;o để giảm c&acirc;n trong v&ograve;ng 3 ng&agrave;y an to&agrave;n v&agrave; hiệu quả? ch&iacute;nh l&agrave; c&acirc;u hỏi của rất nhiều người đang trong qu&aacute; tr&igrave;nh lấy lại v&oacute;c d&aacute;ng thon gọn.</p>\n', '<p style=\"text-align: justify;\">L&agrave;m thế n&agrave;o để&nbsp;giảm c&acirc;n trong v&ograve;ng 3 ng&agrave;y&nbsp;an to&agrave;n v&agrave; hiệu quả? ch&iacute;nh l&agrave; c&acirc;u hỏi của rất nhiều người đang trong qu&aacute; tr&igrave;nh lấy lại v&oacute;c d&aacute;ng thon gọn. Vậy n&ecirc;n, để trả lời được c&acirc;u hỏi n&agrave;y th&igrave; h&ocirc;m nay&nbsp;Svan Clinic &amp; Spa&nbsp;sẽ chia sẻ&nbsp;<em><strong>b&iacute; quyết giảm c&acirc;n</strong></em>&nbsp;nhanh ch&oacute;ng v&agrave; hiệu quả cho tất cả mọi người:</p>\n\n<p style=\"text-align: justify;\"><strong>Ng&agrave;y đầu ti&ecirc;n</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/phuong-phap-giam-can-cap-toc-an-toan-trong-3-ngay-1.jpg\" style=\"width: 500px; height: 344px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Để&nbsp;giảm c&acirc;n hiệu quả v&agrave; an to&agrave;n&nbsp;th&igrave; điều đầu ti&ecirc;n ch&uacute;ng ta cần phải l&agrave;m đ&oacute; ch&iacute;nh l&agrave; chuẩn bị một thực đơn v&ocirc; chất lượng để hỗ trợ. Vậy n&ecirc;n, bữa s&aacute;ng ch&uacute;ng ta cần ăn 1 quả t&aacute;o v&agrave; 1 ly nước &eacute;p hoa quả cho qu&aacute; tr&igrave;nh giảm c&acirc;n. Bữa trưa với bữa tối&nbsp;th&igrave; c&aacute;c bạn n&ecirc;n hạn chế ăn c&aacute;c loại thức ăn chứa tinh bột v&agrave; n&ecirc;n ăn salad trộn với hoa quả, k&egrave;m theo 1 ly nước ấm.</p>\n\n<p style=\"text-align: justify;\">Ngo&agrave;i việc chuẩn bị những thực đơn cho bữa ăn th&igrave; luyện tập thể thao ch&iacute;nh l&agrave;&nbsp;b&iacute; quyết giảm c&acirc;n nhanh&nbsp;tiếp theo m&agrave; ch&uacute;ng ta cần phải thực hiện. Để bảo vệ sức khỏe v&agrave; thực hiện c&aacute;ch giảm c&acirc;n hiệu quả th&igrave; c&aacute;c bạn n&ecirc;n chạy bộ hoặc c&aacute;c b&agrave;i tập nhẹ v&agrave;o mỗi s&aacute;ng v&agrave; chiều.</p>\n\n<p style=\"text-align: justify;\"><strong>Ng&agrave;y thứ hai</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/phuong-phap-giam-can-cap-toc-an-toan-trong-3-ngay-2(1).jpg\" style=\"width: 500px; height: 333px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Cũng tương tư ng&agrave;y đầu ti&ecirc;n, c&aacute;c bạn h&atilde;y chuẩn bị 1 đĩa hoa quả v&agrave; đồ ăn nguội, c&ugrave;ng 1 ly nước ấm cho bữa s&aacute;ng. Bữa trưa v&agrave; tối ch&uacute;ng ta cũng ăn c&aacute;c loại hoa quả hoặc c&oacute; thể ăn trứng luộc để thay đổi khẩu vị, đặc biệt kh&ocirc;ng n&ecirc;n sử dụng lại c&aacute;c m&oacute;n đ&atilde; ăn qu&aacute; nhiều.&nbsp;</p>\n\n<p style=\"text-align: justify;\">Ngo&agrave;i việc chạy bộ hoặc c&aacute;c b&agrave;i thể dục nhẹ như ở ng&agrave;y đầu ti&ecirc;n th&igrave; phương ph&aacute;p giảm c&acirc;n tiếp theo ch&iacute;nh l&agrave; đi bơi. V&agrave; để đốt ch&aacute;y lượng mỡ thừa cho cơ thể th&igrave; c&aacute;c bạn h&atilde;y đi bơi v&agrave;o mỗi s&aacute;ng hoặc chiều để c&oacute; cảm gi&aacute;c thoải m&aacute;i m&agrave; kh&ocirc;ng bị &aacute;p lực trong qu&aacute; tr&igrave;nh giảm c&acirc;n cấp tốc.</p>\n\n<p style=\"text-align: justify;\"><strong>Ng&agrave;y thứ ba</strong></p>\n\n<p style=\"text-align: justify;\">Đối với ng&agrave;y thứ ba th&igrave; ngo&agrave;i việc ăn c&aacute;c loại hoa quả tươi th&igrave; ch&uacute;ng ta h&atilde;y th&ecirc;m v&agrave;o thực đơn của m&igrave;nh một b&aacute;t canh mỗi bữa. Ngo&agrave;i ra, ch&uacute;ng ta c&oacute; thể uống th&ecirc;m cafe để gia tăng nhanh hiệu quả khi giảm c&acirc;n. C&ograve;n bữa tối th&igrave; c&aacute;c bạn h&atilde;y th&ecirc;m một &iacute;t c&aacute; ngừ hoặc thịt b&ograve; xay trong bữa ăn để bổ sung lại dinh dưỡng.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'phuong-phap-giam-can-cap-toc-an-toan-trong-3-ngay', '/Upload/News/b1462af1-47ec-44cd-aeed-e653b9d428b8/boi-loi.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Phương pháp giảm cân cấp tốc an toàn trong 3 ngày', 'Làm thế nào để giảm cân trong vòng 3 ngày an toàn và hiệu quả? chính là câu hỏi của rất nhiều người đang trong quá trình lấy lại vóc dáng thon gọn.', 'bí quyết giảm cân', '44', 'PUBLISHED', '', 'old'),
(48, '3', 'Uống nước trái cây gì để giảm cân hiệu quả?', '<p>Uống g&igrave; để giảm c&acirc;n? Đ&acirc;y l&agrave; c&acirc;u hỏi của rất nhiều người mong muốn lấy lại v&oacute;c d&aacute;ng thọn gọn đặt ra v&agrave; cũng thu h&uacute;t sự quan t&acirc;m của mọi người khi nghe tới.</p>\n', '<p style=\"text-align: justify;\"><em><strong>Uống g&igrave; để giảm c&acirc;n</strong></em>? Đ&acirc;y l&agrave; c&acirc;u hỏi của rất nhiều người mong muốn lấy lại v&oacute;c d&aacute;ng thọn gọn đặt ra v&agrave; cũng thu h&uacute;t sự quan t&acirc;m của mọi người khi nghe tới. Vậy n&ecirc;n, để trả lời được vấn đề n&agrave;y th&igrave;&nbsp;Svan Clinic&nbsp;sẽ gi&uacute;p c&aacute;c bạn giải đ&aacute;p to&agrave;n bộ bằng c&aacute;c loại thức uống ở b&ecirc;n dưới:</p>\n\n<p style=\"text-align: justify;\"><strong>Mật ong</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/uong-nuoc-trai-cay-gi-de-giam-can-hieu-qua-1.jpg\" style=\"width: 500px; height: 340px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Giảm c&acirc;n bằng mật ong&nbsp;ch&iacute;nh l&agrave; phương ph&aacute;p được truyền từ xưa đến nay để gi&uacute;p cho mọi người c&oacute; v&oacute;c d&aacute;ng thon gọn khi sử dụng. V&agrave; để uống mật ong giảm c&acirc;n th&igrave; ch&uacute;ng ta cần chuẩn bị một &iacute;t nước ấm v&agrave; mật ong nguy&ecirc;n chất để gia tăng c&ocirc;ng dụng hiệu quả. Ngo&agrave;i ra, c&aacute;c bạn cũng c&oacute; thể pha mật ong với tr&agrave; để thay đổi khẩu vị v&agrave; c&oacute; cảm gi&aacute;c no l&acirc;u hơn b&igrave;nh thường.</p>\n\n<p style=\"text-align: justify;\"><strong>Nước &eacute;p tr&aacute;i c&acirc;y</strong></p>\n\n<p style=\"text-align: justify;\">Mỗi ng&agrave;y 1 đến 2 ly nước &eacute;p&nbsp;tr&aacute;i c&acirc;y giảm c&acirc;n&nbsp;sẽ gi&uacute;p cơ thể đốt ch&aacute;y lượng lớn mỡ thừa m&agrave; c&ograve;n gi&uacute;p ch&uacute;ng ta cảm gi&aacute;c no l&acirc;u hơn v&agrave; m&agrave; kh&ocirc;ng c&ograve;n cảm gi&aacute;c th&egrave;m ăn. Ngo&agrave;i ra, c&aacute;c bạn cũng c&oacute; thể thay đổi nhiều loại nước &eacute;p kh&aacute;c nhau hoặc hỗn hợp nước &eacute;p để tạo ra một ly nước ngon tuyệt m&agrave; lại bổ dưỡng.</p>\n\n<p style=\"text-align: justify;\"><strong>Nước chanh</strong></p>\n\n<p style=\"text-align: justify;\">Giảm c&acirc;n bằng chanh&nbsp;chắc hẳn đ&atilde; kh&ocirc;ng c&ograve;n xa lạ đối với mọi người trong cuộc sống hằng ng&agrave;y. Nhưng để sử dụng chanh một c&aacute;ch hợp l&yacute; v&agrave; đạt được c&ocirc;ng dụng như mong muốn th&igrave; c&aacute;c bạn h&atilde;y uống chanh n&oacute;ng kh&ocirc;ng đường v&agrave;o mỗi s&aacute;ng hoặc mỗi khi ăn tối xong để hấp thụ to&agrave;n bộ mỡ thừa trong cơ thể.</p>\n\n<p style=\"text-align: justify;\"><strong>Nước &eacute;p chuối</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/uong-nuoc-trai-cay-gi-de-giam-can-hieu-qua-2.jpg\" style=\"width: 500px; height: 313px;\" /></p>\n\n<p style=\"text-align: justify;\">Loại nước uống n&agrave;y tuy nghe c&oacute; vẻ lạ nhưng t&aacute;c dụng giảm c&acirc;n từ chuối thật đ&aacute;ng để ch&uacute;ng ta sử dụng. V&agrave; để c&oacute; được 1 ly nước &eacute;p chuối tuyệt ngon th&igrave; bạn h&atilde;y kết hợp với c&agrave; chua v&agrave; một &iacute;t đ&aacute; để gia tăng c&ocirc;ng hiệu đẹp da từ loại nước uống n&agrave;y.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'uong-nuoc-trai-cay-gi-de-giam-can-hieu-qua', '/Upload/News/f738c38e-5a6c-4bf6-9d03-2b26002a6b3a/nuoc-ep.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Uống nước trái cây gì để giảm cân hiệu quả?', 'Uống gì để giảm cân? Đây là câu hỏi của rất nhiều người mong muốn lấy lại vóc dáng thọn gọn đặt ra và cũng thu hút sự quan tâm của mọi người khi nghe tới.', 'Uống gì để giảm cân', '44', 'PUBLISHED', '', 'old'),
(49, '3', 'Trung thu ấm áp tình thân tại mái ấm Từ Ân - Bà Rịa Vũng Tàu', '<p>Cuộc h&agrave;nh tr&igrave;nh ng&agrave;y 27.08 vừa qua của Svan mong rằng g&oacute;p một phần nho nhỏ cho một m&ugrave;a trung thu &yacute; nghĩa tại m&aacute;i ấm Từ &Acirc;n - B&agrave; Rịa Vũng T&agrave;u</p>\n', '<p style=\"text-align: center;\"><em>Mỗi cuộc h&agrave;nh tr&igrave;nh l&agrave; những niềm y&ecirc;u thương san sẻ</em></p>\n\n<p style=\"text-align: center;\"><em>Mỗi sự dang tay gi&uacute;p đỡ đem lại vạn nụ cười cho trẻ em m&aacute;i ấm Từ &Acirc;n!</em></p>\n\n<p data-mce-style=\"text-align: justify;\">6h30 s&aacute;ng ng&agrave;y 27.08.2016 tr&ecirc;n chuyến xe h&agrave;nh tr&igrave;nh đem y&ecirc;u thương đến với trẻ em mồ c&ocirc;i m&aacute;i ấm ch&ugrave;a Từ &Acirc;n - tại x&atilde; Mỹ Xu&acirc;n, tỉnh B&agrave; Rịa Vũng T&agrave;u. Ch&uacute;ng t&ocirc;i những con người được sinh ra đầy đủ sự che chở bảo vệ từ gia đ&igrave;nh mang trong m&igrave;nh sự nhiệt huyết v&agrave; y&ecirc;u thương tr&ecirc;n cả cuộc h&agrave;nh tr&igrave;nh. Đ&acirc;y l&agrave; sự kết nối lần đầu ti&ecirc;n của Svan Clinic &amp; Spa đến tập thể nh&acirc;n vi&ecirc;n v&agrave; c&aacute;c nh&agrave; hảo t&acirc;m xa gần c&ugrave;ng chung tay g&oacute;p sức để m&ugrave;a trung thu sắp tới &yacute; nghĩa hơn với c&aacute;c b&eacute; m&ocirc; c&ocirc;i.</p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1642\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1642.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1642.jpg\" style=\"width: 500px; height: 300px;\" /><br />\n<em>C&ocirc; Đo&agrave;n B&iacute;ch Ngọc&nbsp;mong muốn sẻ chia nhiều hơn đến những ho&agrave;n cảnh kh&oacute; khăn</em></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1635\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1635.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1635.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1627\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1627.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1627.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\"><span style=\"color:#3d9c74;\"><strong>C&aacute;c th&agrave;nh vi&ecirc;n trong đo&agrave;n tranh thủ xếp những phần qu&agrave; l&ecirc;n xe</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\">Niềm vui như được nh&acirc;n đ&ocirc;i khi ch&agrave;o đ&oacute;n ch&uacute;ng t&ocirc;i l&agrave; những cặp mắt ng&acirc;y tơ, tiếng cười, tiếng bi b&ocirc; ch&agrave;o gọi từ c&aacute;c b&eacute;. Trụ tr&igrave; v&agrave; c&aacute;c sư c&ocirc; rộng mở tấm l&ograve;ng đ&oacute;n mừng đo&agrave;n từ thiện gần 30 c&aacute; nh&acirc;n trước khu&ocirc;n vi&ecirc;n đại sảnh. Một ng&ocirc;i Ch&ugrave;a nhỏ nhưng y&ecirc;n tĩnh v&agrave; l&ograve;ng y&ecirc;u thương th&igrave; bao la gi&agrave;nh cho c&aacute;c b&eacute; v&agrave; người gi&agrave; ho&agrave;n cảnh kh&oacute; khăn. Mỗi người một t&acirc;m trạng ch&uacute;ng t&ocirc;i đ&atilde; kh&ocirc;ng thể kiềm l&ograve;ng trước những c&aacute;i &ocirc;m từ c&aacute;c b&eacute;. Sự hoan hỉ, chờ đợi lẫn c&aacute;i rụt r&egrave; trong &aacute;nh nh&igrave;n chỉ chờ đợi một c&aacute;i giang tay l&agrave; sẽ chạy đến ngay của c&aacute;c em.</p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1716\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1716.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1716.jpg\" style=\"width: 500px; height: 333px;\" /><br />\n<em>8h s&aacute;ng đội từ thiện Svan đ&atilde; đến trước cổng ch&ugrave;a Từ &Acirc;n&nbsp;</em></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1734\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1734.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1734.jpg\" style=\"width: 500px; height: 333px;\" /><br />\n<em>Mỗi người một tay sắp xếp lại vật dụng v&agrave;o đại sảnh Ch&ugrave;a</em></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1730\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1730.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1730.jpg\" style=\"width: 500px; height: 333px;\" /><br />\n<em>Chị&nbsp;Phạm Th&ugrave;y V&acirc;n - Gi&aacute;m&nbsp;đốc Svan clinic &amp; spa đang xếp lại những th&ugrave;ng m&igrave; t&ocirc;m từ thiện</em></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1755\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1755.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1755.jpg\" style=\"width: 500px; height: 333px;\" /><br />\n<em>Sư C&ocirc; chia sẻ về những ho&agrave;n cảnh kh&oacute; khăn của c&aacute;c b&eacute;</em></p>\n\n<p data-mce-style=\"text-align: justify;\">Đến với m&aacute;i ấm Từ &Acirc;n ch&uacute;ng t&ocirc;i lại thấu hiểu nhiều hơn về cuộc sống, được mắt thấy tai nghe nhiều ho&agrave;n cảnh của c&aacute;c em trước khi đến với m&aacute;i ấm n&agrave;y. Niềm vui, nỗi buồn đan xen từng mạch cảm x&uacute;c mỗi khi c&oacute; một đứa b&eacute; được đưa đến đ&acirc;y. C&aacute;c nh&acirc;n vi&ecirc;n trong đo&agrave;n ch&uacute;ng t&ocirc;i người vui đ&ugrave;a với c&aacute;c em, người bồng ẫm trao cho c&aacute;c em sự ấm &aacute;p t&igrave;nh th&acirc;n. Cả ng&agrave;y h&ocirc;m ấy, kh&ocirc;ng chỉ c&oacute; mỗi đo&agrave;n từ thiện của Svan gh&eacute; ch&acirc;n tại đ&acirc;y m&agrave; c&ograve;n c&oacute; kh&aacute; nhiều những nh&agrave; hảo t&acirc;m kh&aacute;c tề tựu.</p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1764\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1764.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1764.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1761\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1761.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1761.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1776\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1776.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1776.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\"><span style=\"color:#3d9c74;\"><strong>Nụ cười, tiếng n&oacute;i bập bẹ của c&aacute;c b&eacute; l&agrave;m ch&uacute;ng t&ocirc;i c&agrave;ng vui hơn</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\">Trẻ em l&agrave; những b&uacute;p măng non, ch&uacute;ng cần được y&ecirc;u thương v&agrave; sự quan t&acirc;m nhiều hơn nữa từ mọi người. Ai c&oacute; c&ocirc;ng g&oacute;p c&ocirc;ng,c&oacute; sức g&oacute;p sức. Svan mong rằng c&aacute;c em kh&ocirc;ng chỉ nhận được t&igrave;nh y&ecirc;u thương nhiều nhất v&agrave;o mỗi m&ugrave;a trung thu th&aacute;ng 7 m&agrave; tất cả c&aacute;c ng&agrave;y c&aacute;c th&aacute;ng trong năm của c&aacute;c em đều được hưởng trọn niềm y&ecirc;u thương, t&igrave;nh th&acirc;n từ những nh&agrave; hảo t&acirc;m khắp mọi nơi tr&ecirc;n đất nước.</p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1645\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1645-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1645-1.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\" style=\"text-align: center;\"><img alt=\"DSC_1751\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/DSC_1751.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/DSC_1751.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: center;\"><span style=\"color:#3d9c74;\"><strong>Gần 30 con người chung tay đem lại nụ cười cho trẻ nhỏ</strong></span></p>\n\n<p data-mce-style=\"text-align: justify;\">V&agrave; ch&uacute;ng ta những người đ&atilde; v&agrave; đang được sống trong hạnh ph&uacute;c h&atilde;y lu&ocirc;n sống thật ch&acirc;n th&agrave;nh. Đối xử tốt đẹp hơn với nhau để mỗi ng&agrave;y lu&ocirc;n tr&agrave;n đầy &yacute; nghĩa của cuộc sống! Một lần nữa Svan Clinic &amp; Spa xin c&aacute;m ơn sự gi&uacute;p đỡ từ c&aacute;c bạn nh&acirc;n vi&ecirc;n v&agrave; nhiều nh&agrave; hảo t&acirc;m đ&atilde; đồng h&agrave;nh c&ugrave;ng ch&uacute;ng t&ocirc;i. Mong rằng tương lai sắp tới ch&uacute;ng ta sẽ chung tay với nhau đi đến nhiều nơi để gi&uacute;p đỡ th&ecirc;m nhiều hơn nhưng ho&agrave;n cảnh kh&oacute; khăn kh&aacute;c.</p>\n', 'trung-thu-am-ap-tinh-than-tai-mai-am-tu-an-ba-ria-vung-tau', '/Upload/News/606d7b75-d70e-44de-821b-293a50be004a/DSC_1642.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, NULL, NULL, '23', 'PUBLISHED', '', 'old'),
(50, '3', 'Chạy bộ giảm cân hiệu quả thế nào là đúng cách?', '<p>Chạy bộ giảm c&acirc;n l&agrave; c&aacute;ch được hầu hết mọi người, cả nam lẫn nữ &aacute;p dụng.&nbsp;Chạy bộ giảm c&acirc;n&nbsp;đ&uacute;ng c&aacute;ch c&oacute; thể đốt ch&aacute;y được 300 calories mỡ thừa, hiệu quả gấp đ&ocirc;i so với c&aacute;c b&agrave;i tập kh&aacute;c.</p>\n', '<p data-mce-style=\"text-align: justify;\">Chạy bộ giảm c&acirc;n l&agrave; c&aacute;ch được hầu hết mọi người, cả nam lẫn nữ &aacute;p dụng.&nbsp;Chạy bộ giảm c&acirc;n&nbsp;đ&uacute;ng c&aacute;ch c&oacute; thể đốt ch&aacute;y được 300 calories mỡ thừa, hiệu quả gấp đ&ocirc;i so với c&aacute;c b&agrave;i tập kh&aacute;c. Kh&ocirc;ng những giảm c&acirc;n m&agrave; h&igrave;nh thức vận động n&agrave;y c&ograve;n rất tốt cho sức khỏe về l&acirc;u d&agrave;i. Thế nhưng, bạn đ&atilde; biết&nbsp;chạy bộ thế n&agrave;o l&agrave; đ&uacute;ng c&aacute;ch hay chưa?&nbsp;Svan Clinic &amp; Spa xin chia sẻ những lưu &yacute; gi&uacute;p bạn điều chỉnh phương ph&aacute;p chạy bộ đ&uacute;ng c&aacute;ch để tối ưu hiệu quả giảm c&acirc;n nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Tốc độ ph&ugrave; hợp</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Khi mới bắt đầu, bạn n&ecirc;n chạy với vận tốc vừa phải v&agrave; trong khoảng thời gian ngắn để cơ thể th&iacute;ch nghi với loại h&igrave;nh vận động n&agrave;y. C&oacute; thể kết hợp chạy bộ 10 ph&uacute;t tiếp đến l&agrave; đi bộ 10 ph&uacute;t, lặp đi lặp lại chu kỳ n&agrave;y sẽ gi&uacute;p cơ thể c&oacute; thời gian hồi sức v&agrave; đảm bảo đốt ch&aacute;y được mỡ thừa. &nbsp;Sau một thời gian, hiểu r&otilde; về sức bền của m&igrave;nh th&igrave; c&oacute; thể bắt đầu tăng tốc độ v&agrave; thời gian để đẩy mạnh&nbsp;qu&aacute; tr&igrave;nh đốt ch&aacute;y năng lượng.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"chay-bo-giam-can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/chay-bo-giam-can.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/chay-bo-giam-can.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Chạy thường xuy&ecirc;n</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Chạy bộ khỏe người th&igrave; bạn c&oacute; thể chạy thứ 2, 4, 6 v&agrave; nghỉ 3, 5, 7. Tuy nhi&ecirc;n, với mục đ&iacute;ch giảm c&acirc;n, bắt buộc bạn phải chạy bộ thường xuy&ecirc;n trong cả tuần. C&agrave;ng chạy nhiều v&agrave; li&ecirc;n tục, qu&aacute; tr&igrave;nh đốt ch&aacute;y calo v&agrave; trao đổi chất diễn ra nhanh hơn từ đ&oacute; hiệu quả giảm c&acirc;n cũng được tăng cao.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Chạy nhanh mỗi ng&agrave;y</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Việc chạy nhanh hay chậm ph&ugrave; thuộc v&agrave;o t&igrave;nh trạng sức khỏe c&aacute; nh&acirc;n. Tuy nhi&ecirc;n, nếu c&oacute; khả năng h&atilde;y luyện tập để chạy nhanh hơn. Theo khoa học nghi&ecirc;n cứu,&nbsp;chạy nhanh sẽ tốn nhiều calories hơn v&agrave; chạy với&nbsp;cường độ mạnh trong thời gian ngắn sẽ&nbsp;đốt ch&aacute;y nhiều chất b&eacute;o hơn khi bạn chạy ở tốc độ chậm r&atilde;i trong một thời gian d&agrave;i.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-chay-bo-giam-can-dung-khoa-hoc-4\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/cach-chay-bo-giam-can-dung-khoa-hoc-4.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-chay-bo-giam-can-dung-khoa-hoc-4.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n\n', 'chay-bo-giam-can-hieu-qua-the-nao-la-dung-cach', '	/Upload/News/3fcc4647-2878-4994-9268-a1d360a9fa17/chay-bo-giam-can.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Chạy bộ giảm cân hiệu quả thế nào là đúng cách?', 'Hãy chạy bộ với tốc độ phù hợp, thường xuyên mỗi ngày là phương pháp chạy bộ giảm cân đúng cách. Ngoài ra, có thể giảm cân bằng công nghệ giảm béo Hifu Lipo', 'chạy bộ giảm cân', '44', 'PUBLISHED', '', 'old'),
(51, '3', 'Những cách trị mụn ở mũi hiệu quả tại nhà', '<p>C&aacute;ch trị mụn tại nh&agrave; thường kh&ocirc;ng mang lại hiệu quả cao nếu như ch&uacute;ng ta kh&ocirc;ng biết được c&aacute;c b&iacute; quyết đặc biệt d&agrave;nh ri&ecirc;ng cho từng loại da.</p>\n', '<p style=\"text-align: justify;\"><em><strong>C&aacute;ch trị mụn</strong></em> tại nh&agrave;&nbsp;thường kh&ocirc;ng mang lại hiệu quả cao nếu như&nbsp;ch&uacute;ng ta kh&ocirc;ng biết được c&aacute;c b&iacute; quyết đặc biệt d&agrave;nh ri&ecirc;ng cho từng loại da. Vậy n&ecirc;n, để dễ d&agrave;ng gi&uacute;p l&agrave;n da loại bỏ c&aacute;c vấn đề về mụn th&igrave; h&ocirc;m nay&nbsp;Svan Clinic And Spa&nbsp;sẽ gi&uacute;p c&aacute;c bạn thực hiện nhờ v&agrave;o c&aacute;c b&iacute; quyết tuyệt vời ở ngay b&ecirc;n dưới:</p>\n\n<p style=\"text-align: justify;\"><strong>Mặt nạ trị mụn đầu đen</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nhung-cach-tri-mun-o-mui-hieu-qua-tai-nha-2.jpg\" style=\"width: 500px; height: 325px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Hiện nay, c&oacute; rất nhiều&nbsp;c&aacute;ch trị mụn đầu đen ở mũi v&agrave; tr&ecirc;n to&agrave;n bộ l&agrave;n da&nbsp;cho mọi người. Nhưng để điều trị một c&aacute;ch đơn giản v&agrave; hiệu quả th&igrave; việc sử dụng một chiếc&nbsp;mặt nạ l&agrave; điều rất cần thiết. V&agrave; để loại bỏ mụn đầu đen tr&ecirc;n l&agrave;n da th&igrave; ch&uacute;ng ta h&atilde;y chuẩn một &iacute;t dưa leo đ&atilde; được xoay nhuyễn, sau đ&oacute; đắp l&ecirc;n v&agrave; massage v&ugrave;ng bị mụn trong v&ograve;ng 30 ph&uacute;t. Lưu &yacute;, ch&uacute;ng ta kh&ocirc;ng n&ecirc;n&nbsp;nặn mụn đầu đen v&agrave;o những giai đoạn đầu khi mụn vừa mới xuất hiện để tr&aacute;nh l&agrave;m tổn thương da.</p>\n\n<p style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn th&acirc;m</strong></p>\n\n<p style=\"text-align: justify;\">Loại mụn n&agrave;y thường suất hiện tr&ecirc;n khu&ocirc;n mặt v&agrave; l&agrave;m mất đi vẻ đẹp vốn c&oacute; của ch&uacute;ng ta. Vậy n&ecirc;n, để c&oacute; thể loại bỏ mụn th&acirc;m th&igrave; điều cần thiết ch&iacute;nh l&agrave; tạo một hỗn hợp được l&agrave;m từ chuối, mật ong v&agrave; nước cốt chanh v&agrave; đắp l&ecirc;n nơi bị th&acirc;m trong v&ograve;ng 10 ph&uacute;t sẽ thấy ngay c&ocirc;ng dụng kỳ lạ đến kh&oacute; tin.</p>\n\n<p style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn trứng c&aacute; v&agrave; mụn bọc</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nhung-cach-tri-mun-o-mui-hieu-qua-tai-nha-1.jpg\" style=\"width: 500px; height: 347px;\" /></p>\n\n<p style=\"text-align: justify;\">Mụn trứng c&aacute; bọc được xem l&agrave; loại mụn rất kh&oacute; chịu đối với mọi người v&agrave; rất dễ d&agrave;ng xuất hiện tr&ecirc;n l&agrave;n da. Ch&iacute;nh v&igrave; thế, để ngăn ngừa c&aacute;c loại mụn n&agrave;y th&igrave; c&aacute;c bạn h&atilde;y xay n&aacute;t tỏi v&agrave; kết hợp với một &iacute;t mật ong hoặc nước dấm gạo để đắp trực tiếp l&ecirc;n những v&ugrave;ng c&oacute; mụn. Ngo&agrave;i ra, phương ph&aacute;p n&agrave;y cũng ch&iacute;nh l&agrave;&nbsp;c&aacute;ch trị mụn c&oacute;c rất hiệu quả đang được nhiều người sử dụng.&nbsp;Lưu &yacute;, v&igrave; mặt nạ được l&agrave;m từ tỏi n&ecirc;n dễ g&acirc;y ra k&iacute;ch ứng đối với l&agrave;n da, thế n&ecirc;n ch&uacute;ng ta h&atilde;y n&ecirc;n sử dụng phương ph&aacute;p n&agrave;y 2 lần trong tuần.</p>\n\n<p style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn c&aacute;m</strong></p>\n\n<p style=\"text-align: justify;\">Mụn c&aacute;m thường xuất hiện ở mũi v&agrave; g&acirc;y ra kh&ocirc;ng &iacute;t kh&oacute; khăn đối với một l&agrave;n da tuyệt đẹp. Cho n&ecirc;n, ch&uacute;ng ta h&atilde;y sử dụng một &iacute;t nước &eacute;p khoai t&acirc;y để b&ocirc;i l&ecirc;n trong v&ograve;ng 30 ph&uacute;t v&agrave; rửa sạch với nước ấm.</p>\n', 'nhung-cach-tri-mun-o-mui-hieu-qua-tai-nha', '/Upload/News/bb4d94aa-d5bc-4512-8f78-9377601a041e/toi-toi.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Những cách trị mụn ở mũi hiệu quả tại nhà', 'Cách trị mụn tại nhà thường không mang lại hiệu quả cao nếu như chúng ta không biết được các bí quyết đặc biệt dành riêng cho từng loại da.', 'cách trị mụn', '44', 'PUBLISHED', '', 'old'),
(52, '3', 'Giảm mỡ bụng sau sinh an toàn hiệu quả cho mẹ và bé', '<p>Sau sinh, chị em thường gặp t&igrave;nh trạng chung l&agrave; tăng c&acirc;n v&agrave; mỡ bụng t&iacute;ch tụ nhiều. Nguy&ecirc;n nh&acirc;n rất dễ hiểu, c&aacute;c mẹ cần cung cấp nhiều chất dinh dưỡng hơn để nu&ocirc;i con b&uacute;</p>\n', '<p data-mce-style=\"text-align: justify;\">Sau sinh, chị em thường gặp t&igrave;nh trạng chung l&agrave; tăng c&acirc;n v&agrave; mỡ bụng t&iacute;ch tụ nhiều. Nguy&ecirc;n nh&acirc;n rất dễ hiểu, c&aacute;c mẹ cần cung cấp nhiều chất dinh dưỡng hơn để nu&ocirc;i con b&uacute;, khi nạp nhiều năng lượng c&ugrave;ng với thời gian sau sinh &iacute;t vận động n&ecirc;n tăng c&acirc;n.&nbsp;Đặc biệt sau sinh, v&ugrave;ng cơ xương chậu bị nới lỏng ra, tạo điều kiện mỡ t&iacute;ch tụ v&ugrave;ng bụng nhiều nhất.</p>\n\n<p data-mce-style=\"text-align: justify;\">Theo b&aacute;c sĩ chuy&ecirc;n khoa, sau khi sinh 3 th&aacute;ng, mẹ c&oacute; thể bắt đầu thực hiện&nbsp;giảm mỡ bụng sau sinh&nbsp;bằng một số phương ph&aacute;p an to&agrave;n sau.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nghệ v&agrave; rượu gừng</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Trong gừng chứa chất Gingerol c&oacute; t&aacute;c dụng l&agrave;m n&oacute;ng v&ugrave;ng bụng, từ đ&oacute; gi&uacute;p ti&ecirc;u mỡ hiệu quả. Trong khi đ&oacute;, rượu được coi l&agrave; chất dẫn tinh dầu trong gừng thấm s&acirc;u v&agrave;o lớp mỡ dưới da bụng, gia tăng ph&acirc;n hủy mỡ ở nơi n&agrave;y. B&ecirc;n cạnh đ&oacute;, nghệ gi&uacute;p da bụng phục hồi c&aacute;c vết nhăn do qu&aacute; tr&igrave;nh mang thai g&acirc;y ra.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"1427684691-giam-beo-bung-bang-ruou-gung--4-\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/1427684691-giam-beo-bung-bang-ruou-gung-4-.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/1427684691-giam-beo-bung-bang-ruou-gung-4-.jpg\" style=\"width: 500px; height: 413px;\" /></p>\n\n<p>Chuẩn bị nguy&ecirc;n liệu gồm:</p>\n\n<ul>\n	<li>\n	<p>&diams; 1 hũ thủy tinh 5 l&iacute;t</p>\n	</li>\n	<li>\n	<p>&diams; 1kg gừng</p>\n	</li>\n	<li>\n	<p>&diams; 1kg nghệ</p>\n	</li>\n	<li>\n	<p>&diams; Rượu trắng 3-4 l&iacute;t</p>\n	</li>\n</ul>\n\n<p>C&aacute;ch thực hiện:</p>\n\n<ul>\n	<li>\n	<p>&diams; Gừng v&agrave; nghệ rửa sạch. Cạo bỏ lớp vỏ ngo&agrave;i, gi&atilde; n&aacute;t</p>\n	</li>\n	<li>\n	<p>&diams; Cho gừng v&agrave; nghệ đ&atilde; gi&atilde; dập v&agrave;o hũ, rồi đổ rượu trắng v&agrave;o sao cho ngập khoảng 2 đốt tay l&agrave; được. Chị em c&oacute; thể hạ thổ hoặc đậy k&iacute;n nắp, đặt ở chỗ tho&aacute;ng m&aacute;t để kh&ocirc;ng bị hư hỏng, l&ecirc;n bọt hay nước chuyển m&agrave;u.</p>\n	</li>\n	<li>\n	<p>&diams; Sau 2 th&aacute;ng rượu gừng nghệ ng&acirc;m sẽ c&oacute; m&agrave;u v&agrave;ng nghệ rất s&aacute;ng. L&uacute;c n&agrave;y, chị em c&oacute; thể mang ra b&ocirc;i l&ecirc;n v&ugrave;ng b&ugrave;ng kết hợp massage nhẹ nh&agrave;ng&nbsp;để thực hiện qu&aacute; tr&igrave;nh l&agrave;m ti&ecirc;u hao lượng mỡ thừa nơi n&agrave;y.</p>\n	</li>\n</ul>\n\n<p><strong>Ngải cứu</strong></p>\n\n<div>\n<p data-mce-style=\"text-align: justify;\">Ngải cứu cũng l&agrave; một trong những nguy&ecirc;n liệu tốt cho việc giảm mỡ bụng sau sinh.&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu:&nbsp;ngải cứu, gạch nung, than hồng</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch thực hiện:</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Rửa sạch khoảng 5 - 6 l&aacute; ngải cứu</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Đặt gạch l&ecirc;n bếp than hồng để nung n&oacute;ng</p>\n	</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\">Trải khăn l&ecirc;n bụng, tiếp đến trải c&aacute;c l&aacute; ngải cứu. Sau đ&oacute;, đặt vi&ecirc;n gạch n&oacute;ng l&ecirc;n v&agrave; d&ugrave;ng khăn kh&aacute;c phủ lại nhằm giữ hơi ấm. Nếu cảm thấy qu&aacute; n&oacute;ng, th&igrave; th&ecirc;m lớp l&aacute; ngải cứu hoặc lớp khăn b&ecirc;n dưới. Giữ gạch tr&ecirc;n bụng như vậy, nếu hết n&oacute;ng th&igrave; thay vi&ecirc;n kh&aacute;c v&agrave; k&eacute;o d&agrave;i thời gian đắp trong khoảng 1 giờ.</p>\n\n<p data-mce-style=\"text-align: justify;\">Sau khi đắp xong, đứng tr&ecirc;n giường v&agrave; quấn dải vải th&ocirc; quanh bụng, c&agrave;ng chặt c&agrave;ng tốt, n&ecirc;n d&ugrave;ng th&ecirc;m gen quấn bụng b&ecirc;n ngo&agrave;i để cố định.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"giam-mo-bung-bang-muoi-rang-ngai-cuu (1)\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/giam-mo-bung-bang-muoi-rang-ngai-cuu-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/giam-mo-bung-bang-muoi-rang-ngai-cuu-1.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n</div>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n\n', 'giam-mo-bung-sau-sinh-an-toan-hieu-qua-cho-me-va-be', '/Upload/News/ff553f84-cbb4-461a-b450-fa51ecf0c9fa/giam-mo-bung-bang-muoi-rang-ngai-cuu-1.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Giảm mỡ bụng sau sinh an toàn hiệu quả cho mẹ và bé', 'Giảm mỡ bụng sau sinh an toàn cho cả mẹ và bé bằng rượu gừng, muối rang, lá ngải cứu và phương pháp giảm béo Tạo form Perfect Shape', 'giảm mỡ bụng sau sinh', '44', 'PUBLISHED', '', 'old'),
(53, '3', 'Cách giảm cân nhanh chóng lấy lại vóc dáng chuẩn', '<p><span style=\"text-align: justify;\">Ngo&agrave;i việc đến spa hoặc ph&ograve;ng tập mỗi ng&agrave;y, nhiều phụ nữ đ&atilde; &aacute;p dụng một số&nbsp;c&aacute;ch giảm c&acirc;n nhanh ch&oacute;ng&nbsp;để lấy lại v&oacute;c d&aacute;ng chuẩn.&nbsp;</span></p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Ngo&agrave;i việc đến spa hoặc ph&ograve;ng tập mỗi ng&agrave;y, nhiều phụ nữ đ&atilde; &aacute;p dụng một số&nbsp;c&aacute;ch giảm c&acirc;n nhanh ch&oacute;ng&nbsp;để lấy lại v&oacute;c d&aacute;ng chuẩn. V&agrave; sau đ&acirc;y l&agrave; những c&aacute;ch giảm c&acirc;n được đ&aacute;nh gi&aacute; c&oacute; mức độ th&agrave;nh c&ocirc;ng cao, nhanh ch&oacute;ng, dễ thực hiện cũng như đảm bảo an to&agrave;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Kh&ocirc;ng bỏ bữa s&aacute;ng</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">C&aacute;c chuy&ecirc;n gia đ&atilde; chứng minh bữa s&aacute;ng l&agrave; v&ocirc; c&ugrave;ng quan trọng, n&oacute; l&agrave; nguồn năng lượng ch&iacute;nh để ch&uacute;ng ta thực hiện c&aacute;c hoạt động cho một ng&agrave;y mới. B&ecirc;n cạnh đ&oacute;, ăn s&aacute;ng đầy đủ gi&uacute;p bạn cảm thấy no l&acirc;u hơn, từ đ&oacute; sẽ ăn &iacute;t thức ăn trong c&aacute;c bữa ăn c&ograve;n lại v&agrave;&nbsp;hiệu quả giảm c&acirc;n được tăng cường.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Theo một số nghi&ecirc;n cứu từ trường Đại học Harvard th&igrave; hầu hết những ai ăn s&aacute;ng đều đặn h&agrave;ng ng&agrave;y sẽ c&oacute; thể giảm được 35% đến 50% bệnh b&eacute;o ph&igrave; v&agrave; nguy cơ bị tiểu đường so với những người thường xuy&ecirc;n nhịn ăn hoặc &iacute;t ăn s&aacute;ng.</p>\n\n<p style=\"text-align: center;\"><img alt=\"Breakfast in bed woman\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/cach-giam-can-nhanh-chong.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-giam-can-nhanh-chong.jpg\" style=\"width: 500px; height: 426px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>Uống nhiều nước</strong></p>\n\n<p style=\"text-align: justify;\">Uống nước nhiều sẽ c&oacute; t&aacute;c dụng đ&agrave;o thải chất cặn bạ trong cơ thể ra ngo&agrave;i nhanh ch&oacute;ng, l&agrave;m da mịn m&agrave;ng hơn. Ngo&agrave;i ra, uống nhiều nước th&igrave; bạn &iacute;t c&oacute; cảm gi&aacute;c đ&oacute;i n&ecirc;n&nbsp;giảm một lượng thức ăn kha kh&aacute; nạp v&agrave;o cơ thể.</p>\n\n<p style=\"text-align: justify;\">C&ugrave;ng với nước lọc, bạn c&oacute; thể sử dụng một số loại nước &eacute;p kh&aacute;c như:&nbsp;nước &eacute;p bưởi, nước &eacute;p t&aacute;o, nước &eacute;p c&agrave; rốt, nước dừa&hellip; để hiệu quả giảm c&acirc;n đạt kết quả tốt.</p>\n\n<p style=\"text-align: center;\"><img alt=\"cach giam can nhanh chong 2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/cach-giam-can-nhanh-chong-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-giam-can-nhanh-chong-2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>Ăn t&aacute;o trước bữa tối</strong></p>\n\n<p style=\"text-align: justify;\">Trong t&aacute;o chứa nhiều chất xơ v&agrave; h&agrave;m lượng vitamin cao, mỗi quả t&aacute;o c&oacute; thể cung cấp&nbsp;5 gam chất xơ. Vitamin sẽ c&oacute; nhiều t&aacute;c dụng trong việc cung cấp chất dinh dưỡng, l&agrave;m đẹp da. Trong khi đ&oacute;, chất xơ sẽ hạn chế cảm gi&aacute;c th&egrave;m ăn. Ch&iacute;nh v&igrave; thế, ăn t&aacute;o l&agrave; c&aacute;ch giảm c&acirc;n hiệu quả lại c&oacute; thể đảm bảo an to&agrave;n cao.</p>\n\n<p style=\"text-align: center;\"><img alt=\"cach giam can nhanh chong 3\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/cach-giam-can-nhanh-chong-3.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-giam-can-nhanh-chong-3.jpg\" style=\"width: 500px; height: 444px;\" /></p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'cach-giam-can-nhanh-chong-lay-lai-voc-dang-chuan', '/Upload/News/f46f9e1c-1a8e-4d0f-a52a-a57679336eea/cach-giam-can-nhanh-chong-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách giảm cân nhanh chóng lấy lại vóc dáng chuẩn', 'Để lấy lại vóc dáng chuẩn, bạn có thể áp dụng các cách giảm cân nhanh chóng lại an toàn như uống nhiều nước, không bỏ bữa sáng, giảm béo dưỡng sinh...', 'cách giảm cân nhanh chóng', '44', 'PUBLISHED', '', 'old'),
(54, '3', 'Mẹo trị sẹo mụn ở lưng cho nam hiệu quả', '<p>C&aacute;ch trị sẹo mụn hiệu quả thường được mọi người quan t&acirc;m trong thời gian qua. Nhưng để c&oacute; b&iacute; quyết trị mụn thật hiệu quả th&igrave; kh&ocirc;ng phải l&agrave; đơn giản.</p>\n', '<p style=\"text-align: justify;\"><em><strong>C&aacute;ch trị sẹo mụn</strong></em>&nbsp;hiệu quả thường được mọi người quan t&acirc;m trong thời gian qua. Nhưng để c&oacute; b&iacute; quyết trị mụn thật hiệu quả th&igrave; kh&ocirc;ng phải l&agrave; đơn giản. Vậy n&ecirc;n, h&ocirc;m nay&nbsp;Svan Clinic &amp; Spa&nbsp;sẽ gi&uacute;p c&aacute;c bạn thực hiện được điều n&agrave;y một c&aacute;ch dễ d&agrave;ng m&agrave; kh&ocirc;ng cần phải đi đến bất kỳ đ&acirc;u để t&igrave;m hiểu nhờ v&agrave;o c&aacute;c&nbsp;phương ph&aacute;p trị mụn&nbsp;rất tuyệt vời ngay ở b&ecirc;n dưới:</p>\n\n<p style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn thịt</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/tri-seo-mun-hieu-qua-1.jpg\" style=\"width: 500px; height: 314px;\" /></p>\n\n<p style=\"text-align: justify;\">Đ&acirc;y được xem l&agrave; loại mụn rất cứng đầu v&agrave; kh&oacute; loại bỏ ra khỏi cơ thể của ch&uacute;ng ta, đặc biệt l&agrave; ở lưng của nam giới. Nhưng giờ đ&acirc;y, bạn c&oacute; thể dễ d&agrave;ng điều trị mụn ở lưng một c&aacute;ch nhanh ch&oacute;ng nhờ v&agrave;o dứa v&agrave; nước cốt chanh. Để sử dụng một c&aacute;ch hiệu quả th&igrave; ch&uacute;ng ta h&atilde;y lấy nước dứa kết hợp với nước cốt chanh v&agrave; thoa l&ecirc;n những v&ugrave;ng bị mụn thịt trong v&ograve;ng 20 ph&uacute;t. V&agrave; sau khi rửa sạch lại với nước ấm trong 2 lần mỗi th&aacute;ng th&igrave; sẽ thấy ngay hiệu quả.</p>\n\n<p style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn đỏ</strong></p>\n\n<p style=\"text-align: justify;\">C&aacute;c loại mụn đỏ thường khiến ch&uacute;ng ta rất kh&oacute; chịu mỗi khi xuất hiện v&agrave; mất đi cảm gi&aacute;c tư tin khi đối diện với mọi người. Vậy n&ecirc;n, để loại bỏ mụn đỏ th&igrave; c&aacute;c bạn h&atilde;y sử dụng đ&aacute; lạnh để chườm v&agrave;o trong v&ograve;ng 5 ph&uacute;t mỗi tối sẽ thấy ngay c&ocirc;ng dụng tuyệt vời n&agrave;y.&nbsp;</p>\n\n<p style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn mủ</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/tri-seo-mun-hieu-qua-2.jpg\" style=\"width: 500px; height: 333px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Mụn mủ thường t&iacute;ch tụ rất nhiều chất độc v&agrave; dễ d&agrave;ng g&acirc;y nhiễm tr&ugrave;ng cho cơ thể mỗi khi xuất hiện. Thế n&ecirc;n, ch&uacute;ng ta h&atilde;y cũng nhau loại bỏ mụn mủ từ tr&agrave; xanh của thi&ecirc;n nhi&ecirc;n. Tr&agrave; xanh c&oacute; c&ocirc;ng dụng diệt khuẩn v&agrave; giảm t&igrave;nh trạng bưng mủ rất hiệu quả. Ch&iacute;nh v&igrave; vậy, ch&uacute;ng ta h&atilde;y lấy nước tr&agrave; xanh đắp l&ecirc;n những v&ugrave;ng c&oacute; mụn để thấy ngay hiệu quả chỉ sau một đ&ecirc;m đến s&aacute;ng.</p>\n\n<p style=\"text-align: justify;\">Ngo&agrave;i ra, để biết th&ecirc;m nhiều phương ph&aacute;p điều trị mụn hơn nữa th&igrave; c&aacute;c bạn h&atilde;y li&ecirc;n hệ&nbsp;08.3888.3999 để nhận được qu&aacute; tr&igrave;nh tư vấn tận t&igrave;nh nhất của Svan Clinic &amp; Spa. Th&ecirc;m v&agrave;o đ&oacute;, c&aacute;c bạn c&ograve;n cơ hội nhận được c&aacute;c qu&agrave; tặng khuyến m&atilde;i đến từ ch&uacute;ng t&ocirc;i v&agrave;o mỗi th&aacute;ng.</p>\n', 'meo-tri-seo-mun-o-lung-cho-nam-hieu-qua', '/Upload/News/5018f6e9-d230-4519-98f3-0c08e98af6d5/tra-xanh.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Mẹo trị sẹo mụn ở lưng cho nam hiệu quả', 'Cách trị sẹo mụn hiệu quả thường được mọi người quan tâm trong thời gian qua. Nhưng để có bí quyết trị mụn thật hiệu quả thì không phải là đơn giản.', 'Cách trị sẹo mụn', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(55, '3', 'Làm sao để giảm cân hiệu quả mà không bỏ cuộc nữa chừng?', '<p>Vất vả giảm c&acirc;n sau 1 tuần lại bắt đầu tăng c&acirc;n kh&ocirc;ng phanh, l&ecirc;n kế hoạch giảm c&acirc;n được v&agrave;i ng&agrave;y lại bỏ dở nửa chừng, hoặc kiệt sức qu&aacute; đến mức ng&atilde; bệnh,&hellip;</p>\n', '<p data-mce-style=\"text-align: justify;\">Vất vả giảm c&acirc;n sau 1 tuần lại bắt đầu tăng c&acirc;n kh&ocirc;ng phanh, l&ecirc;n kế hoạch giảm c&acirc;n được v&agrave;i ng&agrave;y lại bỏ dở nửa chừng, hoặc kiệt sức qu&aacute; đến mức ng&atilde; bệnh,&hellip; Đ&oacute; l&agrave; t&igrave;nh trạng kh&aacute; phổ biến m&agrave; những người giảm c&acirc;n hay gặp phải. Vậy phải&nbsp;l&agrave;m sao để giảm c&acirc;n&nbsp;hiệu quả đ&acirc;y?</p>\n\n<p><strong>Đặt c&acirc;n sức khoẻ trong bếp</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Theo nghi&ecirc;n cứu gần đ&acirc;y của Trường ĐH Y dược Brown th&igrave; ch&igrave;a kho&aacute; của việc giảm c&acirc;n l&agrave; nắm r&otilde; t&igrave;nh trạng c&acirc;n nặng. Bạn n&ecirc;n đặt c&acirc;n sức khỏe ở nơi bạn dễ nh&igrave;n thấy, đặc biệt l&agrave; trong nh&agrave; bếp, v&igrave; mỗi khi muốn ăn, nh&igrave;n thấy c&acirc;n bạn sẽ nhớ mục ti&ecirc;u giảm c&acirc;n của m&igrave;nh m&agrave; hạ quyết t&acirc;m hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Ngo&agrave;i ra, bạn n&ecirc;n ghi lại số c&acirc;n của từng ng&agrave;y (hoặc 2 ng&agrave;y 1 lần). Thời gian tốt nhất để biết ch&iacute;nh x&aacute;c số c&acirc;n của cơ thể l&agrave; v&agrave;o buổi s&aacute;ng. Sau 1 tuần, nếu số c&acirc;n của bạn giảm từ 0,5 &ndash; 1kg l&agrave; bạn đ&atilde; th&agrave;nh c&ocirc;ng. Cũng kh&ocirc;ng n&ecirc;n đặt ra mục ti&ecirc;u qu&aacute; cao để phải giảm c&acirc;n dẫn đến suy nhược hoặc khi kh&ocirc;ng đạt được lại thất vọng v&agrave; bỏ cuộc.</p>\n\n<p style=\"text-align: center;\"><img alt=\"lam sao de giam can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-sao-de-giam-can-e1471624680363.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-sao-de-giam-can-e1471624680363.jpg\" style=\"width: 500px; height: 560px;\" /></p>\n\n<p><strong>Viết nhật k&yacute; giảm c&acirc;n</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Khoan vội cười v&igrave; theo nghi&ecirc;n cứu, những người c&oacute; nhật k&yacute; giảm c&acirc;n c&oacute; thể giảm hơn 2,75kg so với những người kh&aacute;c. V&igrave; vậy, h&atilde;y tập l&agrave;m quen với việc ghi lại chỉ số đo h&agrave;ng ng&agrave;y c&ugrave;ng với thực đơn v&agrave; những sinh hoạt h&agrave;ng ng&agrave;y như giờ đi ngủ hay thời gian tập thể dục.</p>\n\n<p data-mce-style=\"text-align: justify;\">Điều n&agrave;y sẽ gi&uacute;p bạn dựa v&agrave;o số đo mỗi ng&agrave;y m&agrave; x&eacute;t xem thực đơn v&agrave; sinh hoạt của m&igrave;nh đ&atilde; đảm bảo hay chưa để điều chỉnh cho ph&ugrave; hợp. Bạn cũng n&ecirc;n c&oacute; 1 quyển s&aacute;ch dinh dưỡng để c&acirc;n đối thực đơn của m&igrave;nh.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Tuy&ecirc;n bố mọi người bạn sẽ giảm c&acirc;n</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Đừng ngại về điều n&agrave;y. V&igrave; người nh&agrave; hay bạn b&egrave; của bạn sẽ lu&ocirc;n quan t&acirc;m v&agrave; cho bạn những lời khuy&ecirc;n hữu &iacute;ch nhất khi thấy bạn giảm c&acirc;n qu&aacute; sức hay bạn lười biếng kh&ocirc;ng chịu giảm c&acirc;n. Ch&iacute;nh khi n&oacute;i với mọi người quyết t&acirc;m giảm c&acirc;n, bạn sẽ c&oacute; tr&aacute;ch nhiệm hơn với kế hoạch n&agrave;y.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bỏ đồ ăn vặt</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">H&atilde;y dọn sạch đồ ăn vặt nhiều chất b&eacute;o, thay v&agrave;o đ&oacute; l&agrave; c&aacute;c m&oacute;n ăn c&oacute; lợi cho việc giảm c&acirc;n để khi buồn miệng bạn vẫn c&oacute; thể nh&acirc;m nhi. Những m&oacute;n ăn vặt tốt cho giảm c&acirc;n như chuối, t&aacute;o, bưởi, cam, sữa chua hoặc sữa tươi kh&ocirc;ng đường, tr&agrave; xanh, nước lọc,&hellip;</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Kh&ocirc;ng h&agrave; khắc với bản th&acirc;n</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Đừng buộc cơ thể phải nhịn ăn, ki&ecirc;ng khem đủ thứ để rồi cơ thể suy nhược, hoặc kh&ocirc;ng cưỡng lại được đồ ăn ngon sau khi giảm c&acirc;n th&agrave;nh c&ocirc;ng lại ăn uống qu&aacute; nhiều dẫn đến tăng c&acirc;n kh&ocirc;ng kiểm so&aacute;t.</p>\n\n<p data-mce-style=\"text-align: justify;\">Trong tuần, bạn c&oacute; thể d&agrave;nh ri&ecirc;ng một ng&agrave;y để ăn m&oacute;n m&agrave; bạn y&ecirc;u th&iacute;ch, tuy nhi&ecirc;n n&ecirc;n nhớ giảm số lượng so với l&uacute;c chưa bắt đầu giảm c&acirc;n.</p>\n\n<p style=\"text-align: center;\"><img alt=\"lam sao de giam can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-sao-de-giam-can-1-e1471624732466.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-sao-de-giam-can-1-e1471624732466.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Tr&ecirc;n đ&acirc;y chỉ l&agrave; những b&iacute; quyết để bạn quyết t&acirc;m thực hiện đến c&ugrave;ng kế hoạch giảm c&acirc;n. Bạn vẫn phải vạch ra cho m&igrave;nh một chế độ ăn uống hợp l&yacute; với c&aacute;c m&oacute;n &iacute;t b&eacute;o, nhiều xơ v&agrave; protein, tập luyện thể dục thể thao v&agrave; chế độ sinh hoạt l&agrave;nh mạnh. N&ecirc;n nhớ &aacute;p dụng nhẹ nh&agrave;ng nhưng đều đặn, đừng &eacute;p bản th&acirc;n qu&aacute; dẫn đến n&atilde;n ch&iacute; v&agrave; sức khỏe suy kiệt bạn nh&eacute;.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'lam-sao-de-giam-can-hieu-qua-ma-khong-bo-cuoc-nua-chung', '/Upload/News/b0f59a2f-5014-443b-8e8a-5616f93d833b/lam-sao-de-giam-can-1-e1471624732466.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Làm sao để giảm cân hiệu quả mà không bỏ cuộc nửa chừng?', 'Làm sao để giảm cân hiệu quả mà không bỏ cuộc nửa chừng? Đây là câu hỏi mà rất nhiều chị em phụ nữ luôn quan tâm tìm kiếm hiện nay.', 'làm sao để giảm cân', '44', 'PUBLISHED', '', 'old'),
(56, '3', 'Làm thế nào để giảm mỡ bụng tại nhà trong 2 tuần?', '<p>L&agrave;m thế n&agrave;o để giảm mỡ bụng&nbsp;một c&aacute;ch hiệu quả m&agrave; c&oacute;&nbsp;thể&nbsp;thực hiện tại nh&agrave; v&agrave; c&oacute; kết quả chỉ trong v&ograve;ng 2 tuần? Đ&acirc;y l&agrave; c&acirc;u hỏi của rất nhiều người quan t&acirc;m khi đang trong qu&aacute; tr&igrave;nh giảm b&eacute;o cho cơ thể.</p>\n', '<p data-mce-style=\"text-align: justify;\">L&agrave;m thế n&agrave;o để giảm mỡ bụng&nbsp;một c&aacute;ch hiệu quả m&agrave; c&oacute;&nbsp;thể&nbsp;thực hiện tại nh&agrave; v&agrave; c&oacute; kết quả chỉ trong v&ograve;ng 2 tuần? Đ&acirc;y l&agrave; c&acirc;u hỏi của rất nhiều người quan t&acirc;m khi đang trong qu&aacute; tr&igrave;nh giảm b&eacute;o cho cơ thể. V&agrave; nếu đ&atilde; thực hiện chế độ ăn ki&ecirc;n nghi&ecirc;m t&uacute;c, lowcarb, vận động cơ thể thường xuy&ecirc;n m&agrave; bỡ bụng vẫn cứng đầu kh&ocirc;ng chịu tan th&igrave; bạn h&atilde;y &aacute;p dụng ngay c&aacute;ch n&agrave;y nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\">CHUẨN BỊ NGUY&Ecirc;N LIỆU</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams;&nbsp;1 th&igrave;a muối epsom</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; 4 th&igrave;a bột quế</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; 2 th&igrave;a dầu oliu</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Băng, gạc</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; M&agrave;ng bọc thực phẩm</p>\n	</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"lam the nao de giam mo bung\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-the-nao-de-giam-mo-bung.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-the-nao-de-giam-mo-bung.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&Aacute;CH THỰC HIỆN</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams;&nbsp;Cho bột quế, muối, dầu &ocirc; liu v&agrave;o cốc rồi khuấy đều để kh&ocirc;ng bị v&oacute;n cục.</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Đổ nước&nbsp;s&ocirc;i v&agrave;o cốc hỗn hợp&nbsp;sau đ&oacute; cho băng gạc v&agrave;o hỗn hợp v&agrave;&nbsp;khuấy đều.</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Lấy băng ra, quấn quanh người thật chắc để che phần mỡ bụng đi hoặc c&oacute; thể d&ugrave;ng nilong quấn thật chặt lại</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Đợi đủ 1 tiếng th&igrave; th&aacute;o bỏ m&agrave;ng bọc v&agrave; gạc.</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; Lấy khăn lau khắp người v&agrave; thoa kem dưỡng ẩm cho da.</p>\n	</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"lam the nao de giam mo bung 2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-the-nao-de-giam-mo-bung-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-the-nao-de-giam-mo-bung-2.jpg\" style=\"width: 500px; height: 350px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Việc quấn m&agrave;ng bọc thực phẩm với hỗn hợp muối, bột quế v&agrave; dầu oliu c&oacute; t&aacute;c dụng đ&aacute;nh tan lượng dư thừa đồng thời tẩy tế b&agrave;o chết, gi&uacute;p da mịn m&agrave;ng v&agrave; săn chắc hơn. Ch&iacute;nh v&igrave; thế, đ&acirc;y l&agrave; c&aacute;ch v&ocirc; c&ugrave;ng hiệu quả để giảm mỡ bụng. Bạn h&atilde;y &aacute;p dụng c&ocirc;ng thức n&agrave;y 2 lần/tuần v&agrave; cảm nhận r&otilde; rệt kết quả chỉ sau 2 tuần.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'lam-the-nao-de-giam-mo-bung-tai-nha-trong-2-tuan', '/Upload/News/1b1ab8e9-133f-45ca-ba20-67167bb0b639/lam-the-nao-de-giam-mo-bung.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Làm thế nào để giảm mỡ bụng tại nhà trong 2 tuần', 'Làm thế nào để giảm mỡ bụng một cách hiệu quả mà có thể thực hiện tại nhà và có kết quả chỉ trong vòng 2 tuần? Cùng nhau tìm hiểu các cách sau đây nha.', 'làm thế nào để giảm mỡ bụng', '44', 'PUBLISHED', '', 'old'),
(57, '3', 'Thực đơn giảm cân hằng ngày hiệu quả với bí đao', '<p>B&iacute; đao rất tốt cho sức khỏe v&agrave; c&oacute; &iacute;ch trong việc giảm c&acirc;n, ch&uacute;ng dễ chế biến th&agrave;nh nhiều m&oacute;n ăn kh&aacute;c nhau, ph&ugrave; hợp cho&nbsp;thực đơn giảm c&acirc;n hằng ng&agrave;y&nbsp;của bạn.</p>\n', '<p data-mce-style=\"text-align: justify;\">B&iacute; đao rất tốt cho sức khỏe v&agrave; c&oacute; &iacute;ch trong việc giảm c&acirc;n, ch&uacute;ng dễ chế biến th&agrave;nh nhiều m&oacute;n ăn kh&aacute;c nhau, ph&ugrave; hợp cho&nbsp;thực đơn giảm c&acirc;n hằng ng&agrave;y&nbsp;của bạn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Trong b&iacute; c&oacute; chứa nhiều chất dinh dưỡng như vitamin A, B, C, E, kho&aacute;ng chất,&nbsp;canxi sắt, phốt pho v&agrave; nguồn chất xơ kh&aacute; phong ph&uacute;... c&oacute;&nbsp;c&ocirc;ng dụng thanh nhiệt, giải nhiệt cơ thể,&nbsp;l&agrave;m tan đờm trong cổ họng, hết ph&ugrave;, lợi tiểu, hết kh&aacute;t...</p>\n\n<p data-mce-style=\"text-align: justify;\">Ngo&agrave;i ra,&nbsp;b&iacute; đao c&oacute; chứa hợp chất h&oacute;a học hyterin-caperin v&agrave; kh&ocirc;ng chứa chất b&eacute;o n&ecirc;n ngăn ngừa qu&aacute; tr&igrave;nh chuyển h&oacute;a v&agrave; t&iacute;ch tụ mỡ, gi&uacute;p giảm c&acirc;n hiệu quả v&agrave; an to&agrave;n.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;c m&oacute;n ăn từ b&iacute; đao sau đ&acirc;y vừa ngon, bổ dưỡng vừa tốt cho sức khỏe v&agrave; thực hiện nhanh ch&oacute;ng n&ecirc;n đưa v&agrave;o thực đơn giảm c&acirc;n hằng ng&agrave;y&nbsp;cho chị em ch&uacute;ng ta.</p>\n\n<p data-mce-style=\"text-align: justify;\">NƯỚC &Eacute;P B&Iacute; ĐAO</p>\n\n<p data-mce-style=\"text-align: justify;\">B&iacute; đao bỏ vỏ v&agrave; say nhuyễn với nước, cho th&ecirc;m một &iacute;t đường v&agrave; muối v&agrave;o v&agrave; d&ugrave;ng uống mỗi ng&agrave;y. Đ&acirc;y sẽ l&agrave; c&aacute;ch thanh lọc cơ thể v&agrave; giảm c&acirc;n v&ocirc; c&ugrave;ng hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"lam-nuoc-ep-bi-dao-giam-can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-nuoc-ep-bi-dao-giam-can.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-nuoc-ep-bi-dao-giam-can.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">B&Iacute; ĐAO LUỘC</p>\n\n<p data-mce-style=\"text-align: justify;\">B&iacute; gọt vỏ v&agrave; luộc, d&ugrave;ng chung với cơ trong c&aacute;c bữa ăn. Khi ăn b&iacute; đao, bạn sẽ c&oacute; cảm gi&aacute;c no bụng, kh&ocirc;ng muốn ăn nhiều loại thức ăn kh&aacute;c nữa.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"dep-da-va-giam-can-bang-bi-dao-luoc\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/dep-da-va-giam-can-bang-bi-dao-luoc.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/dep-da-va-giam-can-bang-bi-dao-luoc.jpg\" style=\"width: 500px; height: 335px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">CANH B&Iacute; ĐAO VỚI VỎ BƯỞI</p>\n\n<p data-mce-style=\"text-align: justify;\">Vỏ bưởi rửa sạch, th&aacute;i nhỏ, rồi đun trong nồi nước kho&aacute;ng 30 ph&uacute;t, sau đ&oacute; chắt lấy phần nước vỏ bưởi, bỏ phần b&atilde;. B&iacute; đao gọt bỏ, th&aacute;i th&agrave;nh từng miếng nhỏ rồi nấu th&agrave;nh canh c&ugrave;ng với vỏ bưởi. Mỗi ng&agrave;y bạn n&ecirc;n ăn m&oacute;n n&agrave;y&nbsp;2 lần trước bữa ăn.</p>\n\n<p data-mce-style=\"text-align: justify;\">B&Iacute; ĐAO HẤP T&Ocirc;M</p>\n\n<p data-mce-style=\"text-align: justify;\">D&ugrave;ng t&ocirc;m đất l&agrave;m sạch hấp với b&iacute; đao th&aacute;i miếng vừa ăn c&ugrave;ng &iacute;t dầu h&agrave;o v&agrave; dầu vừng.&nbsp;M&oacute;n ăn n&agrave;y c&oacute; thể ăn với cơm v&agrave;o bữa trưa hoặc tối.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-lam-mon-tom-dat-hap-bi-dao-ngot-lanh-dua-com\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/cach-lam-mon-tom-dat-hap-bi-dao-ngot-lanh-dua-com.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-mon-tom-dat-hap-bi-dao-ngot-lanh-dua-com.jpg\" style=\"width: 500px; height: 313px;\" /></p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'thuc-don-giam-can-hang-ngay-hieu-qua-voi-bi-dao', '/Upload/News/bf21366d-02df-40ae-980a-48c95601cc61/lam-nuoc-ep-bi-dao-giam-can.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Thực đơn giảm cân hằng ngày hiệu quả với bí đao', 'Bí đao luộc, nước ép bí đao, canh bí đao với vỏ bưởi, bí đao hấp tôm là những món ăn dễ thực hiện và nên bổ sung vào thực đơn giảm cân hằng ngày của bạn.', 'thực đơn giảm cân hằng ngày', '44', 'PUBLISHED', '', 'old'),
(58, '3', 'Cách trị mụn bằng mật ong từ thiên nhiên đơn giản', '<p>Việc t&igrave;m kiếm c&aacute;c c&aacute;ch trị mụn từ thi&ecirc;n nhi&ecirc;n ch&iacute;nh l&agrave; một trong những ti&ecirc;u ch&iacute; đầu ti&ecirc;n m&agrave; mọi người thường hay hướng tới khi l&agrave;n da gặp c&aacute;c vấn đề về mụn.</p>\n', '<p style=\"text-align: justify;\">Việc t&igrave;m kiếm c&aacute;c&nbsp;c&aacute;ch trị mụn từ thi&ecirc;n nhi&ecirc;n&nbsp;ch&iacute;nh l&agrave; một trong những ti&ecirc;u ch&iacute; đầu ti&ecirc;n m&agrave; mọi người thường hay hướng tới khi l&agrave;n da gặp c&aacute;c vấn đề về mụn. Thế n&ecirc;n, để gi&uacute;p c&aacute;c bạn điều trị mụn một c&aacute;ch đơn giản th&igrave; h&ocirc;m nay&nbsp;Svan Clinic &amp; Spa&nbsp;sẽ gi&uacute;p c&aacute;c bạn nhờ v&agrave;o b&iacute; quyết sử dụng&nbsp;<em><strong>mật ong trị mụn</strong></em>&nbsp;rất hiệu quả ở dưới:</p>\n\n<p style=\"text-align: justify;\"><strong>Nha đam trị mụn</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/cach-tri-mun-bang-mat-ong-tu-thien-nhien-1.jpg\" style=\"width: 500px; height: 334px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Phương ph&aacute;p n&agrave;y rất hiệu quả v&agrave; gi&uacute;p cho l&agrave;n da được mịn m&agrave;ng hơn nhờ v&agrave;o lượng dưỡng chất của nha đam kết hợp với mật ong. V&agrave; để đạt được hiệu quả tuyệt vời trong việc điều trị mụn hiệu quả th&igrave; c&aacute;c bạn h&atilde;y chuẩn bị nước nha đam đ&atilde; được đun s&ocirc;i h&ograve;a với mật ong nguy&ecirc;n chất để l&agrave;m mặt nạ đắp l&ecirc;n v&ugrave;ng bị mụn hoặc c&oacute; thể uống trực tiếp để l&agrave;m đẹp da. Thực hiện li&ecirc;n tục trong 1 th&aacute;ng sẽ cải thiện t&igrave;nh trạng xấu da v&agrave; mụn&nbsp;một c&aacute;ch bất ngờ.</p>\n\n<p style=\"text-align: justify;\"><strong>Rau nhiếp c&aacute; trị mụn</strong></p>\n\n<p style=\"text-align: justify;\">Sự kết hợp giữa mật ong v&agrave; rau nhiếp c&aacute; ch&iacute;nh l&agrave; một phương ph&aacute;p rất tuyệt vời m&agrave; kh&ocirc;ng phải ai cũng biết để gi&uacute;p l&agrave;n da của m&igrave;nh loại bỏ c&aacute;c loại mụn cứng đầu. Đầu ti&ecirc;n th&igrave; ch&uacute;ng ta cần xay nhuyễn rau nhiếp c&aacute; để lấy nước v&agrave; trộn c&ugrave;ng với mật ong để tạo th&agrave;nh một loại hỗn hợp tuyệt vời gi&uacute;p đ&aacute;nh bay c&aacute;c loại mụn trong tuần.</p>\n\n<p style=\"text-align: justify;\"><strong>Trị mụn bằng tỏi</strong></p>\n\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-bang-mat-ong-tu-thien-nhien-2.jpg\" style=\"width: 500px; height: 389px;\" /></strong></p>\n\n<p style=\"text-align: justify;\">Phương ph&aacute;p n&agrave;y đ&ograve;i hỏi phải cần kh&aacute; nhiều thời gian để chuẩn bị. Đầu ti&ecirc;n c&aacute;c bạn h&atilde;y trộn tỏi v&agrave; mật ong lại rồi để v&agrave;o một nơi kh&ocirc;ng c&oacute; &aacute;nh s&aacute;ng trong 2 th&aacute;ng. Sau đ&oacute;, ch&uacute;ng ta c&oacute; thể lấy hỗn hợp đ&oacute; thoa l&ecirc;n những v&ugrave;ng c&oacute; mụn cần điều trị trong 30 ph&uacute;t v&agrave; rửa sạch lại với nước ấm để lấy lại một l&agrave;n da mịn m&agrave;ng kh&ocirc;ng vết mụn.</p>\n', 'cach-tri-mun-bang-mat-ong-tu-thien-nhien-don-gian', '/Upload/News/b3137919-4db8-41c8-b8e4-c2042a9b5b85/nha-dam-1.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách trị mụn bằng mật ong từ thiên nhiên đơn giản', 'Việc tìm kiếm các cách trị mụn từ thiên nhiên chính là một trong những tiêu chí đầu tiên mà mọi người thường hay hướng tới khi làn da gặp các vấn đề về mụn.', 'mật ong trị mụn', '44', 'PUBLISHED', '', 'old'),
(59, '3', 'Cách làm mặt nạ trị nám da đơn giản ngay tại nhà', '<p>Một trong những vấn đề thường gặp về da mặt của ph&aacute;i đẹp đ&oacute; l&agrave; vết n&aacute;m. Những vết n&aacute;m n&agrave;y sẽ l&agrave;m mất đi vẻ tự tin vốn c&oacute; của c&aacute;c bạn.</p>\n', '<p data-mce-style=\"text-align: justify;\">Một trong những vấn đề thường gặp về da mặt của ph&aacute;i đẹp đ&oacute; l&agrave; vết n&aacute;m. Những vết n&aacute;m n&agrave;y sẽ l&agrave;m mất đi vẻ tự tin vốn c&oacute; của c&aacute;c bạn. Nếu ch&uacute;ng kh&ocirc;ng được điều trị đ&uacute;ng c&aacute;ch th&igrave; l&agrave;n da sẽ trở n&ecirc;n sần s&ugrave;i v&agrave; sạm đi. Biết được điều đ&oacute;, Svan Clinic &amp; Spa sẽ giới thiệu một số c&aacute;ch l&agrave;m&nbsp;mặt nạ trị n&aacute;m&nbsp;da đơn giản ho&agrave;n to&agrave;n từ thi&ecirc;n nhi&ecirc;n. Mong muốn rằng c&aacute;c bạn sẽ sở hữu cho m&igrave;nh l&agrave;n da tươi s&aacute;ng v&agrave; mịn m&agrave;ng, khỏe mạnh.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>1. Mặt nạ c&agrave; chua:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu:</p>\n\n<ul>\n	<li>\n	<p>&diams; Bột yến mạch</p>\n	</li>\n	<li>\n	<p>&diams; 1 quả c&agrave; chua ch&iacute;n</p>\n	</li>\n	<li>\n	<p>&diams; Mật ong, sữa chua, nước cốt chanh</p>\n	</li>\n</ul>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><img alt=\"bot_yen_mach_nuoc_ca_chua\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/bot_yen_mach_nuoc_ca_chua.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/bot_yen_mach_nuoc_ca_chua.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p>&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m:&nbsp;Th&aacute;i c&agrave; chua 3 l&aacute;t bỏ cả vỏ v&agrave; hạt sau đ&oacute; say nhuyễn. Trộn bột c&agrave; chua, bột yến mạch sau đ&oacute; th&ecirc;m 1 th&igrave;a sữa chua, v&agrave;i giọt mật ong v&agrave; nước cốt chanh rồi trộn đều. Thoa hỗn hợp l&ecirc;n v&ugrave;ng da bị n&aacute;m v&agrave; nằm thư gi&atilde;n khoảng 20-30 ph&uacute;t th&igrave; rửa sạch bằng nước ấm.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>2. Mặt nạ từ chanh:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu:</p>\n\n<ul>\n	<li>\n	<p>&diams; Nước cốt của &frac12; quả chanh</p>\n	</li>\n	<li>\n	<p>&diams; 1 th&igrave;a đường n&acirc;u</p>\n	</li>\n	<li>\n	<p>&diams; Dầu Olive</p>\n	</li>\n</ul>\n\n<p>C&aacute;ch l&agrave;m:&nbsp;Đầu ti&ecirc;n trộn đều nước cốt chanh v&agrave; đường n&acirc;u sau đ&oacute; th&ecirc;m v&agrave;i giọt dầu Olive. Thoa hỗn hợp l&ecirc;n v&ugrave;ng da n&aacute;m v&agrave; nằm thư gi&atilde;n từ 15-20 ph&uacute;t rồi rửa sạch bằng nước ấm. Ki&ecirc;n tr&igrave; thực hiện 2 lần 1 tuần th&igrave; vết n&aacute;m sẽ mờ dần đi tr&ocirc;ng thấy.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>3. Mặt nạ dưa chuột:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu:</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; 1 quả dưa chuột</p>\n	</li>\n	<li data-mce-style=\"text-align: justify;\">\n	<p>&diams; 50ml sữa tươi</p>\n	</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"mat-na-dua-leo-sua-chua\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/mat-na-dua-leo-sua-chua-e1471796581724.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/mat-na-dua-leo-sua-chua-e1471796581724.jpg\" style=\"width: 500px; height: 334px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m:&nbsp;Th&aacute;i dưa chuột th&agrave;nh c&aacute;c miếng mỏng, bỏ v&agrave;o trong 1 chiếc ch&eacute;n. Sau đ&oacute; đổ sữa tươi v&agrave;o ch&eacute;n v&agrave; ng&acirc;m khoảng 5-10 ph&uacute;t. Vớt ra v&agrave; đắp l&ecirc;n v&ugrave;ng da mặt bị n&aacute;m trong khoảng từ 15-20 ph&uacute;t.</p>\n', 'cach-lam-mat-na-tri-nam-da-don-gian-ngay-tai-nha', '/Upload/News/eb65235c-4016-4ccf-90fc-557d973d307b/bot_yen_mach_nuoc_ca_chua.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách làm mặt nạ trị nám da đơn giản ngay tại nhà', 'Cách làm mặt nạ trị nám da đơn giản bằng cách sử dụng những nguyên liệu thông dụng hoàn toàn từ tự nhiên giúp mang lại cho bạn làn da trắng sáng', 'mặt nạ trị nám', '44', 'PUBLISHED', '', 'old'),
(60, '3', 'Cách giảm béo mặt đơn giản, dễ thực hiện bạn nên sử dụng ngay', '<p>B&eacute;o mặt hầu như xuất hiện với những người bị thừa c&acirc;n, n&ecirc;n c&aacute;ch giảm b&eacute;o mặt ch&iacute;nh l&agrave; thực hiện giảm c&acirc;n.</p>\n', '<p data-mce-style=\"text-align: justify;\">B&eacute;o mặt hầu như xuất hiện với những người bị thừa c&acirc;n, n&ecirc;n c&aacute;ch giảm b&eacute;o mặt ch&iacute;nh l&agrave; thực hiện giảm c&acirc;n. Bạn cần&nbsp;giảm c&acirc;n nặng về mức vừa phải bằng chế độ dinh dưỡng, tập luyện c&ugrave;ng một số b&agrave;i tập tr&ecirc;n khu&ocirc;n mặt, kể cả &aacute;p dụng những c&aacute;ch trang điểm để tr&ocirc;ng khu&ocirc;n mặt thon gọn, khả &aacute;i. Ngo&agrave;i ra, cũng c&oacute; nhiều&nbsp;c&aacute;ch giảm b&eacute;o mặt&nbsp;đơn giản, dễ thực hiện kh&aacute;c bạn c&oacute; thể tham khảo như giảm b&eacute;o mặt bằng mật ong, cần t&acirc;y...</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Mật ong:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu:&nbsp;mật ong, chanh</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch thực hiện:</p>\n\n<p data-mce-style=\"text-align: justify;\">&diams; Vắt lấy 5ml nước cốt chanh h&ograve;a với 30ml mật ong, sau đ&oacute; l&agrave;m n&oacute;ng hỗn hợp bằng l&ograve; vi s&oacute;ng trong 1 ph&uacute;t.</p>\n\n<p data-mce-style=\"text-align: justify;\">&diams; Để bớt n&oacute;ng v&agrave; d&ugrave;ng hỗn hợp mật ong, nước cốt chanh đ&oacute; b&ocirc;i l&ecirc;n mặt, thực hiện c&ugrave;ng với việc masage nhẹ nh&agrave;ng. Sau đ&oacute;, d&ugrave;ng giấy kiến phủ l&ecirc;n, đợi khoảng 20 ph&uacute;t rồi rửa mặt bằng nước ấm. Bạn n&ecirc;n thực hiện phương ph&aacute;p n&agrave;y 1 lần/tuần trong thời gian d&agrave;i để c&oacute; kết quả tốt.</p>\n\n<p style=\"text-align: center;\"><img alt=\"lam-dep-da-voi-mat-ong-rung-nguyen-chat\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-dep-da-voi-mat-ong-rung-nguyen-chat.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-dep-da-voi-mat-ong-rung-nguyen-chat.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Cần t&acirc;y</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu:&nbsp;cần t&acirc;y, c&agrave; rốt, cải xoong</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch thực hiện:&nbsp;rửa sạch c&aacute;c loại nguy&ecirc;n liệu tr&ecirc;n rồi say th&agrave;nh nước &eacute;p để uống. N&ecirc;n uống trước bữa ăn khoảng 20 ph&uacute;t, mỗi ng&agrave;y uống 2 cốc v&agrave;o buổi s&aacute;ng v&agrave; trưa để c&oacute; kết quả giảm b&eacute;o mặt tốt nhất.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"giam beo măt\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/giam-beo-măt.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/giam-beo-m%C4%83t.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n', 'cach-giam-beo-mat-don-gian-de-thuc-hien-ban-nen-su-dung-ngay', '/Upload/News/6336fefc-6c3b-4e5d-9382-f46128053d9b/lam-dep-da-voi-mat-ong-rung-nguyen-chat.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách giảm béo mặt đơn giản, dễ thực hiện bạn nên sử dụng ngay', 'Cách giảm béo mặt đơn giản, dễ thực hiện bằng mật ong, cần tây. Đặc biệt hiệu quả khi áp dụng phương pháp massage thư giãn toàn thân tại Svan Clinic & Spa.', 'cách giảm béo mặt', '44', 'PUBLISHED', '', 'old'),
(61, '3', 'Chế độ ăn kiêng giảm cân khoa học nhất được nhiều người áp dụng', '<p><span style=\"text-align: justify;\">Chế độ ăn ki&ecirc;ng giảm c&acirc;n</span><span style=\"text-align: justify;\">&nbsp;khoa học nhất được &aacute;p dụng phổ biến ch&iacute;nh l&agrave;&nbsp;low-carb. Đ&acirc;y&nbsp;l&agrave; chế độ ăn cắt giảm chất bột đường, th&iacute;ch hợp cho những người muốn giảm b&eacute;o.</span></p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Chế độ ăn ki&ecirc;ng giảm c&acirc;n&nbsp;khoa học nhất được &aacute;p dụng phổ biến ch&iacute;nh l&agrave;&nbsp;low-carb. Đ&acirc;y&nbsp;l&agrave; chế độ ăn cắt giảm chất bột đường, th&iacute;ch hợp cho những người muốn giảm b&eacute;o.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Phương ph&aacute;p giảm c&acirc;n dựa tr&ecirc;n chế độ n&agrave;y được &aacute;p dụng phổ biến tr&ecirc;n to&agrave;n thế giới v&agrave; đạt hiệu quả cao. N&oacute; được c&aacute;c chuy&ecirc;n gia &aacute;p dụng trong việc chữa bệnh b&eacute;o ph&igrave; v&agrave; tiểu đường.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"an kien lowcarb\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/an-kien-lowcarb.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/an-kien-lowcarb.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<h5 data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">NGUY&Ecirc;N L&Yacute; CHẾ ĐỘ ĂN KI&Ecirc;NG LOW-CARB</h5>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Thừa c&acirc;n được h&igrave;nh th&agrave;nh chủ yếu l&agrave; nạp nhiều thức ăn chứa carbohydrate v&agrave;o cơ thể sẽ tạo n&ecirc;n h&agrave;m lượng cao đường&nbsp;glucose trong m&aacute;u. Dẫn dến đường huyết tăng cao, l&uacute;c n&agrave;y cơ thể phải tiết ra insulin để đường huyết trở lại mức b&igrave;nh thường. V&agrave; ch&iacute;nh l&uacute;c n&agrave;y, lượng đường Glucose trong m&aacute;u sẽ h&igrave;nh th&agrave;nh mỡ thừa.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Nếu cắt giảm&nbsp;Carbohydrate th&igrave; cơ thể sẽ quay sang đốt ch&aacute;y mỡ để lấy năng lượng cung cấp cho c&aacute;c hoạt động hằng ng&agrave;y. Từ đ&oacute;, mỡ sẽ kh&ocirc;ng c&oacute; cơ hội t&iacute;ch tụ để g&acirc;y n&ecirc;n t&igrave;nh trạng thừa c&acirc;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Nguy&ecirc;n l&yacute; của chế độ ăn ki&ecirc;ng n&agrave;y&nbsp;bắt buộc người&nbsp;ăn ki&ecirc;ng phải cắt bỏ ho&agrave;n to&agrave;n những loại thức ăn chứa nhiều carbohydrate.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Những m&oacute;n ăn phải ki&ecirc;ng tuyệt đối trong chế độ ăn low-carb:</p>\n\n<ul data-mce-style=\"text-align: justify;\">\n	<li style=\"text-align: justify;\">Ngũ cốc v&agrave; c&aacute;c loại hạt</li>\n	<li style=\"text-align: justify;\">Đường, sữa, b&aacute;nh ga t&ocirc;, kẹo, nước ngọt</li>\n	<li style=\"text-align: justify;\">Tất cả c&aacute;c loại hoa quả</li>\n	<li style=\"text-align: justify;\">Tất cả c&aacute;c loại thức ăn nhanh</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"an kien lowcarb 2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/an-kien-lowcarb-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/an-kien-lowcarb-2.jpg\" style=\"width: 500px; height: 417px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Những m&oacute;n ăn c&oacute; thể ăn thoải m&aacute;i trong chế độ ăn low-carb:</p>\n\n<ul data-mce-style=\"text-align: justify;\">\n	<li style=\"text-align: justify;\">Tất cả c&aacute;c loại thịt v&agrave; mỡ, c&aacute;c loại&nbsp;trứng</li>\n	<li style=\"text-align: justify;\">Dầu thực vật hoặc dầu l&agrave;m từ mỡ động vật</li>\n	<li style=\"text-align: justify;\">C&aacute;c loại hải sản</li>\n	<li style=\"text-align: justify;\">C&aacute;c loại rau xanh v&agrave; củ c&oacute; nhiều chất xơ, kh&ocirc;ng chứa tinh bột v&agrave; đường</li>\n	<li style=\"text-align: justify;\">C&aacute;c loại gia vị</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"giam mo bung voi che do an kien\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/giam-mo-bung-voi-che-do-an-kien-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/giam-mo-bung-voi-che-do-an-kien-1.jpg\" style=\"width: 500px; height: 330px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Một số điều cần lưu &yacute;:</p>\n\n<ul data-mce-style=\"text-align: justify;\">\n	<li style=\"text-align: justify;\">N&ecirc;n ăn thịt tươi chứ kh&ocirc;ng sử dụng thịt đ&oacute;ng hộp.</li>\n	<li style=\"text-align: justify;\">Ch&uacute; &yacute; đến th&agrave;nh phần chế biến khi sử dụng c&aacute;c loại sốt c&agrave; chua, mayonnaise&hellip; v&igrave; ch&uacute;ng c&oacute; thể chứa đường.</li>\n	<li style=\"text-align: justify;\">C&oacute; thể ăn bất cứ l&uacute;c n&agrave;o thấy đ&oacute;i.</li>\n	<li style=\"text-align: justify;\">Phải kết hợp với tập thể dục th&igrave; mới c&oacute; thể giảm c&acirc;n hiệu quả.</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Low-carb thực chất l&agrave; một chế độ ăn ki&ecirc;ng kh&ocirc;ng qu&aacute; khắt khe nhưng bắt buộc thực hiện trong khoảng thời gian d&agrave;i v&agrave; duy tr&igrave; thường xuy&ecirc;n. N&oacute; được xem l&agrave; chế độ ăn ki&ecirc;ng ho&agrave;n hảo, ph&ugrave; hợp cho &aacute;p dụng trong suốt đời để v&oacute;c d&aacute;ng c&acirc;n đối lu&ocirc;n lu&ocirc;n đồng h&agrave;nh c&ugrave;ng bạn v&agrave; c&oacute; được sức khỏe tốt.</p>\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'che-do-an-kieng-giam-can-khoa-hoc-nhat-duoc-nhieu-nguoi-ap-dung', '/Upload/News/93e05955-4504-450a-a0f9-79982ce550ad/an-kien-lowcarb.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Chế độ ăn kiêng giảm cân khoa học nhất được nhiều người áp dụng', 'Chế độ ăn kiêng giảm cân khoa học được áp dụng nhiều chính là low-carb. Đây là chế độ ăn cắt giảm chất bột đường, thích hợp cho những người muốn giảm béo.', 'chế độ ăn kiêng giảm cân', '44', 'PUBLISHED', '', 'old'),
(62, '3', 'Uống gì để giảm cân an toàn và nhanh chóng?', '<p>Uống g&igrave; để giảm c&acirc;n&nbsp;vừa gi&uacute;p thải độc cơ thể m&agrave; chỉ số c&acirc;n nặng cũng giảm đi nhanh ch&oacute;ng?Những loại thức uống dễ l&agrave; dưới đ&acirc;y sẽ gi&uacute;p bạn tăng cường sức đề kh&aacute;ng v&agrave; cơ thể thon gọn.</p>\n', '<p data-mce-style=\"text-align: justify;\">Uống g&igrave; để giảm c&acirc;n&nbsp;vừa gi&uacute;p thải độc cơ thể m&agrave; chỉ số c&acirc;n nặng cũng giảm đi nhanh ch&oacute;ng?Những loại thức uống dễ l&agrave; dưới đ&acirc;y sẽ gi&uacute;p bạn tăng cường sức đề kh&aacute;ng v&agrave; cơ thể thon gọn.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nước lọc</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Hẳn l&agrave; ch&uacute;ng ta lu&ocirc;n nghe n&oacute;i lu&ocirc;n phải uống đủ nước để thanh lọc cơ thể, để da dẻ hồng h&agrave;o, uống đủ nước c&oacute; lợi cho sức khỏe,&hellip;nhưng h&igrave;nh như rất nhiều người bỏ qua nước lọc cho c&acirc;u hỏi uống g&igrave; để giảm c&acirc;n. Ngo&agrave;i việc uống nước thường xuyen gi&uacute;p xua tan cơn đ&oacute;i, giảm cảm gi&aacute;c th&egrave;m ăn, nước lọc c&ograve;n th&uacute;c đẩy ti&ecirc;u h&oacute;a v&agrave; qu&aacute; tr&igrave;nh trao đổi chất, đốt ch&aacute;y nhiều calo hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Tr&agrave; xanh</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Kh&ocirc;ng chỉ giảm c&acirc;n, tr&agrave; xanh c&ograve;n c&oacute; t&aacute;c dụng ngăn ung thư, tim mạch, chống l&atilde;o h&oacute;a, chống oxy h&oacute;a,&hellip;</p>\n\n<p style=\"text-align: center;\"><img alt=\"uong gi de giam can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/uong-gi-de-giam-can-1-e1471759941746.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/uong-gi-de-giam-can-1-e1471759941746.jpg\" style=\"width: 500px; height: 471px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nước chanh</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nước chanh vẫn được xem như một loại nước uống giảm c&acirc;n cấp tốc, tốt cho da, cải thiện v&agrave; kh&ocirc;i phục l&agrave;n da hư tổn, cung cấp vitamin bổ sung cho cơ thể.</p>\n\n<p data-mce-style=\"text-align: justify;\">Lưu &yacute; kh&ocirc;ng uống trực tiếp nước cốt chanh m&agrave; phải pha lo&atilde;ng với nước lọc v&agrave; kh&ocirc;ng uống l&uacute;c đang đ&oacute;i sẽ rất hại cho dạ d&agrave;y v&igrave; trong chanh c&oacute; nhiều axit.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Cafe đen</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Cafe c&oacute; chứa caffeine g&acirc;y mất ngủ, g&acirc;y hại cho sức khỏe nhưng thật ra nếu bạn uống mỗi ng&agrave;y chỉ 1 ly, lại c&oacute; t&aacute;c dụng ngừa ung thư, chống oxy h&oacute;a v&agrave; chất caffein l&agrave; chất x&uacute;c t&aacute;c ho&agrave;n hảo th&uacute;c đẩy qu&aacute; tr&igrave;nh trao đổi chất v&agrave; năng lượng đốt ch&aacute;y calo.</p>\n\n<p data-mce-style=\"text-align: justify;\">Tuy nhi&ecirc;n, h&atilde;y đừng cho th&ecirc;m đường hoặc sữa đặc v&agrave;o caf&eacute; bởi ch&uacute;ng sẽ cung cấp lượng calo kh&ocirc;ng cần thiết, kh&ocirc;ng c&oacute; t&aacute;c dụng giảm c&acirc;n nữa.</p>\n\n<p style=\"text-align: center;\"><img alt=\"uong gi de giam can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/uong-gi-de-giam-can-2-e1471759986169.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/uong-gi-de-giam-can-2-e1471759986169.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nước &eacute;p bưởi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nước &eacute;p bưởi lu&ocirc;n ph&aacute;t huy tối đa c&ocirc;ng dụng giảm mỡ nhanh ch&oacute;ng của n&oacute;. Lượng vitamin C dồi d&agrave;o v&agrave; nhiều kho&aacute;ng chất kh&aacute;c c&oacute; trong bưởi sẽ gi&uacute;p bạn sở hữu một l&agrave;n da mịn m&agrave;ng s&aacute;ng đẹp v&agrave; một th&acirc;n h&igrave;nh thon gọn, chắc khỏe.</p>\n\n<p style=\"text-align: center;\"><img alt=\"uong gi de giam can \" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/uong-gi-de-giam-can-3.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/uong-gi-de-giam-can-3.jpg\" style=\"width: 500px; height: 340px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nước &eacute;p b&iacute; đao</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Hợp chất hyterin &ndash; caperic trong b&iacute; đao c&oacute; khả năng khống chế lượng đường chuyển h&oacute;a th&agrave;nh mỡ n&ecirc;n ngăn chặn được sự t&iacute;ch lũy mỡ trong cơ thể. Ăn nhiều b&iacute; đao c&ograve;n gi&uacute;p ti&ecirc;u trừ nước thừa trong cơ thể, chống b&eacute;o ph&igrave;.</p>\n\n<p data-mce-style=\"text-align: justify;\">B&iacute; đao sống c&oacute; t&iacute;nh x&agrave; ph&ograve;ng rất cao nếu ăn hoặc &eacute;p sống sẽ c&oacute; hại cho hệ ti&ecirc;u h&oacute;a, n&ecirc;n nấu kỹ, uống nước b&iacute; đao luộc hoặc luộc b&iacute; đao rồi mới &eacute;p.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Sữa tươi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Hẳn nhiều bạn sẽ bất ngờ khi sữa cũng nằm trong danh s&aacute;ch thức uống giảm c&acirc;n. Trong sữa c&oacute; rất nhiều canxi sẽ gi&uacute;p cơ thể &ldquo;giết&rdquo; chất b&eacute;o. Bạn uống đều đặn 2-3 ly sữa mỗi ng&agrave;y sẽ giảm được &iacute;t nhất 10 kg trong 6 tuần.</p>\n\n<p data-mce-style=\"text-align: justify;\">Lưu &yacute; khi quyết định sử dụng c&aacute;c đồ uống tr&ecirc;n th&igrave; bạn n&ecirc;n đọc kỹ phương ph&aacute;p sử dụng cũng như liều lượng, tr&aacute;nh sử dụng qu&aacute; nhiều sẽ g&acirc;y t&aacute;c dụng ngược.</p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'uong-gi-de-giam-can-an-toan-va-nhanh-chong', '/Upload/News/2e89ba96-fa42-4b16-bdc5-0205567c8d31/uong-gi-de-giam-can-2-e1471759986169.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Uống gì để giảm cân an toàn và nhanh chóng?', 'Uống gì để giảm cân để giúp thải độc cơ thể? Những loại thức uống dễ là dưới đây sẽ giúp bạn tăng cường sức đề kháng và cơ thể thon gọn.', 'uống gì để giảm cân', '44', 'PUBLISHED', '', 'old'),
(63, '3', 'Trẻ hóa làn da bằng cách đắp mặt nạ trứng gà tại nhà', '<p>Đắp mặt nạ trứng g&agrave;&nbsp;l&agrave; biện ph&aacute;p l&agrave;m trắng da nhanh đồng thời l&agrave;m mờ nếp nhăn v&agrave; se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả được nhiều chị em phụ nữ tin d&ugrave;ng. Nếu &aacute;p dụng thường xuy&ecirc;n đảm bảo bạn sẽ trẻ hơn so với tuổi thật của m&igrave;nh.</p>\n', '<p data-mce-style=\"text-align: justify;\">Đắp mặt nạ trứng g&agrave;&nbsp;l&agrave; biện ph&aacute;p l&agrave;m trắng da nhanh đồng thời l&agrave;m mờ nếp nhăn v&agrave; se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả được nhiều chị em phụ nữ tin d&ugrave;ng. Nếu &aacute;p dụng thường xuy&ecirc;n đảm bảo bạn sẽ trẻ hơn so với tuổi thật của m&igrave;nh.</p>\n\n<p><strong>1. Mặt na mật ong</strong></p>\n\n<p>Loại mặt nạ n&agrave;y th&iacute;ch hợp với ai c&oacute; l&agrave;n da nhờn, lỗ ch&acirc;n l&ocirc;ng to. Đắp mặt nạ với l&ograve;ng trắng trứng g&agrave; sẽ gi&uacute;p c&aacute;c chị em hạn chế nếp nhăn, k&eacute;o d&agrave;i tuổi thanh xu&acirc;n v&agrave; se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả. V&agrave; c&aacute;ch thực hiện cũng v&ocirc; c&ugrave;ng đơn giản.</p>\n\n<p>Nguy&ecirc;n liệu:</p>\n\n<ul>\n	<li>\n	<p>&diams; 1 l&ograve;ng trắng trứng g&agrave;<br />\n	&diams; 1 th&igrave;a mật ong<br />\n	&diams; 1/2 th&igrave;a nước chanh</p>\n	</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-lam-mat-na-mat-ong-va-long-trang-trung-ga\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/cach-lam-mat-na-mat-ong-va-long-trang-trung-ga-e1471674133928.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-mat-na-mat-ong-va-long-trang-trung-ga-e1471674133928.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m:&nbsp;Trộn đều l&ograve;ng trắng trứng g&agrave; c&ugrave;ng với mật ong v&agrave; nước cốt chanh. Đ&aacute;nh b&ocirc;ng hỗn hợp sau đ&oacute; thoa đều l&ecirc;n da mặt. Thư gi&atilde;n trong v&ograve;ng 20 ph&uacute;t v&agrave; rửa sạch bằng nước ấm.</p>\n\n<p><strong>2. Mặt nạ trứng g&agrave;</strong></p>\n\n<p>Nếu da mặt của bạn đang c&oacute; dấu hiệu l&atilde;o h&oacute;a v&agrave; xuất hiện nhiều nếp nhăn th&igrave; loại mặt nạ n&agrave;y l&agrave; giải ph&aacute;p gi&uacute;p mang lại vẻ tươi trẻ cho l&agrave;n da của bạn.</p>\n\n<p style=\"text-align: center;\"><img alt=\"Mặt-Nạ-Dầu-Ô-liu-Chuối-và-Trứng-Gà\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/Mặt-Nạ-Dầu-Ô-liu-Chuối-và-Trứng-Gà-1-e1471674193928.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/M%E1%BA%B7t-N%E1%BA%A1-D%E1%BA%A7u-%C3%94-liu-Chu%E1%BB%91i-v%C3%A0-Tr%E1%BB%A9ng-G%C3%A0-1-e1471674193928.jpg\" style=\"width: 500px; height: 231px;\" /></p>\n\n<p>Nguy&ecirc;n liệu:</p>\n\n<ul>\n	<li>\n	<p>&diams; 1 l&ograve;ng đỏ trứng g&agrave;<br />\n	&diams; 1 th&igrave;a dầu &ocirc; liu<br />\n	&diams; 1 quả chuối ch&iacute;n</p>\n	</li>\n</ul>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m:<br />\nĐầu ti&ecirc;n nghiền nhuyễn chuối v&agrave; trộn đều c&ugrave;ng với dầu &ocirc; liu v&agrave; l&ograve;ng đỏ trứng g&agrave;. Thoa hỗn hợp l&ecirc;n mặt v&agrave; nằm thư gi&atilde;n khoảng 15 ph&uacute;t. Sau đ&oacute; rửa mặt thật sạch với nước ấm. Bạn cũng c&oacute; thể thoa th&ecirc;m nước hoa hồng hay dầu dừa để tăng độ ẩm cho da. H&atilde;y ki&ecirc;n tr&igrave; đắp mặt nạ trứng g&agrave; bạn sẽ gi&uacute;p cải thiện l&agrave;n da của m&igrave;nh.</p>\n', 'tre-hoa-lan-da-bang-cach-dap-mat-na-trung-ga-tai-nha', '/Upload/News/c9d3a0b3-5738-4e9d-b7b8-a963fd7211b6/cach-lam-mat-na-mat-ong-va-long-trang-trung-ga-e1471674133928.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Trẻ hóa làn da bằng cách đắp mặt nạ trứng gà tại nhà', 'Trẻ hóa làn da bằng cách đắp mặt nạ trứng gà là phương pháp đơn giản nhất giúp lấy sự tươi trẻ cho làn da của bạn. Áp dùng ngay cách làm mặt nạ trứng gà nha', 'đắp mặt nạ trứng gà', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(64, '3', 'Những chế độ giảm cân phổ biến hiện nay được ưa thích', '<p>Với những người thừa c&acirc;n, muốn sở hữu v&agrave;&nbsp;duy tr&igrave; một th&acirc;n h&igrave;nh thon gọn, bạn nhất thiết phải c&oacute;&nbsp;chế độ ăn thấp năng lượng. V&agrave; khoa học đ&atilde;&nbsp;nghi&ecirc;n cứu đưa ra những&nbsp;chế độ giảm c&acirc;n&nbsp;c&oacute; thể sử dụng với nhiều đối tượng, chị em c&oacute; thể tham khảo ngay sau đ&acirc;y.</p>\n', '<p data-mce-style=\"text-align: justify;\">Với những người thừa c&acirc;n, muốn sở hữu v&agrave;&nbsp;duy tr&igrave; một th&acirc;n h&igrave;nh thon gọn, bạn nhất thiết phải c&oacute;&nbsp;chế độ ăn thấp năng lượng. V&agrave; khoa học đ&atilde;&nbsp;nghi&ecirc;n cứu đưa ra những&nbsp;chế độ giảm c&acirc;n&nbsp;c&oacute; thể sử dụng với nhiều đối tượng, chị em c&oacute; thể tham khảo ngay sau đ&acirc;y.</p>\n\n<p><strong>Chế độ giảm c&acirc;n&nbsp;Lowcarb</strong></p>\n\n<p>Chế độ&nbsp;giảm c&acirc;n Low Carb l&agrave;&nbsp;cắt giảm những loại thực phẩm chứa nhiều đường,&nbsp;tinh bột. Đường v&agrave; tinh bột l&agrave; hai nguồn năng lượng quan trọng của cở thể. Nếu cắt giảm ch&uacute;ng, cơ thể sẽ phải đốt ch&aacute;y mỡ thừa để lấy năng lượng phục vụ c&aacute;c hoạt động hằng ng&agrave;y. Từ đ&oacute;, gi&uacute;p việc giảm c&acirc;n diễn ra hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&oacute; thể thực hiện chế độ Low Carb&nbsp;trong 2 tuần bằng c&aacute;ch loại bỏ ho&agrave;n to&agrave;n c&aacute;c loại thức ăn chứa nhiều tinh bột v&agrave; đường như: ngũ cốc, c&aacute;c loạt hạt, đường, sữa, b&aacute;nh ngọt, đồ ăn nhanh. Thay v&agrave;o đ&oacute; h&atilde;y ăn c&aacute;c loại rau, củ quả v&agrave; được ăn thịt nạc heo, thịt b&ograve;, hải sản, trứng.</p>\n\n<p data-mce-style=\"text-align: justify;\">Sau hai tuần thực hiện chế độ&nbsp;Low Carb, sẽ&nbsp;c&oacute; 1 tuần nghỉ xả Carb. Trong tuần xả Carb n&agrave;y, bạn được ph&eacute;p ăn mọi thứ m&igrave;nh th&iacute;ch kể cả chất b&eacute;o. Sau đ&oacute; quay lại thực hiện 2 tuần&nbsp;Low Carb. Cứ thế lặp đi lặp lại đến khi đạt hiểu quả giảm c&acirc;n như mong muốn.</p>\n\n<p style=\"text-align: center;\"><img alt=\"Fruits, vegetables and seeds spelling the word low carb\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/Low-Carb-Diet-1.jpeg\" src=\"/files/Files/Kien-thuc-lam-dep/Low-Carb-Diet-1.jpg\" style=\"width: 500px; height: 229px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Detox</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Detox l&agrave; chế độ giảm c&acirc;n bằng c&aacute;ch thay thế ho&agrave;n to&agrave;n chế độ ăn bằng c&aacute;c loại đồ uống thanh lọc, giải độc cơ thể như nước chanh, giấm t&aacute;o... Khi nạp c&aacute;c thức uống n&agrave;y v&agrave;o, cơ thể sẽ loại bỏ c&aacute;c độc tố ra ngo&agrave;i, qu&aacute; tr&igrave;nh trao đổi v&agrave; chuyển h&oacute;a sẽ diễn ra nhanh hơn, ngăn cản ho&agrave;n to&agrave;n qu&aacute; tr&igrave;nh t&iacute;ch mỡ, từ đ&oacute; giảm c&acirc;n hiệu quả.</p>\n\n<p style=\"text-align: center;\"><img alt=\"giam-can-detox\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/giam-can-detox.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/giam-can-detox.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Đồ ăn chay</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Kh&ocirc;ng n&ecirc;n nghĩ ăn chay kh&ocirc;ng đảm bảo dinh dưỡng, nếu bạn c&oacute; chế độ đ&uacute;ng đắn, sẽ đảm bảo cung cấp dinh dưỡng đầy đủ v&agrave; gi&uacute;p giảm c&acirc;n tốt.</p>\n\n<p data-mce-style=\"text-align: justify;\">Ăn chay đ&uacute;ng c&aacute;ch:</p>\n\n<p data-mce-style=\"text-align: justify;\">Đậu tương chứa h&agrave;m lượng đạm&nbsp;cao, ho&agrave;n to&agrave;n c&oacute; thể bổ sung chất thay thế cho thịt. C&ugrave;ng với đ&oacute; l&agrave; bổ sung đa dạng c&aacute;c loại rau củ quả: bắp cải, c&agrave; rốt, dưa chuột, chuối, bưởi... Ăn chay vẫn c&oacute; thể bổ sung tinh bột trong bữa ăn hằng ng&agrave;y nhưng hạn chế để tr&aacute;nh tăng c&acirc;n. B&ecirc;n cạnh đ&oacute; n&ecirc;n hạn chế m&oacute;n chi&ecirc;n, r&aacute;n n&ecirc;n ăn m&oacute;n luộc, canh, đồ hầm...</p>\n\n<p style=\"text-align: center;\"><img alt=\"ăn-chay-giảm-cân\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/ăn-chay-giảm-cân.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/%C4%83n-chay-gi%E1%BA%A3m-c%C3%A2n.jpg\" style=\"width: 500px; height: 291px;\" /></p>\n\n<div style=\"width:100%; border: 2px dashed #3ab572;padding:10px;background:rgba(253, 186, 55, 0.08);\">\n<strong style=\"color:#3ab572;\">Có thể bạn muốn xem :</strong>\n<ul>\n  <li><a href=\"http://www.svanclinic.vn/tag/yoga-giam-mo-bung-2\">TOP 10 Bài tập yoga giảm mỡ bụng có thể tập ngay tại nhà</a> <img src=\"http://www.svanclinic.vn/files/Files/hot-1.gif\" /></li>\n<li><a href=\"http://www.svanclinic.vn/tag/thuc-don-giam-can\">TOP 15 Thực đơn giảm cân hiệu quả và dễ làm dành cho bạn</a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-can\">TOP 12 Cách giảm cân nhanh nhất và hiệu quả nhất </a></li>\n<li><a href=\"http://www.svanclinic.vn/tag/giam-mo-bung\">TOP 14 Bí quyết giảm mỡ bụng hiệu quả nhất </a></li>\n\n</ul>\n\n</div>\n\n<p>&nbsp;</p>\n', 'nhung-che-do-giam-can-pho-bien-hien-nay-duoc-ua-thich', '/Upload/News/aa316776-c7e3-416d-862c-75bf48c492c3/giam-can-detox.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Những chế độ giảm cân phổ biến hiện nay được ưa thích', 'Chế độ giảm cân Lowcarb, Detox hay chế độ ăn chay và giảm béo công nghệ HIFU LIPO là những cách giảm cân hiệu quả, giúp bạn lấy lại vóc dáng thon gọn nhất.', NULL, '44', 'PUBLISHED', '', 'old'),
(65, '3', 'Cách chăm sóc da mặt hằng ngày tại nhà hiệu quả cao', '<p>Để c&oacute; l&agrave;n da đẹp v&agrave; s&aacute;ng mịn th&igrave; c&aacute;c chị em phụ nữ cần &quot;đầu tư&quot; thời gian để chăm s&oacute;c da mặt l&agrave; điều kh&ocirc;ng tr&aacute;nh khỏi.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">Để c&oacute; l&agrave;n da đẹp v&agrave; s&aacute;ng mịn th&igrave; c&aacute;c chị em phụ nữ cần &quot;đầu tư&quot; thời gian để chăm s&oacute;c da mặt l&agrave; điều kh&ocirc;ng tr&aacute;nh khỏi. Mong muốn mỗi n&agrave;ng đều sở hữu cho m&igrave;nh l&agrave;n da tươi trẻ v&agrave; mịn m&agrave;ng th&igrave; Svan Clinic &amp; Spa sẽ bật m&iacute; cho c&aacute;c n&agrave;ng&nbsp;c&aacute;ch chăm s&oacute;c da mặt&nbsp;hiệu quả nhất v&agrave; ph&ugrave; hợp với mọi loại da, qu&aacute; tr&igrave;nh n&agrave;y gồm 5 bước.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bước 1:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">L&agrave;m sạch da mặt l&agrave; bước chăm s&oacute;c cơ bản đầu ti&ecirc;n gi&uacute;p loại bỏ bụi bẩn v&agrave; l&agrave;m th&ocirc;ng tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng, nhất l&agrave; sau khi sử dụng mỹ phẩm. V&ocirc; c&ugrave;ng đơn giản, bạn chỉ cần rửa nước muối ấm mỗi ng&agrave;y từ 2-3 lần/ ng&agrave;y kết hợp với massage v&agrave; vỗ nhẹ l&ecirc;n da để loại bỏ bụi bẩn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"rua-matjpg\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/rua-matjpg-e1471674609551.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/rua-matjpg-e1471674609551.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bước 2:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Đ&acirc;y l&agrave; bước quan trọng tiếp theo m&agrave; rất nhiều chị em m&igrave;nh đ&atilde; bỏ qua. Việc loại bỏ thường xuy&ecirc;n tế b&agrave;o chết sẽ gi&uacute;p loại bỏ c&aacute;c lớp sừng th&acirc;m đen tr&ecirc;n bề mặt v&agrave; mang lại bề mặt da th&ocirc;ng tho&aacute;ng, s&aacute;ng mịn. Chỉ cần sử dụng 1 &iacute;t nước cốt chanh v&agrave; thoa nhẹ l&ecirc;n da mặt l&agrave; bạn đ&atilde; tẩy tế b&agrave;o chết rồi đ&oacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bước 3:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Tia tử ngoại l&agrave; kẻ th&ugrave; của l&agrave;n da, v&igrave; vậy để tr&aacute;nh cho da mặt xuất hiện những nếp nhăn v&agrave; vết n&aacute;m th&igrave; c&aacute;c chị em m&igrave;nh n&ecirc;n sử dụng kem chống nắng khoảng 30 ph&uacute;t trước khi ra ngo&agrave;i nh&eacute;. Th&ocirc;ng thường kem chống nắng c&oacute; chỉ số SPF 30-35 l&agrave; ph&ugrave; hợp với mọi loại da.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bước 4:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Đắp mặt nạ hoa quả l&agrave; phương ph&aacute;p đơn giản nhất để cung cấp những chất dinh dưỡng cần thiết để mang lại l&agrave;n da s&aacute;ng mịn. C&oacute; rất nhiều hoa quả như c&agrave; chua, dưa leo, mật ong, nha đam để bạn lựa chọn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"dapmatna\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/dapmatna-e1471674676169.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/dapmatna-e1471674676169.jpg\" style=\"width: 500px; height: 309px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bước 5:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Ngo&agrave;i những bước chăm s&oacute;c da mặt như tr&ecirc;n th&igrave; chế độ ăn uống, l&agrave;m việc v&agrave; nghỉ ngơi hợp l&yacute; c&oacute; vai tr&ograve; rất quan trọng với l&agrave;n da khỏe mạnh. Kh&ocirc;ng n&ecirc;n thức qu&aacute; khuya để da c&oacute; thời gian t&aacute;i tạo v&agrave; phục hồi kịp thời. Đặc biệt tập thể dục thể thao hằng ng&agrave;y sẽ gi&uacute;p qu&aacute; tr&igrave;nh trao đổi chất diễn ra nhanh v&agrave; gi&uacute;p l&agrave;n da trở n&ecirc;n hồng h&agrave;o v&agrave; khỏe mạnh.</p>\n', 'cach-cham-soc-da-mat-hang-ngay-tai-nha-hieu-qua-cao', '/Upload/News/a42af5b2-2462-47af-a149-571ab7195e97/rua-matjpg-e1471674609551.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách chăm sóc da mặt hằng ngày tại nhà hiệu quả cao', 'Cách chăm sóc da mặt hằng ngày hiệu quả là phương pháp giúp các chị em phụ nữ có làn da trắng khỏe, căn tràn sức sống đồng thời chống được lão hóa da', 'cách chăm sóc da mặt', '44', 'PUBLISHED', '', 'old'),
(66, '3', 'Làm sao để giảm mỡ bụng nhanh nhất mà không cần ép cân', '<p><span style=\"text-align: justify;\">D&acirc;n văn ph&ograve;ng, phụ nữ sau sinh, những người &iacute;t vận động hoặc c&oacute; chế độ ăn uống kh&ocirc;ng hợp l&yacute;... l&agrave; đối tượng dễ bị b&eacute;o bụng nhất.</span></p>\n', '<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">D&acirc;n văn ph&ograve;ng, phụ nữ sau sinh, những người &iacute;t vận động hoặc c&oacute; chế độ ăn uống kh&ocirc;ng hợp l&yacute;... l&agrave; đối tượng dễ bị b&eacute;o bụng nhất. Nhưng&nbsp;l&agrave;m sao để giảm mỡ bụng&nbsp;nhanh nhất,&nbsp;Svan Clinic &amp; Spa xin chia sẻ c&ugrave;ng bạn ngay sau đ&acirc;y</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Chế độ ăn uống</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Ăn uống như thế n&agrave;o để giảm mỡ bụng? Tốt nhất l&agrave; n&ecirc;n tr&aacute;nh xa hoặc hạn chế tối đa đường v&agrave; tinh bột. Thay v&agrave;o đ&oacute;, n&ecirc;n ăn nhiều rau xanh, c&aacute;c loại củ quả. Loại thực phẩm n&agrave;y c&oacute; thể cung cấp đủ Calo cho nguy&ecirc;n ng&agrave;y nhưng kh&ocirc;ng t&iacute;ch tụ mỡ g&acirc;y b&eacute;o bụng. Ch&uacute;ng lại hỗ trợ cho da bạn đẹp, mịn m&agrave;ng, hồng h&agrave;o hơn.&nbsp;Song song đ&oacute; l&agrave; hạn chế ăn vặt giữa c&aacute;c bữa cũng như ăn đ&ecirc;m.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"giam mo bung voi che do an kien\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/giam-mo-bung-voi-che-do-an-kien.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/giam-mo-bung-voi-che-do-an-kien.jpg\" style=\"width: 500px; height: 330px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Giữ nước</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Tăng cường cung cấp nước cho cơ thể sẽ gi&uacute;p bạn &iacute;t c&oacute; cảm gi&aacute;c đ&oacute;i hơn, hạn chế nạp c&aacute;c loại thức ăn v&agrave;o cơ thể. Bạn biết r&otilde; điều n&agrave;y sẽ hỗ trợ giảm c&acirc;n n&oacute;i chung v&agrave; giảm mỡ bụng n&oacute;i ri&ecirc;ng tốt nhất. Cần uống đủ 2 l&iacute;t nước mỗi ng&agrave;y v&agrave; nhớ uống 1 ly nước trước khi ăn để tạo cảm gi&aacute;c no v&agrave; ăn &iacute;t đồ ăn hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">H&atilde;y nhớ uống nước lọc, tr&aacute;nh xa nước ngọt v&igrave; ch&uacute;ng kh&ocirc;ng tốt cho sức khỏe, dễ g&acirc;y ph&ugrave; da, tr&ocirc;ng giống mập hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"lam the nao de giam can\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-the-nao-de-giam-can-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-the-nao-de-giam-can-1.jpg\" style=\"width: 500px; height: 369px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Tập thể dục</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Kh&ocirc;ng cần phải b&agrave;n cải, tập thể dục l&agrave; việc thiết yếu nếu bạn muốn giảm mỡ. Tập thể dục l&agrave; cơ thể đang đốt ch&aacute;y một lượng mỡ thừa v&ocirc; c&ugrave;ng lớn. H&atilde;y bắt đầu th&oacute;i quen tập thể dục ngay b&acirc;y giờ, chọn bộ m&ocirc;n n&agrave;o đ&oacute; ph&ugrave; hợp v&agrave; ki&ecirc;n tr&igrave; tập luyện thường xuy&ecirc;n. Nếu kh&ocirc;ng c&oacute; nhu cầu giảm mỡ th&igrave; thể dục sẽ mang lại cho bạn cuộc sống khỏe mạnh nhất.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"lam the nao de giam can 2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/lam-the-nao-de-giam-can-2-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-the-nao-de-giam-can-2-1.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\"><strong>Ngủ đủ giấc</strong></p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: justify;\">Ngủ thiếu giấc khiến cơ thể mệt mỏi, tổn hao năng lượng v&agrave; cần nạp lại nhiều năng lượng để vận h&agrave;nh c&aacute;c hoạt động kh&aacute;c. Ch&iacute;nh v&igrave; thế, bạn cảm thấy th&egrave;m ăn hơn, v&ocirc; t&igrave;nh lại khiến cơ thể bị b&eacute;o l&ecirc;n v&agrave; mỡ bụng t&iacute;ch tụ mỗi ng&agrave;y một tăng.</p>\n', 'lam-sao-de-giam-mo-bung-nhanh-nhat-ma-khong-can-ep-can', '/Upload/News/57595121-c9cc-4434-b518-13dcfa054f11/lam-the-nao-de-giam-can-2-1.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Làm sao để giảm mỡ bụng nhanh nhất mà không cần ép cân', 'Ăn kiên, uống nhiều nước, ngủ đủ giấc, tập thể dục và phương pháp giảm cân tạo form Perfect Shape là những cách giảm mỡ bụng nhanh nhất.', 'làm sao để giảm mỡ bụng', '44', 'PUBLISHED', '', 'old'),
(67, '3', 'Cách làm đẹp da mặt hoàn toàn từ thiên nhiên', '<p>Khi nền c&ocirc;ng nghiệp mỹ phẩm ng&agrave;y c&agrave;ng ph&aacute;t triển với h&agrave;ng loạt c&aacute;c chủng loại kh&aacute;c nhau th&igrave; kh&oacute; để c&oacute; thể lựa chọn cho m&igrave;nh loại sản phẩm chất lượng v&agrave; ph&ugrave; hợp.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">Khi nền c&ocirc;ng nghiệp mỹ phẩm ng&agrave;y c&agrave;ng ph&aacute;t triển với h&agrave;ng loạt c&aacute;c chủng loại kh&aacute;c nhau th&igrave; kh&oacute; để c&oacute; thể lựa chọn cho m&igrave;nh loại sản phẩm chất lượng v&agrave; ph&ugrave; hợp. Bạn c&oacute; biết rằng c&aacute;ch l&agrave;m đẹp da mặt từ c&aacute;c loại rau quả ho&agrave;n to&agrave;n từ thi&ecirc;n nhi&ecirc;n v&agrave; v&ocirc; c&ugrave;ng đơn giản sau sẽ gi&uacute;p cho bạn c&oacute; được l&agrave;n da trắng mịn v&agrave; rạng ngời. H&atilde;y c&ugrave;ng Svan Clinic &amp; Spa t&igrave;m hiểu nh&eacute;!</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>L&agrave;m đẹp da từ mật ong, chanh tươi v&agrave; trứng g&agrave;</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Đ&acirc;y l&agrave; c&aacute;ch l&agrave;m đẹp da mặt từ thi&ecirc;n nhi&ecirc;n v&ocirc; c&ugrave;ng đơn giản v&agrave; hiệu quả. Sự kết hợp giữa c&aacute;c th&agrave;nh phần gi&agrave;u chất chống oxy h&oacute;a c&oacute; khả năng tẩy da nhẹ v&agrave; cung cấp c&aacute;c dưỡng chất cần thiết cho da, mang lại l&agrave;n da trắng mịn. Ngo&agrave;i ra c&ocirc;ng thức n&agrave;y c&oacute; t&aacute;c dụng ngăn sự h&igrave;nh th&agrave;nh nếp nhăn, m&agrave; mờ vết th&acirc;m n&aacute;m v&agrave; se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"h4\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/h4-e1471599356488.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/h4-e1471599356488.jpg\" style=\"width: 500px; height: 333px;\" /><br />\n<em>C&aacute;ch l&agrave;m: Cho 2 th&igrave;a mật ong v&agrave; một l&ograve;ng trắng trứng g&agrave; v&agrave;o ch&eacute;n v&agrave; đ&aacute;nh tan. Sau đ&oacute; cho th&ecirc;m v&agrave;i giọt nước cốt chanh v&agrave;o v&agrave; khuấy đều. Thoa hỗn hợp l&ecirc;n mặt v&agrave; nằm thư gi&atilde;n khoảng 30 ph&uacute;t sau đ&oacute; rửa mặt sạch bằng nước ấm v&agrave; nhớ b&ocirc;i kem dưỡng da nh&eacute;.</em></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>L&agrave;m đẹp da mặt bằng nghệ tươi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Từ l&acirc;u ch&uacute;ng ta đ&atilde; biết tới c&ocirc;ng dụng &quot; thần kỳ&quot; của nghệ tươi, n&oacute; l&agrave; nguy&ecirc;n liệu chăm s&oacute;c da mặt phổ biến cho ph&aacute;i đẹp. Mặt nạ từ nghệ tươi c&oacute; thể d&ugrave;ng như một chất khử tr&ugrave;ng tự nhi&ecirc;n, gi&uacute;p l&agrave;m mờ sẹo v&agrave; l&agrave;m l&agrave;nh vết thương nhanh ch&oacute;ng đồng thời l&agrave;m đẹp da mặt v&agrave; dưỡng trắng da hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/2.jpg\" style=\"width: 500px; height: 300px;\" /><br />\n<em>C&aacute;ch l&agrave;m: Trộn đều bột nghệ+ 1 th&igrave;a mật ong nguy&ecirc;n chất + 1/2 th&igrave;a bột m&igrave; + 1 &iacute;t nước tạo th&agrave;nh hỗn hợp sền sệt l&agrave; được. Sau đ&oacute; thoa hỗn hợp l&ecirc;n v&ugrave;ng da mặt. Nằm thư gi&atilde;n khoảng 10 ph&uacute;t v&agrave; rửa sạch lại bằng nước ấm.</em></p>\n\n<p data-mce-style=\"text-align: justify;\">Tr&ecirc;n đ&acirc;y l&agrave; những c&aacute;ch gi&uacute;p chị em phụ nữ c&oacute; thể l&agrave;m đẹp da mặt tại nh&agrave; an to&agrave;n v&agrave; đơn giản từ những nguy&ecirc;n liệu thi&ecirc;n nhi&ecirc;n, bởi v&igrave; sắc đẹp của phụ nữ nằm ở l&agrave;n da. Tuy nhi&ecirc;n, những vết sẹo hay nếp nhăn th&igrave; vẫn c&ograve;n tr&ecirc;n da mặt l&agrave;m mất đi vẻ tự tin của chị em m&igrave;nh. Theo nghi&ecirc;n cứu ngay từ những năm 20 tuổi l&agrave;n da đ&atilde; bắt dầu c&oacute; những dấu hiệu l&atilde;o h&oacute;a v&agrave; đến năm 30 tuổi sẽ bộc lộ ra ngo&agrave;i c&aacute;ch r&otilde; rệt hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&oacute; thể bạn muốn xem :</p>\n\n<ul>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cac-cach-lam-trang-da-toan-than-nhanh-va-hieu-qua\"><span style=\"color:#3ab572;\">c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n đơn giản</span></a></li>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/dau-la-cach-triet-tay-long-vinh-vien-nhanh-nhat-tai-tphcm\"><span style=\"color:#3ab572;\">tẩy l&ocirc;ng mặt bằng phương ph&aacute;p tự nhi&ecirc;n</span></a></li>\n	<li data-mce-style=\"text-align: justify;\"><a href=\"http://www.svanclinic.vn/tin-tuc/kien-thuc-lam-dep/cach-lam-da-mat-trang-min-nhanh-va-hieu-qua-nhat-tu-thien-nhien\"><span style=\"color:#3ab572;\">c&aacute;ch l&agrave;m trắng da mặt nhanh nhất trong 1 tuần</span></a></li>\n</ul>\n', 'cach-lam-dep-da-mat-hoan-toan-tu-thien-nhien', '/Upload/News/cd79fa0c-b1fa-450a-87a8-89154aa4ca3d/2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách làm đẹp da mặt hoàn toàn từ thiên nhiên', 'Cách làm đẹp da mặt hoàn toàn từ thiên nhiên giúp tiết kiệm chi phí và vô cùng an toàn cho các chị em phụ nữ', 'cách làm đẹp da mặt', '44', 'PUBLISHED', '', 'old'),
(68, '3', '[ Event online ] Đêm Hội Trăng Rằm - Đêm Hội Hồi Ức', '<p>Một m&ugrave;a trung thu ấm c&uacute;ng sum vầy lại về, đ&acirc;y l&agrave; dịp để ch&uacute;ng ta tỏ l&ograve;ng hiếu k&iacute;nh với &ocirc;ng b&agrave;, cha mẹ.&nbsp;</p>\n', '<p><em>&nbsp;Trung thu l&agrave; tết đo&agrave;n vi&ecirc;n, con ch&aacute;u s&ugrave;m vầy, &ocirc;ng b&agrave; tươi vui!</em></p>\n\n<p>Một m&ugrave;a trung thu ấm c&uacute;ng sum vầy lại về, đ&acirc;y l&agrave; dịp để ch&uacute;ng ta tỏ l&ograve;ng hiếu k&iacute;nh với &ocirc;ng b&agrave;, cha mẹ. Cũng l&agrave; dịp để t&igrave;nh y&ecirc;u thương san sẻ được lan tỏa đến khắp nơi nơi. Quyện m&igrave;nh trong kh&ocirc;ng kh&iacute; đo&agrave;n vi&ecirc;n Svan Clinic &amp; Spa mong muốn gửi tặng c&aacute;c bạn nhiều m&oacute;n qu&agrave; gi&aacute; trị qua mini game chụp lại từng khoảnh khắc sum vầy của gia đ&igrave;nh m&ugrave;a trung thu.</p>\n\n<p style=\"text-align: center;\"><img alt=\"trung-thu\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/08/trung-thu.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/trung-thu.jpg\" style=\"width: 500px; height: 299px;\" /></p>\n\n<p><strong>1. C&aacute;ch thức tham gia:</strong></p>\n\n<ul>\n	<li>\n	<p>Bước 1: Like Fanpage của Svan Clinic and Spa.</p>\n	</li>\n	<li>\n	<p>Bước 2: Ấn tham gia Event.</p>\n	</li>\n	<li>\n	<p>Bước 3: Chia sẻ h&igrave;nh ảnh của bạn(Hoặc người th&acirc;n của bạn) v&agrave;o dịp trung thu k&egrave;m những th&ocirc;ng điệp bạn muốn gửi gắm tới mọi người trong mục sự kiện theo cấu tr&uacute;c:</p>\n	</li>\n</ul>\n\n<p>- Họ t&ecirc;n:</p>\n\n<p>- Nơi đang sinh sống:</p>\n\n<p>- Th&ocirc;ng điệp:</p>\n\n<ul>\n	<li>\n	<p>Bước 4: K&ecirc;u gọi bạn b&egrave; v&agrave;o y&ecirc;u th&iacute;ch bức h&igrave;nh với icon &quot;&nbsp;&hearts; &quot;</p>\n	</li>\n</ul>\n\n<p><strong>2. C&aacute;ch thức t&iacute;nh thưởng:</strong></p>\n\n<ul>\n	<li>\n	<p>Sự kiện diễn ra từ ng&agrave;y 5/9 đến cuối ng&agrave;y 14/09/2016. Những b&agrave;i dự thi hợp lệ mới được t&iacute;nh điểm.</p>\n	</li>\n	<li>\n	<p>10 B&agrave;i dự thi c&oacute; số lượt y&ecirc;u th&iacute;ch cao nhất sẽ được BTC lọc ra v&agrave; chấm điểm .</p>\n	</li>\n	<li>\n	<p>3 B&agrave;i dự thi c&oacute; số điểm cao nhất sẽ được BTC gửi th&ocirc;ng tin tr&uacute;ng giải v&agrave;o ng&agrave;y 15/09/2016.</p>\n	</li>\n</ul>\n\n<p><strong>3. Phần thưởng:</strong></p>\n\n<ul>\n	<li>\n	<p>Giải nhất:&nbsp;Phiếu sử dụng dịch vụ tại Svan Clinic &amp; Spa trị gi&aacute; 5 triệu đồng</p>\n	</li>\n	<li>\n	<p>Giải nh&igrave;:&nbsp;Phiếu sử dụng dịch vụ tại Svan Clinic &amp; Spa trị gi&aacute; 3 triệu đồng</p>\n	</li>\n	<li>\n	<p>Giải ba:&nbsp;Phiếu sử dụng dịch vụ tại Svan Clinic &amp; Spa trị gi&aacute; 2 triệu đồng</p>\n	</li>\n</ul>\n\n<p>C&ograve;n chờ g&igrave; nữa, nhanh tay ghi lại từng gi&acirc;y ph&uacute;t hạnh ph&uacute;c của bạn với người th&acirc;n l&agrave;m kỉ niệm v&agrave; nhận ngay &quot;Lộc&quot; tặng Mẹ l&agrave;m đẹp m&ugrave;a trung thu th&ocirc;i n&agrave;o!</p>\n', '-event-online-dem-hoi-trang-ram-dem-hoi-hoi-uc', '/Upload/News/c3ac76d2-d3ab-44c7-a016-4e9584eddede/trung-thu.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, NULL, NULL, '23', 'PUBLISHED', '', 'old'),
(69, '3', 'Tư vấn điều trị da cùng chuyên gia thẩm mỹ Hoa Kỳ Kimberly', '<p>Ng&agrave;y 14/09/2016, chuy&ecirc;n gia h&agrave;ng đầu về ng&agrave;nh thẩm mỹ chăm s&oacute;c sắc đẹp Hoa Kỳ Kimberly sẽ đến&nbsp;thăm Svan Clinic &amp; Spa</p>\n', '<p data-mce-style=\"text-align: justify;\">Ng&agrave;y 14/09/2016, chuy&ecirc;n gia h&agrave;ng đầu về ng&agrave;nh thẩm mỹ chăm s&oacute;c sắc đẹp Hoa Kỳ Kimberly sẽ đến&nbsp;thăm Svan Clinic &amp; Spa. Chuyến đến&nbsp;thăm lần n&agrave;y kh&ocirc;ng chỉ với mục đ&iacute;ch thắt chặt quan hệ giữa Svan Clinic &amp; Spa v&agrave; tập đo&agrave;n Image&nbsp;Skincare m&agrave; n&oacute; c&ograve;n l&agrave; cơ hội để c&aacute;c vị kh&aacute;ch h&agrave;ng xinh đẹp của Svan c&oacute; cơ hội tr&ograve; chuyện c&ugrave;ng chuy&ecirc;n gia.</p>\n\n<p style=\"text-align: center;\"><img alt=\"600x600-wweb\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/600x600-wweb.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/600x600-wweb.jpg\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&ocirc; Kimberly sẽ gi&agrave;nh nhiều thời gian để chia sẻ kinh nghiệm trong điều trị da chuy&ecirc;n s&acirc;u. Đồng thời chuy&ecirc;n gia cũng tư vấn trực tiếp cho những kh&aacute;ch h&agrave;ng đặt lịch hẹn từ trước. Do đ&oacute;, để sở hữu thời gian v&agrave;ng được tư vấn c&ugrave;ng chuy&ecirc;n gia, kh&aacute;ch h&agrave;ng y&ecirc;u qu&yacute; của Svan h&atilde;y nhanh tay đăng k&iacute; theo form ........ để đội ngũ tư vấn vi&ecirc;n của Svan sắp xếp lịch hẹn cho từng kh&aacute;ch h&agrave;ng. &nbsp;Việc đặt lịch n&agrave;y g&oacute;p phần kh&ocirc;ng l&agrave;m gi&aacute;n đoạn cuộc tư vấn v&agrave; tăng t&iacute;nh ri&ecirc;ng tư cho từng kh&aacute;ch h&agrave;ng trong suốt qu&aacute; tr&igrave;nh điều trị cũng như chia sẻ vấn đề l&agrave;n da bạn đang gặp phải.</p>\n\n<p data-mce-style=\"text-align: justify;\">Đối với trường hợp những kh&aacute;ch h&agrave;ng ở xa muốn được giải đ&aacute;p thắc mắc vui l&ograve;ng gửi c&acirc;u hỏi về cho Svan Clinic &amp; Spa để ch&uacute;ng t&ocirc;i cập nhật v&agrave; trao đổi với chuy&ecirc;n gia. Sau đ&oacute; đội ngũ tư vấn sẽ giải đ&aacute;p đến với từng kh&aacute;ch h&agrave;ng.</p>\n\n<p data-mce-style=\"text-align: justify;\">Kh&ocirc;ng những được tr&ograve; chuyện c&ugrave;ng chuy&ecirc;n gia Kimberly, bất k&igrave; kh&aacute;ch h&agrave;ng n&agrave;o đến với Svan Clinic &amp; Spa sẽ thoải m&aacute;i lựa chọn cho m&igrave;nh những dịch vụ mới với nhiều chương tr&igrave;nh ưu đ&atilde;i hơn 30% trong th&aacute;ng 09/2016.</p>\n\n<p data-mce-style=\"text-align: justify;\">Svan cam kết mang lại cho bạn những gi&aacute; trị sắc đẹp vững bền theo thời gian t&ugrave;y thuộc v&agrave;o thể trạng của từng kh&aacute;ch h&agrave;ng y&ecirc;u qu&yacute;!</p>\n', 'tu-van-dieu-tri-da-cung-chuyen-gia-tham-my-hoa-ky-kimberly', '/Upload/News/cb07f577-1f96-4f3b-aa09-b0ce939d8588/600x600-wweb.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', NULL, NULL, NULL, '23', 'PUBLISHED', '', 'old'),
(70, '3', 'Cách trị mụn hiệu quả nhất áp dụng riêng cho nam giới', '<p>Việc trị mụn ở nam giới r&otilde; r&agrave;ng kh&oacute; hơn nữ giới kh&aacute; nhiều. Ch&iacute;nh v&igrave; thế cần t&igrave;m đ&uacute;ng&nbsp;c&aacute;ch trị mụn hiệu quả nhất&nbsp;để ti&ecirc;u diệt triệt để mụn tr&ecirc;n cơ thể.</p>\n', '<p data-mce-style=\"text-align: justify;\">Việc trị mụn ở nam giới r&otilde; r&agrave;ng kh&oacute; hơn nữ giới kh&aacute; nhiều. Ch&iacute;nh v&igrave; thế cần t&igrave;m đ&uacute;ng&nbsp;c&aacute;ch trị mụn hiệu quả nhất&nbsp;để ti&ecirc;u diệt triệt để mụn tr&ecirc;n cơ thể.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Tại sao mụn nam giới kh&oacute; điều trị?</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Theo rất nhiều chuy&ecirc;n gia da liễu nhận x&eacute;t nội tiết tố cũng với cấu tr&uacute;c da ở nam giới phức tạp hơn so với nữ giới. Do đ&oacute; lượng dầu nhờn tiết ra theo lỗ ch&acirc;n l&ocirc;ng nhiều hơn gấp 2 lần dẫn đến ở nam giới nguy cơ h&igrave;nh th&agrave;nh nh&acirc;n mụn rất cao. V&agrave; đặc biệt một khi mụn đ&atilde; mọc th&igrave; sẽ ở lại l&acirc;u tr&ecirc;n da v&agrave; c&oacute; biểu hiệu lan sang c&aacute;c v&ugrave;ng da xung quanh mất kiểm so&aacute;t. C&aacute;ch điều trị mụn hiệu quả nhất l&uacute;c n&agrave;y l&agrave; c&aacute;c ch&agrave;ng đừng n&ecirc;n dại m&agrave; d&ugrave;ng tay kh&ocirc;ng nặn mụn nh&eacute;. N&oacute; kh&ocirc;ng những ko sạch mụn l&agrave; c&ograve;n l&agrave;m mụn dạng nhẹ biến th&agrave;nh mụn bọc, mụn mủ kh&oacute; điều trị v&agrave; g&acirc;y th&acirc;m sẹo cho da rất nguy hiểm.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"bay-cach-tri-mun-hieu-qua-nhat-cho-nam-gioi\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/bay-cach-tri-mun-hieu-qua-nhat-cho-nam-gioi.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/bay-cach-tri-mun-hieu-qua-nhat-cho-nam-gioi.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn hiệu quả l&uacute;c n&agrave;y l&agrave; g&igrave;?</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Muốn tho&aacute;t khỏi đ&aacute;m mụn kia c&aacute;c ch&agrave;ng n&ecirc;n chịu kh&oacute; chăm s&oacute;c da kỹ c&agrave;ng mỗi ng&agrave;y v&agrave; &aacute;p dụng ngay những c&aacute;ch trị mụn dưới đ&acirc;y m&agrave; Svan Clinic &amp; Spa khuyến c&aacute;o.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn hiệu quả nhất từ sữa chua v&agrave; d&acirc;u t&acirc;y :&nbsp;Hỗn hợp n&agrave;y vừa c&oacute; c&ocirc;ng dụng s&aacute;t khuẩn ngăn ngừa mụn vừa gi&uacute;p da căng mịn từ s&acirc;u b&ecirc;n trong lớp biểu b&igrave;. D&acirc;u t&acirc;y v&agrave; sữa chua l&agrave; nguy&ecirc;n liệu dễ t&igrave;m kiếm mặc d&ugrave; đ&ocirc;i l&uacute;c kh&ocirc;ng hẳn l&agrave; gi&aacute; thấp c&oacute; thể mua được d&acirc;u t&acirc;y nhưng nếu bạn thường xuy&ecirc;n &aacute;p dụng c&aacute;ch n&agrave;y sẽ rất c&oacute; hiệu quả hơn l&agrave; sử dụng những loại mỹ phẩm h&oacute;a chất độc hại cho l&agrave;n da. Bạn chỉ cần từ 2 - 3 quả d&acirc;u t&acirc;y to vừa, &eacute;p nhuyễn ra hoặc cho v&agrave;o m&aacute;y xay sinh tố v&agrave; xay cho thật nhuyễn nhẹ. Sau đ&oacute; cho th&ecirc;m 1 hũ sữa chua kh&ocirc;ng đường v&agrave;o v&agrave; đ&aacute;nh cho s&aacute;nh lại. Bước kế tiếp l&agrave; rửa sạch qua bằng nước lạnh với v&ugrave;ng da cần trị mụn để lớp bụi bẩn b&ecirc;n tr&ecirc;n tr&ocirc;i đi phần n&agrave;o. L&uacute;c n&agrave;y bạn lấy tay b&ocirc;i một lượng vừa đủ hỗn hợp sữa chua v&agrave; d&acirc;u t&acirc;y l&ecirc;n tr&ecirc;n v&agrave; massage theo chiều kim đồng hồ từ 15 - 20 ph&uacute;t. Cứ như thế sau một thời gian nhất định bạn sẽ thấy da m&igrave;nh s&aacute;ng đều m&agrave;u v&agrave; mụn nơi đắp mặt nạ giảm bớt đi kh&aacute; nhiều.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-lam-sua-chua-dau-tay-ngon-mat-ngay-he-2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-lam-sua-chua-dau-tay-ngon-mat-ngay-he-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-sua-chua-dau-tay-ngon-mat-ngay-he-2.jpg\" style=\"width: 500px; height: 311px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn hiệu quả nhất từ trứng g&agrave; :&nbsp;c&aacute;ch l&agrave;m n&agrave;y rất phổ biến hiện nay, trứng g&agrave; kh&ocirc;ng chỉ được xem như mặt nạ lột mụn c&aacute;m m&agrave; n&oacute; c&ograve;n c&oacute; t&aacute;c dụng thẩm thấu ti&ecirc;u diệt mụn ẩn dưới da sau thời gian d&agrave;i sử dụng. Tinh chất chống l&atilde;o h&oacute;a trong trứng g&agrave; khi thẩm thấu v&agrave;o b&ecirc;n trong l&agrave;m cho lỗ ch&acirc;n l&ocirc;ng se kh&iacute;t đồng thời đẩy nhiều nh&acirc;n mụn đ&atilde; ch&iacute;n l&ecirc;n tr&ecirc;n bề mặt da. L&uacute;c n&agrave;y bạn chỉ việc d&ugrave;ng khăn ướt lau sơ qua hoặc l&agrave; rửa mặt lại với sữa tươi kh&ocirc;ng đường l&agrave; l&agrave;n da bạn đ&atilde; s&aacute;ng mịn v&agrave; sạch mụn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"trung_lrpd\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/trung_LRPD.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/trung_LRPD.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Trứng g&agrave; lại l&agrave; loại nguy&ecirc;n liệu dễ t&igrave;m kiếm trong gian bếp của mỗi gia đ&igrave;nh n&ecirc;n bạn kh&ocirc;ng phải tốn qu&aacute; nhiều c&ocirc;ng sức để chọn lựa v&agrave; pha chế.&nbsp;Hy vọng rằng những c&aacute;ch trị mụn tr&ecirc;n đ&acirc;y sẽ gi&uacute;p &iacute;ch được phần n&agrave;o nổi lo trị mụn của nhiều nam giới hiện nay.</p>\n', 'cach-tri-mun-hieu-qua-nhat-ap-dung-rieng-cho-nam-gioi', '/Upload/News/02b9ca6f-9fe6-4090-ba1a-b9c97559eeb8/bay-cach-tri-mun-hieu-qua-nhat-cho-nam-gioi.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách trị mụn hiệu quả nhất áp dụng riêng cho nam giới', 'Việc trị mụn ở nam giới rõ ràng khó hơn nữ giới khá nhiều. Chính vì thế cần tìm đúng cách trị mụn hiệu quả nhất để tiêu diệt triệt để mụn trên cơ thể.', 'cách trị mụn hiệu quả nhất', '44', 'PUBLISHED', '', 'old'),
(71, '3', 'Cách trị mụn đầu đen hiệu quả tại nhà không mất tiền', '<p>L&agrave;m sao để mụn đầu đen biến mất?&nbsp;C&aacute;ch trị mụn đầu đen hiệu quả tại nh&agrave;&nbsp;l&agrave; phương thức an to&agrave;n nhanh ch&oacute;ng trả lại cho bạn l&agrave;n da s&aacute;ng khỏe kh&ocirc;ng c&ograve;n mụn.</p>\n', '<p data-mce-style=\"text-align: justify;\">L&agrave;m sao để mụn đầu đen biến mất?&nbsp;C&aacute;ch trị mụn đầu đen hiệu quả tại nh&agrave;&nbsp;l&agrave; phương thức an to&agrave;n nhanh ch&oacute;ng trả lại cho bạn l&agrave;n da s&aacute;ng khỏe kh&ocirc;ng c&ograve;n mụn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Nhiều người thường v&ocirc; t&igrave;nh bỏ qua kh&ocirc;ng quan t&acirc;m khi một v&agrave;i mụn đầu đen xuất hiện tr&ecirc;n da. V&igrave; thế khi số lượng của ch&uacute;ng h&igrave;nh th&agrave;nh tr&ecirc;n diện rộng mới thật sự lo lắng v&igrave; l&uacute;c n&agrave;y việc chữa trị gặp kh&oacute; khăn hơn gấp đ&ocirc;i. Th&ecirc;m v&agrave;o đ&oacute; nếu diệt mụn đầu đen kh&ocirc;ng đ&uacute;ng c&aacute;ch c&ograve;n l&agrave;m lỗ ch&acirc;n l&ocirc;ng to ra l&agrave; điều kiện để vi khuẩn x&acirc;m nhập tạo n&ecirc;n ổ mụn từ s&acirc;u b&ecirc;n trong l&agrave;n da của bản th&acirc;n.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Cơ chế h&igrave;nh th&agrave;nh mụn</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Thời tiết, m&ocirc;i trường, th&oacute;i quen l&agrave;m cho nội tiết tố trong cơ thể thay đổi mỗi ng&agrave;y. C&oacute; người cơ địa tốt l&agrave;n da lu&ocirc;n khỏe mạnh v&agrave; &iacute;t nhờn nhưng ngược lại nhiều người cơ địa n&oacute;ng, xấu l&agrave;m mụn đầu đen xuất hiện với tầng số b&aacute;o động. V&agrave; mụn đầu đẹn h&igrave;nh th&agrave;nh nhiều nhất ở người da dầu nhờn v&agrave; da kh&ocirc;. Dầu tiết qu&aacute; nhiều tr&ecirc;n da l&agrave;m b&iacute; lỗ ch&acirc;n l&ocirc;ng tạo điền kiện sống l&iacute; tưởng cho vi khuẩn. Chỉ một động t&aacute;c nặn mụn nho nhỏ cũng đủ để vi khuẩn ăn s&acirc;u v&agrave;o trong v&agrave; ph&aacute;t triển. Đối với da kh&ocirc;, l&uacute;c n&agrave;y cơ thể mất nước hoặc bạn kh&ocirc;ng cung cấp độ ẩm kịp thời cho l&agrave;n da khiến da sần s&ugrave;i bong vẩy cũng l&agrave; một trong nhiều nguy&ecirc;n nh&acirc;n dẫn đến mụn h&igrave;nh th&agrave;nh. V&agrave; c&acirc;u hỏi đặt ra l&uacute;c n&agrave;y l&agrave; l&agrave;m sao điều trị mụn đầu đen hiệu quả m&agrave; kh&ocirc;ng tốn qu&aacute; nhiều thời gian c&ugrave;ng c&ocirc;ng sức. C&aacute;c c&aacute;c trị mụn đầu đen hiệu quả tại nh&agrave; sau đ&acirc;y đ&atilde; được Svan Clinic &amp; Spa &aacute;p dụng v&agrave; th&agrave;nh c&ocirc;ng một phần n&agrave;o đ&oacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-tri-mun-dau-den-11\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-tri-mun-dau-den-11.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-dau-den-11.jpg\" style=\"width: 500px; height: 284px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>C&aacute;ch trị mụn tại nh&agrave;</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Thay v&igrave; lo lắng về việc tại sao mụn đầu đen lại mọc tr&ecirc;n da của m&igrave;nh, bạn h&atilde;y &aacute;p dụng ngay c&aacute;c c&aacute;ch dưới đ&acirc;y để trị v&agrave; ph&ograve;ng ngừa mụn đầu đen quay trở lại tr&ecirc;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn đầu đen hiệu quả tại nh&agrave; với bột gạo :&nbsp;c&aacute;ch n&agrave;y được &ocirc;ng b&agrave; cha mẹ &aacute;p dụng từ rất l&acirc;u đời rồi. Ng&agrave;y xưa họ tận dụng nước vo gạo để rửa mặt l&agrave;m s&aacute;ng da v&agrave; trị mụn. Ng&agrave;y nay c&aacute;c c&aacute;ch trị mụn bằng bột gạo được ph&aacute;t minh hiệu quả hơn bằng việc xay nhuyễn gạo th&agrave;nh bột sau đ&oacute; h&ograve;a chung với một hộp sữa chua kh&ocirc;ng đường hoặc sữa tươi nguy&ecirc;n chất v&agrave; đắp ngay l&ecirc;n v&ugrave;ng da bị mụn đầu đen. Tinh chất B1, B6 trong bột gạo hoạt h&oacute;a s&acirc;u b&ecirc;n trong da gi&uacute;p lỗ ch&acirc;n l&ocirc;ng thu nhỏ lại đồng thời đẩy c&ugrave;i mụn l&ecirc;n tr&ecirc;n da. Ch&iacute;nh l&uacute;c n&agrave;y sữa chua cung cấp độ ẩm c&ugrave;ng dưỡng chất c&oacute; lợi oxy h&oacute;a đi nh&acirc;n mụn vừa được đẩy l&ecirc;n đ&oacute; l&agrave;m ch&uacute;ng biến mất ho&agrave;n to&agrave;n. Bạn n&ecirc;n thực hiện c&aacute;ch n&agrave;y tại nh&agrave; 1 tuần 3 lần để mang lại hiệu quả như &yacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"tam-trang_sbcz\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tam-trang_sbcz.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tam-trang_sbcz.jpg\" style=\"width: 500px; height: 419px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn đầu đen hiệu quả tại nh&agrave; với trứng luộc :&nbsp;Nhiều chị em cho rằng đ&acirc;y l&agrave; c&aacute;ch l&agrave;m d&acirc;n gian truyền miệng chưa chắc đ&atilde; hiệu quả. Tuy nhi&ecirc;n, kết quả m&agrave; c&aacute;ch n&agrave;y mang lại vượt xa tưởng tượng của nhiều người lu&ocirc;n đấy. Ch&iacute;nh nhiệt lượng c&ograve;n lưu lại trong trứng g&agrave; luộc sẽ như một đầu nam ch&acirc;m h&uacute;t hết đ&ocirc;c tố từ dưới da v&agrave;o b&ecirc;n trong. Do đ&oacute; c&aacute;c nh&acirc;n mụn một l&agrave; tự ch&iacute;n trồi l&ecirc;n tr&ecirc;n bề mặt da ra ngo&agrave;i hoặc hai l&agrave; theo sức h&uacute;t của trứng g&agrave; tho&aacute;t ra.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"trung-luoc-3-398609-1371244352_500x0\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/trung-luoc-3-398609-1371244352_500x0.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/trung-luoc-3-398609-1371244352_500x0.jpg\" style=\"width: 500px; height: 356px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn cần 1 quả trứng g&agrave; luộc thật ch&iacute;n, sau đ&oacute; vớt ra v&agrave; lột vỏ để hơi nguội một ch&uacute;t v&agrave; lăn đều l&ecirc;n v&ugrave;ng da c&oacute; mụn đầu đen cho đến khi trứng g&agrave; hết n&oacute;ng. Bạn bẻ đ&ocirc;i quả trứng g&agrave; ra sẽ thấy l&ograve;ng đỏ b&ecirc;n trong chuyển sang m&agrave;u đen hoặc loang lỗ. Đ&oacute; ch&iacute;nh l&agrave; c&aacute;c nh&acirc;n mụn bị hấp thu v&agrave;o b&ecirc;n trong trứng g&agrave;. L&uacute;c n&agrave;y da bạn sẽ hơi ửng đỏ do đ&oacute; bạn đợi 5 ph&uacute;t sau đ&oacute; h&atilde;y rửa mặt v&agrave; b&ocirc;i một ch&uacute;t kem dưỡng ẩm l&ecirc;n. &Aacute;p dụng thường xuy&ecirc;n c&aacute;ch n&agrave;y l&agrave;m l&agrave;n da trắng hồng đều m&agrave;u.</p>\n', 'cach-tri-mun-dau-den-hieu-qua-tai-nha-khong-mat-tien', '/Upload/News/066be872-e0a8-4797-b173-c7ea3e161e7e/cach-tri-mun-dau-den-11.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách trị mụn đầu đen hiệu quả tại nhà không mất tiền', 'Làm sao để mụn đầu đen biến mất? Cách trị mụn đầu đen hiệu quả tại nhà là phương thức an toàn nhanh chóng trả lại cho bạn làn da sáng khỏe không còn mụn.', 'cách trị mụn đầu đen hiệu quả tại nhà', '44', 'PUBLISHED', '', 'old'),
(72, '3', 'Cách trị mụn trứng cá hiệu quả an toàn tuyệt đối vừa túi tiền', '<p>Với tiết trời oi ả, l&uacute;c nắng l&uacute;c mưa thất thường dễ khiến cơ thể bức bối m&agrave; ph&aacute;t sinh mụn tr&ecirc;n da.</p>\n', '<p data-mce-style=\"text-align: justify;\">Với tiết trời oi ả, l&uacute;c nắng l&uacute;c mưa thất thường dễ khiến cơ thể bức bối m&agrave; ph&aacute;t sinh mụn tr&ecirc;n da. Đặc biệt với điều kiện tốt như thế th&igrave; vi khuẩn sẽ g&acirc;y n&ecirc;n nhiều ổ mụn trứng c&aacute; g&acirc;y mất thẫm mỹ v&agrave; kh&oacute; chịu cho bạn. Vậy&nbsp;c&aacute;ch trị mụn trứng c&aacute; hiệu quả&nbsp;ra sao th&igrave; mới trị được dứt điểm nhiều nốt mụn trứng c&aacute; như thế? H&atilde;y c&ugrave;ng Svan t&igrave;m hiểu v&agrave; thực hiện ngay nh&eacute;!</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nguy&ecirc;n nh&acirc;n xuất hiện mụn</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Trong cơ thể ch&uacute;ng ta c&oacute; hệ b&agrave;i tiết c&ugrave;ng nội sắc tố, việc mụn xuất hiện nhiều hay &iacute;t m&agrave;u đậm hay nhạt đều phụ thuộc v&agrave;o c&aacute;c yếu tố ch&iacute;nh n&agrave;y. Đặc biệt mụn thường xuất hiện nhiều nhất ở những người c&oacute; l&agrave;n da dầu v&igrave; c&aacute;c hoạt động b&agrave;i tiết dưới da kh&ocirc;ng ổn định g&acirc;y t&igrave;nh trạng vi&ecirc;m nhiễm nang l&ocirc;ng khiến cho vi khuẩn P.acnes sinh s&ocirc;i tạo ổ dịch nhiễm tr&ugrave;ng tạo n&ecirc;n mụn trứng c&aacute;. Trong thời gian n&agrave;y bạn kh&ocirc;ng chăm s&oacute;c v&agrave; khắc phục sớm th&igrave; dần dần mụn trứng c&aacute; sẽ l&acirc;y lan sang c&aacute;c v&ugrave;ng da l&acirc;n cận l&agrave;m cho khu&ocirc;n mặt vừa ngứa ng&aacute;y vừa sưng đỏ v&igrave; vết mụn. Vậy n&ecirc;n thay v&igrave; ngồi đợi mụn từ biến mất h&atilde;y &aacute;p dụng c&aacute;c c&aacute;ch trị mụn trứng c&aacute; hiệu quả tại nh&agrave; gi&aacute; rẻ để lấy lại vẻ đẹp rạng ngời cho l&agrave;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"kem-tri-mun-3\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/kem-tri-mun-3.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/kem-tri-mun-3.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Trị mụn bằng tỏi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">T&iacute;nh kh&aacute;ng khuẩn của tỏi rất mạnh do đ&oacute; cho d&ugrave; l&agrave; mụn bọc hay mụn mủ đều cũng r&uacute;t lui khi gặp n&oacute;. Do đ&oacute;, d&ugrave;ng tỏi để trị mụn trứng cả hiệu quả rất đ&aacute;ng để c&aacute;c bạn mong chờ. Lấy 2 củ tỏi nhỏ lột vỏ rồi gi&atilde; thật l&agrave; nhuyễn đắp l&ecirc;n ngay v&ugrave;ng da bị mụn trứng c&aacute; ngo&agrave;i ra bạn cũng c&oacute; thể nướng sơ qua qua tỏi cho vừa n&oacute;ng v&agrave; cắt l&aacute;t mỏng đắp l&ecirc;n v&ugrave;ng mụn. Lưu &yacute; nhỏ l&agrave; trong tỏi c&oacute; t&iacute;nh oxy h&oacute;a cao do đ&oacute; kh&ocirc;ng n&ecirc;n để qu&aacute; l&acirc;u tr&ecirc;n da, tốt nhất l&agrave; bạn b&ocirc;i qua một lớp mỏng để trong v&ograve;ng 10 ph&uacute;t rồi rửa sạch lại với nước lạnh. Tinh chất trong tỏi sẽ gi&uacute;p c&ugrave;i mụn dưới da gom th&agrave;nh 1 chỗ v&agrave; ch&iacute;n rồi tự rụng ra b&ecirc;n ngo&agrave;i m&agrave; kh&ocirc;ng cần thiết phải nặn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"tac-dung-than-ky-cua-toi\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tac-dung-than-ky-cua-toi.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tac-dung-than-ky-cua-toi.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Trị mụn bằng chanh</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m n&agrave;y vừa thiết thực vừa rẻ tiền v&ocirc; c&ugrave;ng, c&ocirc;ng năng của chanh trong đời sống v&agrave; l&agrave;m đẹp th&igrave; nhiều rồi. Tuy nhi&ecirc;n hiệu quả m&agrave; n&oacute; mang lại trong điều trị mụn trứng c&aacute; th&igrave; bạn phải trực tiếp thử tr&ecirc;n ch&iacute;nh m&igrave;nh mới cảm nhận được hết những bất ngờ. Vitamin C trong chanh kh&ocirc;ng chỉ l&agrave;m s&aacute;ng da m&agrave; c&ograve;n c&oacute; t&aacute;c dụng s&aacute;t khuẩn, ti&ecirc;u diệt những ổ mụn xấu x&iacute; kh&ocirc;ng cho ch&uacute;ng c&oacute; cơ hội sinh s&ocirc;i.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"chanh\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/chanh.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/chanh.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Từ đ&oacute; mụn tự động ch&iacute;n v&agrave; trồi l&ecirc;n tr&ecirc;n bề mặt da dễ d&agrave;ng v&agrave; kh&ocirc;ng l&acirc;y lan ra những v&ugrave;ng xung quanh g&acirc;y mất thẩm mỹ. Bạn n&ecirc;n kết hợp d&ugrave;ng th&ecirc;m kem chống nắng để da kh&ocirc;ng bị sạm đi nh&eacute;.</p>\n', 'cach-tri-mun-trung-ca-hieu-qua-an-toan-tuyet-doi-vua-tui-tien', '/Upload/News/b2e3b978-3fb0-4826-9e4f-f21b62e9533b/tac-dung-than-ky-cua-toi.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách trị mụn trứng cá hiệu quả an toàn tuyệt đối vừa túi tiền', 'Cách trị mụn trứng cá hiệu quả là một trong những mối quan tâm lo ngại của người muốn có làn da đẹp và sạch mụn. Cùng nhau tìm hiểu và áp dụng nha!', 'cách trị mụn trứng cá hiệu quả', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(73, '3', 'Những cách trị mụn hiệu quả trả lại làn da trắng sáng', '<p>&Aacute;p dụng nhanh&nbsp;những c&aacute;ch trị mụn hiệu quả&nbsp;dưới đ&acirc;y để lấy lại sức sống cho l&agrave;n da. Kh&ocirc;ng những gi&uacute;p l&agrave;m l&agrave;n da sạch mụn m&agrave; c&ograve;n trắng s&aacute;ng l&ecirc;n mỗi ng&agrave;y.</p>\n', '<p data-mce-style=\"text-align: justify;\">&Aacute;p dụng nhanh&nbsp;những c&aacute;ch trị mụn hiệu quả&nbsp;dưới đ&acirc;y để lấy lại sức sống cho l&agrave;n da. Kh&ocirc;ng những gi&uacute;p l&agrave;m l&agrave;n da sạch mụn m&agrave; c&ograve;n trắng s&aacute;ng l&ecirc;n mỗi ng&agrave;y.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"tam-biet-lan-da-san-sui-voi-cach-tri-mun-hieu-qua-bang-ipl-1\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tam-biet-lan-da-san-sui-voi-cach-tri-mun-hieu-qua-bang-ipl-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tam-biet-lan-da-san-sui-voi-cach-tri-mun-hieu-qua-bang-ipl-1.jpg\" style=\"width: 500px; height: 334px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Trị mụn với rau củ quả</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Rau củ quả l&agrave; những nguy&ecirc;n liệu cũng cấp nhiều vitamin, tinh chất s&aacute;t khuẩn, dưỡng ẩm rất tốt cho l&agrave;n da. Do đ&oacute;, khi bạn sử dụng rau củ quả l&agrave;m mặt nạ trị mụn cho da đảm bảo sẽ an to&agrave;n m&agrave; c&ograve;n tiết kiệm được tối đa kinh ph&iacute;. Thử xem qua c&aacute;c bước điều chế những c&aacute;ch trị mụn hiệu quả với rau củ quả b&ecirc;n dưới.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn hiệu quả với chanh :&nbsp;vitamin C của chanh c&oacute; t&aacute;c dụng l&agrave;m triệt h&oacute;a c&aacute;c đầu nh&acirc;n mụn khiến ch&uacute;ng kh&ocirc; v&agrave; rụng đi chỉ sau v&agrave;i lần &aacute;p dụng. Đồng thời t&iacute;nh axit của chanh c&ograve;n c&oacute; t&aacute;c dụng tẩy c&aacute;c tế b&agrave;o chết tr&ecirc;n da gi&uacute;p lỗ ch&acirc;n l&ocirc;ng th&ocirc;ng tho&aacute;ng hơn trước c&aacute;c bước dưỡng ẩm kh&aacute;c nhau. Bạn cắt một &iacute;t l&aacute;t chanh v&agrave; đắp đều l&ecirc;n v&ugrave;ng da c&oacute; mụn hoặc pha 3 muỗng nước cốt chanh c&ugrave;ng 2 muỗng mật ong h&ograve;a tan v&agrave;o nhau v&agrave; thoa l&ecirc;n v&ugrave;ng da bị mụn. C&aacute;ch n&agrave;y chỉ n&ecirc;n thực hiện 1 tuần 2 lần v&igrave; axit trong chanh &iacute;t nhiều sẽ b&agrave;o m&ograve;n đi l&agrave;n da do đ&oacute; bạn cũng n&ecirc;n sử dụng kem chống nắng thường xuy&ecirc;n trước khi ra ngo&agrave;i.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"5-loai-trai-cay-bo-sung-vitamin-cho-ngay-he\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/5-loai-trai-cay-bo-sung-vitamin-cho-ngay-he.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/5-loai-trai-cay-bo-sung-vitamin-cho-ngay-he.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn hiệu quả với củ nghệ :&nbsp;c&aacute;ch n&agrave;y được chị em phụ nữ ưa chuộng nhất hiện nay. Nghệ tươi kh&ocirc;ng chỉ d&ugrave;ng để l&agrave;m l&agrave;nh vết thương hở, trị sẹo th&acirc;m m&agrave; c&ograve;n trị mụn rất nhanh ch&oacute;ng nhờ chứa nhiều tinh chất chống oxy h&oacute;a tốt. Bạn c&oacute; thể trực tiếp dụng nghệ tươi b&ocirc;i l&ecirc;n da để qua đ&ecirc;m để trị mụn ngo&agrave;i ra c&aacute;ch tốt hơn l&agrave; bạn lấy 1 &iacute;t bột nghệ sấy kh&ocirc; pha chung với mật ong đắp l&ecirc;n để y&ecirc;n cho đến khi kh&ocirc; mặt nạ th&igrave; đi rửa mặt với sửa rữa mặt để lớp v&agrave;ng bột nghệ tr&ocirc;i đi được sạch hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"nghe\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/nghe.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/nghe.jpg\" style=\"width: 500px; height: 325px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Những c&aacute;ch trị mụn khoa học</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Ngo&agrave;i những c&aacute;ch trị mụn bằng rau củ quả bạn n&ecirc;n kết hợp ăn uống v&agrave; sinh hoạt giờ giấc khoa học để ph&ograve;ng tr&aacute;nh mụn trước khi n&oacute; h&igrave;nh th&agrave;nh. Nhiều người cơ địa tốt bẩm sinh họ kh&ocirc;ng cần qu&aacute; ki&ecirc;ng khem tuy nhi&ecirc;n nếu bạn l&agrave; người c&oacute; cơ địa kh&ocirc;ng tốt th&igrave; n&ecirc;n hạn chế ăn c&aacute;c loại đồ n&oacute;ng, cay v&agrave; gi&agrave;u đạm. V&igrave; ch&iacute;nh những thực phẩm n&agrave;y l&agrave; một trong những nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y l&atilde;o h&oacute;a v&agrave; mụn tr&ecirc;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"dieuchinhgiosinhhoathoplydetranhungthungaynay\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/Dieuchinhgiosinhhoathoplydetranhungthungaynay.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/Dieuchinhgiosinhhoathoplydetranhungthungaynay.jpg\" style=\"width: 500px; height: 332px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">K&egrave;m theo đ&oacute; đối với c&aacute;nh m&agrave;y r&acirc;u th&igrave; n&ecirc;n hạn chế h&uacute;t thuốc l&aacute;, uống nhiều c&agrave; ph&ecirc; v&agrave; rượu bia. Mỗi người n&ecirc;n h&igrave;nh th&agrave;nh th&oacute;i quen nghỉ ngơi đ&uacute;ng giờ giấc v&agrave; vui chơi hợp l&yacute; để giải tỏa căng thẳng sau một ng&agrave;y l&agrave;m việc mệt nhọc. Khoa học đ&atilde; chứng minh khi cơ thể bạn ở trạng th&aacute;i thoải m&aacute;i th&igrave; c&aacute;c hoạt động b&agrave;i tiết mới thật sự ổn định v&agrave; l&agrave;m đẹp l&agrave;n da.</p>\n', 'nhung-cach-tri-mun-hieu-qua-tra-lai-lan-da-trang-sang', '/Upload/News/937dbc68-9075-4e5c-a550-6e268832c72d/5-loai-trai-cay-bo-sung-vitamin-cho-ngay-he.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Những cách trị mụn hiệu quả trả lại làn da trắng sáng', 'Áp dụng nhanh những cách trị mụn hiệu quả dưới đây để lấy lại sức sống cho làn da. Không những giúp da sạch mụn mà còn trắng sáng lên mỗi ngày.', 'những cách trị mụn hiệu quả', '44', 'PUBLISHED', '', 'old'),
(74, '3', 'Cách làm trắng da toàn thân nhanh nhất tại nhà cho chị em phụ nữ', '<p>C&oacute; được l&agrave;n da s&aacute;ng hồng kh&ocirc;ng tỳ vết l&agrave; mong ước của rất nhiều chị em phụ nữ. Svan Clinic &amp; Spa mong rằng&nbsp;c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n nhanh nhất tại nh&agrave;&nbsp;sau đ&acirc;y sẽ c&oacute; hiệu quả.</p>\n', '<p data-mce-style=\"text-align: justify;\">C&oacute; được l&agrave;n da s&aacute;ng hồng kh&ocirc;ng tỳ vết l&agrave; mong ước của rất nhiều chị em phụ nữ. Svan Clinic &amp; Spa mong rằng&nbsp;c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n nhanh nhất tại nh&agrave;&nbsp;sau đ&acirc;y sẽ c&oacute; hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nuoc_vo_gao_6.jpg\" style=\"width: 500px; height: 500px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Mặc d&ugrave; phong tr&agrave;o &quot; Da n&acirc;u &quot; khỏe khoắn đang trở th&agrave;nh mốt tại Việt Nam. Tuy nhi&ecirc;n kh&ocirc;ng phải bất cứ c&ocirc; n&agrave;ng n&agrave;o cũng hợp với n&oacute;, đặc biệt những c&ocirc; n&agrave;ng c&oacute; chiều cao khi&ecirc;m tốn th&igrave; nước da s&aacute;ng trắng mới l&agrave; lựa chọn th&iacute;ch hợp nhất. Chỉ cần ở nh&agrave; v&agrave; &aacute;p dụng ngay những c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n sau đ&acirc;y để da s&aacute;ng v&agrave; sạch nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<p alt=\"nuoc_vo_gao_6\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/nuoc_vo_gao_6.jpg\" src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/nuoc_vo_gao_6.jpg\">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>L&aacute; l&ocirc; hội</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">L&aacute; l&ocirc; hội được xem l&agrave; dược liệu thần k&igrave; trong điều chế c&aacute;c phương ph&aacute;p l&agrave;m đẹp kh&aacute;c nhau gi&agrave;nh cho chị em phụ nữ. Từ ng&agrave;y xưa &ocirc;ng b&agrave; ta đ&atilde; biết d&ugrave;ng l&ocirc; hội để l&agrave;m dịu vết thương bỏng, nấu ch&egrave; thanh nhiệt giải độc cơ thể. Ng&agrave;y nay l&ocirc; hội trở th&agrave;nh b&iacute; quyết l&agrave;m trắng da to&agrave;n th&acirc;n tại nh&agrave; nhanh ch&oacute;ng v&agrave; hiệu quả kh&ocirc;ng k&eacute;m.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/lo-hoi-2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<p alt=\"lo-hoi-2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/lo-hoi-2.jpg\" src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/lo-hoi-2.jpg\">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn n&ecirc;n lựa chọn những t&eacute;p l&aacute; l&ocirc; hội vừa tầm tay, kh&ocirc;ng qu&aacute; to v&igrave; dễ bị thuốc trừ s&acirc;u, thuốc tăng trưởng độc hại. Sau đ&oacute;, bạn rửa sạch bụi đất v&agrave; gọt bỏ phần vỏ ngo&agrave;i đi chỉ giữ lại phần c&ugrave;i thịt l&agrave; v&igrave; để ko mất đi lớp gel tốt nhất c&oacute; trong l&ocirc; hội. Tiếp theo bạn d&ugrave;ng phần c&ugrave;i thịt bỏ v&agrave;o m&aacute;y xay sinh tố xay thật nhuyễn v&agrave; trộn chung với sữa tươi kh&ocirc;ng đường đắp l&ecirc;n v&ugrave;ng da cần l&agrave;m trắng. Để y&ecirc;n trong v&ograve;ng 20 ph&uacute;t rồi massage nhẹ nhạng dọc theo chiều l&ocirc;ng mọc để c&aacute;c dưỡng chất thấm v&agrave;o b&ecirc;n trong nang l&ocirc;ng nu&ocirc;i dưỡng l&agrave;n da mỗi ng&agrave;y.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch đơn giản hơn v&agrave; kh&ocirc;ng mất thời gian qu&aacute; nhiều l&agrave; bạn rửa sạch l&ocirc; hội v&agrave; chẻ n&oacute; ra l&agrave;m hai sau đ&oacute; trực tiếp ch&agrave; s&aacute;t nhẹ l&ecirc;n l&agrave;n da. Thao t&aacute;c n&agrave;y gi&uacute;p lấy đi lớp sừng chết tr&ecirc;n da, l&agrave;m da s&aacute;ng mịn l&ecirc;n &iacute;t nhiều. &Aacute;p dụng thường xuy&ecirc;n để c&oacute; hiệu quả như mong muốn.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Chanh</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">T&iacute;nh axit trong chanh gi&uacute;p loại bỏ độc tố, thanh lọc cơ thể đồng thời l&agrave; th&agrave;nh phần kh&ocirc;ng thể thiếu trong c&aacute;c bước l&agrave;m trắng da tại nh&agrave;. Dưỡng chất vitamin C đi s&acirc;u v&agrave;o n&agrave;ng l&ocirc;ng vừa l&agrave;m nang l&ocirc;ng tự ch&iacute;n rồi rụng đi, lỗ ch&acirc;n l&ocirc;ng se kh&iacute;t cũng l&agrave;m da trong l&aacute;ng mịn hơn hẳn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/tac-dung-cua-chanh.jpg\" style=\"width: 500px; height: 389px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;</p>\n\n<p alt=\"tac-dung-cua-chanh\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tac-dung-cua-chanh.jpg\" src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tac-dung-cua-chanh.jpg\">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p data-mce-style=\"text-align: justify;\">Do đ&oacute;, 3 lần/ tuần bạn đều đặn vắt nước cốt chanh vừa đủ d&ugrave;ng sau đ&oacute; h&ograve;a chung với 1 &iacute;t mật ong v&agrave; sữa tươi kh&ocirc;ng đường d&ugrave;ng massage trước l&uacute;c tắm 30 ph&uacute;t cho tinh chất hỗn hợp t&aacute;c dụng nhanh v&agrave;o trong l&agrave;n da. Sự kết hợp tuyệt với của ba nguy&ecirc;n liệu tr&ecirc;n đ&aacute;nh bay đi sắc tố melanin g&acirc;y đen sạm, đồng thời l&agrave;m mềm da v&agrave; cung cấp đổ ẩm cần thiết. Bạn cũng cần kết hợp th&ecirc;m kem chống nắng trước khi đi ra ngo&agrave;i &aacute;nh s&aacute;ng để l&agrave;n da lu&ocirc;n được bảo vệ tối đa.</p>\n', 'cach-lam-trang-da-toan-than-nhanh-nhat-tai-nha-cho-chi-em-phu-nu', '	/Upload/News/e4a6b3f9-8eee-457b-862b-8cbb285ac959/lo-hoi-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách làm trắng da toàn thân nhanh nhất tại nhà cho chị em phụ nữ', 'Có được làn da sáng hồng không tỳ vết là mong ước của rất nhiều chị em phụ nữ. Svan mong rằng cách làm trắng da toàn thân nhanh nhất tại nhà sẽ có hiệu quả', 'cách làm trắng da toàn thân nhanh nhất tại nhà', '44', 'PUBLISHED', '', 'old'),
(75, '3', 'Cách làm trắng da tự nhiên với thảo dược an toàn', '<p>Vẻ đẹp của l&agrave; da thật sự rất quan trọng với người phụ nữ hiện đại, sở hữu một v&oacute;c d&aacute;ng chuẩn - một l&agrave;n da trắng mịn sẽ gi&uacute;p bạn nổi bật v&agrave; tự tin bất cứ l&uacute;c n&agrave;o.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">Vẻ đẹp của l&agrave; da thật sự rất quan trọng với người phụ nữ hiện đại, sở hữu một v&oacute;c d&aacute;ng chuẩn - một l&agrave;n da trắng mịn sẽ gi&uacute;p bạn nổi bật v&agrave; tự tin bất cứ l&uacute;c n&agrave;o. Do đ&oacute;,&nbsp;c&aacute;ch l&agrave;m trắng da tự nhi&ecirc;n&nbsp;với thảo dược sau đ&acirc;y đảm bảo gi&uacute;p bạn giải quyết ngay c&aacute;c sắc tố xấu&nbsp;g&acirc;y hại cho da.</p>\n\n<p style=\"text-align: center;\"><img alt=\"tr\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tr.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tr.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Ngải cứu:</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Thật ra rất nhiều chị em phụ nữ chưa d&aacute;m &aacute;p dụng ngải cứu trong việc l&agrave;m tr&aacute;nh da một phần v&igrave; chưa hiểu hết c&ocirc;ng dụng của n&oacute;. Tuy nhi&ecirc;n, nếu bạn l&agrave; t&iacute;n đồ y&ecirc;u l&agrave;m đẹp th&igrave; kh&ocirc;ng thể bỏ qua c&aacute;ch l&agrave;m trắng da nhẹ nh&agrave;ng v&agrave; đơn giản n&agrave;y. Ngải cứu được xem l&agrave; thần dược v&igrave; c&ocirc;ng dụng lưu th&ocirc;ng tuần ho&agrave;n m&aacute;u, nu&ocirc;i dưỡng tế b&agrave;o m&aacute;u để da săn chắc v&agrave; hồng h&agrave;o hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/cay-ngai-cuu.jpg\" style=\"width: 500px; height: 294px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch chế biến dung dịch tắm trắng ngải cứu cũng cực k&igrave; đơn giản. Bạn chọn mua một nắm l&aacute; ngải cứu tươi về bỏ l&ecirc;n luộc ch&iacute;n với nước cho đến khi c&aacute;c dinh dưỡng của ngải cứu h&ograve;a tan v&agrave;o nước. Tắt bếp để nguội rồi lấy một tấm vải mỏng lọc lấy b&atilde; để ri&ecirc;ng ra b&ecirc;n ngo&agrave;i, phần nước ch&iacute;n d&ugrave;ng để tắm mỗi ng&agrave;y. Trước khi tắm bạn n&ecirc;n lấy b&atilde; ngải cứu ch&agrave; nhẹ l&ecirc;n da trước để loại bỏ lớp sừng. Việc l&agrave;m n&agrave;y gi&uacute;p cho c&aacute;c dưỡng chất trong ngải cứu ngấm s&acirc;u hơn v&agrave;o b&ecirc;n trong l&agrave;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nha đam</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nha đam l&agrave; thảo dược chứa nhiều vitamin A, B,.. c&ugrave;ng nhiều dưỡng chất t&iacute;ch hợp kh&aacute;c gi&uacute;p s&aacute;t khuẩn v&agrave; l&agrave;m s&aacute;ng da r&otilde; rệt. Ngo&agrave;i ra loại thảo dược n&agrave;y c&ograve;n rẻ tiền v&agrave; dễ t&igrave;m kiếm, bạn c&oacute; thể tự trồng ch&uacute;ng ở nh&agrave; v&agrave; sử dụng thường xuy&ecirc;n để đạt hiểu quả nhiều hơn. Nếu hiện tại bạn chưa c&oacute; nha đam trong nh&agrave; th&igrave; n&ecirc;n mua một &iacute;t trong si&ecirc;u thị để tr&aacute;nh nhiễm h&oacute;a chất độc hại. Sau khi mua về bạn t&aacute;ch vỏ v&agrave; rửa sạch bằng nước muối cho đến khi nha đam bớt nhớt. Thật ra th&igrave; lớp gel nhớp của nha đam mới l&agrave; dưỡng chất tốt nhất cho cơ thể tuy nhi&ecirc;n nhiều bạn da qu&aacute; nhạy cảm sẽ dễ bị ngứa. Tiếp theo bạn bỏ nha đam v&agrave;o m&aacute;y xay chung với 1 hũ sữa chua kh&ocirc;ng đường v&agrave; 4 muỗng mật ong. Xay cho hỗn hợp thật l&agrave; nhuyễn v&agrave; b&ocirc;i đều l&ecirc;n v&ugrave;ng cần l&agrave;m trắng da, massage trong v&ograve;ng 30 ph&uacute;t sau đ&oacute; tắm lại bằng nước n&oacute;ng.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cay-ngai-cuu\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cay-ngai-cuu.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tac-dung-cua-nha-dam-trong-viec-tri-mun-1.jpg\" style=\"text-align: center; width: 500px; height: 334px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Lưu &yacute; : cho d&ugrave; bạn l&agrave;m trắng da bằng c&aacute;ch n&agrave;o th&igrave; cũng n&ecirc;n bảo vệ da từ s&acirc;u b&ecirc;n trong cơ thể bằng việc kết hợp ăn v&agrave; uống nhiều loại thực phẩm l&agrave;m m&aacute;t da v&agrave; thanh lọc cơ thể. Đồng thời phải bảo vệ da b&ecirc;n ngo&agrave;i như sử dụng kem chống nắng v&agrave; c&aacute;c loại tinh chất dưỡng da cần thiết để c&acirc;n bằng độ ẩm cho l&agrave;n da của bản th&acirc;n.</p>\n', 'cach-lam-trang-da-tu-nhien-voi-thao-duoc-an-toan', '/Upload/News/75cebe81-e517-4146-93ce-801283ff8e04/tr.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách làm trắng da tự nhiên với thảo dược an toàn', 'Vẻ đẹp của là da rất quan trọng với người phụ nữ. Vì vậy, cách làm trắng da tự nhiên với thảo dược sau đây đảm bảo giúp bạn giải quyết sắc tố xấu của làn da', 'cách làm trắng da tự nhiên', '44', 'PUBLISHED', '', 'old'),
(76, '3', 'Tắm trắng toàn thân tại nhà da sáng khỏe ngọc ngà', '<p>&quot; Nhất d&aacute;ng nh&igrave; da &quot; l&agrave; kh&aacute;i niệm đ&aacute;nh gi&aacute; c&aacute;i đẹp của nhiều người. V&igrave; thế, nhiều chị em cố gắng để sở hữu l&agrave;n da trắng mịn kh&ocirc;ng tỳ vết với nhiều phương ph&aacute;p kh&aacute;c nhau.</p>\n', '<p data-mce-style=\"text-align: justify;\">&quot; Nhất d&aacute;ng nh&igrave; da &quot; l&agrave; kh&aacute;i niệm đ&aacute;nh gi&aacute; c&aacute;i đẹp của nhiều người. V&igrave; thế, nhiều chị em cố gắng để sở hữu l&agrave;n da trắng mịn kh&ocirc;ng tỳ vết với nhiều phương ph&aacute;p kh&aacute;c nhau. Tuy nhi&ecirc;n, nếu kh&ocirc;ng biết c&aacute;ch sử dụng hoặc lạm dụng qu&aacute; nhiều sẽ l&agrave;m da m&igrave;nh bị lờn hoặc m&ograve;n đi g&acirc;y hậu quả ph&iacute;a sau. Quan t&acirc;m vấn đề n&agrave;y n&ecirc;n Svan Clinic &amp; Spa đem đến cho bạn nhiều phương ph&aacute;p&nbsp;tắm trắng to&agrave;n th&acirc;n tại nh&agrave;&nbsp;da s&aacute;ng khỏe ngọc ng&agrave;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tam-trang-toan-than-sieu-toc-voi-cong-nghe-sinh-hoc-frozen-laser-01-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tam-trang-toan-than-sieu-toc-voi-cong-nghe-sinh-hoc-frozen-laser-01-2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Bột ngũ cốc</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Phương ph&aacute;p n&agrave;y kh&aacute; mới lạ với một số người tại Việt Nam nhưng n&oacute; lại đem hiệu quả cực bất ngờ cho l&agrave;n da của phụ nữ Th&aacute;i Lan. Vậy tại sao chị em phụ nữ Việt kh&ocirc;ng thử &aacute;p dụng c&aacute;ch n&agrave;y cho l&agrave;n da của ch&iacute;nh m&igrave;nh? Kh&iacute; hậu của Th&aacute;i Lan cũng tương tự như ch&uacute;ng ta do đ&oacute; đặc điểm l&agrave;n da c&ugrave;ng c&aacute;ch điều trị l&agrave; như nhau.</p>\n\n<p data-mce-style=\"text-align: justify;\">Phụ nữ Th&aacute;i thường sử dụng 5 loại đậu sau đ&acirc;y để pha chế hỗn hợp l&agrave;m đẹp như sau :&nbsp;đậu đen, đậu xanh, đậu đỏ v&agrave; đậu tương,&nbsp;bột c&aacute;m gạo. Với c&aacute;c loại đậu n&agrave;y, họ tự đem phơi kh&ocirc; rồi rang l&ecirc;n sau đ&oacute; đem đi xay nhuyễn v&agrave; trộn theo liều lượng nhất định để nơi tho&aacute;ng m&aacute;t để d&ugrave;ng dần.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/nhung-thuc-pham-khac-phu-con-doi-khi-giam-can1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/nhung-thuc-pham-khac-phu-con-doi-khi-giam-can1.jpg\" style=\"width: 500px; height: 332px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch d&ugrave;ng :&nbsp;trước khi tắm bạn lấy một lượng vừa đủ hỗn hợp đậu cho v&agrave;o một c&aacute;i b&aacute;t, đổ th&ecirc;m 1 bịch sữa tươi v&agrave; 3 muỗng mật ong. Trộn cho hỗn hợp s&aacute;nh lại rồi b&ocirc;i đều l&ecirc;n v&ugrave;ng da muốn tắm trắng, để khoảng 10 ph&uacute;t cho hỗn hợp thấm đều. Tiếp theo massage đều tay dọc theo chiều l&ocirc;ng mọc để k&iacute;ch th&iacute;ch nang l&ocirc;ng mở đều thấm tinh chất dưỡng da v&agrave;o s&acirc;u b&ecirc;n trong hơn. Tắm sạch lại sau 30 ph&uacute;t masage bạn sẽ thấy da m&aacute;t mịn hơn rất nhiều. &Aacute;p dụng thường xuy&ecirc;n cho cả v&ugrave;ng da mặt để l&agrave;m trắng s&aacute;ng lu&ocirc;n nh&eacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Rượu v&agrave; nước tr&agrave; tươi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Nước tr&agrave; c&oacute; t&aacute;c dụng thanh nhiệt, giải độc cơ thế từ b&ecirc;n trong v&agrave; chống l&atilde;o h&oacute;a da, se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng b&ecirc;n ngo&agrave;i. Kết hợp với t&aacute;c dụng tẩy lớp sừng chết, l&agrave;m sạch s&acirc;u b&ecirc;n trong ch&acirc;n l&ocirc;ng của rượu trắng th&igrave; đ&acirc;y l&agrave; hỗn hợp th&iacute;ch hợp cho những c&oacute; n&agrave;ng c&oacute; l&agrave;n da kh&ocirc; r&aacute;p nhất. Đối với c&ocirc; n&agrave;ng da nhạy cảm cũng y&ecirc;n t&acirc;m phần n&agrave;o v&igrave; đặc t&iacute;nh trung h&ograve;a của hỗn hợp kh&ocirc;ng ảnh hưởng nhiều đến l&agrave;n da của bạn nha.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tam-trang-tai-nha-anh1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/tam-trang-tai-nha-anh1.jpg\" style=\"width: 500px; height: 261px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m :&nbsp;bạn cho nước tr&agrave; mới pha ra b&aacute;t lớn, phần b&atilde; tr&agrave; c&oacute; thể d&ugrave;ng để massage nhẹ sơ qua tr&ecirc;n da một lần trước khi tắm với nước lạnh để l&agrave;m sạch bụi bẩn b&aacute;m ph&iacute;a tr&ecirc;n. Tiếp theo bạn cho rượu v&agrave;o b&aacute;t tr&agrave; rồi d&ugrave;ng khăn mỏng hoặc b&ocirc;ng y tế thấm đều l&ecirc;n da, thoa nhẹ nh&agrave;ng để tr&aacute;nh l&agrave;m tổn thương trầy xướt da. Thời gian massage tốt nhất l&agrave; từ 20 - 30 ph&uacute;t v&igrave; l&uacute;c n&agrave;y lỗ ch&acirc;n l&ocirc;ng bắt đầu thấm hết tinh chất trong hỗn hợp. &Aacute;p dụng một tuần 3 lần để da khỏe v&agrave; trắng mịn hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Hy vọng rằng những c&aacute;ch tắm trắng tr&ecirc;n sẽ gi&uacute;p bạn c&oacute; một l&agrave;n da đẹp như mong muốn!</p>\n', 'tam-trang-toan-than-tai-nha-da-sang-khoe-ngoc-nga', '/Upload/News/b10a58ea-4e46-4153-8cd2-fbc94bbd7291/tam-trang-toan-than-sieu-toc-voi-cong-nghe-sinh-hoc-frozen-laser-01-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Tắm trắng toàn thân tại nhà da sáng khỏe ngọc ngà', 'Có rất nhiều phương pháp tắm trắng toàn thân tại nhà nhưng không phải ai cũng biết cách làm và sử dụng để đạt hiệu quả trắng da như mong muốn.', 'tắm trắng toàn thân tại nhà', '44', 'PUBLISHED', '', 'old'),
(77, '3', 'Căng da mặt không phẫu thuật giá bao nhiêu là phù hợp?', '<p>Căng da mặt kh&ocirc;ng phẫu thuật gi&aacute; bao nhi&ecirc;u&nbsp;l&agrave; ph&ugrave; hợp với thu nhập của nhiều phụ nữ hiện nay? C&acirc;u hỏi n&agrave;y sẽ được Svan Clinic &amp; Spa trả lời ngay dưới b&agrave;i viết n&agrave;y.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">Căng da mặt kh&ocirc;ng phẫu thuật gi&aacute; bao nhi&ecirc;u&nbsp;l&agrave; ph&ugrave; hợp với thu nhập của nhiều phụ nữ hiện nay? C&acirc;u hỏi n&agrave;y sẽ được Svan Clinic &amp; Spa trả lời ngay dưới b&agrave;i viết n&agrave;y. Bạn ho&agrave;n to&agrave;n chủ động về mặt chi ph&iacute; v&agrave; &aacute;p dụng triệt để c&aacute;c c&aacute;ch căng da mặt tại nh&agrave; an to&agrave;n v&agrave; hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cang-da-mat\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cang-da-mat.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cang-da-mat.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">CĂNG DA MẶT BẰNG PHƯƠNG PH&Aacute;P TỰ NHI&Ecirc;N</p>\n\n<p data-mce-style=\"text-align: justify;\">Với những c&aacute;ch căng da tự nhi&ecirc;n&nbsp;bạn kh&ocirc;ng c&ograve;n đặt nặng c&acirc;u hỏi căng da mặt kh&ocirc;ng phẫu thuật gi&aacute; bao nhi&ecirc;u mỗi ng&agrave;y nữa.&nbsp;Mặt nạ l&agrave;m căng da mặt từ thi&ecirc;n nhi&ecirc;n sẽ gi&uacute;p da bạn tươi trẻ v&agrave; x&oacute;a nếp nhăn hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\">Căng da mặt với trứng g&agrave; :&nbsp;c&aacute;ch căng da n&agrave;y tiện dụng v&agrave; gi&aacute; th&igrave; tiết kiệm chi ph&iacute; rất nhiều lu&ocirc;n đấy. Bạn chỉ cần 2 quả trứng g&agrave;, nhớ l&agrave; chỉ lấy l&ograve;ng trắng trứng th&ocirc;i nha sau đ&oacute; h&ograve;a th&ecirc;m v&agrave;o v&agrave;i giọt nước chanh rồi đ&aacute;nh đều l&ecirc;n. Rửa sạch v&ugrave;ng da mặt rồi đắp l&ecirc;n, đợi đến khi thấy da mặt kh&ocirc; th&igrave; rửa lại với nước ấm. Sử dụng 1 tuần 3 lần để da mặt căng mịn v&agrave; s&aacute;ng khỏe nha.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cang-da-mat-voi-long-trang-trung-ga-jpg2_\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cang-da-mat-voi-long-trang-trung-ga.jpg2_.png\" src=\"/files/Files/Kien-thuc-lam-dep/cang-da-mat-voi-long-trang-trung-ga.jpg\" style=\"width: 500px; height: 292px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Căng da mặt với đu đủ v&agrave; sữa tươi :&nbsp;Enzyme Papain c&oacute; nhiều trong đu đủ lấy đi c&aacute;c lớp sừng chết trả lại l&agrave;n da s&aacute;ng khỏe kết hợp với axit lactic trong sữa chua thu nhỏ lỗ ch&acirc;n l&ocirc;ng. Mặt nạ n&agrave;y kh&ocirc;ng chỉ gi&uacute;p bạn căng da mặt m&agrave; c&ograve;n đem lại độ đ&agrave;n hồi, trẻ khỏe từ b&ecirc;n trong gi&agrave;nh cho bạn. Kh&acirc;u chuẩn bị cũng kh&aacute; đơn giản, với hỗn hợp mặt nạ n&agrave;y bạn cần 1 tr&aacute;i đu đủ ch&iacute;n, gọt sạch vỏ rồi đem xay nhuyễn rồi đổ ra một c&aacute;i b&aacute;t. Tiếp theo cho sữa chua k&egrave;m 1 &iacute;t nước &eacute;p dứa v&agrave;o trộn đều l&ecirc;n, rửa sạch vụng da mặt v&agrave; đắp l&ecirc;n massage khoảng 30 ph&uacute;t sau đ&oacute; rửa lại với nước ấm.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"nuoc-ep-tang-vong-nguc-3-blogtamsuvn\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/nuoc-ep-tang-vong-nguc-3-blogtamsuvn.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/nuoc-ep-tang-vong-nguc-3-blogtamsuvn.jpg\" style=\"width: 500px; height: 360px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">SINH HOẠT ĐIỀU ĐỘ GI&Uacute;P CĂNG DA MẶT TỰ NHI&Ecirc;N</p>\n\n<p data-mce-style=\"text-align: justify;\">Nếu bạn c&oacute; kế hoạch chăm s&oacute;c da, vận động cơ thể v&agrave; nghỉ ngơi hợp l&yacute; th&igrave; c&aacute;c yếu tố thời gian hay m&ocirc;i trường sẽ chỉ t&aacute;c động được một &iacute;t v&agrave;o qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a da m&agrave; th&ocirc;i. V&agrave; bạn cũng kh&ocirc;ng cần phải t&igrave;m hiểu c&acirc;u hỏi căng da mặt kh&ocirc;ng phẫu thuật bao nhi&ecirc;u. Một phụ nữ ch&uacute; &yacute; dưỡng da đ&uacute;ng c&aacute;ch sẽ trẻ trung hơn tuổi thật rất nhiều v&agrave; khoa học đ&atilde; chứng minh điều đ&oacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\">Bất kể l&agrave; phụ nữ hay nam giới cũng n&ecirc;n hạn chế h&uacute;t thuốc, uống nhiều rượu bia v&agrave; thức đ&ecirc;m. H&atilde;y lu&ocirc;n để bản th&acirc;n trong một trạng th&aacute;i vui vẻ, lạc quan, thư gi&atilde;n mọi l&uacute;c c&oacute; thể. B&ecirc;n cạnh đ&oacute; n&ecirc;n lựa chọn những loại sản phẩm chăm s&oacute;c da ph&ugrave; hợp với t&igrave;nh trạng da của bản th&acirc;n v&agrave; nắm vững kiến thức chăm s&oacute;c da. Kh&ocirc;ng n&ecirc;n rửa mặt bằng x&agrave; ph&ograve;ng hoặc c&aacute;c loại sửa rữa mặt c&oacute; t&igrave;nh kiềm v&igrave; ch&uacute;ng l&agrave;m da bạn bị kh&ocirc; dể l&ecirc;n mụn v&agrave; l&atilde;o h&oacute;a. Ăn uống n&ecirc;n đ&uacute;ng c&aacute;ch, n&ecirc;n &iacute;t ăn muối v&agrave; uống nhiều nước v&igrave; hơn 70 % trong cơ thể ch&uacute;ng ta l&agrave; nước gi&uacute;p thanh lọc v&agrave; trao đổi chất trong cơ thể được tốt hơn. Từ đ&oacute; c&aacute;c chất độc hại trong m&aacute;u, trong da cũng bị đẩy ra ngo&agrave;i ngay lập tức.</p>\n', 'cang-da-mat-khong-phau-thuat-gia-bao-nhieu-la-phu-hop', '/Upload/News/86165912-03fa-46bb-a68a-5009a0573c64/cang-da-mat.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Căng da mặt không phẫu thuật giá bao nhiêu là phù hợp?', 'Căng da mặt không phẫu thuật giá bao nhiêu là phù hợp với thu nhập của nhiều phụ nữ hiện nay? Câu hỏi này sẽ được Svan trả lời ngay dưới bài viết này.', 'căng da mặt không phẫu thuật giá bao nhiêu', '44', 'PUBLISHED', '', 'old'),
(78, '3', 'Quy chuẩn chăm sóc da mặt tại nhà đúng cách nên áp dụng ngay', '<p>Quy chuẩn&nbsp;chăm s&oacute;c da mặt tại nh&agrave;&nbsp;đ&uacute;ng v&agrave; đủ kh&ocirc;ng chỉ l&agrave;m da bạn sạch mụn m&agrave; c&ograve;n gi&uacute;p bạn th&ecirc;m tự tin với l&agrave;n da trắng hồng, căng mịn.</p>\n', '<p data-mce-style=\"text-align: justify;\">Quy chuẩn&nbsp;chăm s&oacute;c da mặt tại nh&agrave;&nbsp;đ&uacute;ng v&agrave; đủ kh&ocirc;ng chỉ l&agrave;m da bạn sạch mụn m&agrave; c&ograve;n gi&uacute;p bạn th&ecirc;m tự tin với l&agrave;n da trắng hồng, căng mịn. Svan Clinic &amp; Spa mong rằng những kiến thức bổ &iacute;ch m&agrave; ch&uacute;ng t&ocirc;i chia sẻ b&ecirc;n dưới đem lại hiệu quả t&iacute;ch cực cho l&agrave;n da của bạn v&agrave; người th&acirc;n.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Rửa sạch mặt</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"cach-cham-soc-da-mat-hang-ngay-giup-da-trang-min1\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-cham-soc-da-mat-hang-ngay-giup-da-trang-min1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-cham-soc-da-mat-hang-ngay-giup-da-trang-min1.jpg\" style=\"width: 500px; height: 275px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Đ&acirc;y l&agrave; bước rất quan trọng m&agrave; bạn phải lưu &yacute; trong qu&aacute; tr&igrave;nh chăm s&oacute;c v&agrave; nu&ocirc;i dưỡng l&agrave;n da của m&igrave;nh. Như bạn biết sau một ng&agrave;y d&agrave;i hoạt động &iacute;t nhiều l&agrave;n da bị t&aacute;c động trực tiếp bởi bụi bẩn, &aacute;nh s&aacute;ng mặt trời l&agrave;m da của bạn dơ đi v&agrave; b&aacute;m vẩy sừng xấu. Do đ&oacute;, bạn n&ecirc;n rửa sạch mặt với nước lạnh qua 1 lần trước khi d&ugrave;ng sữa rửa mặt. Đặc biệt khi lựa chọn sửa rửa mặt bạn cần xem th&agrave;nh phần của n&oacute; c&oacute; ph&ugrave; hợp với l&agrave;n da nhạy cảm của bản th&acirc;n hay kh&ocirc;ng. Nếu bạn c&oacute; l&agrave;n da kh&ocirc; th&igrave; hạn chế c&aacute;c sản phẩm chăm s&oacute;c da mang t&iacute;nh kiềm nha. Trong qu&aacute; tr&igrave;nh rửa mặt n&ecirc;n kết hợp massage để da săn chắc tăng th&ecirc;m độ đ&agrave;n hồi, n&ecirc;n &iacute;t sử dụng tay để rửa mặt v&igrave; kh&ocirc;ng loại sạch hết được bụi bẩn tr&ecirc;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Tẩy tế b&agrave;o chết</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"cach-cham-soc-da-don-gian-tai-nha\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/Cach-cham-soc-da-don-gian-tai-nha.png\" src=\"/files/Files/Kien-thuc-lam-dep/Cach-cham-soc-da-don-gian-tai-nha.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Nhiều bạn cho rằng rửa mặt qua một lần l&agrave; da đ&atilde; sạch hẳn l&agrave; sai lầm nh&eacute;, v&igrave; &iacute;t nhiều bụi bẩn vẫn c&ograve;n b&aacute;m tr&ecirc;n da tạo th&agrave;nh lớp sừng chết khiến da mặt bạn sần s&ugrave;i v&agrave; lỗ ch&acirc;n l&ocirc;ng to. Ch&iacute;nh v&igrave; thế nếu bạn thật sự muốn loại bỏ hẳn ch&uacute;ng th&igrave; n&ecirc;n tẩy tế b&agrave;o chết 1 tuần 3 lần hoặc l&agrave; &quot; ru ngủ &quot; ch&uacute;ng bằng c&aacute;c loại mặt nạ kh&aacute;c nhau. Điều n&agrave;y gi&uacute;p bạn dưỡng da sẽ thuận lời v&agrave; nhanh ch&oacute;ng hơn. Khi c&aacute;c lớp sừng mất đi th&igrave; lỗ ch&acirc;n l&ocirc;ng th&ocirc;ng tho&aacute;ng gi&uacute;p c&aacute;c dưỡng chất đi s&acirc;u v&agrave;o b&ecirc;n trong da của bạn để nu&ocirc;i dưỡng từng tế b&agrave;o m&ocirc;.</p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn c&oacute; thể sử dụng c&aacute;c loại hỗn hợp tẩy tế b&agrave;o chết tại nh&agrave; như muối hạt, chanh - mật ong, chanh - đường trắng,....massage nhẹ nh&agrave;ng tr&ecirc;n da từ 15 - 20 ph&uacute;t rồi rửa sạch mặt với nước ấm. Lưu &yacute; l&agrave; chỉ massage nhẹ nh&agrave;ng để hạn chế g&acirc;y trầy xướt l&agrave;n da của bạn nha.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Cung cấp tinh chất c&oacute; lợi cho da</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"kem-duong-da-chong-lao-hoa-sakura\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/kem-duong-da-chong-lao-hoa-sakura.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/kem-duong-da-chong-lao-hoa-sakura.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Tranh thủ những thời khắc nghỉ ngơi hoặc buổi tối trước khi đi ngủ để chế biến những loại mặt nạ tự nhi&ecirc;n cung cấp dưỡng chất nu&ocirc;i da s&aacute;ng khỏe từ b&ecirc;n trong. Những nguy&ecirc;n liệu tự nhi&ecirc;n dễ t&igrave;m thấy tại nh&agrave; như sữa chua, cam, chanh, mật ong hoặc đơn giản hơn l&agrave; trứng g&agrave;. Bạn chỉ việc lựa chọn loại hỗn hợp mặt nạ ph&ugrave; hợp với l&agrave;n da của m&igrave;nh v&agrave; đắp l&ecirc;n, để y&ecirc;n trong v&ograve;ng 30 ph&uacute;t rồi rửa sạch lại với nước ấm trước khi đi ngủ.</p>\n', 'quy-chuan-cham-soc-da-mat-tai-nha-dung-cach-nen-ap-dung-ngay', '/Upload/News/c4dc92ec-0db0-4ad6-b4c3-3e915cf90cd2/Cach-cham-soc-da-don-gian-tai-nha.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Quy chuẩn chăm sóc da mặt tại nhà đúng cách nên áp dụng ngay', 'Quy chuẩn chăm sóc da mặt tại nhà đúng và đủ không chỉ làm da bạn sạch mụn mà còn giúp bạn thêm tự tin với làn da trắng hồng, căng mịn.', 'chăm sóc da mặt tại nhà', '44', 'PUBLISHED', '', 'old'),
(79, '3', 'Tắm trắng bao nhiêu tiền mới mang lại hiệu quả?', '<p>Tắm trắng bao nhi&ecirc;u tiền&nbsp;được cộng động l&agrave;m đẹp quan t&acirc;m kh&aacute; nhiều hiện nay. Bởi thời tiết Việt Nam ng&agrave;y c&agrave;ng ảnh hưởng xấu&nbsp;đối với l&agrave;n da của mỗi người.</p>\n', '<p data-mce-style=\"text-align: justify;\">Tắm trắng bao nhi&ecirc;u tiền&nbsp;được cộng động l&agrave;m đẹp quan t&acirc;m kh&aacute; nhiều hiện nay. Bởi thời tiết Việt Nam ng&agrave;y c&agrave;ng ảnh hưởng xấu&nbsp;đối với l&agrave;n da của mỗi người. Da trắng khỏe l&uacute;c n&agrave;o cũng dễ phối đồ v&agrave; đem lại phong th&aacute;i&nbsp;tự tin trong giao tiếp.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"tam-trang-tai-nha-voi-bun-khoang2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/Tắm-trắng-tại-nhà-với-bùn-khoáng2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/T%E1%BA%AFm-tr%E1%BA%AFng-t%E1%BA%A1i-nh%C3%A0-v%E1%BB%9Bi-b%C3%B9n-kho%C3%A1ng2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Hiện nay c&oacute; rất nhiều dịch vụ l&agrave;m trắng da cấp tốc hay đơn giản l&agrave; thuốc uống k&iacute;ch trắng tr&agrave;n lan tr&ecirc;n thị trường l&agrave;m rối suy nghĩ của nhiều chị em phụ nữ. Từ đ&oacute; c&acirc;u hỏi tắm trắng bao nhi&ecirc;u tiền trở n&ecirc;n nặng nề hơn mỗi khi nhắc đến. Svan Clinic &amp; Spa khuy&ecirc;n bạn n&ecirc;n b&igrave;nh tĩnh v&agrave; lựa chọn phương ph&aacute;p ph&ugrave; hợp cho bản th&acirc;n đ&uacute;ng đắn, đừng vung tiền v&ocirc; tội vạ m&agrave; kh&ocirc;ng được g&igrave;. Ngo&agrave;i ra, chị em n&ecirc;n thử sử dụng một v&agrave;i mặt nạ tắm trắng tự nhi&ecirc;n c&oacute; thể tiết kiệm phần n&agrave;o chi ph&iacute; v&agrave; sử dụng được ngay tại nh&agrave; sau đ&acirc;y.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nho tươi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Chứa h&agrave;m lượng vitamin c kh&aacute; cao cộng với chất Bioflavonoid gi&uacute;p l&agrave;n da thẩm thấu vitamin C tốt hơn gấp 2 lần. Hai chất n&agrave;y kết hợp lại gi&uacute;p chống lại qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a v&agrave; tăng độ đ&agrave;n hồi cho da tr&ecirc;n cả tuyệt vời.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"meo-chon-nho-tuoi-ngoai-cho-cho-moi-nguoi-1\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/meo-chon-nho-tuoi-ngoai-cho-cho-moi-nguoi-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/meo-chon-nho-tuoi-ngoai-cho-cho-moi-nguoi-1.jpg\" style=\"width: 500px; height: 345px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Nguy&ecirc;n liệu cần chuẩn bị bao gồm :&nbsp;1 ch&ugrave;m nho khoảng 12 - 15 quả t&ugrave;y theo độ to nhỏ, 1 tr&aacute;i cam tươi, 3 th&igrave;a mật ong v&agrave; 1 bịch sữa tươi.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m :&nbsp;rửa sạch nho v&agrave; cam sau đ&oacute; b&oacute;c vỏ rồi loại bỏ hạt đi rồi cho v&agrave;o m&aacute;y xay sinh tố. Tiếp theo đổ hết mật ong v&agrave; sữa tươi v&agrave;o k&egrave;m theo 1 &iacute;t gừng tươi th&aacute;i mỏng. Xay thật l&agrave; nhuyễn rồi đổ nhanh ra một c&aacute;i b&aacute;t to rồi d&ugrave;ng dụng cụ đắp mặt nạ qu&eacute;t đều hỗn hợp l&ecirc;n da cần tắm trắng để trong v&ograve;ng 30 ph&uacute;t cho t&igrave;nh chất ngấm v&agrave;o da. Tiếp theo massage nhẹ th&ecirc;m 10 ph&uacute;t rồi tắm lại với nước lạnh, bạn sẽ cảm thấy da mịn v&agrave; s&aacute;ng l&ecirc;n sau một thời gian sử dụng.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Khoai t&acirc;y</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn chuẩn bị khoảng 2 củ khoai t&acirc;y rửa sạch, gọt bỏ v&agrave; bỏ v&agrave;o nồi hấp ch&iacute;n sau đ&oacute; vớt ra nghiền n&aacute;t v&agrave; trộn th&ecirc;m một bịch sữa tươi v&agrave;o thế l&agrave; ch&uacute;ng ta c&oacute; hỗn hợp trắng da an to&agrave;n v&agrave; &iacute;t tốn tiền rồi. Khoai t&acirc;y chứa nhiều dưỡng chất ngăn chặn được qu&aacute; tr&igrave;nh diễn ra l&atilde;o h&oacute;a của l&agrave;n da, sữa tươi c&acirc;n bằng đổ ẩm cần thiết cho l&agrave;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-tam-trang-bang-khoai-tay-hieu-qua-nhat02\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-tam-trang-bang-khoai-tay-hieu-qua-nhat02.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tam-trang-bang-khoai-tay-hieu-qua-nhat02.jpg\" style=\"width: 500px; height: 247px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Loại hỗn hợp tắm trắng n&agrave;y cực th&iacute;ch hợp cho c&ocirc; n&agrave;ng da kh&ocirc; v&agrave; sần s&ugrave;i bởi lỗ ch&acirc;n l&ocirc;ng to nh&eacute;. Bạn tắm sơ qua với nước lạnh để đẩy bớt bụi bẩn tr&ecirc;n bề mặt da ra ngo&agrave;i rồi đắp hỗn hợp l&ecirc;n v&agrave; massage nhẹ nh&agrave;ng. Thực hiện đều đặn một tuần 2 - 3 lần để thấy được hiệu quả tốt hơn.&nbsp;B&ecirc;n cạnh đ&oacute;, d&ugrave; bạn thực hiện c&aacute;ch tắm trắng da n&agrave;o cũng n&ecirc;n cần d&ugrave;ng th&ecirc;m kem chống nắng bảo vệ da mọi l&uacute;c mọi nơi nha. Ch&uacute;c bạn may mắn với c&aacute;c phương ph&aacute;p trắng da tr&ecirc;n đ&acirc;y!</p>\n', 'tam-trang-bao-nhieu-tien-moi-mang-lai-hieu-qua', '/Upload/News/7f6a568a-5796-4151-8488-b7c462d8a7b8/T?m-tr?ng-t?i-nhà-v?i-bùn-khoáng2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Tắm trắng bao nhiêu tiền mới mang lại hiệu quả nhanh chóng?', 'Tắm trắng bao nhiêu tiền được cộng động làm đẹp quan tâm khá nhiều hiện nay. Bởi thời tiết Việt Nam ngày càng khó chịu đối với làn da của mỗi người.', 'tắm trắng bao nhiêu tiền', '44', 'PUBLISHED', '', 'old'),
(80, '3', 'Cách làm trắng da toàn thân cấp tốc hiệu quả được ưa thích', '<p>Bạn c&oacute; một buổi hẹn quan trọng v&agrave; bạn muốn m&igrave;nh thật xinh đẹp với bộ c&aacute;nh sang trọng. Tuy nhi&ecirc;n l&agrave;n da kh&ocirc;ng đều m&agrave;u lại l&agrave; trở ngại lớn nhất hiện tại.</p>\n', '<p data-mce-style=\"text-align: justify;\">Bạn c&oacute; một buổi hẹn quan trọng v&agrave; bạn muốn m&igrave;nh thật xinh đẹp với bộ c&aacute;nh sang trọng. Tuy nhi&ecirc;n l&agrave;n da kh&ocirc;ng đều m&agrave;u lại l&agrave; trở ngại lớn nhất hiện tại. Đừng lo, Svan sẽ gi&uacute;p bạn khắc phục điều nay với&nbsp;c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n cấp tốc&nbsp;hiệu quả ngay tại nh&agrave;.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"lam-trang-da-bang-thuc-pham-tu-nhien\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/lam-trang-da-bang-thuc-pham-tu-nhien.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/lam-trang-da-bang-thuc-pham-tu-nhien.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&Aacute;CH L&Agrave;M TRẮNG DA TO&Agrave;N TH&Acirc;N CẤP TỐC VỚI HỖN HỢP C&Aacute;M GẠO - NGHỆ</p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m đẹp n&agrave;y đ&atilde; phổ biến từ ng&agrave;y xưa v&agrave; được nhiều người ưa chuộng với t&iacute;nh th&acirc;n thiện với l&agrave;n da của n&oacute;. C&aacute;m gạo chứa nhiều vitamin B1, B6 c&ugrave;ng với dưỡng chất tinh nghệ tẩy đi tế b&agrave;o chết v&agrave; c&aacute;c sắc tố melanin tr&ecirc;n da. Chỉ n&ecirc;n &aacute;p dụng hỗn hợp n&agrave;y 1 tuần khoảng 2 lần để da trắng s&aacute;ng những kh&ocirc;ng bị b&agrave;o m&ograve;n v&agrave; nổi mụn nha.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"tam-trang-21\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tam-trang-21.png\" src=\"/files/Files/Kien-thuc-lam-dep/tam-trang-21.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Thực hiện :&nbsp;bạn mua một &iacute;t nghệ tươi, rửa sạch c&aacute;t b&agrave;o vỏ rồi gi&atilde; nhuyễn hoặc xay n&aacute;t. Tiếp theo trộn nghệ với c&aacute;m gạo cho đến khi hỗn hợp s&aacute;nh mịn lại, nếu thấy chưa s&aacute;nh lắm th&igrave; bạn cho th&ecirc;m 1 &iacute;t sữa tươi kh&ocirc;ng đường v&agrave;o. Bạn thoa đều hỗn hợp khắp cơ thể, đặc biệt l&agrave; v&ugrave;ng đầu gối, mu b&agrave;n tay v&igrave; đ&acirc;y l&agrave; những v&ugrave;ng dễ bị th&acirc;m v&agrave; tiếp x&uacute;c nhiều với &aacute;nh nắng mặt trời bị th&acirc;m sạm nhiều. Điều n&agrave;y l&agrave;m cho cơ thể trắng mịn đều m&agrave;u chứ kh&ocirc;ng phải chỗ trắng chỗ đen nh&igrave;n&nbsp;mất đi vẻ đẹp của cơ thể.</p>\n\n<p data-mce-style=\"text-align: justify;\">C&Aacute;CH L&Agrave;M TRẮNG DA TO&Agrave;N TH&Acirc;N CẤP TỐC VỚI DẤM&nbsp;V&Agrave; ĐƯỜNG N&Acirc;U</p>\n\n<p data-mce-style=\"text-align: justify;\">Hỗn hợp n&agrave;y c&oacute; t&aacute;c dụng l&agrave;m trắng da nhanh ch&oacute;ng v&agrave; an to&agrave;n. Ngo&agrave;i ra n&oacute; c&ograve;n dễ t&igrave;m kiếm v&agrave; &iacute;t chi ph&iacute; ph&ugrave; hợp với t&uacute;i tiền của nhiều người. T&iacute;nh axit trong&nbsp;dấm gi&uacute;p l&agrave;m trắng da rất nhanh v&agrave; đường d&ugrave;ng kh&aacute;ng khuẩn đồng thời tẩy da trắng v&agrave; tăng độ đ&agrave;n hội cho da. Bạn chỉ n&ecirc;n d&ugrave;ng một lượng vừa đủ hỗn hợp n&agrave;y cho t&ugrave;y loại da kh&aacute;c nhau để kh&ocirc;ng g&acirc;y hiện tượng lờn v&agrave; b&agrave;o m&ograve;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"tay-te-bao-chet-va-lam-kem-tam-trang-voi-duong-nau\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/tẩy-tế-bào-chết-và-làm-kem-tắm-trắng-với-đường-nâu.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/t%E1%BA%A9y-t%E1%BA%BF-b%C3%A0o-ch%E1%BA%BFt-v%C3%A0-l%C3%A0m-kem-t%E1%BA%AFm-tr%E1%BA%AFng-v%E1%BB%9Bi-%C4%91%C6%B0%E1%BB%9Dng-n%C3%A2u.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch l&agrave;m :&nbsp;bạn pha giấm với nước ấm theo tỉ lệ 1: 3. Rồi bạn cho th&ecirc;m tỉ lệ 2 phần đường n&acirc;u v&agrave;o, hạn chế đ&aacute;nh tan đường trong nước giầm v&igrave; như thế kh&ocirc;ng mang lại hiệu quả tắm trắng cao nhất. D&ugrave;ng tay lấy hỗn hợp massage đều l&ecirc;n da một lần trước khi tắm để loại bỏ hết bụi bận v&agrave; một l&agrave;n sau khi đ&atilde; tắm qua với nước lạnh trước đ&oacute;. Ri&ecirc;ng lần thứ 2 sau khi l&agrave;m xong bạn n&ecirc;n tắm lại bằng nước ấm để tinh chất thấm s&acirc;u hơn v&agrave;o nang l&ocirc;ng. Để tăng cường l&agrave;m trắng, bạn thoa th&ecirc;m những tinh chất dưỡng da từ thi&ecirc;n nhi&ecirc;n hoặc nước hoa hồng để da s&aacute;ng trắng tự nhi&ecirc;n.&nbsp;Kết hợp sử dụng kem chống nắng c&ugrave;ng c&aacute;c biện ph&aacute;p bảo vệ hằng ng&agrave;y để da lu&ocirc;n được bảo vệ tốt nhất nha.</p>\n', 'cach-lam-trang-da-toan-than-cap-toc-hieu-qua-duoc-ua-thich', '/Upload/News/8a860415-4de9-4d72-883f-dc45473fd7f9/lam-trang-da-bang-thuc-pham-tu-nhien.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách làm trắng da toàn thân cấp tốc hiệu quả được ưa thích', 'Bạn có một buổi tiệc và bạn không hài lòng về làn da của mình. Cách làm trắng da toàn thân cấp tốc sau đây là giải pháp tốt nhất mà Svan gửi tặng bạn.', 'cách làm trắng da toàn thân cấp tốc', '44', 'PUBLISHED', '', 'old');
INSERT INTO `posts` (`id`, `author_id`, `title`, `excerpt`, `body`, `slug`, `image`, `created_at`, `updated_at`, `seo_title`, `meta_description`, `meta_keywords`, `category_id`, `status`, `featured`, `old`) VALUES
(81, '3', 'Cách trị mụn bọc nhanh nhất - không lo tác dụng phụ', '<p>C&aacute;ch trị mụn bọc nhanh nhất&nbsp;tại nh&agrave; ch&iacute;nh l&agrave; phương ph&aacute;p giải quyết c&aacute;c loại mụn cứng đầu hiện nay. Mụn bọc l&agrave; loại mụn đ&aacute;ng gh&eacute;t nhất, g&acirc;y sưng tấy v&agrave; mất thẩm mỹ.</p>\n', '<p data-mce-style=\"text-align: justify;\">C&aacute;ch trị mụn bọc nhanh nhất&nbsp;tại nh&agrave; ch&iacute;nh l&agrave; phương ph&aacute;p giải quyết c&aacute;c loại mụn cứng đầu hiện nay. Mụn bọc l&agrave; loại mụn đ&aacute;ng gh&eacute;t nhất, g&acirc;y sưng tấy v&agrave; mất thẩm mỹ. Nếu kh&ocirc;ng c&oacute; biện ph&aacute;p kịp thời th&igrave; lũ mụn bọc sẽ l&acirc;y lan đến c&aacute;c v&ugrave;ng da kh&aacute;c.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Kem đ&aacute;nh răng</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Buổi tối trước khi đi ngủ chỉ cần thoa một lớp kem đ&aacute;nh răng thật mỏng l&ecirc;n v&ugrave;ng da bị mụn v&agrave; để qua đ&ecirc;m, s&aacute;ng mai rửa sạch lại bằng nước ấm. Đơn giản v&agrave; &iacute;t tốn k&eacute;m!</p>\n\n<p data-mce-style=\"text-align: justify;\">Kh&ocirc;ng chỉ gi&uacute;p bạn loại bỏ những nốt mụn đầu đen cứng đầu, kem đ&aacute;nh răng c&ograve;n được xem l&agrave; c&aacute;ch trị mụn bọc khi c&oacute; thể &ldquo;ti&ecirc;u diệt&rdquo;, giảm t&igrave;nh trạng sưng tấy khi bị mụn bọc tấn c&ocirc;ng. Những hoạt chất s&aacute;t khuẩn c&oacute; trong nguy&ecirc;n liệu n&agrave;y sẽ l&agrave;m giảm sự sưng tấy, đồng thời ti&ecirc;u vi&ecirc;m v&agrave; l&agrave;m sạch da, bởi vậy c&aacute;c nốt mụn bọc đau đớn sẽ kh&ocirc;ng c&ograve;n cơ hội&ldquo;l&agrave;m phiền&rdquo;&nbsp;bạn nữa đ&acirc;u.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Mật ong</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Trong mật ong c&oacute; chứa dưỡng chất kh&aacute;ng sinh gi&uacute;p ngăn chặn nhiễm tr&ugrave;ng, ngăn chặn mụn bọc kh&ocirc;ng ph&aacute;t triển xấu đi hoặc biến th&agrave;nh c&aacute;c nang mụn nghi&ecirc;m trọng hơn. B&ecirc;n cạnh việc ngăn ngừa mụn bọc hiệu quả, mật ong c&ograve;n l&agrave;m cho lỗ ch&acirc;n l&ocirc;ng được se kh&iacute;t, gi&uacute;p da săn chắc v&agrave; th&ecirc;m khỏe mạnh.</p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn c&oacute; thể chấm mật ong v&agrave;o nốt mụn để qua đ&ecirc;m sẽ gi&uacute;p l&agrave;m kh&ocirc; nốt mụn, hoặc th&ecirc;m v&agrave;i giọt chanh th&agrave;nh hỗn hợp thoa l&ecirc;n da, đắp mặt nạ n&agrave;y trong v&ograve;ng 15-20p, sau đ&oacute; rửa sạch bằng nước ấm.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-tri-mun-boc-nhanh-nhat-1\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-tri-mun-boc-nhanh-nhat-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-boc-nhanh-nhat-1.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Tỏi</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn chỉ cần gi&atilde; nhuyễn tỏi đắp l&ecirc;n nốt mụn hoặc cắt th&agrave;nh l&aacute;t mỏng ch&agrave; s&aacute;t nhẹ nh&agrave;ng l&ecirc;n v&ugrave;ng da bị mụn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Tỏi đặc t&iacute;nh kh&aacute;ng khuẩn, kh&aacute;ng vi&ecirc;m tự nhi&ecirc;n n&ecirc;n tỏi c&oacute; t&aacute;c dụng l&agrave;m giảm sưng tấy v&agrave; vi&ecirc;m mủ do mụn bọc g&acirc;y ra giảm đi nhanh ch&oacute;ng. Ngo&agrave;i ra tỏi c&ograve;n tẩy sạch c&aacute;c tế b&agrave;o chết, gi&uacute;p da th&ocirc;ng tho&aacute;ng v&agrave; ngăn ngừa được c&aacute;c t&aacute;c nh&acirc;n g&acirc;y mụn.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nha đam</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Th&ecirc;m một c&aacute;ch trị mụn bọc được nhiều người &aacute;p dụng đ&oacute; l&agrave; nha đam. Nha đam được coi như l&agrave; một loại mỹ phẩm l&agrave;m đẹp từ thi&ecirc;n nhi&ecirc;n, l&agrave; b&iacute; quyết để l&agrave;m da th&ecirc;m mịn m&agrave;ng v&agrave; tươi trẻ. Nhựa nha đam c&oacute; t&aacute;c dụng kh&aacute;ng khuẩn v&agrave; g&acirc;y t&ecirc;, k&iacute;ch th&iacute;ch sự tổng hợp c&aacute;c collagen v&agrave; sợi elastin se lỗ ch&acirc;n l&ocirc;ng, giảm mụn một c&aacute;ch hiệu quả.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-tri-mun-boc-nhanh-nhat-2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-tri-mun-boc-nhanh-nhat-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-boc-nhanh-nhat-2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch n&agrave;y cũng kh&aacute; đơn giản, cắt đ&ocirc;i l&aacute; nha đam, lấy nhựa nha đam đắm trực tiếp l&ecirc;n v&ugrave;ng mụn. Hoặc c&oacute; thể xay nhuyễn nha đam c&ugrave;ng v&agrave;i giọt nước chanh, d&ugrave;ng hỗn hợp n&agrave;y đắp mặt nạ trong v&ograve;ng 15 ph&uacute;t, sau đ&oacute; rửa sạch bằng nước ấm.</p>\n\n<p data-mce-style=\"text-align: justify;\">Từ những nguy&ecirc;n liệu đơn giản, dễ t&igrave;m b&ecirc;n tr&ecirc;n c&oacute; thể gi&uacute;p bạn x&oacute;a tan đi nỗi &aacute;m ảnh về mụn bọc.</p>\n', 'cach-tri-mun-boc-nhanh-nhat-khong-lo-tac-dung-phu', '/Upload/News/db51536d-67c2-4fb8-9ffe-5bd4c3393076/cach-tri-mun-boc-nhanh-nhat-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'ách trị mụn bọc nhanh nhất - không lo tác dụng phụ', 'Cách trị mụn bọc nhanh nhất tại nhà chính là phương pháp giải quyết các loại mụn cứng đầu hiện nay. Mụn bọc là loại mụn đáng ghét nhất, gây sưng tấy và mất thẩm mỹ. ', 'cách trị mụn bọc nhanh nhất', '44', 'PUBLISHED', '', 'old'),
(82, '3', 'Bất ngờ cách trị mụn hiệu quả nhất bằng dầu dừa', '<p>C&aacute;ch trị mụn hiệu quả nhất&nbsp;của chị em phụ nữ&nbsp;ch&iacute;nh l&agrave; dầu dừa thường được c&aacute;c cụ b&agrave; xưa d&ugrave;ng thoa l&ecirc;n t&oacute;c, gi&uacute;p t&oacute;c b&oacute;ng mượt v&agrave; c&ograve;n được d&ugrave;ng để thoa l&ecirc;n những v&ugrave;ng nứt hoặc chai sạn để l&agrave;m mịn da.</p>\n', '<p>C&aacute;ch trị mụn hiệu quả nhất&nbsp;của chị em phụ nữ&nbsp;ch&iacute;nh l&agrave; dầu dừa thường được c&aacute;c cụ b&agrave; xưa d&ugrave;ng thoa l&ecirc;n t&oacute;c, gi&uacute;p t&oacute;c b&oacute;ng mượt v&agrave; c&ograve;n được d&ugrave;ng để thoa l&ecirc;n những v&ugrave;ng nứt hoặc chai sạn để l&agrave;m mịn da. Ng&agrave;y nay dầu dừa được sử dụng rất nhiều như một tuyệt chi&ecirc;u l&agrave;m đẹp từ thi&ecirc;n nhi&ecirc;n.</p>\n\n<p><strong>Dầu dừa</strong></p>\n\n<p>Dầu dừa chứa nhiều Vitamin E, axit lauric v&agrave; acid capric. Vitamin E c&oacute; t&aacute;c dụng l&agrave;m sạch lỗ ch&acirc;n l&ocirc;ng, điều tiết việc tiết b&atilde; nhờn của da mặt, gi&uacute;p da mặt của bạn c&acirc;n bằng độ PH cần thiết để tr&aacute;nh nhiễm tr&ugrave;ng ở c&aacute;c lỗ ch&acirc;n l&ocirc;ng. Axit lauric v&agrave; acid capric gi&uacute;p bảo vệ da khỏi mụn trứng c&aacute; v&agrave; c&aacute;c nhiễm tr&ugrave;ng kh&aacute;c, bằng c&aacute;ch ti&ecirc;u diệt c&aacute;c vi khuẩn c&oacute; hại, chống vi&ecirc;m, sưng.</p>\n\n<p>Với những hoạt chất tr&ecirc;n dầu dừa được xem l&agrave; mỹ phẩm tự nhi&ecirc;n v&agrave; l&agrave; c&aacute;ch trị mụn hiệu quả nhất m&agrave; kh&ocirc;ng sợ bị k&iacute;ch ứng. Dầu dừa cũng ph&ugrave; hợp cho da dầu v&igrave; kh&ocirc;ng g&acirc;y nhờn qu&aacute; nhiều nếu bạn massage hay thoa l&ecirc;n mặt.</p>\n\n<p style=\"text-align: center;\"><img alt=\"cach-tri-mun-hieu-qua-nhat-1\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-tri-mun-hieu-qua-nhat-1.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-hieu-qua-nhat-1.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p><strong>C&ocirc;ng thức dầu dừa</strong></p>\n\n<p>Bạn lưu &yacute; chọn dầu dừa nguy&ecirc;n chất v&agrave; c&oacute; xuất xứ r&otilde; r&agrave;ng để tr&aacute;nh những t&aacute;c dụng phụ kh&ocirc;ng mong muốn khi trị mụn bằng dầu dừa nh&eacute;.</p>\n\n<p>Bước 1: Việc đầu ti&ecirc;n cần l&agrave;m l&agrave; phải l&agrave;m sạch da mặt.</p>\n\n<p>C&aacute;c bạn c&oacute; thể d&ugrave;ng loại sữa rửa mặt ph&ugrave; hợp với loại da của m&igrave;nh (hoặc phải tẩy trang để l&agrave;m sạch cặn mỹ phẩm tr&ecirc;n da nếu bạn trang điểm), bạn cũng c&oacute; thể rửa mặt bằng nước ấm gi&uacute;p lỗ ch&acirc;n l&ocirc;ng nở rộng tạo điều kiện hấp thu dưỡng chất nhanh ch&oacute;ng.</p>\n\n<p>Bước 2:</p>\n\n<p>Sau đ&oacute; d&ugrave;ng 10ml dầu dừa v&agrave;o l&ograve;ng b&agrave;n tay, thoa đều khắp da mặt, nằm thư gi&atilde;n từ 15-20 ph&uacute;t v&agrave; rửa sạch lại với nước. Ngo&agrave;i ra bạn cũng c&oacute; thể trộn 2 th&igrave;a dầu dừa với &frac12; th&igrave;a nước chanh tươi th&agrave;nh hỗn hợp v&agrave; m&aacute;t xa nhẹ nh&agrave;ng, axit trong chanh v&agrave; dầu dừa sẽ gi&uacute;p bạn tẩy tế b&agrave;o chết, giảm mụn sưng, vi&ecirc;m hiệu quả.</p>\n\n<p style=\"text-align: center;\"><img alt=\"cach-tri-mun-hieu-qua-nhat-2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-tri-mun-hieu-qua-nhat-2.jpeg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-tri-mun-hieu-qua-nhat-2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p>C&aacute;ch trị mụn hiệu quả nhất bằng dầu dừa v&ocirc; c&ugrave;ng đơn giản, dễ l&agrave;m, v&igrave; thế bạn c&oacute; thể thực hiện 3-4 lần trong tuần v&agrave;o c&aacute;c buổi tối. M&aacute;ch nhỏ n&egrave;, bạn cũng c&oacute; thể uống 1, 2 muỗng dầu dừa trước bữa s&aacute;ng, bữa trưa để n&acirc;ng cao hiệu quả nh&eacute;.</p>\n\n<p>Một điểm bạn cần lưu &yacute; nữa l&agrave; dầu dừa&nbsp;chỉ hiệu quả đối với những trường hợp l&agrave;n da bị mụn trứng c&aacute; nhẹ. C&ograve;n với những trường hợp da bị mụn bọc, mụn c&aacute;m, mụn thịt hoặc bị trứng c&aacute; ở mức độ nặng th&igrave; bạn n&ecirc;n tham khảo những phương ph&aacute;p điều trị mụn hiện đại hơn.</p>\n\n<p data-mce-style=\"text-align: center;\">Gọi ngay đến số hotline ch&uacute;ng t&ocirc;i: 08.3888.3999 &ndash; 09.3888.3999. để được tư vấn miễn ph&iacute; về dịch vụ.</p>\n', 'bat-ngo-cach-tri-mun-hieu-qua-nhat-bang-dau-dua', '/Upload/News/36540f68-eb22-4d7a-a849-2c1ea4437395/cach-tri-mun-hieu-qua-nhat-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'ất ngờ cách trị mụn hiệu quả nhất bằng dầu dừa', 'Cách trị mụn hiệu quả nhất của chị em phụ nữ chính là dầu dừa thường được các cụ bà xưa dùng thoa lên tóc, giúp tóc bóng mượt và còn được dùng để thoa lên những vùng nứt hoặc chai sạn để làm mịn da.', 'trị mụn hiệu quả', '44', 'PUBLISHED', '', 'old'),
(83, '3', 'Cách làm trắng da tự nhiên ngay tại nhà với sữa đậu nành', '<p>Với nguy&ecirc;n liệu l&agrave; sữa đậu n&agrave;nh, bạn ho&agrave;n to&agrave;n c&oacute; thể thực hiện&nbsp;c&aacute;ch l&agrave;m trắng da tự nhi&ecirc;n&nbsp;ngay tại nh&agrave; để sở hữu cho m&igrave;nh l&agrave;n da s&aacute;ng hồng v&agrave; mịn m&agrave;ng.</p>\n', '<p>Với nguy&ecirc;n liệu l&agrave; sữa đậu n&agrave;nh, bạn ho&agrave;n to&agrave;n c&oacute; thể thực hiện&nbsp;c&aacute;ch l&agrave;m trắng da tự nhi&ecirc;n&nbsp;ngay tại nh&agrave; để sở hữu cho m&igrave;nh l&agrave;n da s&aacute;ng hồng v&agrave; mịn m&agrave;ng.</p>\n\n<p>Trong đậu n&agrave;nh chứa nhiều vitamin E n&ecirc;n gi&uacute;p cho l&agrave;n da trắng mềm hơn. B&ecirc;n cạnh đ&oacute;, loại hạt n&agrave;y c&ograve;n chứa một số hợp chất c&oacute; t&aacute;c dụng tăng cường hoocmone trong cơ thể nữ, từ đ&oacute; cải thiện sắc tố da rất tốt. H&atilde;y c&ugrave;ng Svan Clinic &amp; Spa t&igrave;m hiểu nhanh c&aacute;ch l&agrave;m trắng da với sữa đậu n&agrave;nh nh&eacute;.</p>\n\n<p style=\"text-align: center;\"><img alt=\"cach-lam-trang-da-tu-nhien-2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-lam-trang-da-tu-nhien-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-trang-da-tu-nhien-2.jpg\" style=\"width: 500px; height: 328px;\" /></p>\n\n<p><strong>Nguy&ecirc;n liệu:</strong></p>\n\n<ul>\n	<li>\n	<p>100 ml sữa đậu n&agrave;nh</p>\n	</li>\n	<li>\n	<p>100 ml nước lọc</p>\n	</li>\n	<li>\n	<p>Bột Baking soda, khoảng 1 th&igrave;a nhỏ</p>\n	</li>\n	<li>\n	<p>L&ograve;ng trắng trứng g&agrave;</p>\n	</li>\n</ul>\n\n<p>C&aacute;ch thực hiện:</p>\n\n<p>Trộn thật đều, thật nhuyễn tất cả c&aacute;c loại nguy&ecirc;n liệu đ&oacute; lại với nhau trong hộp c&oacute; nắp. Sau đ&oacute;, đậy nắp hộp v&agrave; để hỗn hợp trong tầng lạnh khoảng 3 ng&agrave;y mới c&oacute; thể lấy ra d&ugrave;ng. Lưu &yacute; trong thời gian cất tủ lạnh hạn chế mở nắp ra thường xuy&ecirc;n sẽ l&agrave;m hư hỏng hợp chất kh&ocirc;ng tốt trong qu&aacute; tr&igrave;nh sử dụng.</p>\n\n<p style=\"text-align: center;\"><img alt=\"cach-lam-trang-da-tu-nhien\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-lam-trang-da-tu-nhien.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-trang-da-tu-nhien.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p>Sau 3 ng&agrave;y bạn đ&atilde; c&oacute; thể bắt đầu thực hiện c&aacute;ch l&agrave;m trắng da tự nhi&ecirc;n rồi nh&eacute;. Đầu ti&ecirc;n h&atilde;y tắm sạch cơ thể bằng nước ấm v&agrave; lau kh&ocirc;. Sau đ&oacute; bạn d&ugrave;ng tay lấy 1 &iacute;t hỗn hợp sữa đậu n&agrave;nh n&agrave;y b&ocirc;i l&ecirc;n to&agrave;n th&acirc;n, vừa b&ocirc;i vừa massage nhẹ nh&agrave;ng để dưỡng chất thấm s&acirc;u v&agrave;o da. Chờ trong khoảng 10 ph&uacute;t rồi tắm lại cơ thể bằng nước ấm.Bạn n&ecirc;n thực hiện thường xuy&ecirc;n mỗi ng&agrave;y để đạt được hiệu quả trắng da tự nhi&ecirc;n v&agrave; mịn m&agrave;ng. Kết hợp uống th&ecirc;m sữa đậu n&agrave;nh để l&agrave;m đẹp da từ b&ecirc;n trong cơ thể.</p>\n\n<p data-mce-style=\"text-align: center;\">Gọi ngay đến số hotline ch&uacute;ng t&ocirc;i: 08.3888.3999 &ndash; 09.3888.3999. để được tư vấn miễn ph&iacute; về dịch vụ.</p>\n', 'cach-lam-trang-da-tu-nhien-ngay-tai-nha-voi-sua-dau-nanh', '/Upload/News/11c341e8-e0cc-42aa-a847-26ac32bb4a8d/cach-lam-trang-da-tu-nhien.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'cách làm trắng da tự nhiên ngay tại nhà với sữa đậu nành', 'Với nguyên liệu là sữa đậu nành, bạn hoàn toàn có thể thực hiện cách làm trắng da tự nhiên ngay tại nhà để sở hữu cho mình làn da sáng hồng và mịn màng.', 'cách làm trắng da tự nhiên', '44', 'PUBLISHED', '', 'old'),
(84, '3', 'Triệt lông giá bao nhiêu là câu hỏi được tìm kiếm rất nhiều!', '<p>Triệt l&ocirc;ng gi&aacute; bao nhi&ecirc;u&nbsp;l&agrave; vấn đề đặc biệt được quan t&acirc;m. Với nam giới, l&ocirc;ng rậm c&oacute; thể xem l&agrave; b&igrave;nh thường, thậm ch&iacute; l&agrave; đẹp.&nbsp;</p>\n', '<p>Triệt l&ocirc;ng gi&aacute; bao nhi&ecirc;u&nbsp;l&agrave; vấn đề đặc biệt được quan t&acirc;m. Với nam giới, l&ocirc;ng rậm c&oacute; thể xem l&agrave; b&igrave;nh thường, thậm ch&iacute; l&agrave; đẹp. Ngược lại, ph&aacute;i nữ lu&ocirc;n t&igrave;m c&aacute;ch tẩy đi những đ&aacute;m l&ocirc;ng xấu x&iacute; đ&oacute;. C&oacute; rất nhiều phương để triệt l&ocirc;ng, nhưng một số c&aacute;ch triệt tẩy thường kh&ocirc;ng mang lại kết quả triệt để.</p>\n\n<p style=\"text-align: center;\"><img alt=\"triet-long-vinh-vien-gia-bao-nhieu-hinh-2\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/triet-long-vinh-vien-gia-bao-nhieu-hình-2.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/triet-long-vinh-vien-gia-bao-nhieu-h%C3%ACnh-2.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p>Tuy nhi&ecirc;n, liệu tr&igrave;nh triệt l&ocirc;ng sẽ kh&aacute;c nhau với mỗi người. N&oacute; t&ugrave;y thuộc v&agrave;o t&igrave;nh trạng l&ocirc;ng tr&ecirc;n cơ thể mỗi c&aacute; nh&acirc;n v&agrave; bạn c&oacute; nhu cầu triệt l&ocirc;ng ở v&ugrave;ng n&agrave;o tr&ecirc;n cơ thế, n&ecirc;n gi&aacute; cả cũng kh&ocirc;ng giống nhau.</p>\n\n<p>V&igrave; sao mỗi c&aacute; nh&acirc;n c&oacute; t&igrave;nh trạng l&ocirc;ng kh&aacute;c nhau</p>\n\n<p>L&ocirc;ng được h&igrave;nh th&agrave;nh do c&aacute;c yếu tố di truyền, th&oacute;i quen ăn uống hoặc c&oacute; thể do bệnh tật&hellip; Việc mọc l&ocirc;ng rậm l&agrave; do androgen bị k&iacute;ch th&iacute;ch nhiều. Những người bị u tuyến thượng thận, u nang buồn trứng, d&ugrave;ng thuốc tr&aacute;nh thai, c&aacute;c thuốc c&oacute; chứa steroid&hellip; sẽ khiến androgen tiết ra nhiều hơn n&ecirc;n việc l&ocirc;ng mọc rậm rất dễ xuất hiện.</p>\n\n<p>C&aacute;ch triệt tận gốc</p>\n\n<p>Hiện nay, c&oacute; rất nhiều c&aacute;ch triệt l&ocirc;ng như d&ugrave;ng gel, đ&aacute; m&agrave;i, dao cạo, thuốc, nguy&ecirc;n liệu tự nhi&ecirc;n&hellip; khiến cho vấn đề triệt l&ocirc;ng trở n&ecirc;n mơ hồ hơn rất nhiều. V&igrave; vậy, c&oacute; rất nhiều người thay v&igrave; mua c&aacute;c loại thuốc h&oacute;a học họ lại chọn cho m&igrave;nh c&aacute;c c&aacute;ch triệt l&ocirc;ng từ thi&ecirc;n nhi&ecirc;n để đảm bảo t&uacute;i tiền kh&ocirc;ng bị cạn kiệt.</p>\n\n<p>Triệt l&ocirc;ng với ph&egrave;n chua : B&igrave;nh thường ph&egrave;n chua được nhiều người d&ugrave;ng để khử m&ugrave;i h&ocirc;i ch&acirc;n. Tuy nhi&ecirc;n h&atilde;y &aacute;p dụng ngay c&ocirc;ng dụng triệt l&ocirc;ng gi&aacute; rẻ n&agrave;y tại nh&agrave; để đ&aacute;nh bay đ&aacute;m vi &ocirc; l&ocirc;ng kh&ocirc;ng cần thiết tr&ecirc;n cơ thể. Bạn chỉ cần h&ograve;a tan một &iacute;t ph&egrave;n chua với nước sau đ&oacute; b&ocirc;i l&ecirc;n v&ugrave;ng l&ocirc;ng cần triệt để kh&ocirc; khoảng 20 ph&uacute;t l&agrave; được. Ph&egrave;n chua sẽ thấm s&acirc;u v&agrave;o c&aacute;c lỗ nang l&ocirc;ng l&agrave;m ch&uacute;ng tự động ch&iacute;n v&agrave; rụng đi từ từ.</p>\n\n<p style=\"text-align: center;\"><img alt=\"chua-hoi-nach-bang-phen-chua-123\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/chua-hoi-nach-bang-phen-chua-123.png\" src=\"/files/Files/Kien-thuc-lam-dep/chua-hoi-nach-bang-phen-chua-123.jpg\" style=\"width: 500px; height: 329px;\" /></p>\n\n<p>Triệt l&ocirc;ng với Chuối : Loại nguy&ecirc;n liệu n&agrave;y dễ t&igrave;m kiếm v&agrave; c&aacute;c dưỡng chất của n&oacute; c&oacute; c&ocirc;ng dụng kh&aacute; tốt để triệt sạch l&ocirc;ng tr&ecirc;n cơ thể. Bạn n&ecirc;n mua một &iacute;t chuối ch&iacute;n sau đ&oacute; b&oacute;c vỏ v&agrave; nghiền n&aacute;t trộn th&ecirc;m 1 &iacute;t mật ong đ&atilde; chuẩn bị sẳn trước đ&oacute; v&agrave; b&ocirc;i đều l&ecirc;n v&ugrave;ng da cần triệt l&ocirc;ng. Sau đ&oacute; đắp th&ecirc;m 1 miếng vải mỏng l&ecirc;n đợi đến khi hỗn hợp kh&ocirc; th&igrave; lột nhẹ miếng vải ngược chiều l&ocirc;ng mọc. Từng mảng l&ocirc;ng sẽ đi theo hỗn hợp đ&atilde; kh&ocirc; ra ngo&agrave;i. &Aacute;p dụng thường xuy&ecirc;n để đạt hiệu quả cao.</p>\n', 'triet-long-gia-bao-nhieu-la-cau-hoi-duoc-tim-kiem-rat-nhieu', '/Upload/News/2d624c64-381d-4968-8e74-71198816e043/triet-long-vinh-vien-gia-bao-nhieu-hình-2.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Triệt lông vĩnh viễn giá bao nhiêu là câu hỏi được tìm kiếm rất nhiều!', 'Triệt lông vĩnh viễn giá bao nhiêu là vấn đề đặc biệt được quan tâm. Với nam giới, lông rậm có thể xem là bình thường, thậm chí là đẹp.', 'triệt lông vĩnh viễn giá bao nhiêu', '44', 'PUBLISHED', '', 'old'),
(85, '3', 'Sử dụng cách làm trắng da toàn thân tự nhiên tại nhà hiệu quả', '<p>D&ugrave; l&agrave;n da n&acirc;u thời gian gần đ&acirc;y đang gọi l&agrave; mốt nhưng&nbsp;phụ nữ Ch&acirc;u &Aacute; với đặc th&ugrave; về v&oacute;c d&aacute;ng th&igrave; l&agrave;n da trắng hồng, khỏe khoắn vẫn ph&ugrave; hợp v&agrave; được ưa chuộng hơn.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">D&ugrave; l&agrave;n da n&acirc;u thời gian gần đ&acirc;y đang gọi l&agrave; mốt nhưng&nbsp;phụ nữ Ch&acirc;u &Aacute; với đặc th&ugrave; về v&oacute;c d&aacute;ng th&igrave; l&agrave;n da trắng hồng, khỏe khoắn vẫn ph&ugrave; hợp v&agrave; được ưa chuộng hơn. Nhiều chị em vẫn lu&ocirc;n t&igrave;m kiếm cho m&igrave;nh một phương ph&aacute;p l&agrave;m trắng da hiệu quả. Thấu hiểu được điều đ&oacute;, Svan Clinic &amp; Spa giới thiệu đến bạn&nbsp;c&aacute;ch l&agrave;m trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n&nbsp;v&agrave; c&aacute;c bước tiến h&agrave;nh sử dụng cũng như pha chế.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"kemlamtrangdatoanthantunhien\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/kemlamtrangdatoanthantunhien.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/kemlamtrangdatoanthantunhien.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>C&agrave; rốt</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn c&oacute; thể chế biến c&agrave; rốt theo nhiều c&ocirc;ng thức l&agrave;m trắng da tự nhi&ecirc;n kh&aacute;c nhau m&agrave; kh&ocirc;ng ảnh hưởng đến t&uacute;i tiền v&agrave; thời gian. C&agrave; rốt l&agrave; thực phẩm cung cấp nhiều vitamin A v&agrave; dưỡng chất tốt cho cơ thể như mắt, dạ d&agrave;y,... đặc biệt l&agrave; mang lại l&agrave;n da s&aacute;ng khỏe tuyệt vời. Th&ecirc;m v&agrave;o đ&oacute; c&agrave; rốt c&ograve;n chứa nhiều beta carotene, phenolic acid,... ngăn ngừa sự l&atilde;o h&oacute;a, giảm vết nhăn khi phụ nữ bước sang tuổi 30.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"cach-lam-trang-da-voi-ca-rot\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/cach-lam-trang-da-voi-ca-rot.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/cach-lam-trang-da-voi-ca-rot.jpg\" style=\"width: 500px; height: 334px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">&nbsp;C&aacute;ch thực hiện :&nbsp;bạn rửa sạch c&agrave; rốt, gọt sạch vỏ rồi bỏ v&agrave;o xay thật nhuyễn với m&aacute;y xay sinh tố. C&oacute; thể cho th&ecirc;m 1 bịch sữa tươi kh&ocirc;ng đường k&egrave;m một &iacute;t bột m&igrave; th&agrave;nh hỗn hợp sền sệt. Sau đ&oacute; bạn đắp hỗn hợp l&ecirc;n v&ugrave;ng da cần tắm trắng khắp cơ thể v&agrave; xoa nhẹ theo hướng l&ocirc;ng mộc kho&agrave;ng 30 ph&uacute;t th&igrave; tắm lại với nước sạch. Bạn sẽ cảm nhận l&agrave;n da trắng mịn sau 3 tuần &aacute;p dụng đều đặn. Đồng thời muốn tăng hiệu quả nu&ocirc;i dưỡng da từ b&ecirc;n trong th&igrave; bạn n&ecirc;n uống th&ecirc;m nước &eacute;p c&agrave; rốt mỗi ng&agrave;y.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Chuối</strong></p>\n\n<p data-mce-style=\"text-align: justify;\">Ăn chuối hoặc uống sinh tố chuỗi mỗi ng&agrave;y vừa gi&uacute;p đẹp da vừa gi&uacute;p giảm c&acirc;n nặng. Một đặc điểm c&oacute; lợi kh&aacute;c l&agrave;m l&agrave;m trắng da to&agrave;n th&acirc;n với chuối kh&aacute; được ưa chuộng trong vạn b&iacute; kiếp l&agrave;m đẹp của chị em hiện tại. Bởi v&igrave; chuối chứa nhiều vitamin, kho&aacute;ng chất magie, kali, kẽm,... gi&uacute;p ngăn ngừa nếp nhăn, tr&ugrave;ng nh&atilde;o da v&agrave; trắng s&aacute;ng từ b&ecirc;n trong.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"4f7bac02-6f53-44d4-8aec-a38f560f301a\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/4f7bac02-6f53-44d4-8aec-a38f560f301a.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/4f7bac02-6f53-44d4-8aec-a38f560f301a.jpg\" style=\"width: 500px; height: 284px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">C&aacute;ch thực hiện :&nbsp;bạn n&ecirc;n mua những quả chuối thật ch&iacute;n, b&oacute;c vỏ v&agrave; xay nhuyễn với 1 &iacute;t mật ong, sữa b&ograve; đ&atilde; chuẩn bị trước đ&oacute; th&agrave;nh hỗn hợp sền sệch. Tiếp theo rửa sạch da qua một lần cho tr&ocirc;i đi lớp bụi bẩn tr&ecirc;n bề mặt, sau đ&oacute; đắp hỗn hợp l&ecirc;n đều khắp cơ thể v&agrave; để thật kh&ocirc; trong v&ograve;ng 30 ph&uacute;t. Cuối c&ugrave;ng bạn tắm lại với nước lạnh v&agrave; thoa một lớp kem dưỡng ẩm tự nhi&ecirc;n v&agrave; thư gi&atilde;n. Đối với c&aacute;ch n&agrave;y bạn &aacute;p dụng được cho cả da mặt bởi t&iacute;nh dịu nhẹ của n&oacute;.</p>\n\n<p data-mce-style=\"text-align: justify;\">Lưu &yacute; nhỏ l&agrave; d&ugrave; d&ugrave;ng phương thức n&agrave;o cũng phải bảo vệ da tối đa với kem chống nắng v&agrave; chăm chỉ chăm s&oacute;c da thường xuy&ecirc;n với c&aacute;c bước tẩy tế b&agrave;o chết cho da k&egrave;m theo nước hoa hồng hoặc x&ocirc;ng hơi thường xuy&ecirc;n vừa gi&uacute;p da khỏe vừa căng mịn.</p>\n', 'su-dung-cach-lam-trang-da-toan-than-tu-nhien-tai-nha-hieu-qua', '/Upload/News/9ec0da9b-5fcb-4153-abb8-c363d58b5481/kemlamtrangdatoanthantunhien.jpg	', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Sử dụng cách làm trắng da toàn thân tự nhiên tại nhà hiệu quả', 'Sử dụng cách làm trắng da toàn thân tự nhiên tại nhà là biện pháp an toàn ít tốn kém kinh phí. Đặc biệt bạn còn có thể tự pha chế theo ý thích của bản thân.', 'cách làm trắng da toàn thân tự nhiên', '44', 'PUBLISHED', '', 'old'),
(86, '3', 'Cách chăm sóc da mặt hằng ngày an toàn và khỏe da', '<p>C&aacute;ch chăm s&oacute;c da mặt hằng ng&agrave;y&nbsp;rất quan trọng đối với việc c&oacute; hay kh&ocirc;ng một l&agrave;n rạng rỡ sạch mụn, trắng s&aacute;ng như &yacute;.</p>\n', '<p data-mce-style=\"text-align: justify;\">C&aacute;ch chăm s&oacute;c da mặt hằng ng&agrave;y&nbsp;rất quan trọng đối với việc c&oacute; hay kh&ocirc;ng một l&agrave;n rạng rỡ sạch mụn, trắng s&aacute;ng như &yacute;. Ch&iacute;nh v&igrave; thế, bạn h&atilde;y c&ugrave;ng Svan Clinic &amp; Spa điểm qua những c&aacute;ch chăm s&oacute;c da mặt hằng ng&agrave;y hiệu quả nha.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Rửa mặt</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"e1ee430452ede58d7faebdac2a4a6244dbb5266b\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/e1ee430452ede58d7faebdac2a4a6244dbb5266b.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/e1ee430452ede58d7faebdac2a4a6244dbb5266b.jpg\" style=\"width: 500px; height: 357px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Rửa mặt l&agrave; c&aacute;ch chăm s&oacute;c da mặt hằng ng&agrave;y thiết yếu v&igrave; sau một ng&agrave;y d&agrave;i l&agrave;m việc da của bạn đang ở t&igrave;nh trạng b&aacute;m bụi, vi khuẩn g&acirc;y hại cho da. L&uacute;c n&agrave;y bước rửa mặt cực k&igrave; quan trọng, việc rửa mặt đ&ugrave;ng kh&ocirc;ng chỉ l&agrave;m l&agrave; sạch mụn m&agrave; c&ograve;n khỏe khoắn, phục hồi đồ căng mịn của l&agrave;n da. Bạn n&ecirc;n rửa mặt với một số loại sữa rửa mặt dịu nhẹ v&agrave; hạn chế t&iacute;nh kiềm đối với những người da kh&ocirc;. Khi rửa mặt cũng cần massage kết hợp l&agrave;m lưu th&ocirc;ng c&aacute;ch mạch m&aacute;u tr&ecirc;n mặt để ch&uacute;ng lưu th&ocirc;ng tốt để th&uacute;c đẩy qu&aacute; tr&igrave;nh tăng sinh Collagen. Bạn chỉ cần rửa mặt hai lần mỗi ng&agrave;y v&agrave;o buổi s&aacute;ng v&agrave; buổi tối l&agrave; đủ, tr&aacute;nh rửa mặt qu&aacute; nhiều lần sẽ l&agrave;m lỗ ch&acirc;n l&ocirc;ng to v&agrave; b&agrave;o m&ograve;n da.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Nước hoa hồng</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"41e685d06627d954146a9be49964928915e4ef0f\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/41e685d06627d954146a9be49964928915e4ef0f.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/41e685d06627d954146a9be49964928915e4ef0f.jpg\" style=\"width: 500px; height: 421px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Đ&acirc;y l&agrave; c&aacute;ch chăm s&oacute;c da mặt hằng ng&agrave;y chuy&ecirc;n dụng được ph&aacute;i đẹp đ&aacute;nh gi&aacute; tốt. Nước hoa hồng kh&ocirc;ng chỉ l&agrave;m m&aacute;t da m&agrave; c&ograve;n se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng v&agrave; s&aacute;t khuẩn tr&aacute;nh được vi&ecirc;c h&igrave;nh th&agrave;nh mụn. C&oacute; rất nhiều loại nước hoa hồng hiện nay, t&ugrave;y v&agrave;o t&igrave;nh trạng da của bạn m&agrave; n&ecirc;n lựa chọn những loại nước hoa hồng ph&ugrave; hợp. Nếu bạn si&ecirc;ng hơn một ch&uacute;t c&oacute; thể tự chưng cất nước hoa hồng để d&agrave;nh s&agrave;i sẽ tốt hơn v&igrave; kh&ocirc;ng c&oacute; h&oacute;a chất g&igrave; nhiều.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Kem dưỡng da</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"fe84483aaf76cc81c0ed8100b53e611f84710642\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/fe84483aaf76cc81c0ed8100b53e611f84710642.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/fe84483aaf76cc81c0ed8100b53e611f84710642.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Nhiều bạn cho rằng da m&igrave;nh đ&atilde; đẹp rồi n&ecirc;n kh&ocirc;ng cần d&ugrave;ng kem dưỡng da l&agrave; sai lầm nha. Thực ra d&ugrave; da bạn đ&atilde; đẹp hay chưa đẹp th&igrave; cũng cần phải d&ugrave;ng một &iacute;t kem dưỡng v&agrave;o buổi tối trước khi đi ngủ. V&igrave; l&uacute;c n&agrave;y da bạn đang ở trạng th&aacute;i thư gi&atilde;n v&agrave; hấp thu dinh dưỡng tốt nhất. B&ecirc;n cạnh đ&oacute;, kem dưỡng c&acirc;n bằng độ ẩm tốt nhất cho da bởi vậy s&aacute;ng h&ocirc;m sau bạn sẽ thấy da m&igrave;nh mịn m&agrave;ng v&agrave; tươi trẻ hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\"><strong>Massage mặt</strong></p>\n\n<p style=\"text-align: center;\"><img alt=\"57afef0efeeaa2dbb7b80c80680932e382109988\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/57afef0efeeaa2dbb7b80c80680932e382109988.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/57afef0efeeaa2dbb7b80c80680932e382109988.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Khi b&ocirc;i kem dưỡng da bạn n&ecirc;n kết hợp lu&ocirc;n massage mặt với v&agrave;i bước cơ bản để tăng t&iacute;nh đ&agrave;n hồi cho l&agrave;n da. N&oacute; c&ograve;n c&oacute; t&aacute;c d&ugrave;ng l&agrave;m lỗ ch&acirc;n l&ocirc;ng thu nhỏ, loại bỏ c&aacute;c chất bụi c&ograve;n s&oacute;t lại b&ecirc;n dưới lỗ ch&acirc;n l&ocirc;ng. Bạn chia đều mặt th&agrave;nh 2 phần v&agrave; massage nhẹ nh&agrave;ng để bảo đảm tinh chất sẽ thấm đều v&agrave; độ phục hồi l&agrave;n da sẽ nhanh hơn.</p>\n\n<p data-mce-style=\"text-align: justify;\">Ngo&agrave;i những c&aacute;ch chăm s&oacute;c da mặt đơn giản như thế n&agrave;y bạn cũng n&ecirc;n lưu &yacute; bổ sung thực phẩm c&oacute; lợi cho l&agrave;n da. Hạn chế suy nghĩ, thức đ&ecirc;m hoặc ăn qu&aacute; nhiều c&aacute;c chất b&eacute;o, cay, n&oacute;ng kh&ocirc;ng tốt cho sức khỏe. V&agrave; nhớ b&ocirc;i kem ch&oacute;ng nắng trước 30 ph&uacute;t khi ra khỏi nh&agrave; để bảo vệ tối đa l&agrave;n da của bạn.</p>\n', 'cach-cham-soc-da-mat-hang-ngay-an-toan-va-khoe-da', '/Upload/News/779e72e2-34d3-48cd-9423-9315d4b34ec3/57afef0efeeaa2dbb7b80c80680932e382109988.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách chăm sóc da mặt hằng ngày an toàn và khỏe da', 'Cách chăm sóc da mặt hằng ngày rất quan trọng trong việc có hay không một làn da đẹp, sạch mụn và lỗ chân lông thu nhỏ. Áp dụng đúng và đủ ngay nhé bạn!', 'Cách chăm sóc da mặt hằng ngày', '44', 'PUBLISHED', '', 'old'),
(87, '3', 'Triệt lông tại nhà hoàn toàn hiệu quả và an toàn', '<p>Bạn đ&atilde; bao giờ nghĩ đến phương ph&aacute;p&nbsp;triệt l&ocirc;ng tại nh&agrave;&nbsp;chưa? C&aacute;ch thức l&agrave;m rất dễ v&agrave; hiệu quả mang lại th&igrave; tr&ecirc;n cả tuyệt vời.&nbsp;</p>\n', '<p data-mce-style=\"text-align: justify;\">Bạn đ&atilde; bao giờ nghĩ đến phương ph&aacute;p&nbsp;triệt l&ocirc;ng tại nh&agrave;&nbsp;chưa? C&aacute;ch thức l&agrave;m rất dễ v&agrave; hiệu quả mang lại th&igrave; tr&ecirc;n cả tuyệt vời. C&ograve;n chờ g&igrave; nữa nhanh tay thực hiện để đuổi sạch đ&aacute;m vi-&ocirc;-l&ocirc;ng kh&oacute; chịu tr&ecirc;n cơ thể nha.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"waxing8\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/waxing8.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/waxing8.jpg\" style=\"width: 500px; height: 291px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">DẦU DỪA</p>\n\n<p data-mce-style=\"text-align: justify;\">Trước giờ ch&uacute;ng ta hay nghe nhiều mẹo d&ugrave;ng dầu dừa l&agrave;m đẹp da, d&agrave;i mi, d&agrave;i t&oacute;c. H&ocirc;m nay Svan m&aacute;ch bạn mẹo d&ugrave;ng dầu dừa triệt sạch l&ocirc;ng tại nh&agrave; rất hay v&agrave; an to&agrave;n.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/Dau-dua-Lava-nguyen-chat-100-khong-hoa-chat-.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/Dau-dua-Lava-nguyen-chat-100-khong-hoa-chat-.jpg\" style=\"width: 500px; height: 375px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn cần chuẩn bị một lượng dầu dừa vừa đủ cho v&agrave;o trong một c&aacute;i b&aacute;t nhỏ. Tiếp theo rửa sạch v&ugrave;ng l&ocirc;ng cần triệt, lau kh&ocirc; rồi b&ocirc;i dầu dừa l&ecirc;n ngược theo chiều l&ocirc;ng mọc. Vừa thoa vừa massage nhẹ nh&agrave;ng đến khi dầu dừa kh&ocirc; v&agrave; ngấm v&agrave;o b&ecirc;n trong th&igrave; th&ocirc;i. Bạn d&ugrave;ng khăn mỏng lau qua rồi c&oacute; thể d&ugrave;ng nhiếp nhổ nhẹ l&agrave; l&ocirc;ng đi ra theo. Sau tất cả những bước n&agrave;y bạn l&agrave;m sạch lại v&ugrave;ng vừa tẩy l&ocirc;ng với nước lạnh v&agrave; b&ocirc;i ngay một lớp tinh chất hoa hồng để tr&aacute;nh t&igrave;nh trạng th&acirc;m lỗ ch&acirc;n l&ocirc;ng.</p>\n\n<p data-mce-style=\"text-align: justify;\">SỮA CHUA</p>\n\n<p data-mce-style=\"text-align: justify;\">Ngo&agrave;i c&ocirc;ng dụng l&agrave;m đẹp da, nghệ tươi v&agrave; tinh bột nghệ c&ograve;n đem lại hiệu quả cao trong điều trị triệt l&ocirc;ng tại nh&agrave;. Nghệ c&oacute; chứa nhiều chất dinh dưỡng gi&uacute;p kh&aacute;ng vi&ecirc;m, l&agrave;m l&agrave;nh vết thương v&agrave; l&agrave;m nang l&ocirc;ng tự ch&iacute;n rồi rụng đi.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"nghe-suachua\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/nghe-suachua.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/nghe-suachua.jpg\" style=\"width: 500px; height: 300px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Bạn cho một &iacute;t tinh bột nghệ v&agrave;o trong b&aacute;t hoặc c&oacute; thể &eacute;p lấy nước cốt thoa trực tiếp l&ecirc;n v&ugrave;ng cần triệt l&ocirc;ng cũng tốt. Tiếp theo bạn cho th&ecirc;m 1 hũ sữa chua v&agrave;o v&agrave; trộn đều hỗn hợp l&ecirc;n. Bạn l&agrave;m sạch v&ugrave;ng da cần điều trị triệt l&ocirc;ng rồi d&ugrave;ng que gỗ thoa hỗn hợp l&ecirc;n đều khắp v&ugrave;ng triệt l&ocirc;ng sau đ&oacute; d&ugrave;ng 1 miếng vải mỏng d&aacute;n chồng l&ecirc;n tr&ecirc;n. Đợi đến khi hỗn hợp thật l&agrave; kh&ocirc; th&igrave; giật nhẹ miếng vải ngược theo chiều l&ocirc;ng mọc. Tiếp theo sửa sạch v&ugrave;ng da vừa tẩy l&ocirc;ng với nước ấm v&agrave; một &iacute;t sữa tắm để da giảm bớt độ mẩn cảm. &Aacute;p dụng c&aacute;ch n&agrave;y 2 tuần/lần để đạt hiệu quả giảm l&ocirc;ng như mong muốn.</p>\n\n<p data-mce-style=\"text-align: justify;\">BẰNG CHUỐI</p>\n\n<p data-mce-style=\"text-align: justify;\">Một số th&agrave;nh phần dưỡng chất trong chuối rất tốt với da nhưng lại l&agrave; vũ kh&iacute; chống lại đ&aacute;m vi-&ocirc;-l&ocirc;ng xấu x&iacute; tr&ecirc;n cơ thể. Bạn chỉ cần v&agrave;i quả chuối ch&iacute;n, đem nghiền n&aacute;t trộn chung với 3 muỗng mật ong c&ugrave;ng một &iacute;t sữa chua l&agrave; c&oacute; hỗn hợp triệt l&ocirc;ng si&ecirc;u tốc.</p>\n\n<p data-mce-style=\"text-align: justify;\" style=\"text-align: center;\"><img alt=\"1_orpq\" data-mce-src=\"http://www.svanclinic.vn/wp-content/uploads/2016/09/1_ORPQ.jpg\" src=\"/files/Files/Kien-thuc-lam-dep/1_ORPQ.jpg\" style=\"width: 500px; height: 382px;\" /></p>\n\n<p data-mce-style=\"text-align: justify;\">Rửa sạch v&ugrave;ng cần triệt l&ocirc;ng rồi b&ocirc;i hết hỗn hợp l&ecirc;n để kh&ocirc; trong 30 - 40 ph&uacute;t cho đến khi tinh chất ngấm s&acirc;u v&agrave;o b&ecirc;n trong oxy ch&acirc;n l&ocirc;ng l&agrave;m ch&uacute;ng ch&iacute;n đi. V&igrave; thế sau khi hỗn hợp kh&ocirc; bạn d&ugrave;ng khăn ướt lau nhẹ đi nếu để &yacute; kĩ sẽ thấy rất nhiều nang l&ocirc;ng rụng ra theo. &Aacute;p dụng đều đặn 1 tuần/lần sẽ đem lại kết quả rất tốt v&agrave; ngạc nhi&ecirc;n cho nhiều người.</p>\n\n<p data-mce-style=\"text-align: justify;\">Lưu &yacute; :&nbsp;trong qu&aacute; tr&igrave;nh điều trị triệt l&ocirc;ng bạn n&ecirc;n hạn chế ma s&aacute;t qu&aacute; mạnh l&agrave;m trầy xướt v&ugrave;ng nang l&ocirc;ng g&acirc;y ra hiện tương sưng tấy kh&ocirc;ng tốt.</p>\n', 'triet-long-tai-nha-hoan-toan-hieu-qua-va-an-toan', '/Upload/News/a0ea6399-15de-4a1c-aec1-6b8082c98ecd/nghe-suachua.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Triệt lông vĩnh viễn tại nhà hoàn toàn hiệu quả và an toàn', 'Bạn đã bao giờ nghĩ đến phương pháp triệt lông vĩnh viễn tại nhà chưa? Cách thức làm rất dễ và hiệu quả mang lại thì trên cả tuyệt vời.', 'triệt lông vĩnh viễn tại nhà', '44', 'PUBLISHED', '', 'old'),
(88, '3', 'Cách chăm sóc da mặt tại nhà trắng mịn', '<p>Qu&aacute; tr&igrave;nh chăm s&oacute;c da mặt hằng ng&agrave;y tại nh&agrave; thường được mọi người rất quan t&acirc;m v&agrave; ch&uacute; trọng để bảo vệ l&agrave;n da khỏi c&aacute;c t&aacute;c hại đến từ b&ecirc;n ngo&agrave;i.</p>\n', '<p style=\"text-align: justify;\">Qu&aacute; tr&igrave;nh&nbsp;<em><strong>chăm s&oacute;c da mặt</strong></em> hằng ng&agrave;y tại nh&agrave;&nbsp;thường được mọi người rất quan t&acirc;m v&agrave; ch&uacute; trọng để bảo vệ l&agrave;n da khỏi c&aacute;c t&aacute;c hại đến từ b&ecirc;n ngo&agrave;i. Ch&iacute;nh v&igrave; thế h&ocirc;m nay ch&uacute;ng ta h&atilde;y c&ugrave;ng nhau t&igrave;m hiểu c&aacute;c c&aacute;ch đơn giản gi&uacute;p c&aacute;c bạn chăm s&oacute;c được l&agrave;n da của m&igrave;nh một c&aacute;ch hiệu quả</p>\n\n<p style=\"text-align: justify;\">B&agrave;i viết dưới đ&acirc;y, sẽ hướng dẫn những b&iacute; quyết nhỏ bổ sung trong kế hoạch dưỡng trắng da mịn của c&aacute;c bạn.</p>\n\n<p style=\"text-align: justify;\"><strong>Bước 1: L&agrave;m sạch da mặt</strong></p>\n\n<p style=\"text-align: justify;\">Đ&acirc;y được coi l&agrave; bước chăm s&oacute;c quan trọng đầu ti&ecirc;n m&agrave; bạn kh&ocirc;ng thể bỏ qua.</p>\n\n<p style=\"text-align: justify;\">- Rửa mặt thường xuy&ecirc;n 2-3 v&agrave;o mỗi s&aacute;ng thức dậy v&agrave; trước khi đi ngủ. Điều n&agrave;y sẽ gi&uacute;p bạn c&oacute; một lan da sạch sẽ, những tế b&agrave;o chết, bụi bẩn đ&atilde; được tho&aacute;t ra ngo&agrave;i. Đồng thời lỗ ch&acirc;n l&ocirc;ng th&ocirc;ng tho&aacute;ng, gi&uacute;p da mặt dễ hấp thu những dưỡng chất trong kem dưỡng hơn.</p>\n\n<p style=\"text-align: justify;\">- Quy tắc kh&ocirc;ng bao giờ được qu&ecirc;n đ&oacute; l&agrave; phải tẩy trang sạch sẽ sau mỗi lần trang điểm, kể cả kem chống nắng tr&aacute;nh g&acirc;y t&igrave;nh trạng ứ tắc lỗ ch&acirc;n l&ocirc;ng v&agrave; x&acirc;m nhập của c&aacute;c chất g&acirc;y hại.</p>\n\n<p style=\"text-align: justify;\">- Lựa chọn c&aacute;c loại sữa rửa mặt, kem dưỡng da hay loại dung dịch b&ocirc;i trực tiếp l&ecirc;n da đều phải t&igrave;m kiểu kỹ c&aacute;c th&agrave;nh phần v&agrave; độ t&iacute;ch hợp với l&agrave;n da của bạn. Lựa chọn cẩn thận sẽ gi&uacute;p bạn đạt được hiệu quả tốt hơn đồng thời kh&ocirc;ng bị k&iacute;ch ứng da đối với c&aacute;c chất c&oacute; trong th&agrave;nh phần sản phẩm kh&aacute;c nhau.</p>\n\n<p style=\"text-align: justify;\">- Cần lưu &yacute; n&ecirc;n thoa nước hoa hồng ngay sau khi rửa mặt để gi&uacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, chăm s&oacute;c da mặt hiệu quả, gi&uacute;p da bạn từng bước cải thiện độ trắng s&aacute;ng v&agrave; mịn m&agrave;ng.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/nhung-luu-y-giam-mun-cam-trong-mua-he-cho-ban-gai2.jpg\" style=\"width: 500px; height: 330px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>Bước 2: Tẩy tế b&agrave;o da chết</strong></p>\n\n<p style=\"text-align: justify;\">Đ&acirc;y l&agrave; một th&oacute;i quen kh&ocirc;ng phải chị em n&agrave;o cũng l&agrave;m theo, bạn c&oacute; biết đ&acirc;y l&agrave; một bước chăm s&oacute;c da cực kỳ quan trọng, bởi ch&iacute;nh v&igrave; c&ocirc;ng đoạn n&agrave;y sẽ gi&uacute;p loại bỏ lớp sừng th&ecirc;m đen b&ecirc;n ngo&agrave;i, l&agrave;m th&ocirc;ng tho&aacute;ng v&agrave; se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, gi&uacute;p da hấp thụ tối đa kem dưỡng.</p>\n\n<p style=\"text-align: justify;\">Tr&ecirc;n thị trường c&oacute; rất nhiều loại muổi tẩy tế b&agrave;o chết ph&ugrave; hợp với từng loại da mặt. Hoặc bạn c&oacute; thể tận dụng những nguy&ecirc;n liệu tự nhi&ecirc;n để chăm s&oacute;c da tại nh&agrave; như: Vắt nước cốt chanh sau đ&oacute; thoa một &iacute;t đều l&ecirc;n v&ugrave;ng bạn muốn tẩy, massage nhẹ nh&agrave;ng khoảng 10 ph&uacute;t sau đ&oacute; rửa mặt bằng nước sạch. Tuy nhi&ecirc;n, với c&aacute;ch l&agrave;m n&agrave;y, bạn chỉ n&ecirc;n &aacute;p dụng 2-3 lần/1 th&aacute;ng.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/tac-dung-cua-viec-tay-te-bao-chet.jpg\" style=\"width: 500px; height: 344px;\" /></p>\n\n<p style=\"text-align: justify;\"><strong>Bước 3: Thoa kem chống năng mỗi khi ra ngo&agrave;i đường</strong></p>\n\n<p style=\"text-align: justify;\">Kem chống nắng được coi l&agrave; mặt nạ bảo vệ, chăm s&oacute;c da mặt c&oacute; t&aacute;c dụng tuyệt vời gi&uacute;p bạn chống lại c&aacute;c tia tử ngoại, những loại tia &aacute;nh s&aacute;ng nguy hại của mặt trời. Th&oacute;i quen b&ocirc;i kem chống nắng đều đặn trước khi ra ngo&agrave;i v&agrave; cả những ng&agrave;y mưa bạn cũng kh&ocirc;ng n&ecirc;n bỏ qua để hạn chế nguy&ecirc;n nh&acirc;n khiến da bạn trở n&ecirc;n nhăn nheo, sạm đen, xuất hiện c&aacute;c vết n&aacute;m, t&agrave;n nhang, ....</p>\n\n<p style=\"text-align: justify;\">Lựa chọn loại kem chống nắng ph&ugrave; hợp với từng loại da mặt:</p>\n\n<p style=\"text-align: justify;\">- Đối với da kh&ocirc;:&nbsp;n&ecirc;n chọn kem chống nắng c&oacute; c&aacute;c chất dưỡng ẩm, kh&ocirc;ng thấm nước v&agrave; SPF cũng kh&ocirc;ng cần phải qu&aacute; cao.</p>\n\n<p style=\"text-align: justify;\">- Đối với da nhờn:&nbsp;loại n&agrave;y dễ bị mụn v&igrave; vậy việc&nbsp;chăm s&oacute;c da mặt&nbsp;nhờn cũng rất kh&oacute;, bạn n&ecirc;n chọn dạng gel hoặc dạng xịt v&agrave; chọn kem c&oacute; th&agrave;nh phần, ghi ch&uacute;&nbsp;&ldquo;No sebum&rdquo;&nbsp;hoặc&nbsp;&ldquo;Oil Free&rdquo;&nbsp;tức l&agrave; kh&ocirc;ng g&acirc;y nhờn, kh&ocirc;ng dầu rất tốt cho bạn.</p>\n\n<p style=\"text-align: justify;\">- Da hỗn hợp:&nbsp;rất kh&oacute; lựa chọn loại kem chăm s&oacute;c da mặt, chống nắng ph&ugrave; hợp, tốt nhất bạn n&ecirc;n chọn loại kem chống nắng c&oacute; c&ocirc;ng thức tổng hợp.</p>\n\n<p style=\"text-align: justify;\"><strong>Bước 4: Điều chỉnh th&oacute;i quen sinh hoạt h&agrave;ng ng&agrave;y</strong></p>\n\n<p style=\"text-align: justify;\">Ngo&agrave;i những b&iacute; quyết chia sẻ để chăm s&oacute;c da mặt h&agrave;ng ng&agrave;y, bạn cũng n&ecirc;n thay đổi điều chỉnh một chế độ sinh hoạt ph&ugrave; hợp.</p>\n\n<p style=\"text-align: justify;\">- Ngủ đủ giấc 8 tiếng mỗi ng&agrave;y l&agrave; c&aacute;ch chăm s&oacute;c da mặt tốt nhất, kh&ocirc;ng n&ecirc;n thức khuya v&igrave; n&oacute; sẽ l&agrave;m rối loạn sự tảo đổi chất của da, nhất l&agrave; da mặt khiến da trở n&ecirc;n kh&ocirc;, sần s&ugrave;i, thiếu sinh kh&iacute; l&uacute;c n&agrave;o cũng nhợt nhạt,&hellip;</p>\n\n<p style=\"text-align: justify;\">- Hạn chế việc sử dụng c&aacute;c thiết bị điện tử như: điện thoại, m&aacute;y t&iacute;nh,&hellip;li&ecirc;n tục trong ng&agrave;y. Bởi việc tiếp x&uacute;c n&agrave;y khiến lỗ ch&acirc;n l&ocirc;ng to ra, trong xấu x&iacute; v&agrave; dễ bị mụn hơn. Kh&ocirc;ng những thế, n&oacute; c&ograve;n g&acirc;y k&iacute;ch th&iacute;ch đối với bộ n&atilde;o khiến ch&uacute;ng ta dễ bị đau đầu, dễ bị kh&ocirc; mắt,&hellip;</p>\n\n<p style=\"text-align: justify;\">- Kh&ocirc;ng n&ecirc;n v&agrave; hạn chế tối đa việc sử dụng c&aacute;c chất k&iacute;ch th&iacute;ch v&igrave; n&oacute; k&iacute;ch th&iacute;ch tr&iacute; n&atilde;o cũng như l&agrave;n da phải l&agrave;m việc, đồng thời nếu sử dụng thường xuy&ecirc;n ch&uacute;ng l&agrave;n da của bạn sẽ nhanh ch&oacute;ng bị l&atilde;o h&oacute;a hơn, l&agrave;n da đầy những đốm n&acirc;u xấu x&iacute;, cảm gi&aacute;c l&uacute;c n&agrave;o cũng x&aacute;m xịt...</p>\n\n<p style=\"text-align: justify;\">- Tập thể dục thể thao hằng ng&agrave;y kh&ocirc;ng những gi&uacute;p bạn c&oacute; thể lực tốt hơn, n&acirc;ng cao sức đề kh&aacute;ng m&agrave; c&ograve;n l&agrave;m cho qu&aacute; tr&igrave;nh trao đổi chất diễn ra tốt hơn, th&uacute;c đẩy chăm s&oacute;c da từ sau b&ecirc;n trong, tăng sức đ&agrave;n hồi cho l&agrave;n da khỏe hơn.</p>\n\n<p style=\"text-align: justify;\">- T&igrave;m một kh&ocirc;ng gian thư gi&atilde;n: Sau những ng&agrave;y l&agrave;m việc/ học h&agrave;nh căng thẳng mệt mỏi, bạn n&ecirc;n tim đến một nơi c&oacute; thể gi&uacute;p bạn cảm thấy thoải m&aacute;i, xả stress, hoặc ở nh&agrave; ch&uacute;ng ta sẽ c&oacute; những c&aacute;ch để &quot;h&oacute;a giải&quot; những &aacute;p lực bằng việc tự mở một b&agrave;i nhạc lắc lư theo n&oacute; hoặc tranh thủ l&uacute;c tắm vận động một v&agrave;i thao t&aacute;c nhẹ nh&agrave;ng, gi&uacute;p điều h&ograve;a kh&iacute; huyết đồng thời cải thiện tinh thần phấn chấn, vui vẻ hơn.</p>\n\n<p style=\"text-align: justify;\">- Ăn đủ chất, bổ sung c&aacute;c dưỡng chất, Vitamin, Ăn nhiều rau xanh v&agrave; tr&aacute;i c&acirc;y, uống sữa hằng ng&agrave;y,&hellip;C&aacute;c bạn cũng c&oacute; thể d&ugrave;ng c&aacute;c loại tr&aacute;i c&acirc;y n&agrave;y (c&agrave; chua, đu đủ, c&agrave; rốt,&hellip;) th&agrave;nh hỗn hợp l&agrave;m mặt nạ để đắp l&ecirc;n mặt, đảm bảo sẽ v&ocirc; c&ugrave;ng hiệu quả đấy.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/files/Files/Kien-thuc-lam-dep/an-trai-cay-sau-bua-com.jpg\" style=\"width: 500px; height: 333px;\" /></p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<p style=\"text-align: justify;\">Tr&ecirc;n đ&acirc;y l&agrave; những b&iacute; quyết vừa&nbsp;trắng da tại nh&agrave;&nbsp;vừa gi&uacute;p bạn sở hữu một l&agrave;n da l&aacute;ng mịn v&agrave; khỏe mạnh. Để đạt được hiệu quả cao nhất bạn n&ecirc;n ki&ecirc;n tr&igrave; v&agrave; tạo th&oacute;i quen mới, kết hợp tồng hợp tất cả c&aacute;c c&aacute;ch chăm s&oacute;c da mặt &nbsp;tại nh&agrave; tr&ecirc;n.</p>\n\n<p style=\"text-align: justify;\">Ch&uacute;c c&aacute;c chị em sẽ sở hữu một l&agrave;n da trắng mịn nh&eacute;!</p>\n', 'cach-cham-soc-da-mat-tai-nha-trang-min', '/Upload/News/987f95a0-1620-499b-94c9-f9cd73468a06/hinh-anh.jpg', '2018-04-25 18:09:14', '2018-04-25 18:09:14', 'Cách chăm sóc da mặt tại nhà trắng mịn', 'Quá trình chăm sóc da mặt hằng ngày tại nhà thường được mọi người rất quan tâm và chú trọng để bảo vệ làn da khỏi các tác hại đến từ bên ngoài.', 'chăm sóc da mặt', '44', 'PUBLISHED', '', 'old');
